INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The position vector of the points which divides internally in the ratio 2 :3 the join of the points \\(2{\\bf{a}} - 3{\\bf{b}}\\) and \\(3{\\bf{a}} - 2{\\bf{b}},\\)is ','\(a\)\\(\\frac{{12}}{5}{\\bf{a}} +\\frac{{13}}{5}{\\bf{b}}\\)','\(b\)\\(\\frac{{12}}{5}{\\bf{a}} - \\frac{{13}}{5}{\\bf{b}}\\)','\(c\)\\(\\frac{3}{5}{\\bf{a}} - \\frac{2}{5}{\\bf{b}}\\)','\(d\)None of these','2','VECTOR ALGEBRA','2','Answer = \(b\)Position vectors of the points which divides internally is,\\(\\frac{{3(2{\\bf{a}} - 3{\\bf{b}}) + 2(3{\\bf{a}} - 2{\\bf{b}})}}{5} =\\frac{{12{\\bf{a}} - 13{\\bf{b}}}}{5}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If position vector of points A, B, C are respectively i, j, k and \\(AB =CX,\\) then position vector of point X is','\(a\)\\( - \\,{\\bf{i}} + {\\bf{j}} + {\\bf{k}}\\)','\(b\)\\({\\bf{i}} - {\\bf{j}} + {\\bf{k}}\\)','\(c\)\\({\\bf{i}} + {\\bf{j}} - {\\bf{k}}\\)','\(d\)\\({\\bf{i}} + {\\bf{j}} + {\\bf{k}}\\)','1','VECTOR ALGEBRA','2','Answer = \(a\)\\(\\overrightarrow {AB}  = \\overrightarrow {CX}  \\Rightarrow {\\bf{j}} -{\\bf{i}} = {\\rm{P}}{\\rm{.V}}{\\rm{.}}\\,{\\rm{of}}\\,X - {\\bf{k}} \\Rightarrow{\\rm{P}}{\\rm{.V}}{\\rm{.}}\\,{\\rm{of}}\\,X =  - {\\bf{i}} + {\\bf{j}} + {\\bf{k}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If a and b are P.V. of two points A and B and C divides AB in ratio 2 : 1,then P.V. of C is ','\(a\)\\(\\frac{{{\\bf{a}} + 2{\\bf{b}}}}{3}\\)','\(b\)\\(\\frac{{2{\\bf{a}} + {\\bf{b}}}}{3}\\)','\(c\)\\(\\frac{{{\\bf{a}} + 2}}{3}\\)','\(d\)\\(\\frac{{{\\bf{a}} + {\\bf{b}}}}{2}\\)','1','VECTOR ALGEBRA','2','Answer = \(a\)It is obvious.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(A,\\,B,\\,C\\) are the vertices of a triangle whose position vectors area, b, c and G is the centroid of the \\(\\Delta ABC,\\) then \\(\\overrightarrow {GA} + \\overrightarrow {GB} \\, + \\overrightarrow {GC} \\) is ','\(a\)0','\(b\)\\(\\overrightarrow A  + \\overrightarrow B  + \\overrightarrow C \\)','\(c\)\\(\\frac{{{\\bf{a}} + {\\bf{b}} + {\\bf{c}}}}{3}\\)','\(d\)\\(\\frac{{{\\bf{a}} + {\\bf{b}}- {\\bf{c}}}}{3}\\)','1','VECTOR ALGEBRA','2','Answer = \(a\)Position vectors of vertices A, B and C of the triangle  ABC = a, b andc. We know that position vector of centroid of the triangle (G)=\\(\\frac{{{\\bf{a}} + {\\bf{b}} + {\\bf{c}}}}{3}\\).Therefore ,\\(\\overrightarrow {GA}  + \\overrightarrow {GB}  + \\overrightarrow{GC} \\)\\(= \\left( {{\\bf{a}} - \\frac{{{\\bf{a}} + {\\bf{b}} + {\\bf{c}}}}{3}} \\right) +\\left( {{\\bf{b}} - \\frac{{{\\bf{a}} + {\\bf{b}} + {\\bf{c}}}}{3}} \\right) + \\left({{\\bf{c}} - \\frac{{{\\bf{a}} + {\\bf{b}} + {\\bf{c}}}}{3}} \\right)\\)\\(= \\frac{1}{3}[2{\\bf{a}} - {\\bf{b}} - {\\bf{c}} + 2{\\bf{b}} - {\\bf{a}} - {\\bf{c}}+ 2{\\bf{c}} - {\\bf{a}} - {\\bf{b}}] = {\\bf{0}}.\\)',CURRENT_TIMESTAMP);
