INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({\\bf{p}} = {\\bf{i}} - 2{\\bf{j}} + 3{\\bf{k}}\\) and \\({\\bf{q}} =3{\\bf{i}} + {\\bf{j}} + 2{\\bf{k}},\\) then a vector along r which is linearcombination of p and q and also perpendicular to q is ','\(a\)\\({\\bf{i}} + 5{\\bf{j}} - 4{\\bf{k}}\\)','\(b\)\\({\\bf{i}} - 5{\\bf{j}} + 4{\\bf{k}}\\)','\(c\)\\( - \\frac{1}{2}\\,({\\bf{i}} + 5{\\bf{j}} - 4{\\bf{k}})\\)','\(d\)None of these','3','VECTOR ALGEBRA','2','Answer = \(c\)\\({\\bf{r}} = {\\bf{p}} + \\lambda \\,{\\bf{q}} \\Rightarrow{\\bf{r}}\\,.\\,{\\bf{q}} = {\\bf{p}}\\,.\\,{\\bf{q}} + \\lambda \\,{\\bf{q}}\\,.\\,{\\bf{q}}\\)\\(\\Rightarrow 0 = 7 + 14\\lambda \\,\\, \\Rightarrow \\lambda \\,\\, =  - \\frac{1}{2}\\)Therefore, \\({\\bf{r}} =  - \\frac{1}{2}({\\bf{i}} + 5{\\bf{j}} - 4{\\bf{k}}).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({\\bf{d}} = \\lambda \\,({\\bf{a}} \\times {\\bf{b}}) + \\mu \\,({\\bf{b}}\\times {\\bf{c}}) + \\nu \\,({\\bf{c}} \\times {\\bf{a}})\\)and\\([{\\bf{a}}\\,{\\bf{b}}\\,{\\bf{c}}] = \\frac{1}{8},\\) then \\(\\lambda  + \\mu  + \\nu \\) is equal to','\(a\)\\(8{\\bf{d}}\\,.\\,({\\bf{a}} + {\\bf{b}} + {\\bf{c}})\\)','\(b\)\\(8{\\bf{d}}\\, \\times\\,({\\bf{a}} + {\\bf{b}} + {\\bf{c}})\\)','\(c\)\\(\\frac{{{\\bf{d}}\\,}}{8}.\\,({\\bf{a}} + {\\bf{b}} +{\\bf{c}})\\)','\(d\)\\(\\frac{{{\\bf{d}}\\,}}{8} \\times \\,({\\bf{a}} + {\\bf{b}} +{\\bf{c}})\\)','1','VECTOR ALGEBRA','2','Answer = \(a\)\\({\\bf{d}}\\,.\\,{\\bf{c}} = \\lambda ({\\bf{a}} \\times {\\bf{b}})\\,.\\,{\\bf{c}}+ \\mu ({\\bf{b}} \\times {\\bf{c}})\\,.\\,{\\bf{c}} + \\nu ({\\bf{c}} \\times{\\bf{a}})\\,.{\\bf{c}}\\)\\(= \\lambda \\,[{\\bf{a}}\\,{\\bf{b}}\\,{\\bf{c}}] + 0 + 0 = \\lambda\\,[{\\bf{a}}\\,{\\bf{b}}\\,{\\bf{c}}] = \\frac{\\lambda }{8}\\)Hence \\(\\lambda  = 8({\\bf{d}}\\,.\\,{\\bf{c}}),\\) \\(\\mu  =8({\\bf{d}}\\,.\\,{\\bf{a}})\\) and \\(\\nu  = 8({\\bf{d}}\\,.\\,{\\bf{b}})\\)Therefore, \\(\\lambda  + \\mu  + \\nu  = 8{\\bf{d}}\\,.\\,{\\bf{c}} +8{\\bf{d}}.\\,{\\bf{a}} + 8{\\bf{d}}\\,.\\,{\\bf{b}}\\)\\(= 8{\\bf{d}}\\,.\\,({\\bf{a}} + {\\bf{b}} + {\\bf{c}}).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If a and b are mutually perpendicular vectors, then \\({({\\bf{a}} +{\\bf{b}})^2} = \\)','\(a\)\\({\\bf{a}}\\, \\bot \\,{\\bf{b}}\\)','\(b\)a \\(\\vert{}\\)\\(\\vert{}\\) b','\(c\)Angle between a and b is \\({60^o}\\)','\(d\)None of these','4','VECTOR ALGEBRA','2','Answer = \(d\)It is obvious, since \\({\\bf{a}}\\,.\\,{\\bf{b}} = 0.\\)Hence \\({({\\bf{a}} + {\\bf{b}})^2} = {a^2} + {b^2} = {({\\bf{a}} - {\\bf{b}})^2}.\\)',CURRENT_TIMESTAMP);
