INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The harmonic mean of 3, 7, 8, 10, 14 is','\(a\)\\(\\frac{{3 + 7 + 8 + 10 + 14}}{5}\\)','\(b\)\\(\\frac{1}{3} + \\frac{1}{7} +\\frac{1}{8} + \\frac{1}{{10}} + \\frac{1}{{14}}\\)','\(c\)\\(\\frac{{\\frac{1}{3} + \\frac{1}{7} + \\frac{1}{8} + \\frac{1}{{10}} +\\frac{1}{{14}}}}{4}\\)','\(d\)\\(\\frac{5}{{\\frac{1}{3} + \\frac{1}{7} + \\frac{1}{8} +\\frac{1}{{10}} + \\frac{1}{{14}}}}\\)','4','MEASURERS OF CENTRAL TENDENCY DISPERSION','2','Answer = \(d\)\\({\\rm{H}}{\\rm{.M}}. = \\frac{n}{{\\frac{1}{{{x_1}}} + \\frac{1}{{{x_2}}} +..... + \\frac{1}{{{x_n}}}}} = \\frac{5}{{\\frac{1}{3} + \\frac{1}{7} + \\frac{1}{8} +\\frac{1}{{10}} + \\frac{1}{{14}}}}\\).',CURRENT_TIMESTAMP);
