INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The weighted mean of first n natural numbers whose weights are equal to thesquares of corresponding numbers is','\(a\)\\(\\frac{{n + 1}}{2}\\)','\(b\)\\(\\frac{{3n(n + 1)}}{{2(2n + 1)}}\\)','\(c\)\\(\\frac{{(n + 1)(2n + 1)}}{6}\\)','\(d\)\\(\\frac{{n(n + 1)}}{2}\\)','2','MEASURERS OF CENTRAL TENDENCY DISPERSION','2','Answer = \(b\)Weighted mean = \\(\\frac{{{{1.1}^2} + {{2.2}^2} + ...... +n.{n^2}}}{{{1^2} + {2^2} + ...... + {n^2}}}\\)\\( = \\frac{{\\Sigma {n^3}}}{{\\Sigma {n^2}}} = \\frac{{\\frac{{n(n +1)}}{2}\\,\\,\\frac{{n(n + 1)}}{2}}}{{\\frac{{n(n + 1)(2n + 1)}}{6}}}\\)\\(= \\frac{{3n(n + 1)}}{{2(2n + 1)}}.\\)',CURRENT_TIMESTAMP);
