INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(^{15}{C_{3r}}{ = ^{15}}{C_{r + 3}}\\), then the value of \\(r\\) is','\(a\)\\(\\frac{{n - r}}{r}\\)','\(b\)\\(\\frac{{n + r - 1}}{r}\\)','\(c\)\\(\\frac{{n - r + 1}}{r}\\)','\(d\)\\(\\frac{{n - r - 1}}{r}\\)','1','PERMUTATIONS AND COMBINATION','2','Answer = \(a\)\\(^{15}{C_{3r}}{ = ^{15}}{C_{r + 3}}{ \\Rightarrow ^{15}}{C_{15 - 3r}}{ =^{15}}{C_{r + 3}}\\)\\(\\Rightarrow 15 - 3r = r + 3 \\Rightarrow r = 3.\\)',CURRENT_TIMESTAMP);
