INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In an examination there are three multiple choice questions and eachquestion has 4 choices. Number of ways in which a student can fail to get allanswers correct, is','\(a\)11','\(b\)12','\(c\)27','\(d\)63','4','PERMUTATIONS AND COMBINATION','2','Answer = \(d\)Each question can be answered in 4 ways and all questions can be answeredcorrectly in only one way, so required number of ways \\( = {4^3} - 1 = 63\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\alpha { = ^m}{C_2}\\), then \\(^\\alpha {C_2}\\)is equal to','\(a\)\\(^{m + 1}{C_4}\\)','\(b\)\\(^{m - 1}{C_4}\\)','\(c\)\\(3\\,.{\\;^{m + 2}}{C_4}\\)','\(d\)\\(3\\;.{\\;^{m + 1}}{C_4}\\)','4','PERMUTATIONS AND COMBINATION','2','Answer = \(d\)\\(\\alpha { = ^m}{C_2} \\Rightarrow \\alpha  = \\frac{{m(m - 1)}}{2}\\)\\(\\therefore \\)\\(^\\alpha C_2 = ^{m(m - 1)/2}C_2 = \\frac{1}{2}.\\frac{m(m - 1)}{2}\\left \\lbrace\\frac{m(m - 1)}{2} - 1 \\right \\rbrace\\)\\(= \\frac{1}{8}m(m - 1)(m - 2)(m + 1)\\)\\(= \\frac{1}{8}(m + 1)\\;m(m - 1)(m - 2) = 3\\;.{\\;^{m + 1}}{C_4}\\)',CURRENT_TIMESTAMP);
