INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a city no two persons have identical set of teeth and there is no personwithout a tooth. Also no person has more than 32 teeth. If we disregard the shapeand size of tooth and consider only the positioning of the teeth, then themaximum population of the city is','\(a\)\\({2^{32}}\\)','\(b\)\\({(32)^2} - 1\\)','\(c\)\\({2^{32}} - 1\\)','\(d\)\\({2^{32 - 1}}\\)','3','PERMUTATIONS AND COMBINATION','2','Answer = \(c\)We have 32 places for teeth. For each place we have two choices eitherthere is a tooth or there is no tooth. Therefore the number of ways to fill upthese places is \\({2^{32}}\\). As there is no person without a tooth, the maximumpopulation is \\({2^{32}} - 1\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(^{2n}{C_2}{:^n}{C_2} = 9:2\\) and \\(^n{C_r} = 10\\), then \\(r = \\)','\(a\)1','\(b\)2','\(c\)4','\(d\)5','2','PERMUTATIONS AND COMBINATION','2','Answer = \(b\)\\(\\left( {\\frac{{(2n)\\;!}}{{2\\;!\\;(2n - 2)\\;!}}} \\right)\\,\\,2 = \\left({\\frac{{n\\;!}}{{2\\;!(n - 2)\\;!}}} \\right)\\,\\,9\\)\\(\\Rightarrow (2n)(2n - 1)2 = 9n(n - 1) \\Rightarrow n = 5\\)Now \\(^5{C_r} = 10 \\Rightarrow r = 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If  \\(^{10}{C_r}{ = ^{10}}{C_{r + 2}}\\), then  \\(^5{C_r}\\) equals ','\(a\)120','\(b\)10','\(c\)360','\(d\)5','4','PERMUTATIONS AND COMBINATION','2','Answer = \(d\)\\(^{10}{C_r}{ = ^{10}}{C_{r + 2}} \\Rightarrow r + r + 2 = 10 \\Rightarrowr = 4\\)\\(\\therefore \\)\\(^5{C_r}{ = ^5}{C_4} = \\frac{{5\\;!}}{{1\\;!\\;4\\;!}} = 5.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(^n{C_r} = 84,{\\;^n}{C_{r - 1}} = 36\\) and \\(^n{C_{r + 1}} = ), then\\(n\\) equals','\(a\)8','\(b\)9','\(c\)10','\(d\)5','2','PERMUTATIONS AND COMBINATION','2','Answer = \(b\)\\(\\frac{{n - r + 1}}{r} = \\frac{{84}}{{36}} = \\frac{7}{3}\\) and \\(\\frac{{n- r}}{{r + 1}} = \\frac{{Answer = }{{84}} = \\frac{3}{2}\\)\\(\\therefore \\)\\(\\frac{7}{3}r - 1 = n - r = \\frac{3}{2}(r + 1)\\)or \\(14r - 6 = 9r + 9\\)  or \\(r = 3\\).  So, \\(n = 9\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(^n{C_3} + {\\,^n}{C_4} > {\\,^{n + 1}}{C_3},\\)then','\(a\)\\(n > 6\\)','\(b\)\\(n > 7\\)','\(c\)\\(n < 6\\)','\(d\)None of these','1','PERMUTATIONS AND COMBINATION','2','Answer = \(a\)\\({}^n{C_3} + {}^n{C_4} > {}^{n + 1}{C_3}\\)\\(\\Rightarrow{}\\) \\({}^{n + 1}{C_4} > {}^{n + 1}{C_3}\\,\\,(\\,{}^n{C_r} +{}^n{C_{r + 1}} = {}^{n + 1}{C_{r + 1}})\\)\\(\\Rightarrow{}\\) \\(\\frac{{{}^{n + 1}{C_4}}}{{{}^{n + 1}{C_3}}} >1\\)\\(\\Rightarrow{}\\)  \\(\\frac{{n - 2}}{4} > 1\\) \\( \\Rightarrow n > 6\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Value of r for which \\(^{15}{C_{r + 3}} = {\\,^{15}}{C_{2r - 6}}\\) is ','\(a\)2','\(b\)4','\(c\)6','\(d\)- 9','3','PERMUTATIONS AND COMBINATION','2','Answer = \(c\)Either \\(r + 3 = 2r - 6\\)or \\(r + 3 + 2r - 6 = 15,\\,\\,({\\,^n}{C_r} = {\\,^n}{C_{n - r}})\\)\\(\\Rightarrow{}\\) \\(r = 9\\) or \\(r = 6\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(^{n + 1}{C_3} = 2{\\,^n}{C_2},\\) then n =','\(a\)\\(\\left( {\\begin{array}{*{20}{c}}n\\\\{r - 1}\\end{array}} \\right)\\)','\(b\)\\(\\left( {\\begin{array}{*{20}{c}}n\\\\r\\end{array}} \\right)\\)','\(c\)\\(\\left( {\\begin{array}{*{20}{c}}n\\\\{r + 1}\\end{array}} \\right)\\)','\(d\)\\(\\left( {\\begin{array}{*{20}{c}}{n + 1}\\\\{r + 1}\\end{array}} \\right)\\)','3','PERMUTATIONS AND COMBINATION','2','Answer = \(c\)\\(^{n + 1}{C_3} = 2\\,.{\\,^n}{C_2}\\)\\(\\Rightarrow{}\\) \\(\\frac{{(n + 1)!}}{{3!\\,.\\,(n - 2)!}} =2\\,.\\,\\frac{{n!}}{{2!.(n - 2)!}}\\) \\(\\Rightarrow{}\\) \\(\\frac{{n + 1}}{{3\\,.\\,2!}}= \\frac{2}{{2!}}\\)\\(\\Rightarrow{}\\) \\(n + 1 = 6\\,\\, \\Rightarrow \\,n = 5\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The least value of natural number n satisfying  \\(C(n,\\,5) + C(n,\\,6)\\,\\, >C(n + 1,\\,5)\\) is','\(a\)11','\(b\)10','\(c\)12','\(d\)13','1','PERMUTATIONS AND COMBINATION','2','Answer = \(a\)\\(^n{C_5}\\, + {\\,^n}{C_6}\\,\\, > \\,{\\,^{n + 1}}{C_5}\\) \\(\\Rightarrow{}\\)\\(^{n + 1}{C_6}\\,\\, > \\,{\\,^{n + 1}}{C_5}\\)\\(\\Rightarrow{}\\) \\(\\frac{{(n + 1)!}}{{6!\\,.\\,(n - 5)!}}\\,.\\,\\frac{{5!\\,.\\,(n -4)!}}{{(n + 1)!}}\\,\\, > \\,\\,1\\) \\(\\Rightarrow{}\\) \\(\\frac{{(n - 4)}}{6}\\,\\, >\\,\\,1\\)\\(\\Rightarrow{}\\) \\(n - 4\\,\\, > \\,\\,6\\,\\,\\,\\, \\Rightarrow \\,\\,\\,n\\,\\,\\, >\\,\\,10\\)Hence according to options n = 11.',CURRENT_TIMESTAMP);
