INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(^{43}{C_{r - 6}} = {\\,^{43}}{C_{3r + 1}},\\) then the value of r is','\(a\)12','\(b\)8','\(c\)6','\(d\)10','1','PERMUTATIONS AND COMBINATION','2','Answer = \(a\) Given, \\(^{43}{C_{r - 6}}\\, = {\\,^{43}}{C_{3r + 1}}\\)\\(\\Rightarrow{}\\) \\(r - 6 = 3r + 1\\) or \\(r - 6 + 3r + 1 = 43\\)\\(\\Rightarrow{}\\) \\(r =  - \\frac{7}{2}\\) (impossible) or \\(r = 12\\).',CURRENT_TIMESTAMP);
