INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let \\({T_n}\\) denote the number of triangles which can be formed using thevertices of a regular polygon of \\(n\\) sides. If \\({T_{n + 1}} - {T_n} = 21,\\)then \\(n\\) equals','\(a\)5','\(b\)7','\(c\)6','\(d\)4','2','PERMUTATIONS AND COMBINATION','2','Answer = \(b\)Clearly, \\(^n{C_3}\\, = {T_n}.\\)So, \\(^{n + 1}{C_3}\\, - {\\,^n}{C_3}\\, = 21\\) \\(\\Rightarrow{}\\)\\((\\,{\\,^n}{C_3}\\, + {\\,^n}{C_2}\\,)\\, - {\\,^n}{C_3}\\, = 21\\)\\(\\therefore{}\\) \\(^n{C_2}\\, = \\,21\\) or \\(n(n - 1) = 42 =7\\,.\\,6\\,\\,\\,\\,\\therefore \\,\\,\\,n = 7\\).',CURRENT_TIMESTAMP);
