INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','A polygon has 35 diagonals, then the number of its sides is','\(a\)8','\(b\)9','\(c\)10','\(d\)11','3','PERMUTATIONS AND COMBINATION','2','Answer = \(c\)Since \\(^n{C_2}\\, - \\,n\\, = 35\\) \\(\\Rightarrow{}\\)\\(\\frac{{n!}}{{2!\\,\\,(n - 2)!}} - n = 35\\)\\(\\Rightarrow{}\\) \\(n(n - 1) - 2n = 70\\) \\(\\Rightarrow{}\\) \\({n^2} - 3n = 70\\)\\(\\Rightarrow{}\\) \\({n^2} - 3n - 70 = 0\\) \\(\\Rightarrow{}\\) \\((n + 7)\\,\\,(n -10) = 0\\) \\(\\Rightarrow{}\\) \\(n\\,\\, = \\,\\,10\\).',CURRENT_TIMESTAMP);
