INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Number of ways of selection of 8 letters from 24 letters of which 8 are\\(a\\), 8 are \\(b\\) and the rest unlike, is given by','\(a\)\\({2^7}\\)','\(b\)\\(8\\;.\\;{2^8}\\)','\(c\)\\(10\\;.\\;{2^7}\\)','\(d\)None of these','3','PERMUTATIONS AND COMBINATION','2','Answer = \(c\)The number of selections = coefficient of \\({x^8}\\)in \\((1 + x + {x^2} +......... + {x^8})(1 + x + {x^2} + ...... + {x^8}).{(1 + x)^8}\\)= coefficient of\\({x^8}\\) in \\({\\frac{{(1 - {x^9})}}{{{{(1 - x)}^2}}}^2}{(1 + x)^8}\\)= coefficient of \\({x^8}\\) in \\({(1 + x)^8}{(1 - x)^{ - 2}}\\)= coefficient of \\({x^8}\\) in \\({(^8}{C_0}{ + ^8}{C_1}x{ + ^8}{C_2}{x^2} +....... + {\\,^8}{C_8}{x^8})\\)\\(\\times (1 + 2x + 3{x^2} + 4{x^3} + ...... + 9{x^8} + .....)\\)\\(= 9\\;.{\\;^8}{C_0} + 8\\;.{\\;^8}{C_1} + 7\\;.{\\;^8}{C_2} + ......... +1\\;.{\\;^8}{C_8}\\)\\( = {C_0} + 2{C_1} + 3{C_2} + ..... + 9{C_8}\\)\\([{C_r}{ = ^8}{C_r}]\\)Now \\({C_0}x + {C_1}{x^2} + ....... + {C_8}{x^9} = x{(1 + x)^8}\\)Differentiating with respect to \\(x\\), we get\\({C_0} + 2{C_1}x + 3{C_2}{x^2} + ...9{C_8}{x^8} = {(1 + x)^8} + 8x{(1 + x)^7}\\)Putting \\(x = 1,\\;\\)we get\\(= {2^8} + 8\\;.\\;{2^7} = {2^7}.(2 + 8) = 10\\;.\\;{2^7}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If\\(^n{P_4} = 30{\\,^n}{C_5}\\), then \\(n = \\)','\(a\)6','\(b\)7','\(c\)8','\(d\)9','3','PERMUTATIONS AND COMBINATION','2','Answer = \(c\)\\(\\frac{{^n{P_4}}}{{^n{C_5}}} = 30 \\Rightarrow \\frac{{n\\;!}}{{(n - 4)\\;!}}\\times \\frac{{5\\;!\\;.\\;(n - 5)\\;!}}{{n\\;!}} = 30\\)\\(\\Rightarrow (n - 4) = 4 \\Rightarrow n = 8.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The number of ordered triplets of positive integers which are solutions ofthe equation \\(x + y + z = ) is','\(a\)6005','\(b\)4851','\(c\)5081','\(d\)None of these','2','PERMUTATIONS AND COMBINATION','2','Answer = \(b\)The number of triplets of positive integers which are solutions of \\(x + y+ z = Answer = ).\\( = \\)coefficient of \\({x^{Answer = }\\) in \\({(x + {x^2} + {x^3} + .....)^3}\\)= coefficient of \\({x^{Answer = }\\) in \\({x^3}{(1 - x)^{ - 3}}\\)= coefficient of \\({x^{Answer = }\\) in\\({x^3}\\left( {1 + 3x + 6{x^2} + .... + \\frac{{(n + 1)(n + 2)}}{2}{x^n} + .....}\\right)\\)\\(= \\frac{{(97 + 1)(97 + 2)}}{2} = 49 \\times 99 = 4',CURRENT_TIMESTAMP);
