INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(^{n}{P_4} = 24.{\\,^n}{C_5},\\) then the value of n is','\(a\)10','\(b\)15','\(c\)9','\(d\)5','3','PERMUTATIONS AND COMBINATION','2','Answer = \(c\)Given, \\(^n{P_4} = 24\\,.{\\,^n}{C_5}.\\)Therefore,\\(n(n - 1)\\,(n - 2)\\,(n - 3)\\)\\(= 24 \\times \\frac{{n(n - 1)\\,(n - 2)\\,(n - 3)\\,(n - 4)}}{{5.4.3.2.1}}\\)\\( \\Rightarrow \\)\\(1 = \\frac{{(n - 4)}}{5}\\)  \\(\\Rightarrow{}\\) \\(n - 4 = 5\\) \\(\\Rightarrow{}\\) \\(n = 9\\).',CURRENT_TIMESTAMP);
