INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a  single throw of two dice, the probability of getting more than 7 is','\(a\)\\(\\frac{7}{{36}}\\)','\(b\)\\(\\frac{7}{{12}}\\)','\(c\)\\(\\frac{5}{{12}}\\)','\(d\)\\(\\frac{5}{{36}}\\)','3','PROBABILITY','2','Answer = \(c\)Required probability is\\(P({\\rm{getting}}\\,8) + P(9) + P(10) + P(11) + P(12)\\)\\(= \\frac{5}{{36}} + \\frac{4}{{36}} + \\frac{3}{{36}} + \\frac{2}{{36}} +\\frac{1}{{36}} = \\frac{{15}}{{36}} = \\frac{5}{{12}}.\\)',CURRENT_TIMESTAMP);
