INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','There are n letters and n addressed envelopes. The probability that all theletters are not kept in the right envelope, is','\(a\)\\(\\frac{1}{{n\\,!}}\\)','\(b\)\\(1 - \\frac{1}{{n\\,!}}\\)','\(c\)\\(1 - \\frac{1}{n}\\)','\(d\)None of these','2','PROBABILITY','2','Answer = \(b\)Required probability is \\(1 - P\\) (All letters in right envelope) \\( = 1- \\frac{1}{{n\\,!}}\\){As there are total number of \\(n\\,!\\) ways in which letters can take envelopesand just one way in which they have corresponding envelopes}.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','From a book containing ages, one page is selected randomly. The probability that the sum of thedigits of the page number of the selected page is 11, is','\(a\)\\(\\frac{2}{{25}}\\)','\(b\)\\(\\frac{9}{{100}}\\)','\(c\)\\(\\frac{{11}}{{100}}\\)','\(d\)None of these','1','PROBABILITY','2','Answer = \(a\)Favourable ways \\(\\{ 29,\\,92,\\,38,\\,83,\\,47,\\,74,\\,56,\\,65\\} \\)Hence required probability \\( = \\frac{8}{{Answer = } = \\frac{2}{{25}}.\\)',CURRENT_TIMESTAMP);
