INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If a dice is thrown twice, then the probability of getting 1in the first throw only is','\(a\)\\(\\frac{1}{{36}}\\)','\(b\)\\(\\frac{3}{{36}}\\)','\(c\)\\(\\frac{5}{{36}}\\)','\(d\)\\(\\frac{1}{6}\\)','3','PROBABILITY','2','Answer = \(c\)Probability of getting 1 in first throw \\( = \\frac{1}{6}\\)Probability of not getting 1 in second throws \\( = \\frac{5}{6}\\)Both are independent events, so the required probability \\( = \\frac{1}{6} \\times\\frac{5}{6} = \\frac{5}{{36}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Two cards are drawn one by one at random from a pack of 52 cards. Theprobability that both of them are king, is','\(a\)\\(\\frac{2}{{13}}\\)','\(b\)\\(\\frac{1}{{169}}\\)','\(c\)\\(\\frac{1}{{221}}\\)','\(d\)\\(\\frac{{30}}{{221}}\\)','3','PROBABILITY','2','Answer = \(c\)Probability of first card to be a king\\( = \\frac{4}{{52}}\\)and probability of also second to be a king \\( = \\frac{3}{{51}}\\)Hence required probability \\( = \\frac{4}{{52}} \\times \\frac{3}{{51}} =\\frac{1}{{Answer = }\\).',CURRENT_TIMESTAMP);
