INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','From a pack of 52 cards two are drawn with replacement. The probability,that the first is a diamond and the second is a king, is','\(a\)\\(\\frac{1}{{26}}\\)','\(b\)\\(\\frac{{17}}{{2704}}\\)','\(c\)\\(\\frac{1}{{52}}\\)','\(d\)None of these','3','PROBABILITY','2','Answer = \(c\)Required probability \\( = P({\\rm{Diamond}})\\,.\\,P({\\rm{king}})\\)\\(= \\frac{{13}}{{52}}.\\frac{4}{{52}} = \\frac{1}{{52}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Two dice are thrown simultaneously. The probability of getting the sum 2 or8 or 12 is','\(a\)\\(\\frac{5}{{18}}\\)','\(b\)\\(\\frac{7}{{36}}\\)','\(c\)\\(\\frac{7}{{18}}\\)','\(d\)\\(\\frac{5}{{36}}\\)','2','PROBABILITY','2','Answer = \(b\)The sum 2 can be found in one way i.e. \\((1,\\,\\,1)\\)The sum 8 can be found in five ways i.e. \\((6,\\,\\,2),\\)\\((6,\\,\\,2),\\,(5,\\,\\,3),\\) \\(\\,(4,\\,\\,4),\\,(3,\\,\\,5),\\,(2,\\,\\,6)\\). Similarly thesum twelve can be found in one way i.e., \\((6,\\,\\,6).\\)Hence required probability \\( = \\frac{7}{{36}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','A dice is thrown twice. The probability of getting 4, 5 or 6 in the firstthrow and 1, 2, 3 or 4 in the second throw is','\(a\)1','\(b\)\\(\\frac{1}{3}\\)','\(c\)\\(\\frac{7}{{36}}\\)','\(d\)None of these','2','PROBABILITY','2','Answer = \(b\)Let \\(P\(a\)\\) and \\(P\(b\)\\) be the probability of the events then\\(P(A\\,\\,{\\rm{and}}\\,\\,B) = P\(a\)\\,.\\,P\(b\) = \\frac{1}{2} \\times \\frac{2}{3} =\\frac{1}{3}.\\)',CURRENT_TIMESTAMP);
