INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','A box contains 6 nails and 10nuts. Half of the nails and half of the nuts are rusted. If one item is chosenat random, what is the probability that it is rusted or is a nail','\(a\)\\(\\frac{3}{{16}}\\)','\(b\)\\(\\frac{5}{{16}}\\)','\(c\)\\(\\frac{{11}}{{16}}\\)','\(d\)\\(\\frac{{14}}{{16}}\\)','3','PROBABILITY','2','Answer = \(c\)Total rusted items \\( = 3 + 5 = 8\\,;\\) unrusted nails \\( = 3\\).\\(\\therefore \\) Required probability \\( = \\frac{{3 + 8}}{{6 + 10}} =\\frac{{11}}{{16}}.\\)',CURRENT_TIMESTAMP);
