INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Two dice are thrown simultaneously. What is the probability of obtaining amultiple of 2 on one of them and a multiple of 3 on the other','\(a\)\\(\\frac{5}{{36}}\\)','\(b\)\\(\\frac{{11}}{{36}}\\)','\(c\)\\(\\frac{1}{6}\\)','\(d\)\\(\\frac{1}{3}\\)','2','PROBABILITY','2','Answer = \(b\)Favourable cases for one are three i.e. 2, 4 and 6 and for other are twoi.e. 3, 6.Hence required probability \\( = \\left[ {\\left( {\\frac{{3 \\times 2}}{{36}}}\\right){\\rm{ }}2 - \\frac{1}{{36}}} \\right] = \\frac{{11}}{{36}}\\){As same way happen when dice changes numbers among themselves}',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','A problem of mathematics is given to three students whose chances of solvingthe problem are 1/3, 1/4 and 1/5 respectively. The probability that the question will be solved is                     ','\(a\)\\(\\frac{2}{3}\\)','\(b\)\\(\\frac{3}{4}\\)','\(c\)\\(\\frac{4}{5}\\)','\(d\)\\(\\frac{3}{5}\\)','4','PROBABILITY','2','Answer = \(d\)The probability of students not solving the problem are \\(1 - \\frac{1}{3}= \\frac{2}{3},\\) \\(1 - \\frac{1}{4} = \\frac{3}{4}\\) and \\(1 - \\frac{1}{5} =\\frac{4}{5}\\)Therefore the probability that the problem is not solved by any one of them \\( =\\frac{2}{3} \\times \\frac{3}{4} \\times \\frac{4}{5} = \\frac{2}{5}\\)Hence the probability that problem is solved\\( = 1 - \\frac{2}{5} =\\frac{3}{5}\\).',CURRENT_TIMESTAMP);
