INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','A card is drawn from a well shuffled pack of cards. The probability ofgetting a queen of club or king of heart is','\(a\)\\(\\frac{1}{{52}}\\)','\(b\)\\(\\frac{1}{{26}}\\)','\(c\)\\(\\frac{1}{{18}}\\)','\(d\)None of these','2','PROBABILITY','2','Answer = \(b\)The probability of card to be queen of club is \\(\\frac{1}{{52}}\\)  andalso probability of card to be a king of heart is \\(\\frac{1}{{52}}\\).Both are mutually exclusive events, hence the required probability \\( =\\frac{1}{{52}} + \\frac{1}{{52}} = \\frac{2}{{52}} = \\frac{1}{{26}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a simultaneous throw of three coins, what is the probability of getting atleast 2 tails','\(a\)\\(\\frac{1}{8}\\)','\(b\)\\(\\frac{1}{4}\\)','\(c\)\\(\\frac{1}{2}\\)','\(d\)None of these','3','PROBABILITY','2','Answer = \(c\)Total ways are 8 and favourable ways are 4\\(S = \\left\\{ {HHH,\\,HHT\\,.......TTT} \\right\\}.\\)Hence probability \\( = \\frac{4}{8} = \\frac{1}{2}.\\)',CURRENT_TIMESTAMP);
