INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Two dice are thrown together. If the numbers appearing on the two dice aredifferent, then what is the probability that the sum is 6','\(a\)\\(\\frac{5}{{36}}\\)','\(b\)\\(\\frac{1}{6}\\)','\(c\)\\(\\frac{2}{{15}}\\)','\(d\)None of these','3','PROBABILITY','2','Answer = \(c\)Obviously numbers will be \\(\\left( {\\begin{array}{*{20}{c}}{\\rm{I}}&{{\\rm{II}}}\\\\{5,}&1\\\\{4,}&2\\\\{2,}&4\\\\{1,}&5\\end{array}} \\right)\\). Hence required probability \\( = \\frac{4}{{6\\,\\,.\\,\\,5}}= \\frac{2}{{15}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','A man and a woman appear in an interview for two vacancies in the same post.The probability of man\\'s selection is 1/4 and that of the woman\\'s selection is 1/3. What is the probability that none of them will be selected','\(a\)\\(\\frac{1}{2}\\)','\(b\)\\(\\frac{1}{{12}}\\)','\(c\)\\(\\frac{1}{4}\\)','\(d\)None of these','1','PROBABILITY','2','Answer = \(a\)Let \\({E_1}\\) be the event that man will be selected and \\({E_2}\\) theevent that woman will be selected. Then\\(P({E_1}) = \\frac{1}{4}\\) so \\(P({\\bar E_1}) = 1 - \\frac{1}{4} = \\frac{3}{4}\\)and \\(P({E_2}) = \\frac{1}{3}\\)So \\(P({\\bar E_2}) = \\frac{2}{3}\\)Clearly \\({E_1}\\) and \\({E_2}\\) are independent events.So, \\(P({\\bar E_1} \\cap {\\bar E_2}) = P({\\bar E_1}) \\times P({\\bar E_2}) =\\frac{3}{4} \\times \\frac{2}{3} = \\frac{1}{2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Three fair coins are tossed. If both heads and tails appears, then theprobability that exactly one head appears, is','\(a\)\\(\\frac{3}{8}\\)','\(b\)\\(\\frac{1}{6}\\)','\(c\)\\(\\frac{1}{2}\\)','\(d\)\\(\\frac{1}{3}\\)','3','PROBABILITY','2','Answer = \(c\)Since both heads and tails appears, so\\(n(S) = \\{ HHT,\\,HTH,\\,THH,\\,HTT,\\,THT,\\,TTH\\}\\)\\(n(E) = \\{ HTT,\\,THT,\\,TTH\\}\\)Hence required probability \\( = \\frac{3}{6} = \\frac{1}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','A bag contains 4 white, 5 black and 6 red balls. If a ball is drawn atrandom, then what is the probability that the drawn ball is either white or red','\(a\)\\(\\frac{4}{{15}}\\)','\(b\)\\(\\frac{1}{2}\\)','\(c\)\\(\\frac{2}{5}\\)','\(d\)\\(\\frac{2}{3}\\)','4','PROBABILITY','2','Answer = \(d\)Probability for white ball \\(P(W) = \\frac{4}{{15}}\\)Probability for red ball \\(P(R) = \\frac{6}{{15}}\\)Probability (white or red ball) \\( = P(W) + P(R)\\)\\(= \\frac{4}{{15}} + \\frac{6}{{15}} = \\frac{{10}}{{15}} = \\frac{2}{3}.\\)',CURRENT_TIMESTAMP);
