INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Two dice are tossed. The probability that the total score is a prime numberis','\(a\)\\(\\frac{1}{6}\\)','\(b\)\\(\\frac{5}{{12}}\\)','\(c\)\\(\\frac{1}{2}\\)','\(d\)None of these','2','PROBABILITY','2','Answer = \(b\)Prime numbers are \\(\\{ 2,\\,3,\\,5,\\,7,\\,11\\} .\\)Hence required probability\\(= \\frac{{1 + 2 + 4 + 6 + 2}}{{36}} = \\frac{{15}}{{36}} = \\frac{5}{{12}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Three persons work independently on a problem. If the respectiveprobabilities that they will solve it are 1/3, 1/4 and 1/5, then the probabilitythat none can solve it','\(a\)\\(\\frac{2}{5}\\)','\(b\)\\(\\frac{3}{5}\\)','\(c\)\\(\\frac{1}{3}\\)','\(d\)None of these','1','PROBABILITY','2','Answer = \(a\)Required probability\\( = \\left( {1 - \\frac{1}{3}} \\right){\\rm{ }}\\left( {1 - \\frac{1}{4}}\\right){\\rm{ }}\\left( {1 - \\frac{1}{5}} \\right)\\)\\(= \\frac{2}{3}.\\frac{3}{4}.\\frac{4}{5} = \\frac{2}{5}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Two dice are thrown. The probability that the sum of the points on two dicewill be 7, is ','\(a\)\\(\\frac{5}{{36}}\\)','\(b\)\\(\\frac{6}{{36}}\\)','\(c\)\\(\\frac{7}{{36}}\\)','\(d\)\\(\\frac{8}{{36}}\\)','2','PROBABILITY','2','Answer = \(b\)Since favourable ways are 6. Total ways are 36.Hence probability = \\(\\frac{6}{{36}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The probability that an event will fail to happen is 0.05. The probabilitythat the event will take place on 4 consecutive occasions is ','\(a\)0.00000625','\(b\)0.18543125','\(c\)0.00001875','\(d\)0.81450625','4','PROBABILITY','2','Answer = \(d\)We have \\(P(\\bar A) = 0.05 \\Rightarrow P\(a\) = 0.95\\)Hence the probability that the event will take place in 4 consecutive occasions\\(= {\\{ P\(a\)\\} ^4} = {(0.95)^4} = 0.Answer = 0',CURRENT_TIMESTAMP);
