INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','A number is chosen at random from first ten natural numbers. The probabilitythat number is odd and perfect square is','\(a\)\\(\\frac{2}{9}\\)','\(b\)\\(\\frac{2}{5}\\)','\(c\)\\(\\frac{3}{7}\\)','\(d\)\\(\\frac{1}{5}\\)','4','PROBABILITY','2','Answer = \(d\)Odd and prefect square \\(( < 10)\\) are \\(1,\\,\\,9.\\)Hence required probability \\( = \\frac{2}{{10}} = \\frac{1}{5}.\\)',CURRENT_TIMESTAMP);
