INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let \\({S_1},\\;{S_2},.......\\)be squares such that for each \\(n \\ge 1\\), thelength of a side of \\({S_n}\\) equals the length of a diagonal of \\({S_{n + 1}}\\).If the length of a side of \\({S_1}\\)is\\(10cm\\), then for which of the followingvalues of \\(n\\) is the area of \\({S_n}\\) less then \\(1\\;sq\\;cm\\) ','\(a\)7','\(b\)8','\(c\)9','\(d\)10','(b,','PROGRESSIONS','2','Answer = (b,c,d) Given \\({x_n} = {x_{n + 1}}\\sqrt 2 \\)\\(\\therefore \\)\\({x_1} = {x_2}\\sqrt 2 ,\\;{x_2} = {x_3}\\sqrt 2 ,\\;{x_n} = {x_{n + 1}}\\sqrt 2\\)On multiplying \\({x_1} = {x_{n + 1}}{(\\sqrt 2 )^n}\\)\\( \\Rightarrow \\)\\({x_{n +1}} = \\frac{{{x_1}}}{{{{(\\sqrt 2 )}^n}}}\\)Hence \\({x_n} = \\frac{{{x_1}}}{{{{(\\sqrt 2 )}^{n - 1}}}}\\)Area of \\({S_n} = x_n^2 = \\frac{{x_1^2}}{{{2^{n - 1}}}} < 1\\)\\( \\Rightarrow\\)\\({2^{n - 1}} > x_1^2\\;\\;\\;\\;\\;\\;({x_1} = 10)\\)\\(\\therefore \\)\\({2^{n - 1}} > Answer = )But \\({2^7} > Answer = \\;{2^8} > Answer = \\;\\)etc.\\(\\therefore \\)\\(n - 1 = 7,\\;8,\\;9.....\\)\\( \\Rightarrow \\)\\(n = 8,\\;9,\\;10.....\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({S_1},\\;{S_2},\\;{S_3},...........{S_m}\\) are the sums of \\(n\\) terms of\\(m\\) A.P.\\'s whose first terms are \\(1,\\;2,\\;3,\\;...............,m\\) and commondifferences are \\(1,\\;3,\\;5,\\;...........2m - 1\\) respectively, then \\({S_1} +{S_2} + {S_3} + .......{S_m} = \\)','\(a\)\\(\\frac{1}{2}mn(mn + 1)\\)','\(b\)\\(mn(m + 1)\\)','\(c\)\\(\\frac{1}{4}mn(mn - 1)\\)','\(d\)None of the above','1','PROGRESSIONS','2','Answer = \(a\)Here \\(a = 1,\\;2,\\;3,\\,........,m;\\;\\;\\;d = 1,\\;3,\\;5,........,2m - 1\\)and \\(n = n\\), then \\({S_1} + {S_2} + ....... + {S_m} = \\frac{1}{2}mn(mn + 1)\\)\\(\\left[ {{\\rm{Using}}\\;S\\; = \\frac{m}{2}(a +l).\\;{\\rm{Since}}\\;{S_1},\\;{S_2},\\;{S_3},......{S_m}\\;{\\rm{form}}\\;{\\rm{an}}\\;{\\rm{A}}{\\rm{.P}}{\\rm{.}}}\\right]\\)',CURRENT_TIMESTAMP);
