INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({a_1},\\,{a_2},....,{a_{n + 1}}\\) are in A.P., then\\(\\frac{1}{{{a_1}{a_2}}} + \\frac{1}{{{a_2}{a_3}}} + ..... + \\frac{1}{{{a_n}{a_{n+ 1}}}}\\) is ','\(a\)\\(\\frac{{n - 1}}{{{a_1}{a_{n + 1}}}}\\)','\(b\)\\(\\frac{1}{{{a_1}{a_{n + 1}}}}\\)','\(c\)\\(\\frac{{n + 1}}{{{a_1}{a_{n + 1}}}}\\)','\(d\)\\(\\frac{n}{{{a_1}{a_{n + 1}}}}\\)','4','PROGRESSIONS','2','Answer = \(d\)\\({a_1},{a_2},{a_3},.......,{a_{n + 1}}\\) are in A.P.and common difference = dLet \\(S = \\frac{1}{{{a_1}{a_2}}} + \\frac{1}{{{a_2}{a_3}}} + .......... +\\frac{1}{{{a_n}{a_{n + 1}}}}\\)\\(\\Rightarrow{}\\) \\(S = \\frac{1}{d}\\,\\left\\{ {\\frac{d}{{{a_1}{a_2}}} +\\frac{d}{{{a_2}{a_3}}} + ...... + \\frac{d}{{{a_n}\\,\\,{a_{n + 1}}}}} \\right\\}\\)\\(\\Rightarrow{}\\) \\(S = \\frac{1}{d}\\,\\left\\{ {\\frac{{{a_2} -{a_1}}}{{{a_1}{a_2}}} + \\frac{{{a_3} - {a_2}}}{{{a_2}{a_3}}} + ...... +\\frac{{{a_{n + 1}} - {a_n}}}{{{a_n}\\,\\,\\,{a_{n + 1}}}}} \\right\\}\\)\\(\\Rightarrow{}\\) \\(S = \\frac{1}{d}\\left\\{ {\\frac{1}{{{a_1}}} -\\frac{1}{{{a_2}}} + \\frac{1}{{{a_2}}} - \\frac{1}{{{a_3}}} + ....... +\\frac{1}{{{a_n}}} - \\frac{1}{{{a_{n + 1}}}}} \\right\\}\\)\\(\\Rightarrow{}\\) \\(S = \\frac{1}{d}\\left\\{ {\\frac{1}{{{a_n}}} - \\frac{1}{{{a_{n+ 1}}}}} \\right\\} = \\frac{1}{d}\\left\\{ {\\frac{{{a_{n + 1}} - {a_1}}}{{{a_1}{a_{n+ 1}}}}} \\right\\}\\)\\(\\Rightarrow{}\\) \\(S = \\frac{1}{d}\\left( {\\frac{{nd}}{{{a_1}{a_{n + 1}}}}}\\right) = \\frac{n}{{{a_1}{a_{n + 1}}}}\\).Trick:  Check for \\(n = 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sum of the first \\(n\\) terms of a series be \\(5{n^2} + 2n\\), then itssecond term is','\(a\)7','\(b\)17','\(c\)24','\(d\)42','2','PROGRESSIONS','2','Answer = \(b\)\\({T_2} = {S_2} - {S_1}\\)= \\(5{(2)^2} + 2(2) - \\{ 5{(1)^2} + 2(1)\\}  = 24 - 7 = 17\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let the sequence \\({a_1},{a_2},{a_3},.............{a_{2n}}\\) form an A.P.Then \\(a_1^2 - a_2^2 + a_3^3 - ......... + a_{2n - 1}^2 - a_{2n}^2 = \\)','\(a\)\\(\\frac{n}{{2n - 1}}(a_1^2 - a_{2n}^2)\\)','\(b\)\\(\\frac{{2n}}{{n - 1}}(a_{2n}^2 -a_1^2)\\)','\(c\)\\(\\frac{n}{{n + 1}}(a_1^2 + a_{2n}^2)\\)','\(d\)None of these','1','PROGRESSIONS','2','Answer = \(a\)Since \\({a_1},\\;{a_2},\\,{a_3},...........,{a_n}\\) form an A.P.therefore, \\({a_2} - {a_1} = {a_4} - {a_3} = ....... = {a_{2n}} - {a_{2n - 1}} =d\\)Here \\(a_1^2 - a_2^2 + a_3^2 - a_4^2 + ....... + a_{2n - 1}^2 - a_{2n}^2\\)\\(= ({a_1} - {a_2})({a_1} + {a_2}) + ({a_3} - {a_4})({a_3} + {a_4}) + ........\\)\\(...... + ({a_{2n - 1}} - {a_{2n}})({a_{2n - 1}} + {a_{2n}})\\)\\(=  - d({a_1} + {a_2} + ....... + {a_{2n}}) =  - d\\left\\{ {\\frac{{2n}}{2}({a_1}+ {a_{2n}})} \\right\\}\\)Also we know \\({a_{2n}} = {a_1} + (2n - 1)d\\)\\( \\Rightarrow \\)\\(d =\\frac{{{a_{2n}} - {a_1}}}{{2n - 1}}\\)\\( \\Rightarrow \\)\\(- d = \\frac{{{a_1} - {a_{2n}}}}{{2n - 1}}.\\)\\(\\therefore \\) Therefore the sum is= \\(\\frac{{n({a_1} - {a_{2n}}).({a_1} + {a_{2n}})}}{{2n - 1}} = \\frac{n}{{2n -1}}(a_1^2 - a_{2n}^2)\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If sum of \\(n\\) terms of an A.P. is \\(3{n^2} + 5n\\) and \\({T_m} = ) then\\(m = \\)           ','\(a\)26','\(b\)27','\(c\)28','\(d\)None of these','2','PROGRESSIONS','2','Answer = \(b\)Obviously\\(Answer = = (3{m^2} + 5m) - \\{ 3{(m - 1)^2} + 5(m - 1)\\}\\)\\(= (3{m^2} + 5m) - 3{m^2} + 6m - 3 - 5m + 5\\)\\( \\Rightarrow \\)\\(Answer = = 6m + 2\\)\\( \\Rightarrow \\)\\(m = 27.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({S_n} = nP + \\frac{1}{2}n(n - 1)Q\\), where \\({S_n}\\) denotes the sum ofthe first \\(n\\) terms of an A.P., then the common difference is','\(a\)\\(P + Q\\)','\(b\)\\(2P + 3Q\\)','\(c\)\\(2Q\\)','\(d\)\\(Q\\)','4','PROGRESSIONS','2','Answer = \(d\)Obviously \\({S_n} = \\frac{n}{2}\\{ 2P + (n - 1)Q\\} \\), hence \\(d = Q\\).Aliter : \\(d = {T_2} - {T_1}\\)\\( = ({S_2} - {S_1}) - {S_1}\\)\\(= {S_2} - 2{S_1} = 2P + Q - 2P = Q.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let \\({S_n}\\)denotes the sum of \\(n\\) terms of an A.P. If \\({S_{2n}} =3{S_n}\\), then ratio \\(\\frac{{{S_{3n}}}}{{{S_n}}} = \\)  ','\(a\)4','\(b\)6','\(c\)8','\(d\)10','2','PROGRESSIONS','2','Answer = \(b\)\\({S_{2n}} = 3{S_n}\\)\\( \\Rightarrow \\)\\(\\frac{{2n}}{2}\\{ 2a + (2n - 1)d\\}  = 3\\;.\\;\\frac{n}{2}\\{ 2a + (n - 1)d\\}\\)\\( \\Rightarrow \\)\\(2a = (n + 1)d\\)Put \\(2a = (n + 1)d\\) in \\(\\frac{{{S_{3n}}}}{{{S_n}}}\\), we get its value 6.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The first term of an A.P. of consecutive integers is \\({p^2} + 1\\) The sumof \\((2p + 1)\\) terms of this series can be expressed as','\(a\)\\({(p + 1)^2}\\)','\(b\)\\({(p + 1)^3}\\)','\(c\)\\((2p + 1){(p + 1)^2}\\)','\(d\)\\({p^3} + {(p + 1)^3}\\)','4','PROGRESSIONS','2','Answer = \(d\)\\({S_{2p + 1}} = \\frac{{2p + 1}}{2}\\{ 2({p^2} + 1) + (2p + 1 - 1)\\,1\\} \\)\\(= \\left( {\\frac{{2p + 1}}{2}} \\right)\\,(2{p^2} + 2p + 2) = (2p + 1)({p^2} + p +1)\\)\\(= {p^3} + {(p + 1)^3}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The sum of the first four terms of an A.P. is 56. The sum of the last fourterms is  If its first term is 11, the number of terms is','\(a\)10','\(b\)11','\(c\)12','\(d\)None of these','2','PROGRESSIONS','2','Answer = \(b\)\\(11 + (11 + d) + (11 + 2d) + (11 + 3d) = 56\\) \\( \\Rightarrow \\)\\(d = 2\\)and \\(\\{ 11 + (n - 1)2\\}  + \\{ 11 + (n - 2)2\\} \\)\\(+ \\{ 11 + (n - 3)2\\}  + \\{ 11 + (n - 4)2\\}  = Answer = )\\( \\Rightarrow \\)\\(n = 11.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The number of terms of the A.P. 3,7,11,15...to be taken so that the sum isis ','\(a\)5','\(b\)10','\(c\)12','\(d\)14','4','PROGRESSIONS','2','Answer = \(d\)\\(S = \\frac{n}{2}[2a + (n - 1)d]\\)\\(\\Rightarrow{}\\) \\(Answer = = \\frac{n}{2}\\left[ {6 + (n - 1)4}\\right]\\)\\(\\Rightarrow{}\\) \\(Answer = = n\\,[6 + 4n - 4]\\)\\(\\Rightarrow{}\\) \\(Answer = = 2n + 4{n^2}\\) \\(\\Rightarrow{}\\) \\(Answer = = 2{n^2} + n\\)\\(\\Rightarrow{}\\) \\(2{n^2} + n - Answer = = 0\\)\\(\\Rightarrow{}\\) \\( \\Rightarrow \\)\\( = \\frac{{ - 1 \\pm \\sqrt {Answer = }}}{4}\\)\\(\\frac{3}{7}\\)Taking (+) sign, \\((a - d)a = 24\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','There are 15 terms in an arithmetic progression. Its first term is 5 andtheir sum is  The middle term is ','\(a\)23','\(b\)26','\(c\)29','\(d\)32','2','PROGRESSIONS','2','Answer = \(b\)According to the given condition\\(\\frac{{15}}{2}[10 + 14 \\times d] = Answer = )\\( \\Rightarrow \\)\\(d = 3\\)Hence middle term \\(i.e.\\)\\({8^{th}}\\) term is given by\\(5 + 7 \\times 3 = 26.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sum of the 10 terms of an A.P. is 4 times to the sum of its 5 terms,then the ratio of first term and common difference is ','\(a\)\\(1:2\\)','\(b\)\\(2:1\\)','\(c\)\\(2:3\\)','\(d\)\\(3:2\\)','1','PROGRESSIONS','2','Answer = \(a\)Under conditions, we get\\(\\frac{{10}}{2}\\left\\{ {2a + (10 - 1)d} \\right\\} = 4\\left[ {\\frac{5}{2}\\left[ {2a+ (5 - 1)d} \\right]} \\right]\\)\\( \\Rightarrow \\)\\(2a + 9d = 4a + 8d\\) or \\(\\frac{a}{d} = \\frac{1}{2}\\)Hence \\(a:d = 1:2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Three number are in A.P. such that their sum is 18 and sum of their squaresis  The greatest number among them is','\(a\)10','\(b\)11','\(c\)12','\(d\)None of these','2','PROGRESSIONS','2','Answer = \(b\)Let three number of A.P. \\(a - d,\\,a,\\,\\)\\(a + d\\)Sum = 18, and \\({(a - d)^2} + {a^2} + {(a + d)^2} = 58\\)\\(a - d + a + a + d = 18\\)\\(a = 6\\) and \\({(6 - d)^2} + 36 + {(6 + d)^2} = Answer = )= \\(36 + {d^2} + 36 + {d^2} = Answer = )\\( = 2{d^2} + 72 = Answer = )\\( = 2{d^2} = 50\\) \\(\\Rightarrow{}\\) \\(d = 5\\).Hence Numbers are 1, 6, 11, i.e. maximum number is 11.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\frac{{3 + 5 + 7 + ..........{\\rm{to}}\\;n\\;{\\rm{terms}}}}{{5 + 8 + 11 +.........{\\rm{to}}\\;10\\;{\\rm{terms}}}} = 7\\), then the value of\\(n\\) is','\(a\)35','\(b\)36','\(c\)37','\(d\)40','1','PROGRESSIONS','2','Answer = \(a\)We have \\(\\frac{{3 + 5 + 7 + ......{\\rm{upto}}\\;n\\;{\\rm{terms}}}}{{5 + 8+ 11 + ........{\\rm{upto}}\\;10\\;{\\rm{terms}}}} = 7\\)\\(\\Rightarrow \\frac{{\\frac{n}{2}[6 + (n - 1)2]}}{{\\frac{{10}}{2}[10 + (10 -1)3]}} = 7 \\Rightarrow \\frac{{n(2n + 4)}}{{10 \\times 37}} = 7\\)\\( \\Rightarrow \\)\\({n^2} + 2n - Answer =  = 0 \\Rightarrow (n + 37)(n - 35) = 0\\)Hence \\(n = 35\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({A_1},\\,{A_2}\\) be two arithmetic means between \\(\\frac{1}{3}\\) and\\(\\frac{1}{{24}}\\) , then their values are','\(a\)\\(\\frac{7}{{72}},\\,\\frac{5}{{36}}\\)','\(b\)\\(\\frac{{17}}{{72}},\\,\\frac{5}{{36}}\\)','\(c\)\\(\\frac{7}{{36}},\\,\\frac{5}{{72}}\\)','\(d\)\\(\\frac{5}{{72}},\\,\\frac{{17}}{{72}}\\)','2','PROGRESSIONS','2','Answer = \(b\)Here  \\(\\frac{1}{3},\\;{A_1},\\;{A_2},\\;\\frac{1}{{24}}\\) will be in A.P.,then \\({A_1} - \\frac{1}{3} = \\frac{1}{{24}} - {A_2}\\)\\( \\Rightarrow \\)\\({A_1} +{A_2} = \\frac{3}{8}\\)......(i)Now, \\({A_1}\\) is a arithmetic mean of \\(\\frac{1}{3}\\) and \\({A_2}\\),  we have\\(2{A_1} = \\frac{1}{3} + {A_2} \\Rightarrow 2{A_1} - {A_2} = \\frac{1}{3}\\)                 ......(ii)From (i) and (ii), we get, \\({A_1} = \\frac{{17}}{{72}}\\) and \\({A_2} =\\frac{5}{{36}}\\).Aliter : As we have formula \\({A_m} = a + \\frac{{m(b - a)}}{{n + 1}}\\)where \\(n = 2,\\;a = \\frac{1}{3},\\;b = \\frac{1}{{24}}\\)\\(\\therefore \\)\\({A_1} = \\frac{1}{3} + \\frac{{ - 7/24}}{3} = \\frac{{17}}{{72}}\\)\\({A_2} = \\frac{1}{3} + \\frac{{ - 14/24}}{3} = \\frac{{10}}{{72}} = \\frac{5}{{36}}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If  \\(\\frac{{{a^{n + 1}} + {b^{n + 1}}}}{{{a^n} + {b^n}}}\\) be the A.M. of\\(a\\) and \\(b\\), then \\(n = \\)','\(a\)1','\(b\)\\( - 1\\)','\(c\) 0','\(d\) None of these','3','PROGRESSIONS','2','Answer = \(c\)\\(\\frac{{{a^{n + 1}} + {b^{n + 1}}}}{{{a^n} + {b^n}}} = \\frac{{a +b}}{2}\\)\\( \\Rightarrow \\) \\({a^{n + 1}} - a{b^n} + {b^{n + 1}} - b{a^n} = 0\\)\\(\\Rightarrow \\)\\((a - b)({a^n} - {b^n}) = 0\\)If \\({a^n} - {b^n} = 0\\). Then \\({\\left( {\\frac{a}{b}} \\right)^n} = 1 = {\\left({\\frac{a}{b}} \\right)^0}\\). Hence \\(n = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','A number is the reciprocal of the other. If the arithmetic mean of the twonumbers be \\(\\frac{{13}}{{12}}\\), then the numbers are','\(a\)\\(\\frac{1}{4},\\;\\frac{4}{1}\\)','\(b\)\\(\\frac{3}{4},\\;\\frac{4}{3}\\)','\(c\)\\(\\frac{2}{5},\\;\\frac{5}{2}\\)','\(d\)\\(\\frac{3}{2},\\;\\frac{2}{3}\\)','4','PROGRESSIONS','2','Answer = \(d\)Suppose that required numbers \\(a\\) and \\(b\\). Therefore according to theconditions \\(a = \\frac{1}{b}\\)and \\(\\frac{{a + b}}{2} = \\frac{{13}}{{12}}\\)\\( \\Rightarrow \\)\\(a + b =\\frac{{13}}{6}\\)\\( \\Rightarrow \\)\\(a + \\frac{1}{a} = \\frac{{13}}{6} \\Rightarrow 6{a^2} - 13a + 6 = 0\\)\\( \\Rightarrow \\) \\(\\left( {a - \\frac{3}{2}} \\right)\\,\\left( {a - \\frac{2}{3}}\\right) = 0\\)\\( \\Rightarrow \\)\\(a = \\frac{3}{2}\\) and \\(b = \\frac{2}{3}\\)or \\(a = \\frac{2}{3}\\) and \\(b = \\frac{3}{2}\\).Trick : Find the A.M. of option \(a\), \(b\), \(c\), \(d\) one by one.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(A\\) be an arithmetic mean between two numbers and \\(S\\) be the sum of\\(n\\) arithmetic means between the same numbers, then','\(a\)\\(S = n\\,A\\)','\(b\)\\(A = n\\,S\\)','\(c\)\\(A = S\\)','\(d\)None of these','1','PROGRESSIONS','2','Answer = \(a\)Let the two quantities be \\(a\\) and \\(b\\) and let\\({A_1},\\;{A_2}.......,{A_n}\\) be the \\(n\\) A.M.\\'s between them. Then\\(a,\\;{A_1},\\;{A_2}......{A_n},\\;b\\) are in A.P. and let \\(d\\) be the commondifference.Now \\({T_{n + 2}} = b = a + (n + 2 - 1)d \\Rightarrow d = \\frac{{b - a}}{{n +1}}\\)Also \\({A_1} + {A_2} + ...... + {A_n} = {S_{n + 1}} - a\\)\\(= \\frac{1}{2}(n + 1)\\left[ {2a + (n + 1 - 1)\\frac{{(b - a)}}{{(n + 1)}}}\\right] - a\\)=\\(\\frac{n}{2}[2a + (b - a)] = \\frac{n}{2}(a + b) = n\\left( {\\frac{{a + b}}{2}}\\right) = nA\\).Trick: Let 1,3,5,7,9 is in A.P. In this series  \\(A = 5,n = 3,S = 15\\)\\(\\Rightarrow{}\\) \\(S = nA\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The arithmetic mean of first n natural number ','\(a\)\\(\\frac{{n - 1}}{2}\\)','\(b\)\\(\\frac{{n + 1}}{2}\\)','\(c\)\\(\\frac{n}{2}\\)','\(d\)\\(n\\)','2','PROGRESSIONS','2','Answer = \(b\)A.M. \\( = \\frac{{1 + 2 + 3 + ...... + n}}{n} = \\frac{{\\frac{1}{2}n(n +1)}}{n} = \\frac{{n + 1}}{2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The sum of \\(n\\) arithmetic means between \\(a\\) and \\(b\\), is','\(a\)\\(\\frac{{n(a + b)}}{2}\\)','\(b\)\\(n(a + b)\\)','\(c\)\\(\\frac{{(n + 1)(a + b)}}{2}\\)','\(d\)\\((n + 1)(a + b)\\)','1','PROGRESSIONS','2','Answer = \(a\)The sum of \\(n\\) arithmetic mean between \\(a\\) and \\(b\\) \\( =\\frac{n}{2}(a + b)\\).Aliter : As we know \\({A_1} + {A_2} + ...... + {A_n} = nA\\),  where \\(a = A{R^{p- 1}},\\;b = A{R^{q - 1}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','After inserting \\(n\\) A.M.\\'s between 2and 38, the sum of the resulting progression is  The value of  \\(n\\) is','\(a\)10','\(b\)8','\(c\)9','\(d\)None of these','2','PROGRESSIONS','2','Answer = \(b\)The resulting progression will have \\(n + 2\\) terms with 2 as the firstterm and 38 as the last term.Therefore the sum of the progression\\(= \\frac{{n + 2}}{2}(2 + 38) = 20(n + 2).\\)By hypothesis, \\(20(n + 2) = Answer = )\\( \\Rightarrow \\)\\(n = 8\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The mean of the series \\(a,a + nd,\\,\\,a + 2nd\\) is ','\(a\)\\(a + (n - 1)\\,d\\)','\(b\)\\(a + nd\\)','\(c\)\\(a + (n + 1)\\,d\\)','\(d\)None of these','2','PROGRESSIONS','2','Answer = \(b\)Mean \\( = \\frac{{a + (a + nd) + (a + 2nd)}}{3}\\)\\(= \\frac{{3a + 3nd}}{3} = a + nd.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(f(x + y,x - y) = xy\\,,\\) then the arithmetic mean of \\(f(x,y)\\) and\\(f(y,x)\\) is [AMU , 05]','\(a\)\\(x\\)','\(b\)\\(y\\)','\(c\)0','\(d\)1','3','PROGRESSIONS','2','Answer = \(c\)Let \\(x + y = u,\\,\\,x - y = v\\)\\(\\Rightarrow{}\\) \\(x = \\frac{{u + v}}{2},y = \\frac{{u - v}}{2}\\),\\(\\thereforef(u,v) = \\left( {\\frac{{u + v}}{2}} \\right).\\left( {\\frac{{u - v}}{2}} \\right)\\)Now,\\(\\frac{{f(x,y) + f(y,x)}}{2} = \\frac{{\\left( {\\frac{{x + y}}{2}.\\frac{{x -y}}{2}} \\right) + \\left( {\\frac{{y + x}}{2}.\\frac{{y - x}}{2}} \\right)}}{2} =0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\log 2,\\;\\log ({2^n} - 1)\\) and \\(\\log ({2^n} + 3)\\) are in A.P., then n =','\(a\)5/2','\(b\)\\({\\log _2}5\\)','\(c\) \\( {\\log _3}5\\)','\(d\) 2','2','PROGRESSIONS','2','Answer = \(b\)As, \\(\\log 2,\\;\\log ({2^n} - 1)\\) and \\(\\log ({2^n} + 3)\\) are in A.P.Therefore, \\(2\\log ({2^n} - 1) = \\log 2 + \\log ({2^n} + 3)\\)\\(\\Rightarrow ({2^n} - 5)({2^n} + 1) = 0\\)As \\({2^n}\\) cannot be negative, hence \\({2^n} - 5 = 0\\)\\( \\Rightarrow {2^n} = 5\\) or \\(n = {\\log _2}5\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sum of two extreme numbers of an A.P. with four terms is 8 andproduct of remaining two middle term is 15, then greatest number of the serieswill be ','\(a\)5','\(b\)7','\(c\)9','\(d\)11','2','PROGRESSIONS','2','Answer = \(b\)Let four numbers are \\(a - 3d,\\,a - d,\\;a + d,\\;a + 3d\\).Now  \\(\\frac{b}{a} = \\frac{d}{c}\\)\\( \\Rightarrow \\)\\(a = 4\\)and \\((a - d)(a + d) = 15\\)\\( \\Rightarrow \\)\\({a^2} - {d^2} = 15\\)\\( \\Rightarrow\\)\\(d = 1\\)Thus required numbers are 1, 3, 5, 7.Hence greatest number is 7.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sides of a right angled traingle are in A.P., then the sides areproportional to ','\(a\)1: 2: 3','\(b\)2: 3: 4','\(c\)3: 4: 5','\(d\)4: 5: 6','3','PROGRESSIONS','2','Answer = \(c\)Let the sides of the triangle be \\(a - d,\\;a,\\;a + d\\), then hypotenusebeing the greatest side \\(i.e.\\)\\(a + d\\).So, \\({(a + d)^2} = {a^2} + {(a - d)^2}\\)\\( \\Rightarrow \\)\\({a^2} + {d^2} + 2ad = {a^2} + {a^2} - 2ad + {d^2} \\Rightarrow a = 4d\\)Therefore ratio of the side \\(s = a - d:a:a + d\\)\\(= (4d - d)\\;:\\;4d:(4d + d) = 3:4:5.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Three numbers are in A.P. whose sum is 33 and product is   then the smallest number from these numbers is','\(a\)4','\(b\)8','\(c\)11','\(d\)14','1','PROGRESSIONS','2','Answer = \(a\)Suppose that three numbers  are \\(a + d,\\;a,\\;a - d,\\) therefore \\(a + d +a + a - d = 33\\)\\( \\Rightarrow \\)\\(a = 11\\)\\(a(a + d)(a - d) = Answer = )\\( \\Rightarrow \\)\\(11(Answer = - {d^2}) = Answer = )\\(\\Rightarrow \\)\\(d = 7\\)Then required numbers are 4, 11, 18.Hence smallest number is 4.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(a,\\;b,\\;c,\\;d,\\;e,\\;f\\) are in A.P., then the value of \\(e - c\\) will be[Pb. CET , 91]','\(a\)\\(2(c - a)\\)','\(b\)\\(2(f - d)\\)','\(c\)\\(2(d - c)\\)','\(d\)\\(d - c\\)','3','PROGRESSIONS','2','Answer = \(c\)\\(a,\\;b,\\;c,\\;d,\\;e,\\;f\\) are in A.P.So \\(b - a = c - b = d - c = e - d = f - e = K\\)Where \\(K\\) is a common difference.Now, \\(d - c = e - d\\)\\( \\Rightarrow \\)\\(e + c = 2d\\).\\(e{\\rm{-}}c + {\\rm{2}}c = 2d \\Rightarrow e - c = 2(d - c).\\)Trick : Check by putting\\(a = 1,\\;b = 2,\\;c = 3,\\;d = 4,\\;e = 5\\) and \\(f = 6\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sum of three numbers of a arithmetic sequence is 15 and the sum oftheir squares is 83, then the numbers are','\(a\)4, 5, 6','\(b\)3, 5, 7','\(c\)1, 5, 9','\(d\)2, 5, 8','2','PROGRESSIONS','2','Answer = \(b\)Let three numbers are \\(a - d,\\;a,\\;a + d\\).We get \\(a - d + a + a + d = 15\\)\\( \\Rightarrow \\)\\(a = 5\\)and \\({(a - d)^2} + {a^2} + {(a + d)^2} = 83\\)\\( \\Rightarrow \\)\\({a^2} + {d^2} - 2ad + {a^2} + {a^2} + {d^2} + 2ad = 83\\)\\( \\Rightarrow \\)\\(2({a^2} + {d^2}) + {a^2} = 83\\)Putting \\(a = 5\\)\\( \\Rightarrow \\)\\(2(25 + {d^2}) + 25 = 83\\)\\( \\Rightarrow \\)\\(2{d^2} = 8\\)\\(\\Rightarrow \\)\\(d = 2\\)Thus numbers are 3, 5, 7.Trick : Since \\(3 + 5 + 7 = 15\\) and \\({3^2} + {5^2} + {7^2} = 83\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sum of three consecutive terms of an A.P. is 51 and the product oflast and first term is  then the numbers are ','\(a\)21, 17, 13','\(b\)20,~16, 12','\(c\)22, 18, 14','\(d\)24, 20, 16','1','PROGRESSIONS','2','Answer = \(a\)Let consecutive terms of an A.P. are \\(a - d,\\;a,\\;a + d\\).Under given condition, \\((a - d) + a + (a + d) = 51\\)\\( \\Rightarrow \\) \\(a = 17\\) and \\((a - d)(a + d) = Answer = )\\( \\Rightarrow\\)\\({a^2} - {d^2} = Answer = )\\( \\Rightarrow \\) \\( - {d^2} = Answer = - Answer = )\\( \\Rightarrow \\)\\(d = 4\\)Hence consecutive terms are 13, 17, 21.Trick : Both conditions are satisfied by \(a\) \\(i.e.\\) 21, 17, 13.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\frac{1}{{p + q}},\\;\\frac{1}{{r + p}},\\;\\frac{1}{{q + r}}\\) are inA.P., then','\(a\)\\(p,\\;,q,\\;r\\) are in A.P.','\(b\)\\({p^2},\\;{q^2},\\;{r^2}\\) are in A.P.','\(c\)\\(\\frac{1}{p},\\;\\frac{1}{q},\\;\\frac{1}{r}\\) are in A.P.','\(d\)None of these','2','PROGRESSIONS','2','Answer = \(b\)Since \\(\\frac{1}{{p + q}},\\;\\frac{1}{{r + q}}\\) and \\(\\frac{1}{{q + r}}\\)are in A.P.\\(\\therefore \\)\\(\\frac{1}{{r + q}} - \\frac{1}{{p + q}} = \\frac{1}{{q + r}} - \\frac{1}{{r + p}}\\)\\( \\Rightarrow \\)\\(\\frac{{p + q - r - p}}{{(r + p)(p + q)}} = \\frac{{r + p - q - r}}{{(q + r)(r +p)}}\\)\\( \\Rightarrow \\) \\(\\frac{{q - r}}{{p + q}} = \\frac{{p - q}}{{q + r}}\\) or\\({q^2} - {r^2} = {p^2} - {q^2}\\)\\(\\therefore \\)\\(2{q^2} = {r^2} + {p^2}\\)Therefore \\({p^2},\\;{q^2},\\;{r^2}\\) are in A.P.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The difference between an integer and its cube is divisible by','\(a\)4','\(b\)6','\(c\)9','\(d\)None of these','2','PROGRESSIONS','2','Answer = \(b\)It can easily proved by putting \\(n = 2,\\;3,\\;4........\\).The difference between an integar and its cube is divisible by 6.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(a,\\,b,\\,c\\) are in A.P., then \\((a + 2b - c)\\)\\((2b + c - a)\\)\\((c + a- b)\\)  equals','\(a\)\\(\\frac{1}{2}abc\\)','\(b\)abc','\(c\)2abc','\(d\)4 abc','4','PROGRESSIONS','2','Answer = \(d\)\\((a + 2b - c)\\,(2b + c - a)\\,(c + a - b)\\)\\( = (a + a + c - c)(a + c + c - a)(2b - b)\\)\\(= 4\\,abc.\\)\\((a,b,c\\) are in A.P., \\(\\therefore 2b = a + c)\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Four numbers are in arithmetic progression. The sum of first and last term is 8and the product of both middle terms is 15. The least number of the series is   ','\(a\)4 ','\(b\)3','\(c\)2','\(d\)1','4','PROGRESSIONS','2','Answer = \(d\)Let \\({A_1},{A_2},{A_3}\\) and \\({A_4}\\) are four numbers in A.P.\\({A_1} + {A_4} = 8\\)    \\(\\ldots{} \\)..(i)   and \\({A_2}.\\,{A_3} = 15\\)         \\(\\ldots{} \\)..(ii)The sum of terms equidistant from the beginning and end is constant and is equalto sum of first and last terms.Hence, \\({A_2} + {A_3} = {A_1} + {A_4} = 8\\)                        \\(\\ldots{} \\)..(iii)From (ii) and (iii),\\({A_2} + \\frac{{15}}{{{A_2}}} = 8\\) \\(\\Rightarrow{}\\)  \\(A_2^2 - 8{A_2} + 15 =0\\)\\({A_2} = 3\\,\\,{\\rm{or}}\\,\\,5\\) and \\({A_3} = 5\\,\\,\\,{\\rm{or}}\\,\\,{\\rm{3}}\\).As we know, \\({A_2} = \\frac{{{A_1} + {A_3}}}{2}\\) \\(\\Rightarrow{}\\) \\({A_1} =2{A_2} - {A_3}\\)\\(\\Rightarrow{}\\) \\({A_1} = 2 \\times 3 - 5 = 1\\) and \\({A_4} = 8 - {A_1} = 7\\)Hence the series is, 1, 3, 5, 7.So that least number of series is 1.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If twice the 11\\(^{th}\\) term of an A.P. is equal to 7 times of its 21\\(^{st}\\) term, then its 25\\(^{th}\\) term is equal to','\(a\)24','\(b\)120','\(c\)0','\(d\)None of these','3','PROGRESSIONS','2','Answer = \(c\)Let the first term of A.P. is a and common difference is d.11\\(^{th}\\) term of A.P. = \\(a + 10d\\)21\\(^{st}\\) term of A.P. = a+ 20d\\(2(a + 10d) = 7(a + 20d)\\)\\(\\Rightarrow{}\\)\\(2a + 20d = 7a + Answer = \\)\\(5a + Answer =  = 0\\)\\(\\Rightarrow{}\\)\\(a + 24d = 0\\)Hence 25\\(^{th}\\) term is 0.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(x,y,z\\) are in A.P. and \\({\\tan ^{ - 1}}x,{\\tan ^{ - 1}}y\\)and \\({\\tan ^{- 1}}z\\) are also in A.P., then','\(a\)\\(x = y = z\\)','\(b\)\\(x = y =  - z\\)','\(c\)\\(x = 1;y = 2;z = 3\\)','\(d\)\\(x = 2;y = 4;z = 6\\)(e)\\(x = 2y = 3z\\)','1','PROGRESSIONS','2','Answer = \(a\)\\(2{\\tan ^{ - 1}}y = {\\tan ^{ - 1}}x + {\\tan ^{ - 1}}\\)z\\(\\Rightarrow{}\\)\\({\\tan ^{ - 1}}\\left( {\\frac{{2y}}{{1 - {y^2}}}} \\right) ={\\tan ^{ - 1}}\\left( {\\frac{{x + z}}{{1 - xz}}} \\right)\\)\\(\\Rightarrow{}\\)\\(\\frac{{2y}}{{1 - {y^2}}} = \\frac{{x + z}}{{1 - xz}}\\)But \\(2y = x + z\\)\\(\\therefore{}\\)\\(1 - {y^2} = 1 - xz\\)\\(\\Rightarrow{}\\) \\({y^2} = xz\\)\\(xyz\\) are both in G.P. and A.P.,  \\(\\therefore{}\\) \\(x = y = z\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(a,\\,b,\\,c\\) are in G.P., then','\(a\)\\(a({b^2} + {a^2}) = c({b^2} + {c^2})\\)\(b\)\\(a({b^2} + {c^2}) = c({a^2} +{b^2})\\)\(c\)\\({a^2}(b + c) = {c^2}(a + b)\\)\(d\)None of these604.\\({7^{th}}\\) term of the sequence \\(\\sqrt 2 ,\\;\\sqrt {10} ,\\;5\\sqrt 2,\\;.......\\)is','','\(b\)\\(a({b^2} + {c^2}) = c({a^2} +{b^2})\\)\(c\)\\({a^2}(b + c) = {c^2}(a + b)\\)\(d\)None of these604.\\({7^{th}}\\) term of the sequence \\(\\sqrt 2 ,\\;\\sqrt {10} ,\\;5\\sqrt 2,\\;.......\\)is\(a\)\\(125\\sqrt {10} \\)','\(b\)\\(25\\sqrt 2 \\)\(c\)125\(d\)\\(125\\sqrt 2 \\)','2','PROGRESSIONS','2','Answer = \(b\)If \\(a,\\;b,\\;c\\) are in G.P. Then \\({b^2} = ac\\)\\( \\Rightarrow \\) \\({b^2}(a - c) = ac(a - c)\\)\\( \\Rightarrow \\)\\({b^2}a - {b^2}c = {a^2}c - a{c^2}\\)\\( \\Rightarrow \\)\\(a({b^2} + {c^2}) = c({a^2} + {b^2}).\\)Trick : Put \\(a = 1,\\;b = 2,\\;c = 4\\) and check the alternates.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the \\({4^{th}},\\;{7^{th}}\\) and \\({10^{th}}\\) terms of  a G.P. be\\(a,\\;b,\\;c\\) respectively, then the relation between \\(a,\\;b,\\;c\\) is','\(a\)\\(b = \\frac{{a + c}}{2}\\)','\(b\)\\({a^2} = bc\\)','\(c\)\\({b^2} = ac\\)','\(d\)\\({c^2} = ab\\)','3','PROGRESSIONS','2','Answer = \(c\)Let first term of G.P. \\( = A\\) and common ratio \\( = r\\)We know that \\({n^{th}}\\)term of G.P. = \\(A{r^{n - 1}}\\)Now \\({t_4} = a = A{r^3},\\;{t_7} = b = A{r^6}\\)and \\({t_{10}} = c = A{r^9}\\)Relation \\({b^2} = ac\\)is true because \\({b^2} = {(A{r^6})^2} = {A^2}{r^{12}}\\)and \\(ac = (A{r^3})(A{r^9}) = {A^2}{r^{12}}\\)Aliter : As we know, if \\(xy + 2{y^2} + yz = xy + xz + {y^2} + yz\\) in A.P.,then \\( = 2{n^2} + 5n - 2{n^2} + 4n - 2 - 5n + 5 = 4n + 3\\) terms of a G.P. arealways in G.P., therefore, \\(a,\\;b,\\;c\\) will be in G.P.\\(i.e.\\)\\(2,\\;5,\\;8,\\;11,\\;14 = 40\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the first  term of a G.P. be 5 and common ratio be \\( - 5\\), then whichterm is ','\(a\)\\({6^{th}}\\)','\(b\)\\({5^{th}}\\)','\(c\)\\({7^{th}}\\)','\(d\)\\({8^{th}}\\)','2','PROGRESSIONS','2','Answer = \(b\)Given that first term \\(a = 5\\) and common ratio \\(r =  - 5\\). Supposethat \\({n^{th}}\\)term is Answer = ,then \\(a{r^{n - 1}} = Answer =  \\Rightarrow 5{( - 5)^{n - 1}} = Answer =  \\Rightarrow {( -5)^{n - 1}} = {5^4}\\)Hence n=5.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The number which should be added to the numbers 2, 14, 62so that the resulting numbers may be in G.P., is','\(a\)1','\(b\)2','\(c\)3','\(d\)4','2','PROGRESSIONS','2','Answer = \(b\)Suppose that the added number be \\(x\\)then \\(x + 2,\\;x + 14,\\;x + 62\\) be in G.P.Therefore \\( (x+14)^2 = (x+2)(x+62)  \\)\\(\\Rightarrow x^2 +Answer = +28x=x^2 +64x+Answer =  \\)\\( \\Rightarrow 36x=72\\Rightarrow x=2 \\)Trick : \(a\) Let 1 is added, then the numbers will be 3, 15, 63 which areobviously not in G.P.\(b\) Let 2 is added, then the numbers will be 4, 16, 64 which are obviously inG.P.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({(p + q)^{th}}\\) term of a G.P. be \\(m\\) and \\({(p - q)^{th}}\\)term be\\(n\\), then the \\({p^{th}}\\) term will be    [RPET ; MP PET , 99]','\(a\)\\(m/n\\)','\(b\)\\(\\sqrt {mn} \\)','\(c\) mn','\(d\) 0','2','PROGRESSIONS','2','Answer = \(b\)Given that \\(m = a{r^{p + q - 1}}\\) and  \\(n = a{r^{p - q - 1}}\\)\\({r^{p + q - 1 - p + q + 1}} = \\frac{m}{n} \\Rightarrow r = {\\left( {\\frac{m}{n}}\\right)^{1/(2q)}}\\)and \\(a = \\frac{m}{{{{\\left( {\\frac{m}{n}} \\right)}^{(p + q - 1)/2q}}}}\\)Now \\({p^{th}}\\)term \\( = a{r^{p - 1}} = \\frac{m}{{{{\\left( {\\frac{m}{n}}\\right)}^{(p + q - 1)/2q}}}}{\\left( {\\frac{m}{n}} \\right)^{(p - 1)/2q}}\\)\\(= m{\\left( {\\frac{m}{n}} \\right)^{(p - 1)/2q - (p + q - 1)/(2q)}} = m{\\left({\\frac{m}{n}} \\right)^{ - 1/2}} = {m^{1 - 1/2}}{n^{1/2}}\\)\\(= {m^{1/2}}{n^{1/2}} = \\sqrt {mn} .\\)Aliter : As we know each term in a G.P. is geometric mean of the termsequidistant from it. Here \\({(p + q)^{th}}\\)and \\({(p - q)^{th}}\\) terms areequidistant from \\({p^{th}}\\) term \\(i.e.\\) at a distance of \\(q\\). Therefore,\\({p^{th}}\\) term will be G.M. of \\({(p + q)^{th}}\\) and \\({(p -q)^{th}}\\)\\(i.e.\\)\\(\\sqrt {mn} \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The terms of a G.P. are positive. If each term is equal to the sum of twoterms that follow it, then the common ratio is','\(a\)\\(\\frac{{\\sqrt 5  - 1}}{2}\\)','\(b\)\\(\\frac{{1 - \\sqrt 5 }}{2}\\)','\(c\)1','\(d\)\\(2\\sqrt 5 \\)','1','PROGRESSIONS','2','Answer = \(a\)Under condition \\({T_n} = {T_{n + 1}} + {T_{n + 2}}\\)\\(\\Rightarrow a{r^{n - 1}} = a{r^n} + a{r^{n + 1}} \\Rightarrow {r^{n - 1}} ={r^n}(1 + r)\\)\\( \\Rightarrow \\)\\({r^2} + r - 1 = 0 \\Rightarrow r = \\frac{{ - 1 \\pm \\sqrt {1 + 4} }}{2} = \\frac{{- 1 \\pm \\sqrt 5 }}{2}\\)Since, each term is \\( + ve\\).Hence common ratio is \\(\\frac{{\\sqrt 5  - 1}}{2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(x,\\,2x + 2,\\,3x + 3,\\)are in G.P., then the fourth term is','\(a\)27','\(b\)\\( - 27\\)','\(c\)13.5','\(d\)\\( - 13.5\\)','4','PROGRESSIONS','2','Answer = \(d\)Given that \\(x,\\;2x + 2,\\;3x + 3\\) are in G.P.Therefore, \\({(2x + 2)^2} = x(3x + 3) \\Rightarrow {x^2} + 5x + 4 = 0\\)\\(\\Rightarrow (x + 4)(x + 1) = 0 \\Rightarrow x =  - 1,\\; - 4\\)Now first term \\(a = x\\)Second term \\(ar = 2(x + 1)\\)\\( \\Rightarrow r = \\frac{{2(x + 1)}}{x}\\)then \\({4^{th}}\\)term \\( = a{r^3}\\)\\( = x{\\left[ {\\frac{{2(x + 1)}}{x}}\\right]^3} = \\frac{8}{{{x^2}}}{(x + 1)^3}\\)Putting \\(x =  - 4\\)We get \\({T_4} = \\frac{8}{{16}}{( - 3)^3} =  - \\frac{{27}}{2} =  - 13.5\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the ratio of the sum of first three terms and the sum of first six termsof a G.P.  be :  then the common ratio r is','\(a\)\\(\\frac{3}{5}\\) ','\(b\)\\(\\frac{5}{3}\\)','\(c\)\\(\\frac{2}{3}\\)','\(d\)\\(\\frac{3}{2}\\)','1','PROGRESSIONS','2','Answer = \(a\)Here \\(\\frac{{{S_3}}}{{{S_6}}} = \\frac{{Answer = }{{Answer = } \\Rightarrow\\frac{{a({r^3} - 1)/(r - 1)}}{{a({r^6} - 1)/(r - 1)}} = \\frac{{Answer = }{{Answer = }\\)\\( \\Rightarrow \\)\\(({r^3} - 1)Answer = = Answer = {r^6} - 1) \\Rightarrow {r^3} = \\frac{{27}}{{Answer = }\\Rightarrow r = \\frac{3}{5}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(x,\\;y,\\;z\\) are in G.P. and \\({a^x} = {b^y} = {c^z}\\), then [IIT , 68]','\(a\)\\({\\log _a}c = {\\log _b}a\\)','\(b\)\\({\\log _b}a = {\\log _c}b\\)','\(c\)\\({\\log _c}b = {\\log _a}c\\)','\(d\)None of these','2','PROGRESSIONS','2','Answer = \(b\)\\(x,\\;y,\\;z\\) are in G.P., then  \\({y^2} = x\\;.\\;z\\)Now \\({a^x} = {b^y} = {c^z} = m\\)\\( \\Rightarrow \\)\\(x{\\log _e}a = y{\\log _e}b = z{\\log _e}c = {\\log _e}m\\)\\( \\Rightarrow \\)\\(x = {\\log _a}m,\\;y = {\\log _b}m,z = {\\log _c}m\\)Again as \\(x,\\;y,\\;z\\) are in G.P., so  \\({(p - r)^2} = {(p + r)^2} - 4pr =16{K^2} - 16{K^2} = 0\\)\\( \\Rightarrow \\)\\(\\frac{{{{\\log }_b}m}}{{{{\\log }_a}m}} = \\frac{{{{\\log}_c}m}}{{{{\\log }_b}m}}\\)\\( \\Rightarrow \\)\\({\\log _b}a = {\\log _c}b.\\)',CURRENT_TIMESTAMP);
