INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(a,\\;b,\\;c\\) are \\({p^{th}},\\;{q^{th}}\\) and \\({r^{th}}\\)terms of aG.P., then \\({\\left( {\\frac{c}{b}} \\right)^p}{\\left( {\\frac{b}{a}}\\right)^r}{\\left( {\\frac{a}{c}} \\right)^q}\\) is equal to','\(a\)1','\(b\)\\({a^P}{b^q}{c^r}\\)','\(c\)\\({a^q}{b^r}{c^p}\\)','\(d\)\\({a^r}{b^p}{c^q}\\)','1','PROGRESSIONS','2','Answer = \(a\)\\(a = A{R^{p - 1}},\\;b = A{R^{q - 1}},\\;c = A{R^{r - 1}}\\)\\(\\therefore \\)\\({\\left( {\\frac{c}{b}} \\right)^p}{\\left( {\\frac{b}{a}} \\right)^r}{\\left({\\frac{a}{c}} \\right)^q} = {\\left( {\\frac{{A{R^{r - 1}}}}{{A{R^{q - 1}}}}}\\right)^p}{\\left( {\\frac{{A{R^{q - 1}}}}{{A{R^{p - 1}}}}} \\right)^r}{\\left({\\frac{{A{R^{p - 1}}}}{{A{R^{r - 1}}}}} \\right)^q}\\)',CURRENT_TIMESTAMP);
