INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The sum of infinity of a geometric progression is \\(\\frac{4}{3}\\) and thefirst term is \\(\\frac{3}{4}\\). The common ratio is  ','\(a\)15/23','\(b\)7/15','\(c\)7/8','\(d\)15/7','1','PROGRESSIONS','2','Answer = \(a\)According to condition \\(\\frac{{3/4}}{{1 - r}} = \\frac{4}{3}\\)\\(\\Rightarrow \\)\\(r = \\frac{7}{{16}}\\).',CURRENT_TIMESTAMP);
