INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The contrapositive of \\((p \\vee q) \\Rightarrow r\\) is','\(a\)\\(r \\Rightarrow (p \\vee q)\\)','\(b\)\\(\\sim r \\Rightarrow (p \\vee q)\\)','\(c\)\\(\\sim r \\Rightarrow \\;\\sim p\\; \\wedge \\sim q\\)','\(d\)\\(p \\Rightarrow (q \\vee r)\\)','3','MATHEMATICAL LOGIC  BOOLEAN ALGEBRA','2','Answer = \(c\)Contrapositive of \\(p \\Rightarrow q\\) is \\(\\sim q \\Rightarrow \\;\\sim p\\)\\(\\therefore{}\\) contrapositive of \\((p \\vee q) \\Rightarrow r\\) is\\(\\sim r \\Rightarrow \\;\\sim (p \\vee q)\\) i.e. \\(\\sim r \\Rightarrow (\\sim p\\; \\wedge \\sim q)\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(p \\Rightarrow (q \\vee r)\\) is false, then the truth values of p, q, rare respectively','\(a\)T, F, F','\(b\)F, F, F','\(c\)F, T, T','\(d\)T, T, F','1','MATHEMATICAL LOGIC  BOOLEAN ALGEBRA','2','Answer = \(a\)\\(p \\Rightarrow q\\) is false only when p is true and q is false.\\(\\therefore{}\\) \\(p \\Rightarrow q\\) is false when p is true and \\(q \\vee r\\) isfalse, and \\(q \\vee r\\) is false when both q and r are false.Hence truth values of p, q and r are respectively T, F, F.',CURRENT_TIMESTAMP);
