INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\({81^{(1/{{\\log }_5}3)}} + {27^{{{\\log }_{_9}}36}} +{3^{4/{{\\log }_{_7}}9}}\\) is equal to','\(a\)49\(b\)625\(c\)216\(d\) 890512.\\(7\\log \\left( {\\frac{{16}}{{15}}} \\right) + 5\\log \\left({\\frac{{25}}{{24}}} \\right) + 3\\log \\left( {\\frac{{81}}{{80}}} \\right)\\)is equalto','','\(b\)625\(c\)216\(d\) 890512.\\(7\\log \\left( {\\frac{{16}}{{15}}} \\right) + 5\\log \\left({\\frac{{25}}{{24}}} \\right) + 3\\log \\left( {\\frac{{81}}{{80}}} \\right)\\)is equalto\(a\)0','\(b\)1\(c\)\\(\\log 2\\)\(d\)\\(\\log 3\\)','4','LOGARITHMS, INDICES','2','Answer = \(d\)\\({81^{(1/{{\\log }_5}3)}} + {27^{{{\\log }_9}36}} + {3^{4/{{\\log }_7}9}}\\)\\(= {3^{4{{\\log }_3}5}} + {3^{3.\\frac{1}{2}{{\\log }_3}36}} + {3^{4{{\\log }_9}7}}\\)\\(= {3^{{{\\log }_3}{5^4}}} + {3^{{{\\log }_3}{{36}^{3/2}}}} + {3^{{{\\log}_3}{7^{4/2}}}}\\)\\(= {5^4} + {36^{3/2}} + {7^2} = ',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({\\log _4}5 = a\\)and \\({\\log _5}6 = b,\\)then \\({\\log _3}2\\)is equal to','\(a\)\\(\\frac{1}{{2a + 1}}\\)','\(b\)\\(\\frac{1}{{2b + 1}}\\)','\(c\)\\(2ab + 1\\)','\(d\) \\(\\frac{1}{{2ab - 1}}\\)','4','LOGARITHMS, INDICES','2','Answer = \(d\)\\(ab = {\\log _4}5.{\\log _5}6 = {\\log _4}6 = \\frac{1}{2}{\\log _2}6\\)\\(ab = \\frac{1}{2}(1 + {\\log _2}3) \\Rightarrow 2ab - 1 = {\\log _2}3\\)\\(\\therefore \\)5\\({\\log _3}2 = \\frac{1}{{2ab - 1}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({\\log _k}x.\\,{\\log _5}k = {\\log _x}5,k \\ne 1,k > 0,\\)then x is equal to','\(a\)k','\(b\)\\(\\frac{1}{5}\\)','\(c\)5','\(d\)None of these','(b,','LOGARITHMS, INDICES','2','Answer = (b,c) \\({\\log _k}x.{\\log _5}k = {\\log _x}5\\)\\( \\Rightarrow \\)\\({\\log _5}x ={\\log _x}5\\)\\( \\Rightarrow \\)\\({\\log _x}5 = \\frac{1}{{{{\\log }_x}5}}\\)\\( \\Rightarrow \\)\\(1 = - 2B = 0\\)\\( \\Rightarrow \\)\\({\\log _x}5 =  \\pm 1\\)\\( \\Rightarrow \\)\\({x^{ \\pm \\,1}} = 5\\)\\( \\Rightarrow \\)\\(x = 5,\\,\\frac{1}{5}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({\\log _5}a.{\\log _a}x = 2,\\)then x is equal to','\(a\)125','\(b\)\\({a^2}\\)','\(c\)25 ','\(d\) None of these','3','LOGARITHMS, INDICES','2','Answer = \(c\) \\({\\log _5}a.{\\log _a}x = 2\\)\\( \\Rightarrow \\) \\( = \\frac{{{{\\log}_e}a}}{{{{\\log }_e}b}} \\times \\frac{{{{\\log }_e}b}}{{{{\\log }_e}c}} \\times\\frac{{{{\\log }_e}c}}{{{{\\log }_e}a}} = 1\\)\\( \\Rightarrow \\)\\(x = {5^2} = 25\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({a^2} + 4{b^2} = 12ab,\\)then \\(\\log (a + 2b)\\)is','\(a\)\\(\\frac{1}{2}[\\log a + \\log b - \\log 2]\\)','\(b\)\\(\\log \\frac{a}{2} + \\log\\frac{b}{2} + \\log 2\\)','\(c\)\\(\\frac{1}{2}[\\log a + \\log b + 4\\log 2]\\)','\(d\)\\(\\frac{1}{2}[\\log a - \\log b +4\\log 2]\\)','3','LOGARITHMS, INDICES','2','Answer = \(c\)\\({a^2} + 4{b^2} = 12ab\\)\\( \\Rightarrow \\)\\({a^2} + 4{b^2} + 4ab = 16ab\\)\\(\\Rightarrow \\)\\({(a + 2b)^2} = 16ab\\)\\( \\Rightarrow \\)\\(2\\log (a + 2b) = \\log 16 + \\log a + \\log b\\)\\(\\therefore \\)\\(\\log (a + 2b) = \\frac{1}{2}[\\log a + \\log b + 4\\log 2]\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(A = {\\log _2}{\\log _2}{\\log _4}+ 2{\\log _{\\sqrt 2 \\,}}\\,2,\\)then Ais equal to','\(a\)2','\(b\)3','\(c\)5','\(d\)7','3','LOGARITHMS, INDICES','2','Answer = \(c\) \\(A = {\\log _2}{\\log _2}{\\log _4}Answer = ) + \\(2{\\log _2}_{^{1/2}}\\,2\\)\\(= {\\log _2}{\\log _2}{\\log _4}{4^4} + 2 \\times \\frac{1}{{(1/2)}}{\\log _2}2\\)\\(= {\\log _2}{\\log _2}4 + 4 = {\\log _2}{\\log _2}{2^2} + 4\\)\\(= {\\log _2}2 + 4 = 1 + 4 = 5.\\)Answer = d)\\({\\log _{Answer = }}{x^2} = {\\log _{{{10}^3}}}{x^2} = 2{\\log _{{{10}^3}}}x =\\frac{2}{3}{\\log _{10}}x = \\frac{2}{3}y\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(x = {\\log _a}(bc),y = {\\log _b}(ca),z = {\\log _c}(ab),\\)then which ofthe following is equal to 1','\(a\)\\(x + y + z\\)','\(b\)\\({(1 + x)^{ - 1}} + {(1 + y)^{ - 1}} + {(1 + z)^{ - 1}}\\)','\(c\)\\(xyz\\)','\(d\) None of these','2','LOGARITHMS, INDICES','2','Answer = \(b\)\\(x = {\\log _a}bc\\)\\( \\Rightarrow \\)\\(1 + x = {\\log _a}a + {\\log _a}bc ={\\log _a}abc\\)\\(\\therefore \\)\\({(1 + x)^{ - 1}} = {\\log _{abc}}a\\)\\(\\therefore{}\\)\\({(1 + x)^{ - 1}} + {(1 + y)^{ - 1}} + {(1 + z)^{ - 1}} = {\\log_{abc}}a + {\\log _{abc}}b + {\\log _{abc}}c\\)\\(= {\\log _{abc}}abc = 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(a = {\\log _{24}}12,\\,b = {\\log _{36}}24\\)and \\(c = {\\log _{48}}36,\\)then1+abc is equal to ','\(a\)\\(2ab\\)','\(b\)\\(2ac\\)','\(c\) 2bc ','\(d\) 0','3','LOGARITHMS, INDICES','2','Answer = \(c\)\\(a = {\\log _{24}}12 = \\frac{{\\log 12}}{{\\log 24}} = \\frac{{2\\log 2 + \\log3}}{{3\\log 2 + \\log 3}}\\)\\(b = {\\log _{36}}24 = \\frac{{3\\log 2 + \\log 3}}{{2(\\log 2 + \\log 3)}}\\)\\(c = {\\log _{48}}36 = \\frac{{2(\\log 2 + \\log 3)}}{{4\\log 2 + \\log 3}}\\)\\(\\therefore \\)\\(abc = \\frac{{2\\,\\,\\log 2 + \\log 3}}{{4\\log 2 + \\log 3}}\\)\\(\\Rightarrow{}\\) \\(1 + abc = \\frac{{6\\log 2 + 2\\log 3}}{{4\\log 2 + \\log 3}} =2.\\frac{{3\\log 2 + \\log 3}}{{4\\log 2 + \\log 3}} = 2bc\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({a^x} = b,{b^y} = c,{c^z} = a,\\)then value of xyz is','\(a\)0','\(b\)1','\(c\)2','\(d\)3','2','LOGARITHMS, INDICES','2','Answer = \(b\)\\({a^x} = b \\Rightarrow \\)\\(x\\log a = \\log b\\)\\(\\Rightarrow{}\\) \\(x = \\frac{{\\log b}}{{\\log a}} = {\\log _a}b\\)Similarly \\(y = {\\log _b}c,\\,z = {\\log _c}a\\)\\(x\\ln a = y\\ln b = xy\\ln (ab) = k\\,{\\rm{(say)}}\\)\\(xyz = {\\log _a}b.{\\log _b}c.{\\log _c}a = 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({\\log _{10}}2 = 0.3,{\\log _{10}}3 = 0.2,\\)the number of digitsin\\({3^{12}} \\times {2^8}\\)is','\(a\)7\(b\)8\(c\)9\(d\)10523.\\(\\sum\\limits_{n = 1}^n {\\frac{1}{{{{\\log }_{{2^n}}}','\(a\)}}}  = \\)','','\(b\)8\(c\)9\(d\)10523.\\(\\sum\\limits_{n = 1}^n {\\frac{1}{{{{\\log }_{{2^n}}}\(a\)}}}  = \\)\(a\)\\(\\frac{{n(n + 1)}}{2}{\\log _a}2\\)\(b\)\\(\\frac{{n(n + 1)}}{2}{\\log _2}a\\)\(c\)\\(\\frac{{{{(n + 1)}^2}{n^2}}}{4}{\\log _2}a\\)\(d\)None of these','3','LOGARITHMS, INDICES','2','Answer = \(c\)\\(y = {3^{12}} \\times {2^8}\\)\\( \\Rightarrow \\)\\({\\log _{10}}y = 12{\\log_{10}}3 + 8{\\log _{10}}2\\)\\(= 12 \\times 0.Answer = 2 + 8 \\times 0.Answer = 3\\)\\(= 5.Answer = 4 + 2.Answer = 4 = 8.Answer = 8\\)\\(\\therefore \\)Number of digits in \\(y\\) = 8 + 1= 9.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The solution of the equation \\({\\log _7}{\\log _5}\\) \\((\\sqrt {{x^2} + 5 + x}) = 0\\)','\(a\)\\(x = 2\\)\(b\)\\(x = 3\\)\(c\)\\(x = 4\\)\(d\)\\(x =  - 2\\)525.\\({\\log _4}\\)18 is','','\(b\)\\(x = 3\\)\(c\)\\(x = 4\\)\(d\)\\(x =  - 2\\)525.\\({\\log _4}\\)18 is\(a\)A rational number','\(b\)An irrational number\(c\)A prime number\(d\) None of these','3','LOGARITHMS, INDICES','2','Answer = \(c\) \\({\\log _7}{\\log _5}(\\sqrt {{x^2} + 5 + x} ) = 0 = {\\log _7}1\\)\\( \\Rightarrow \\)\\(Answer = + \\sqrt {Answer = }  = [{11^2} + {(\\sqrt {13} )^2}] + 2\\,.\\,11.\\sqrt {13}\\)\\( \\Rightarrow \\)\\({({x^2} + 5 + x)^{1/2}} = 5\\)\\( \\Rightarrow \\)\\(({x^2} + x + 5) = 25\\)\\( \\Rightarrow \\)\\({x^2} + x - 20 = 0\\)\\( \\Rightarrow \\)\\((x - 4)\\,(x + 5) = 0\\)\\( \\Rightarrow \\)\\(x = \\,4,\\, -5\\)\\(\\Rightarrow{}\\) \\(x = 4\\).',CURRENT_TIMESTAMP);
