INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\(\\frac{{15}}{{\\sqrt {10}  + \\sqrt {20}  + \\sqrt {40}  - \\sqrt5  - \\sqrt {80} }}\\)is','\(a\)\\(\\sqrt 5 (5 + \\sqrt 2 )\\)','\(b\)\\(\\sqrt 5 (2 + \\sqrt 2 )\\)','\(c\)\\(\\sqrt 5 (1 + \\sqrt 2 )\\)','\(d\)\\(\\sqrt 5 (3 + \\sqrt 2 )\\)','3','LOGARITHMS, INDICES','2','Answer = \(c\)Given fraction\\(= \\frac{{15}}{{\\sqrt {10}  + \\sqrt {20}  + \\sqrt {40}  - \\sqrt 5  - \\sqrt {80}}}\\)\\(= \\frac{{15}}{{\\sqrt {10}  + 2\\sqrt 5  + 2\\sqrt {10}  - \\sqrt 5  - 4\\sqrt 5 }}\\)\\(= \\frac{{15}}{{3\\sqrt {10}  - 3\\sqrt 5 }} = \\frac{5}{{\\sqrt {10}  - \\sqrt 5}}\\,.\\,\\frac{{\\sqrt {10}  + \\sqrt 5 }}{{\\sqrt {10}  + \\sqrt 5 }}\\)\\(= \\sqrt {10}  + \\sqrt 5  = \\sqrt 5 (\\sqrt 2  + 1)\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The rationalising factor of \\({a^{1/3}} + {a^{ - 1/3}}\\)is','\(a\)\\({a^{1/3}} - {a^{ - 1/3}}\\)\(b\)\\({a^{2/3}} + {a^{ - 2/3}}\\)\(c\)\\({a^{2/3}} - {a^{ - 2/3}}\\)\(d\)\\({a^{2/3}} + {a^{ - 2/3}} - 1\\)548.\\(\\sqrt {(3 + \\sqrt 5 )} \\) is equal to','\(a\)\\(\\sqrt 5  + 1\\)\(b\)\\(\\sqrt 3  + \\sqrt 2 \\)\(c\)\\((\\sqrt 5  + 1)/\\sqrt 2 \\)\(d\)\\(\\frac{1}{2}(\\sqrt 5  + 1)\\)549.\\(\\sqrt[4]{{(17 + 12\\sqrt 2 )}} = \\)','','\(b\)\\({a^{2/3}} + {a^{ - 2/3}}\\)\(c\)\\({a^{2/3}} - {a^{ - 2/3}}\\)\(d\)\\({a^{2/3}} + {a^{ - 2/3}} - 1\\)548.\\(\\sqrt {(3 + \\sqrt 5 )} \\) is equal to\(a\)\\(\\sqrt 5  + 1\\)\(b\)\\(\\sqrt 3  + \\sqrt 2 \\)\(c\)\\((\\sqrt 5  + 1)/\\sqrt 2 \\)\(d\)\\(\\frac{1}{2}(\\sqrt 5  + 1)\\)549.\\(\\sqrt[4]{{(17 + 12\\sqrt 2 )}} = \\)\(a\)\\(\\sqrt 2  + 1\\)\(b\)\\({2^{1/4}}(\\sqrt 2  + 1)\\)\(c\)\\(2\\sqrt 2  + 1\\)\(d\)None of these','4','LOGARITHMS, INDICES','2','Answer = \(d\)Let\\(x = {a^{1/3}},y = {a^{ - 1/3}}\\)then \\(a = {x^3},{a^{ - 1}} ={y^3}\\)\\({x^3} + {y^3} = (x + y)({x^2} - xy + {y^2})\\)So, rationlising factor is \\(({x^2} - xy + {y^2})\\). Put the value of x and y.Thus the required rationlising factor is \\({a^{2/3}} + {a^{ - 2/3}} - 1\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\(\\sqrt {(x + 1)}  - \\sqrt {(x - 1)}  = \\sqrt {(4x - 1)} \\), \\(x\\in R\\)has','\(a\)One solution\(b\)Two solution\(c\)Four solution\(d\)No solution551.\\({a^{m{{\\log }_a}n}} = \\)','','\(b\)Two solution\(c\)Four solution\(d\)No solution551.\\({a^{m{{\\log }_a}n}} = \\)\(a\)\\({a^{mn}}\\)','\(b\)\\({m^n}\\)\(c\)\\({n^m}\\)\(d\)None of these','4','LOGARITHMS, INDICES','2','Answer = \(d\)Given \\(\\sqrt {(x + 1)}  - \\sqrt {(x - 1)}  = \\sqrt {(4x - 1)} \\).....(i)Squaring both sides, we get, \\( - 2\\sqrt {({x^2} - 1)}  = 2x - 1\\)Squaring again, we get, \\(x = \\frac{5}{4},\\) which does not satisfy eq. (i).Hence, there is no solution of the given equation.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({({a^m})^n} = {a^{{m^n}}}\\), then the value of \\'m\\' in terms of \\'n\\' is','\(a\)n\(b\)\\({n^{1/m}}\\)\(c\)\\({n^{1/(n - 1)}}\\)\(d\) None of these553.\\({({x^5})^{1/3}}{(16{x^3})^{2/3}}\\)\\({\\left( {\\frac{1}{4}{x^{4/9}}}\\right)^{ - 3/2}} = \\)','','\(b\)\\({n^{1/m}}\\)\(c\)\\({n^{1/(n - 1)}}\\)\(d\) None of these553.\\({({x^5})^{1/3}}{(16{x^3})^{2/3}}\\)\\({\\left( {\\frac{1}{4}{x^{4/9}}}\\right)^{ - 3/2}} = \\)\(a\)\\({(x/4)^3}\\)','\(b\)\\({(4x)^3}\\)\(c\)\\(8{x^3}\\)\(d\)None of these','3','LOGARITHMS, INDICES','2','Answer = \(c\)\\(\\sqrt {{2^2} + {{(\\sqrt 2 )}^2} - 2\\,.\\,2\\sqrt 2 }  = 2 - \\sqrt 2 \\)\\( \\Rightarrow \\)\\(n = {m^{n - 1}} \\Rightarrow m = {n^{\\frac{1}{{n - 1}}}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({a^{1/x}} = {b^{1/y}} = {c^{1/z}}\\)and \\({b^2} = ac\\)then\\(x + z = \\)','\(a\)y','\(b\)2y','\(c\)2xyz','\(d\)None of these','2','LOGARITHMS, INDICES','2','Answer = \(b\)\\({a^{1/x}} = {b^{1/y}} = {c^{1/z}} = k\\,({\\rm{say}}) \\Rightarrow a ={k^x},\\,b = {k^y},\\,c = {k^z}\\)\\({b^2} = ac \\Rightarrow {({k^y})^2} = {k^x}.{k^z}\\)\\( \\Rightarrow \\)\\({k^{2y}} = {k^{x + z}} \\Rightarrow x + z = 2y.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({a^x} = bc,{b^y} = ca,\\,{c^z} = ab,\\)then \\(xyz\\)=','\(a\)0','\(b\)1','\(c\)\\(x + y + z\\)','\(d\)\\(x + y + z + 2\\)','4','LOGARITHMS, INDICES','2','Answer = \(d\)\\({a^x}.{b^y}.{c^z} = bc.ca.ab = {a^2}{b^2}{c^2}\\)\\( \\Rightarrow \\)\\({a^{x - 2}}{b^{y - 2}}{c^{z - 2}} = 1 = {a^0}{b^0}{c^0}\\)\\(\\therefore \\)\\(x = y = z = 2\\)\\(\\therefore \\)\\(xyz = {2^3} = 8 = x + y + z + 2.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({a^x} = {(x + y + z)^y},{a^y} = {(x + y + z)^z}\\), \\({a^z} = {(x + y +z)^x},\\) then','\(a\)\\(x = y = z = a/3\\)','\(b\)\\(x + y + z = a/3\\)','\(c\)\\(x + y + z = 0\\)','\(d\) None of these','1','LOGARITHMS, INDICES','2','Answer = \(a\)\\({a^x}.{a^y}.{a^z} = {(x + y + z)^{y + z + x}}\\)\\( \\Rightarrow \\,\\,{a^{x + y + z}} = {(x + y + z)^{x + y + z}}\\)\\( \\Rightarrow\\)\\(x + y + z = a\\)Now, \\({a^x} = {(x + y + z)^y} = {a^y}\\)\\( \\Rightarrow \\) \\(x = y\\), similarly\\(y = z\\)\\(\\therefore \\)\\(x = y = z = \\frac{a}{3}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({a^{x - 1}} = bc,{b^{y - 1}} = ca,{c^{z - 1}} = ab,\\)then \\(\\sum {(1/x)= } \\)','\(a\)1','\(b\)0','\(c\)abc','\(d\)None of these','1','LOGARITHMS, INDICES','2','Answer = \(a\)\\({a^{x - 1}} = bc \\Rightarrow {a^x} = abc\\)\\(\\therefore \\)\\({a^x} = {b^y} = {c^z} = abc = k\\,({\\rm{say}})\\)\\( \\Rightarrow \\)\\(a = {k^{1/x}} \\Rightarrow \\frac{1}{x} = {\\log _k}a\\);\\(\\therefore{}\\)\\(\\sum\\limits_{}^{} {\\frac{1}{x} = {{\\log }_k}a + {{\\log }_k}b +{{\\log }_k}c}  = {\\log _k}abc = {\\log _{abc}}abc = 1\\){\\footnotesize .}',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\frac{{{{({2^{n + 1}})}^m}({2^{2n}}){2^n}}}{{{{({2^{m +1}})}^n}{2^{2m}}}} = 1,\\)then m =','\(a\)0','\(b\)1','\(c\)n','\(d\)2n','4','LOGARITHMS, INDICES','2','Answer = \(d\)\\({2^{m(n + 1) + 2n + n}} = {2^{(m + 1)n + 2m}}\\)\\( \\Rightarrow \\)\\(mn + m + 3n = mn + 2m + n\\)\\( \\Rightarrow \\)\\(m = 2n.\\)',CURRENT_TIMESTAMP);
