INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({a^x} = {b^y} = {(ab)^{xy}},\\)then \\(x + y = \\)','\(a\)0','\(b\)1','\(c\)xy','\(d\)None of these','2','LOGARITHMS, INDICES','2','Answer = \(b\)\\({a^x} = {b^y} = {(ab)^{xy}}\\)\\( \\Rightarrow \\)\\(x\\ln a = y\\ln b = xy\\ln (ab) = k\\,{\\rm{(say)}}\\)\\(\\ln a = \\frac{k}{x},\\,\\,\\ln b = \\frac{k}{y}\\)\\(\\ln (a\\,b) = \\frac{k}{{xy}}\\)\\( \\Rightarrow \\) \\(\\ln a + \\ln b =\\frac{k}{{xy}}\\) \\( \\Rightarrow \\)\\(\\frac{k}{x} + \\frac{k}{y} = \\frac{k}{{xy}}\\)\\( \\Rightarrow \\)  \\(\\frac{1}{x} + \\frac{1}{y} = \\frac{1}{{xy}}\\) \\( \\Rightarrow\\) \\(\\frac{{x + y}}{{xy}} = \\frac{1}{{xy}}\\); \\(\\therefore \\) \\(x + y = 1\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(x = {2^{1/3}} - {2^{ - 1/3}},\\)then \\(2{x^3} + 6x = \\)','\(a\)1','\(b\)2','\(c\)3','\(d\) None of these','3','LOGARITHMS, INDICES','2','Answer = \(c\)\\(x = {2^{1/3}} - {2^{ - 1/3}}\\)\\( \\Rightarrow \\)\\({x^3} = 2 - {2^{ - 1}} - {3.2^{1/3}}{.2^{ - 1/3}}({2^{1/3}} - {2^{ - 1/3}})\\)\\( \\Rightarrow \\)\\({x^3} = 2 - \\frac{1}{2} - 3x\\) \\(\\Rightarrow{}\\) \\({x^3} + 3x= \\frac{3}{2}\\)\\(\\therefore \\)\\(2{x^3} + 6x = 3.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Solution of the equation \\({(x)^{x\\sqrt x }} = {(x\\sqrt x )^x}\\)are','\(a\)9/4','\(b\)1','\(c\)-1','\(d\)0','(a,','LOGARITHMS, INDICES','2','Answer = (a,b) \\({x^{x\\sqrt x }} = {(x\\sqrt x )^x} \\Rightarrow {x^{{x^{3/2}}}} ={({x^{3/2}})^x}\\)\\( \\Rightarrow \\)\\({x^{{x^{3/2}}}} = {x^{(3/2)x}}\\)\\( \\Rightarrow \\)\\({x^{3/2}} = \\frac{3}{2}x\\)\\( \\Rightarrow \\)\\({x^{1/2}} =\\frac{3}{2}\\)\\( \\Rightarrow \\)\\(x = \\frac{9}{4}\\)Also \\(x = 1\\) is an obvious solution.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({5^{x - 1}} + 5.{(0.2)^{x - 2}} = 26,\\)then x  may have the value','\(a\)25','\(b\)1','\(c\)3','\(d\)None of these','(b,','LOGARITHMS, INDICES','2','Answer = (b,c) \\({5^{x - 1}} + 5\\,{(0.2)^{x - 2}} = 26\\)\\( \\Rightarrow \\)\\({5^{x -1}} + 5\\,.\\,{\\left( {\\frac{1}{5}} \\right)^{x - 2}} = 26\\)\\(\\Rightarrow{}\\)\\({5^{x - 1}} + {5^{3 - x}} = 26\\)\\( \\Rightarrow \\)\\({5^{x -1}} + 25\\,.\\,{5^{ - (x - 1)}} - 26 = 0\\)\\(\\Rightarrow{}\\)\\({5^{2(x - 1)}} - 26.\\,{5^{(x - 1)}} + 25 = 0\\)\\(\\Rightarrow{}\\)\\({5^{2(x - 1)}} - {5^{x - 1}} - {25.5^{x - 1}} + 25 = 0\\)\\(\\Rightarrow{}\\)\\({5^{x - 1}}({5^{x - 1}} - 1) - 25({5^{x - 1}} - 1) = 0\\)\\(\\Rightarrow{}\\)\\(({5^{x - 1}} - 25)({5^{x - 1}} - 1) =0\\)\\(\\Rightarrow{}\\)\\(({5^{x - 1}} - {5^2})\\,({5^{x - 1}} - {5^0}) = 0\\)\\(\\Rightarrow{}\\) \\({5^{x - 1}} = {5^2}\\)or \\({5^{x - 1}} = {5^0}\\)\\(\\Rightarrow \\)\\(x = 3,\\,1\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let \\(\\frac{7}{{{2^{1/2}} + {2^{1/4}} + 1}}\\)\\( = A + B{.2^{1/4}} +C{.2^{1/2}} + D{.2^{3/4}}\\), then','\(a\)\\(A = 1\\)','\(b\)\\(B = 3\\)','\(c\)\\(C = 2\\)','\(d\)\\(D = 1\\)','(a,','LOGARITHMS, INDICES','2','Answer = (a, c, d) \\(\\frac{7}{{{2^{1/2}} + {2^{1/4}} + 1}} = \\frac{{7\\,.\\,({2^{1/4}}- 1)}}{{({2^{1/4}} - 1)\\,[{{({2^{1/4}})}^2} + {2^{1/4}}.1 + {1^2}]}}\\)= \\(\\frac{{7\\,.\\,({2^{1/4}} - 1)}}{{{2^{3/4}} - 1}} = A + B\\,.\\,{2^{1/4}} +C.\\,{2^{1/2}} + D{.2^{3/4}}\\)\\(\\Rightarrow{}\\) \\(7\\,.\\,{2^{1/4}} - 7 = (A - D)\\,{2^{3/4}} + (2B - A) + (2C -B){.2^{1/4}}\\)\\(+ (2D - C){2^{1/2}}\\)\\(\\Rightarrow{}\\) \\((2B - A + 7) + (A - D){2^{3/4}} + (2C - B - 7){2^{1/4}} +(2D - C){2^{1/2}} = 0\\)\\(\\Rightarrow{}\\) \\(2B - A + 7 = A - D = 2C - B - 7 = 2D - C = 0\\)\\(\\Rightarrow{}\\) .',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Solution of the equation\\({4.9^{x - 1}} = 3\\sqrt {({2^{2x + 1}})} \\) has thesolution','\(a\)3','\(b\)2','\(c\)3/2','\(d\)2/3','3','LOGARITHMS, INDICES','2','Answer = \(c\)\\(4.\\,{9^{x - 1}} = 3\\,.\\,\\sqrt {({2^{2x + 1}})} \\)\\( \\Rightarrow\\)\\({3^{2x - 2 - 1}} = {2^{\\frac{{2x + 1}}{2} - 2}}\\)\\( \\Rightarrow \\) \\({3^{2x - 3}} = {2^{\\frac{{2x - 3}}{2}}}\\) \\( \\Rightarrow \\)\\({2^{\\frac{{2x - 3}}{2}}} = {\\left( {{3^{\\frac{{2x - 3}}{2}}}} \\right)^2}\\)\\( \\Rightarrow \\)\\(2x - 3 = 0\\),  \\(\\therefore \\) \\(x = \\frac{3}{2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Solution of the equation\\({9^x} - {2^{x + \\frac{1}{2}}} = {2^{x +\\frac{3}{2}}} - {3^{2x - 1}}\\)','\(a\)\\({\\log _9}(9/\\sqrt 8 )\\)\(b\)\\({\\log _{\\left( {9/2} \\right)}}(9/\\sqrt 8 )\\)\(c\)\\({\\log _e}(9/\\sqrt 8 )\\)\(d\)None of these568.\\(\\frac{{{{[4 + \\sqrt {(15)} ]}^{3/2}} + {{[4 - \\sqrt {(15)}]}^{3/2}}}}{{{{[6 + \\sqrt {(35)} ]}^{3/2}} - {{[6 - \\sqrt {(35)} ]}^{3/2}}}} = \\)','','\(b\)\\({\\log _{\\left( {9/2} \\right)}}(9/\\sqrt 8 )\\)\(c\)\\({\\log _e}(9/\\sqrt 8 )\\)\(d\)None of these568.\\(\\frac{{{{[4 + \\sqrt {(15)} ]}^{3/2}} + {{[4 - \\sqrt {(15)}]}^{3/2}}}}{{{{[6 + \\sqrt {(35)} ]}^{3/2}} - {{[6 - \\sqrt {(35)} ]}^{3/2}}}} = \\)\(a\)1','\(b\)7/13\(c\)13/7\(d\)None of these','2','LOGARITHMS, INDICES','2','Answer = \(b\)\\({9^x} - {2^{x + (1/2)}} = {2^{x + (3/2)}} - {3^{2x - 1}}\\)\\(\\Rightarrow{}\\) \\({3^{2x}} + \\frac{1}{3}{.3^{2x}} = {2.2^{x + \\frac{1}{2}}} +{2^{x + \\frac{1}{2} - 2}}\\)\\(\\Rightarrow{}\\) \\(4\\,.\\,{3^{2x - 1}} = 3.\\,{2^{x + \\frac{1}{2}}}\\)\\(\\Rightarrow{}\\) \\({3^{2x - 2}} = {2^{x + \\frac{1}{2} - 2}}\\)\\(\\Rightarrow{}\\) \\({3^{2x - 2}} = {2^{x - \\frac{3}{2}}}\\) \\(\\Rightarrow{}\\)\\({\\left( {\\frac{9}{2}} \\right)^{x - 1}} = {2^{ - 1/2}}\\)\\(\\Rightarrow{}\\) \\((x - 1)\\,{\\log _{9/2}}9/2 =  - \\frac{1}{2}{\\log _{9/2}}2\\)\\(\\Rightarrow{}\\) \\(x - 1 =  - \\frac{1}{2}{\\log _{9/2}}2\\)\\(\\Rightarrow{}\\) \\(x = 1 - {\\log _{9/2}}\\sqrt 2  = {\\log _{9/2}}9/2 - {\\log_{9/2}}\\sqrt 2 \\)\\(\\Rightarrow{}\\) \\(x = {\\log _{9/2}}(9/2\\sqrt 2 )\\);   \\(\\therefore \\) \\(x ={\\log _{9/2}}(9/\\sqrt 8 )\\).',CURRENT_TIMESTAMP);
