INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in triangle \\(ABC,\\frac{{{a^2} - {b^2}}}{{{a^2} + {b^2}}} = \\frac{{\\sin(A - B)}}{{\\sin (A + B)}}\\), then the triangle is','\(a\)Right angled','\(b\)Isosceles','\(c\)Right angled or isosecles','\(d\)Right angled isosecles','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\frac{{\\sin (A - B)}}{{\\sin (A + B)}} = \\frac{{{a^2} - {b^2}}}{{{a^2} +{b^2}}}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{\\sin (A - B)}}{{\\sin (A + B)}} = \\frac{{{{\\sin }^2}A- {{\\sin }^2}B}}{{{{\\sin }^2}A + {{\\sin }^2}B}}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{\\sin (A - B)}}{{\\sin C}} = \\frac{{\\sin (A - B)\\sin(A + B)}}{{{{\\sin }^2}A + {{\\sin }^2}B}}\\)\\(\\Rightarrow{}\\)  \\(\\sin (A - B){\\rm{ }}\\left[ {\\frac{1}{{\\sin C}} -\\frac{{\\sin C}}{{{{\\sin }^2}A + {{\\sin }^2}B}}} \\right] = 0\\)Either \\(\\sin (A - B) = 0 \\Rightarrow A = B\\) i.e. isosceles or \\({\\sin ^2}A +{\\sin ^2}B = {\\sin ^2}C\\) or \\({a^2} + {b^2} = {c^2}\\) i.e., right angledtriangle.',CURRENT_TIMESTAMP);
