INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in triangle \\(ABC,\\cos A = \\frac{{\\sin B}}{{2\\sin C}}\\), then thetriangle is','\(a\)Equilateral','\(b\)Isosceles','\(c\)Right angled','\(d\)None of these','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\cos A = \\frac{{\\sin B}}{{2\\sin C}} \\Rightarrow \\frac{{{b^2} + {c^2} -{a^2}}}{{2bc}} = \\frac{b}{{2c}}\\)\\(\\Rightarrow{}\\) \\({b^2} + {c^2} - {a^2} - {b^2} = 0\\)\\(\\Rightarrow{}\\)\\({c^2}= {a^2}\\).',CURRENT_TIMESTAMP);
