INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a triangle \\(ABC\\), \\(\\cos A + \\cos B + \\cos C = \\frac{3}{2}\\), thenthe triangle is ','\(a\)Isosceles ','\(b\)Equilateral','\(c\)Right angled','\(d\)None of these','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)Putting \\(\\cos A = \\frac{{{b^2} + {c^2} - {a^2}}}{{2bc}}\\)in givenrelation, we get \\(\\frac{{{b^2} + {c^2} - {a^2}}}{{2bc}} + \\frac{{{c^2} + {a^2} -{b^2}}}{{2ac}}\\)\\( + \\frac{{{a^2} + {b^2} - {c^2}}}{{2ab}} = \\frac{3}{2}\\)\\(\\Rightarrow{}\\) \\(a({b^2} + {c^2}) + b({c^2} + {a^2}) + c({a^2} + {b^2})\\)\\(= {a^3} + {b^3} + {c^3} + 3abc\\)\\(\\Rightarrow{}\\) \\({(b - c)^2}(b + c - a) + {(c - a)^2}(c + a - b)\\)\\( + {(a - b)^2}(a + b - c) = 0\\)\\(\\ldots{} \\)..(i)In triangle, \\(b + c - a > 0\\)etc. and hence (i) will hold good if each factoris zero so that \\(a = b = c\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a triangle ABC the sides \\(AB\\)and AC are perpendicular, then the trueequation is','\(a\)\\(\\tan A + \\tan B = 0\\)','\(b\)\\(\\tan B + \\tan C = 0\\)','\(c\)\\(\\tan A + 2\\tan C = 0\\)','\(d\)\\(\\tan B.\\tan C = 1\\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)\\(\\tan A = \\tan (\\pi  - B - C)\\);  \\(\\infty  =  - \\frac{{\\tan B + \\tanC}}{{1 - \\tan B\\tan C}}\\)\\( \\Rightarrow 1 - \\tan B\\tan C = 0\\) \\(\\Rightarrow{}\\) \\(\\tan B\\tan C = 1\\).',CURRENT_TIMESTAMP);
