INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the area of a triangle ABC is \\(\\Delta{}\\), then \\({a^2}\\sin 2B +{b^2}\\sin 2A\\) is equal to ','\(a\)\\(3\\Delta \\)','\(b\)\\(2\\Delta \\)','\(c\)\\(4\\Delta \\)','\(d\)\\( - 4\\Delta \\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\Delta  = \\frac{1}{2}bc\\sin A \\Rightarrow \\frac{1}{2}{k^2}\\sin B\\sinC\\sin A = \\Delta \\)\\({a^2}\\sin 2B + {b^2}\\sin 2A = 2({a^2}\\sin B\\cos B + {b^2}\\sin A\\cos A)\\)\\(= 2{k^2}({\\sin ^2}A\\sin B\\cos B + {\\sin ^2}B\\sin A\\cos A)\\)\\( = 2{k^2}(\\sin A\\sin B)(\\sin C)\\)\\(= 2{k^2}(\\sin A\\sin B\\sin C) = 4\\Delta .\\)',CURRENT_TIMESTAMP);
