INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The values of \\(\\theta \\) satisfying \\(\\sin 7\\theta  = \\sin 4\\theta  - \\sin\\theta \\) and \\(0 < \\theta  < \\frac{\\pi }{2}\\) are','\(a\)\\(\\frac{\\pi }{9},\\frac{\\pi }{4}\\)','\(b\)\\(\\frac{\\pi }{3},\\frac{\\pi }{9}\\)','\(c\)\\(\\frac{\\pi }{6},\\frac{\\pi }{9}\\)','\(d\)\\(\\frac{\\pi }{3},\\frac{\\pi }{4}\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\sin 7\\theta  + \\sin \\theta  - \\sin 4\\theta  = 0\\)\\( \\Rightarrow \\)\\(2\\sin 4\\theta \\cos 3\\theta  - \\sin 4\\theta  = 0\\)\\( \\Rightarrow \\)\\(\\sin 4\\theta (2\\cos 3\\theta  - 1) = 0 \\Rightarrow \\sin 4\\theta  = 0,\\,{\\rm{}}\\cos 3\\theta  = \\frac{1}{2}\\)Now sin\\(4\\theta  = 0\\) \\( \\Rightarrow \\) \\(4\\theta  = \\pi \\) \\( \\Rightarrow \\)\\(\\theta  = \\frac{\\pi }{4}\\).and \\(\\cos 3\\theta  = \\frac{1}{2}\\) \\( \\Rightarrow \\) \\(3\\theta  = \\frac{\\pi}{3}\\) \\( \\Rightarrow \\) \\(\\theta  = \\frac{\\pi }{9}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The expression \\((1 + \\tan x + {\\tan ^2}x)\\) \\((1 - \\cot x + {\\cot ^2}x)\\)has the positive values for x, given by','\(a\)\\(0 \\le x \\le \\frac{\\pi }{2}\\)','\(b\)\\(0 \\le x \\le \\pi \\)','\(c\)For all \\(x \\in R\\)','\(d\)\\(x \\ge 0\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)The expression is \\(\\frac{{(1 + \\tan x + {{\\tan }^2}x)(1 + {{\\tan }^2}x -\\tan x)}}{{{{\\tan }^2}x}}\\)= \\(\\frac{{{{(1 + {{\\tan }^2}x)}^2} - {{\\tan }^2}x}}{{{{\\tan }^2}x}}\\)Obviously, \\(1 + {\\tan ^2}x \\ge {\\tan ^2}x,{\\rm{  }}\\forall {\\rm{ }}x\\). Henceit is positive for all value of x.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(5\\cos 2\\theta  + 2{\\cos ^2}\\frac{\\theta }{2} + 1 = 0, - \\pi  < \\theta < \\pi \\), then \\(\\theta  = \\)','\(a\)\\(\\frac{\\pi }{3}\\)','\(b\)\\(\\frac{\\pi }{3},{\\cos ^{ - 1}}\\frac{3}{5}\\)','\(c\)\\({\\cos ^{ - 1}}\\frac{3}{5}\\)','\(d\)\\(\\frac{\\pi }{3},\\pi  - {\\cos ^{ -1}}\\frac{3}{5}\\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)\\(5\\cos 2\\theta  + 2{\\cos ^2}\\frac{\\theta }{2} + 1 = 0\\)\\( \\Rightarrow \\)\\(5(2{\\cos ^2}\\theta  - 1) + (1 + \\cos \\theta ) + 1 = 0\\)\\( \\Rightarrow \\)\\(10{\\cos ^2}\\theta  + \\cos \\theta  - 3 = 0\\)\\( \\Rightarrow \\)\\((5\\cos \\theta  + 3)\\,(2\\cos \\theta  - 1) = 0\\)\\( \\Rightarrow \\)\\(\\cos \\theta  = \\frac{1}{2},\\,\\cos \\theta  =  - \\frac{3}{5} \\Rightarrow \\theta  =\\frac{\\pi }{3},\\,\\pi  - {\\cos ^{ - 1}}\\left( {\\frac{3}{5}} \\right).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\cos \\theta  = \\frac{{ - 1}}{2}\\)and \\({0^o} < \\theta  < {o}\\),then the values of \\(\\theta \\)are','\(a\)\\({120^o}\\)and \\({300^o}\\)','\(b\)\\({60^o}\\)and \\({120^o}\\)','\(c\)\\({120^o}\\)and \\({240^o}\\)','\(d\)\\({60^o}\\)and \\({240^o}\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)Given, \\(\\cos \\theta  = \\frac{{ - 1}}{2}\\) and \\({0^o} < \\theta  <{Answer = o}\\). We know that \\(\\cos {60^o} = \\frac{1}{2}\\) and \\(\\cos ({Answer = o} -{60^o})\\) \\( =  - \\cos {60^o} =  - \\frac{1}{2}\\) or \\(\\cos {Answer = o} =  -\\frac{1}{2}\\).Similarly \\(\\cos ({Answer = o} + {60^o})\\) \\( =  - \\cos {60^o} =  - \\frac{1}{2}\\)or \\(\\cos {Answer = o} =  - \\frac{1}{2}.\\) Therefore  \\(\\theta  = {Answer = o}\\) and\\({Answer = o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\((2\\cos x - 1)(3 + 2\\cos x) = 0,\\,0 \\le x \\le 2\\pi \\), then \\(x = \\)','\(a\)\\(\\frac{\\pi }{3}\\)','\(b\)\\(\\frac{\\pi }{3},\\frac{{5\\pi }}{3}\\)','\(c\)\\(\\frac{\\pi }{2},\\frac{{5\\pi }}{3},{\\cos ^{ - 1}}\\left( { - \\frac{3}{2}}\\right)\\)','\(d\)\\(\\frac{{5\\pi }}{3}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\((2\\cos x - 1)\\,\\,(3 + 2\\cos x) = 0\\)Then \\(\\cos x = \\frac{1}{2}{\\rm{  as}}\\,{\\rm{ }}\\cos x \\ne \\frac{{ - 3}}{2}\\)\\( \\Rightarrow \\)\\(x = 2n\\pi  \\pm \\frac{\\pi }{3};\\,\\,\\left\\{ \\begin{array}{l}{\\rm{ for }}n = 0,\\,\\,x = \\frac{\\pi }{3},\\,\\frac{{5\\pi }}{3}\\\\{\\rm{ for }}n = 1,\\,\\,x = \\frac{{5\\pi }}{3}\\end{array} \\right\\}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(0 \\le x \\le \\pi \\)and \\({81^{{{\\sin }^2}x}} + {81^{{{\\cos }^2}x}} =30\\), then x =','\(a\)\\(\\pi /6\\)','\(b\)\\(\\pi /2\\)','\(c\)\\(\\pi /4\\)','\(d\)\\(3\\pi /4\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)We have, \\({81^{{{\\sin }^2}x}} + {81^{{{\\cos }^2}x}} = 30\\)Now check by options, put \\(x = \\frac{\\pi }{6}\\)then \\({(81)^{{{\\sin }^2}\\pi /6}} + {(81)^{{{\\cos }^2}\\pi /6}} = 30\\)\\( \\Rightarrow (81)^{sin^{2}\\pi /6} + (81)^{cos^{2}\\pi /6}=30 \\Rightarrow {(81)^{1/4}} + {(81)^{3/4}} = 30\\Rightarrow 30=30\\)Hence \(a\) is the correct answer.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\theta  = \\sqrt 3 \\cos \\theta , - \\pi  < \\theta  < 0\\), then\\(\\theta  = \\)','\(a\)\\( - \\frac{{5\\pi }}{6}\\)','\(b\)\\( - \\frac{{4\\pi }}{6}\\)','\(c\)\\(\\frac{{4\\pi }}{6}\\)','\(d\)\\(\\frac{{5\\pi }}{6}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\tan \\theta  = \\sqrt 3  = \\tan \\frac{\\pi }{3} \\Rightarrow \\theta  =n\\pi  + \\frac{\\pi }{3}\\)For \\( - \\pi  < \\theta  < 0\\)\\({\\rm{Put}}\\,\\,n =  - 1\\), we get \\(\\theta  =  - \\pi  + \\frac{\\pi }{3} =\\frac{{ - 2\\pi }}{3}{\\rm{or }}\\frac{{ - 4\\pi }}{6}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\(\\theta \\) in between \\({0^o}\\)and \\({o}\\)and satisfyingthe equation \\(\\tan \\theta  + \\frac{1}{{\\sqrt 3 }} = 0\\)is equal to  ','\(a\)\\(\\theta  = {150^o}\\)and \\({300^o}\\)','\(b\)\\(\\theta  = {120^o}\\)and \\({300^o}\\)','\(c\)\\(\\theta  = {60^o}\\)and \\({240^o}\\)','\(d\)\\(\\theta  = {150^o}\\)and \\({330^o}\\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)We have, \\(\\tan \\theta  + \\frac{1}{{\\sqrt 3 }} = 0\\) or \\(\\tan \\theta  = - \\frac{1}{{\\sqrt 3 }}\\)\\(\\theta \\) lies in between \\(0^\\circ \\) and \\(Answer = \\circ \\)\\(\\therefore \\) \\(\\theta  = Answer = \\circ \\) and \\(Answer = \\circ \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The solution of equation \\({\\cos ^2}\\theta  + \\sin \\theta  + 1 = 0\\) lies inthe interval      ','\(a\)\\(\\left( { - \\frac{\\pi }{4},\\frac{\\pi }{4}} \\right)\\)','\(b\)\\(\\left( {\\frac{\\pi}{4},\\frac{{3\\pi }}{4}} \\right)\\)','\(c\)\\(\\left( {\\frac{{3\\pi }}{4},\\frac{{5\\pi }}{4}} \\right)\\)','\(d\)\\(\\left({\\frac{{5\\pi }}{4},\\frac{{7\\pi }}{4}} \\right)\\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)We have, \\({\\cos ^2}\\theta  + \\sin \\theta  + 1 = 0\\)\\(\\Rightarrow{}\\)  \\(1 - {\\sin ^2}\\theta  + \\sin \\theta  + 1 = 0\\)\\(\\Rightarrow{}\\)  \\({\\sin ^2}\\theta  - \\sin \\theta  - 2 = 0\\) \\(\\Rightarrow{}\\)\\((\\sin \\theta  + 1)\\,(\\sin \\theta  - 2) = 0\\)\\(\\sin \\theta  = 2\\), which is not possible and \\(\\sin \\theta  =  - 1\\).Therefore, solution of given equation lies in the interval \\(\\left( {\\frac{{5\\pi}}{4},\\,\\frac{{7\\pi }}{4}} \\right)\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The number of solution of the equation \\(2\\cos ({e^x}) = {5^x} + {5^{ -x}}\\), are         ','\(a\)No solution','\(b\)One solution','\(c\)Two solutions','\(d\)Infinitely many solutions','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)We know \\(\\frac{{{5^x} + {5^{ - x}}}}{2} \\ge 1\\),  (using A.M. \\( \\ge \\)G.M.)But since \\({\\rm{cos}}\\,({e^x}) \\le 1\\)So, there does not exist any solution.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The most general value of \\(\\theta \\)satisfying the equations \\(\\tan \\theta =  - 1\\) and  \\(\\cos \\theta  = \\frac{1}{{\\sqrt 2 }}\\)is','\(a\)\\(n\\pi  + \\frac{{7\\pi }}{4}\\)','\(b\)\\(n\\pi  + {( - 1)^n}\\frac{{7\\pi }}{4}\\)','\(c\)\\(2n\\pi  + \\frac{{7\\pi }}{4}\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\tan \\theta  =  - 1 = \\tan \\left( {2\\pi  - \\frac{\\pi }{4}} \\right)\\),\\(\\cos \\theta  = \\frac{1}{{\\sqrt 2 }} = \\cos \\left( {2\\pi  - \\frac{\\pi }{4}}\\right)\\)Hence general value is \\(2n\\pi  + \\left( {2\\pi  - \\frac{\\pi }{4}} \\right) =2n\\pi  + \\frac{{7\\pi }}{4}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The most general value of \\(\\theta \\)which will satisfy both the equations\\(\\sin \\theta  =  - \\frac{1}{2}\\)and \\(\\tan \\theta  = \\frac{1}{{\\sqrt 3 }}\\)is','\(a\)\\(n\\pi  + {( - 1)^n}\\frac{\\pi }{6}\\)','\(b\)\\(n\\pi  + \\frac{\\pi }{6}\\)','\(c\)\\(2n\\pi  \\pm \\frac{\\pi }{6}\\)','\(d\)None of these','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)\\(\\sin \\theta  =  - \\frac{1}{2} = \\sin \\left( { - \\frac{\\pi }{6}} \\right)= \\sin {\\rm{ }}\\left( {\\pi  + \\frac{\\pi }{6}} \\right)\\)\\(\\tan \\theta  = \\frac{1}{{\\sqrt 3 }} = \\tan \\left( {\\frac{\\pi }{6}} \\right) =\\tan \\left( {\\pi  + \\frac{\\pi }{6}} \\right) \\Rightarrow \\theta  = \\left( {\\pi  +\\frac{\\pi }{6}} \\right)\\)Hence general value of \\(\\theta \\) is \\(2n\\pi  + \\frac{{7\\pi }}{6}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Common roots of the equations \\(2{\\sin ^2}x + {\\sin ^2}2x = 2\\) and  \\(\\sin2x + \\cos 2x = \\tan x,\\) are','\(a\)\\(x = (2n - 1)\\frac{\\pi }{2}\\)','\(b\)\\(x = (2n + 1)\\frac{\\pi }{4}\\)','\(c\)\\(x = (2n + 1)\\frac{\\pi }{3}\\)','\(d\)None of these','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(2{\\sin ^2}x + {\\sin ^2}2x = 2\\)          ......(i)and \\(\\sin 2x + \\cos 2x = \\tan x\\).....(ii)Solving (i), \\({\\sin ^2}2x = 2{\\cos ^2}x\\)\\(\\Rightarrow{}\\)\\(2{\\cos ^2}x\\cos 2x = 0\\)\\(\\Rightarrow{}\\)\\(x = (2n +1)\\frac{\\pi }{2}{\\rm{ or }}x = (2n + 1)\\frac{\\pi }{4}\\)\\(\\therefore \\)  Common roots are \\((2n \\pm 1)\\frac{\\pi }{4}\\)Solving (ii), \\(\\frac{{2\\tan x + 1 - {{\\tan }^2}x}}{{1 + {{\\tan }^2}x}} = \\tanx\\)\\( \\Rightarrow \\)\\({\\tan ^3}x + {\\tan ^2}x - \\tan x - 1 = 0\\)\\( \\Rightarrow \\) \\(({\\tan ^2}x - 1)\\,(\\tan x + 1) = 0\\) \\( \\Rightarrow \\)\\(x = m\\pi  \\pm \\frac{\\pi }{4}\\)Trick :  For \\(n = 0\\), option \(a\) gives \\(\\theta  =  - \\frac{\\pi }{2}\\) whichsatisfies the equation (i) but does not satisfy the (ii). Now option \(b\) gives\\(\\theta  = \\frac{\\pi }{4}\\) which satisfies both the equations.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(r\\,\\sin \\theta  = 3,r = 4(1 + \\sin \\theta ),\\,\\,0 \\le \\theta  \\le 2\\pi,\\)then \\(\\theta  = \\)','\(a\)\\(\\frac{\\pi }{6},\\frac{\\pi }{3}\\)','\(b\)\\(\\frac{\\pi }{6},\\frac{{5\\pi }}{6}\\)','\(c\)\\(\\frac{\\pi }{3},\\frac{\\pi }{4}\\)','\(d\)\\(\\frac{\\pi }{2},\\pi \\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)Eliminating \\(r\\), we get  \\(\\therefore \\) \\(\\sin \\theta  =\\frac{1}{2},\\, - \\frac{3}{2}\\) (rejected)\\( \\Rightarrow \\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\cos \\theta  =  - \\frac{1}{{\\sqrt 2 }}\\)and \\(\\tan \\theta  = 1\\), thenthe general value of \\(\\theta \\)is','\(a\)\\(2n\\pi  + \\frac{\\pi }{4}\\)','\(b\)\\((2n + 1)\\,\\pi  + \\frac{\\pi }{4}\\)','\(c\)\\(n\\pi  + \\frac{\\pi }{4}\\)','\(d\)\\(n\\pi  \\pm \\frac{\\pi }{4}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\cos \\theta  =  - \\frac{1}{{\\sqrt 2 }} \\Rightarrow \\theta  =\\frac{{3\\pi }}{4},\\,\\frac{{5\\pi }}{4}\\); \\(\\tan \\theta  = 1 \\Rightarrow \\theta  =\\frac{\\pi }{4},\\,\\frac{{5\\pi }}{4}\\)\\(\\therefore \\) The general value is \\(2n\\pi  + \\frac{{5\\pi }}{4}\\) or \\((2n +1)\\pi  + \\frac{\\pi }{4}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin (A + B)\\)=1 and \\(\\cos (A - B) = \\frac{{\\sqrt 3 }}{2},\\)then thesmallest positive values of A and B are','\(a\)\\({60^o},{\\rm{ }}{30^o}\\)','\(b\)\\({75^o},{\\rm{ }}{15^o}\\)','\(c\)\\({45^o},{\\rm{ }}{60^o}\\)','\(d\)\\({45^o},{\\rm{ }}{45^o}\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\sin (A + B) = 1\\) and \\(\\cos (A - B) = \\frac{{\\sqrt 3 }}{2}\\)\\( \\Rightarrow \\) \\(A + B = \\frac{\\pi }{2}\\) and \\(A - B = \\frac{\\pi }{6}\\) \\(\\Rightarrow \\) \\(A = \\frac{\\pi }{3},\\,B = \\frac{\\pi }{6}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The smallest positive angle which satisfies the equation \\(2{\\sin ^2}\\theta + \\sqrt 3 \\cos \\theta  + 1 = 0\\), is','\(a\)\\({45^o}\\) and \\({60^o}\\)','\(b\)\\({45^o}\\)and \\({90^o}\\)','\(c\)\\({45^o}\\)only','\(d\)\\({90^o}\\)only','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(2 - 2{\\cos ^2}\\theta  + \\sqrt 3 \\cos \\theta  + 1 = 0\\)\\( \\Rightarrow \\)\\(2{\\cos ^2}\\theta  - \\sqrt 3 \\cos \\theta  - 3 = 0\\)\\( \\Rightarrow \\)\\(\\cos \\theta  = \\frac{{\\sqrt 3  \\pm \\sqrt {3 + 24} }}{4} = \\frac{{\\sqrt 3 (1 \\pm3)}}{4} = \\sqrt 3 \\left( { - \\frac{1}{2}} \\right)\\)\\( \\Rightarrow \\)\\(\\theta  = \\frac{{5\\pi }}{6}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\(\\theta \\) satisfying the given equation \\(\\cos \\theta  +\\sqrt 3 \\sin \\theta \\) = 2, is   ','\(a\)\\(\\frac{\\pi }{3}\\)','\(b\)\\(\\frac{{5\\pi }}{3}\\)','\(c\)\\(\\frac{{2\\pi }}{3}\\)','\(d\)\\(\\frac{{4\\pi }}{3}\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\sin \\left( {\\theta  + \\frac{\\pi }{6}} \\right) = 1 = \\sin \\left({\\frac{\\pi }{2}} \\right) \\Rightarrow \\theta  = \\frac{\\pi }{2} - \\frac{\\pi }{6} =\\frac{\\pi }{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\cos A\\sin \\left( {A - \\frac{\\pi }{6}} \\right)\\)is maximum, then thevalue of A is equal to','\(a\)\\(\\frac{\\pi }{3}\\)','\(b\)\\(\\frac{\\pi }{4}\\)','\(c\)\\(\\frac{\\pi }{2}\\)','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\cos A\\sin \\left( {A - \\frac{\\pi }{6}} \\right) = \\frac{1}{2}\\,\\left[{\\sin \\left( {2A - \\frac{\\pi }{6}} \\right) - \\sin \\frac{\\pi }{6}} \\right]\\)But \\(\\sin \\left( {2A - \\frac{\\pi }{6}} \\right) - \\frac{1}{2}\\) attain maximumvalue at \\(2A - \\frac{\\pi }{6} = \\frac{\\pi }{2} \\Rightarrow A = \\frac{\\pi }{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\cos {40^o} = x\\) and \\(\\cos \\theta  = 1 - 2{x^2}\\), then the possiblevalues  of \\(\\theta \\)lying between \\({0^o}\\)and \\({o}\\)is','\(a\)\\({100^o}\\)and \\({260^o}\\)','\(b\)\\({80^o}\\)and\\({280^o}\\)','\(c\)\\({280^o}\\)and \\({110^o}\\)','\(d\)\\({110^o}\\)and \\({260^o}\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)Here \\(\\cos \\theta  = 1 - 2{\\cos ^2}{40^o}\\) = \\( - (2{\\cos ^2}{40^o} -1)\\)\\( =  - \\cos (2 \\times {40^o})\\)= \\( - \\cos {80^o}\\)= \\(\\cos ({Answer = o} + {80^o}) = \\cos ({Answer = o} - {80^o})\\)Hence, \\(\\cos Answer = \\circ {\\rm{and}}\\cos Answer = \\circ \\) i.e., \\(\\theta  = Answer = \\circ\\) and Answer = (^\\circ{}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The only value of x for which \\({2^{\\sin x}} + {2^{\\cos x}} > {2^{1 -(1/\\sqrt 2 )}}\\) holds, is','\(a\)\\(\\frac{{5\\pi }}{4}\\)','\(b\)\\(\\frac{{3\\pi }}{4}\\)','\(c\)\\(\\frac{\\pi }{2}\\)','\(d\)All values of x','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)Since A.M. \\( \\ge \\) G.M. \\(\\frac{1}{2}({2^{\\sin x}} + {2^{\\cos x}}) \\ge\\sqrt {{2^{\\sin x}}{{.2}^{\\cos x}}} \\)\\( \\Rightarrow \\)\\({2^{\\sin x}} + {2^{\\cos x}} \\ge {2.2^{\\frac{{\\sin x + \\cos x}}{2}}}\\)\\( \\Rightarrow \\)\\({2^{\\sin x}} + {2^{\\cos x}} \\ge {2^{1 + \\frac{{\\sin x + \\cos x}}{2}}}\\)and we know that \\(\\sin x + \\cos x \\ge  - \\sqrt 2 \\)\\(\\therefore \\)  \\({2^{\\sin x}} + {2^{\\cos x}} > {2^{1 - (1/\\sqrt 2 )}}\\),  for\\(x = \\frac{{5\\pi }}{4}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\((1 + \\tan \\theta )(1 + \\tan \\varphi ) = 2\\), then \\(\\theta  + \\varphi\\)=','\(a\)\\({30^o}\\)','\(b\)\\({45^o}\\)','\(c\)\\({60^o}\\)','\(d\)\\({75^o}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\((1 + \\tan \\theta )\\,\\,(1 + \\tan \\varphi ) = 2 \\Rightarrow \\frac{{\\tan\\theta  + \\tan \\varphi }}{{1 - \\tan \\theta \\tan \\varphi }} = 1\\)\\( \\Rightarrow \\)  \\(\\tan (\\theta  + \\varphi ) = 1\\) \\( \\Rightarrow \\) \\(\\theta + \\varphi  = \\frac{\\pi }{4}\\) = 45\\(^{o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan (\\pi \\cos \\theta ) = \\cot (\\pi \\sin \\theta ),\\)then the value of\\(\\cos \\left( {\\theta  - \\frac{\\pi }{4}} \\right)\\)=','\(a\)\\(\\frac{1}{{2\\sqrt 2 }}\\)','\(b\)\\(\\frac{1}{{\\sqrt 2 }}\\)','\(c\)\\(\\frac{1}{{3\\sqrt 2 }}\\)','\(d\)\\(\\frac{1}{{4\\sqrt 2 }}\\)',' (a','TRIGONOMETRY(EPH)','2','Answer =  \(a\) \\(\\tan (\\pi \\cos \\theta ) = \\tan \\left( {\\frac{\\pi }{2} - \\pi \\sin\\theta } \\right)\\)\\(\\therefore \\)\\(\\,\\sin \\theta  + \\cos \\theta  = \\frac{1}{2}\\)\\(\\Rightarrow \\,\\cos \\left( {\\theta  - \\frac{\\pi }{4}} \\right) = \\frac{1}{{2\\sqrt2 }}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan (\\pi \\cos \\theta ) = \\cot (\\pi \\sin \\theta )\\), then \\(\\sin \\left({\\theta  + \\frac{\\pi }{4}} \\right)\\) equals','\(a\)\\(\\frac{1}{{\\sqrt 2 }}\\)','\(b\)\\(\\frac{1}{2}\\)','\(c\)\\(\\frac{1}{{2\\sqrt 2 }}\\)','\(d\)\\(\\frac{{\\sqrt 3 }}{2}\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\tan (\\pi \\cos \\theta ) = \\tan \\left( {\\frac{\\pi }{2} - \\pi \\sin \\theta} \\right)\\)\\(\\therefore \\sin \\theta  + \\cos \\theta  = \\frac{1}{2}\\) \\(\\Rightarrow{}\\)\\(\\sin\\left( {\\theta  + \\frac{\\pi }{4}} \\right) = \\frac{1}{{2\\sqrt 2 }}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\(\\theta \\)lying between 0 and \\(\\pi /2\\)and satisfying theequation  \\(\\left| {\\,\\begin{array}{*{20}{c}}{1 + {{\\sin }^2}\\theta }&{{{\\cos }^2}\\theta }&{4\\sin 4\\theta }\\\\{{{\\sin }^2}\\theta }&{1 + {{\\cos }^2}\\theta }&{4\\sin 4\\theta }\\\\{{{\\sin }^2}\\theta }&{{{\\cos }^2}\\theta }&{1 + 4\\sin 4\\theta }\\end{array}\\,} \\right| = 0\\)','\(a\)\\(\\frac{{7\\pi }}{{24}}\\) or \\(\\frac{{11\\pi }}{{24}}\\)','\(b\)\\(\\frac{{5\\pi}}{{24}}\\)','\(c\)\\(\\frac{\\pi }{{24}}\\)','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)The given determinant(Applying \\({R_1} \\to {R_1} - {R_3}\\) and \\({R_2} \\to {R_2} - {R_3}\\))  reducesto \\(\\left| {\\,\\begin{array}{*{20}{c}}1&0&{ - 1}\\\\0&1&{ - 1}\\\\{{{\\sin }^2}\\theta }&{{{\\cos }^2}\\theta }&{1 + 4\\sin 4\\theta }\\end{array}\\,} \\right|\\, = 0\\)\\( \\Rightarrow \\)\\(1 + 4\\sin 4\\theta  + {\\cos ^2}\\theta  + {\\sin ^2}\\theta  = 0\\)(By expanding along \\({R_1})\\)\\(\\Rightarrow{}\\) \\(4\\sin 4\\theta  =  - 2\\) \\(\\Rightarrow{}\\) \\(\\sin 4\\theta  =\\frac{{ - 1}}{2}\\)\\(\\Rightarrow{}\\) \\(4\\theta  = \\frac{{7\\pi }}{6}\\) or \\(\\frac{{11\\pi }}{6}\\),(\\(0 < 4\\theta  < 2\\pi \\))Since, \\(0 < \\theta  < \\frac{\\pi }{2}\\) \\(\\Rightarrow{}\\) \\(0 < 4\\theta  < 2\\pi\\) \\(\\Rightarrow{}\\) \\(\\theta  = \\frac{{7\\pi }}{{24}},\\,\\,\\frac{{11\\pi}}{{24}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If cot \\((\\alpha  + \\beta ) = 0,\\)then \\(\\sin (\\alpha  + 2\\beta ) = \\)','\(a\)\\(\\sin \\alpha \\)','\(b\)\\(\\cos \\alpha \\)','\(c\)\\(\\sin \\beta \\)','\(d\)\\(\\cos 2\\beta \\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)Given, cot \\((\\alpha  + \\beta ) = 0 \\Rightarrow \\cos (\\alpha  + \\beta ) =0\\)\\(\\Rightarrow{}\\)  \\(\\alpha  + \\beta  = (2n + 1)\\frac{\\pi }{2},n \\in I\\)\\(\\therefore{}\\)\\(\\sin (\\alpha  + 2\\beta ) = \\sin (2\\alpha  + 2\\beta  - \\alpha)\\)=\\(\\sin {\\rm{ }}[(2n + 1){\\rm{ }}\\pi  - \\alpha ]\\)\\( = \\sin (\\,2n\\pi  + \\pi  - \\alpha \\,)\\) = \\(\\sin (\\,\\pi  - \\alpha\\,)\\, = \\sin \\alpha \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If n is any integer, then the general solution of the equation \\(\\cos x -\\sin x = \\frac{1}{{\\sqrt 2 }}\\) is ','\(a\)\\(x = 2n\\pi  - \\frac{\\pi }{{12}}\\) or \\(x = 2n\\pi  + \\frac{{7\\pi }}{{12}}\\)','\(b\)\\(x = n\\pi  \\pm \\frac{\\pi }{{12}}\\)','\(c\)\\(x = 2n\\pi  + \\frac{\\pi }{{12}}\\) or \\(x = 2n\\pi  - \\frac{{7\\pi }}{{12}}\\)','\(d\)\\(x = n\\pi  + \\frac{\\pi }{{12}}\\) or \\(x = n\\pi  - \\frac{{7\\pi }}{{12}}\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)Given equation is,\\(\\cos x - \\sin x = \\frac{1}{{\\sqrt 2 }}\\)Dividing equation by \\(\\sqrt 2 \\), \\(\\frac{1}{{\\sqrt 2 }}\\cos x -\\frac{1}{{\\sqrt 2 }}\\sin x = \\frac{1}{2}\\)\\(\\cos \\left( {\\frac{\\pi }{4} + x} \\right) = \\cos \\frac{\\pi }{3}\\). Hence,\\(\\frac{\\pi }{4} + x = 2n\\pi  \\pm \\frac{\\pi }{3}\\)\\(x = 2n\\pi  + \\frac{\\pi }{3} - \\frac{\\pi }{4} = 2n\\pi  + \\frac{\\pi }{{12}}\\)or\\(x = 2n\\pi  - \\frac{\\pi }{3} - \\frac{\\pi }{4} = 2n\\pi  - \\frac{{7\\pi}}{{12}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The general solution of \\(\\sin x - \\cos x = \\sqrt 2 \\), for any integer nis','\(a\)\\(n\\pi \\)','\(b\)\\(2n\\pi  + \\frac{{3\\pi }}{4}\\)','\(c\)\\(2n\\pi \\)','\(d\)\\((2n + 1)\\,\\pi \\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\sin x\\frac{1}{{\\sqrt 2 }} - \\cos x\\frac{1}{{\\sqrt 2 }} = 1 \\Rightarrow\\cos \\left( {x + \\frac{\\pi }{4}} \\right) =  - 1\\)\\(\\Rightarrow{}\\)  \\(x + \\frac{\\pi }{4} = 2n\\pi  \\pm \\pi  \\Rightarrow 2n\\pi  +\\frac{{3\\pi }}{4}{\\rm{or}}\\,\\,2n\\pi  - \\frac{{5\\pi }}{4}\\) .',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(12{\\cot ^2}\\theta  - 31\\,{\\rm{cosec }}\\theta  + {\\rm{32}} = {\\rm{0}}\\),then the value of \\(\\sin \\theta \\) is','\(a\)\\(\\frac{3}{5}\\) or 1','\(b\)\\( - \\sin (B + 2C) = \\frac{1}{2}\\) or \\(\\frac{{ -2}}{3}\\)','\(c\)\\(\\frac{4}{5}\\) or \\(\\frac{3}{4}\\)','\(d\)\\( \\pm \\frac{1}{2}\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(12{\\cot ^2}\\theta  - 31\\cos ec\\theta  + 32 = 0\\)\\(12({\\rm{cos}}{\\rm{e}}{{\\rm{c}}^2}\\theta  - 1) - 31{\\rm{cos}}{\\rm{ec }}\\theta  +{\\rm{32}} = {\\rm{0}}\\)\\(12{\\rm{cos}}{\\rm{e}}{{\\rm{c}}^2}\\theta  - 31\\,{\\rm{cos}}{\\rm{ec }}\\theta  +{\\rm{20}} = {\\rm{0}}\\)\\(12\\,{\\rm{cos}}{\\rm{e}}{{\\rm{c}}^2}\\theta  - 16\\,\\,{\\rm{cos}}{\\rm{ec }}\\theta  -15{\\rm{cos}}{\\rm{ec}}\\theta  + 20 = {\\rm{0}}\\)\\((4\\cos {\\rm{ec}}\\theta  - 5)(3\\cos {\\rm{ec}}\\theta  - 4) = 0\\)\\({\\rm{cos}}{\\rm{ec}}\\theta  = \\frac{5}{4},\\frac{4}{3}\\);  \\(\\therefore{}\\)\\(\\sin \\theta  = \\frac{4}{5},\\frac{3}{4}\\).',CURRENT_TIMESTAMP);
