INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Period of \\(\\frac{{\\sin \\theta  + \\sin 2\\theta }}{{\\cos \\theta  + \\cos2\\theta }}\\)is','\(a\)\\(2\\pi \\)','\(b\)\\(\\pi \\)','\(c\)\\(\\frac{{2\\pi }}{3}\\)','\(d\)\\(\\frac{\\pi }{3}\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\frac{{\\sin \\theta  + \\sin 2\\theta }}{{\\cos \\theta  + \\cos 2\\theta }} =\\frac{{2\\sin \\,\\left( {\\frac{{3\\theta }}{2}} \\right)\\,\\cos \\left( {\\frac{\\theta}{2}} \\right)}}{{2\\,\\cos \\left( {\\frac{{3\\theta }}{2}} \\right)\\,\\cos \\left({\\frac{\\theta }{2}} \\right)}} = \\tan \\left( {\\frac{{3\\theta }}{2}} \\right)\\)Hence period \\( = \\frac{{2\\pi }}{3}\\).',CURRENT_TIMESTAMP);
