INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Period of \\(\\sin \\frac{x}{2} - \\cos \\frac{x}{3}\\) is','\(a\)\\(2\\pi \\)','\(b\)\\(4\\pi \\)','\(c\)\\(8\\pi \\)','\(d\)\\(12\\pi \\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)Period of \\(\\sin \\frac{x}{2}\\) is \\(4\\pi \\) and period of \\(\\cos\\frac{x}{3}\\) is \\(6\\pi \\). Hence period of expression is \\(12\\pi \\) (L.C.M.).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Period of cot \\(3x - \\cos (4x + 3)\\)is','\(a\)\\(\\frac{\\pi }{3}\\)','\(b\)\\(\\frac{\\pi }{4}\\)','\(c\)\\(\\pi \\)','\(d\)\\(2\\pi \\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)Period of \\(\\cot 3x\\) is \\(\\frac{\\pi }{3}\\) and period of \\(\\cos (4x +3)\\) is \\(\\frac{\\pi }{2}\\) \\( \\Rightarrow \\) L.C.M. is \\(\\pi \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Period of \\(|2\\sin 3\\theta  + 4\\cos 3\\theta |\\)is','\(a\)\\(\\frac{{2\\pi }}{3}\\)','\(b\)\\(\\pi \\)','\(c\)\\(\\frac{\\pi }{2}\\)','\(d\)\\(\\frac{\\pi }{3}\\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)Period of \\(2\\sin 3\\theta \\) is \\(\\frac{{2\\pi }}{3}\\) and period of\\(4\\cos 3\\theta \\) is \\(\\frac{{2\\pi }}{3}\\). Therefore period of the expressionis \\(\\frac{\\pi }{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The period of \\({\\sin ^4}x + {\\cos ^4}x\\)is      ','\(a\)\\(\\pi /2\\)','\(b\)\\(\\pi \\)','\(c\)\\(2\\pi \\)','\(d\)\\(3\\pi /2\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)Let \\(f(x) = {\\sin ^4}x + {\\cos ^4}x\\)= \\({({\\sin ^2}x + {\\cos ^2}x)^2} - 2{\\sin ^2}x{\\cos ^2}x\\)= \\(1 - \\frac{{4{{\\sin }^2}x{{\\cos }^2}x}}{2} = 1 - \\frac{{{{\\sin}^2}2x}}{2}\\)=\\(1 - \\frac{1}{4}(2{\\sin ^2}2x) = 1 - \\left( {\\frac{{1 - \\cos x}}{4}} \\right)\\)\\( = \\frac{3}{4} + \\frac{1}{4}\\cos 4x\\)Hence the period of function = \\(\\frac{{2\\pi }}{4} = \\frac{\\pi }{2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The period of the function \\(f(\\theta ) = \\sin \\frac{\\theta }{3} + \\cos\\frac{\\theta }{2}\\)is','\(a\)\\(3\\pi \\)','\(b\)\\(6\\pi \\)','\(c\)\\(9\\pi \\)','\(d\)\\(12\\pi \\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)Period of \\(\\sin (\\theta /3) = 6\\pi \\) and period of \\(\\cos (\\theta /2) =4\\pi \\)L.C.M. of \\(6\\pi \\)and \\(4\\pi \\)= \\(12\\pi \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the period of the function \\(f(x) = \\sin \\left( {\\frac{x}{n}} \\right)\\)is\\(4\\pi \\), then n is equal to ','\(a\)1','\(b\)4','\(c\)8','\(d\)2','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)\\(\\sin \\,\\left( {\\frac{x}{n}} \\right) = \\sin \\,\\left( {2\\pi  +\\frac{x}{n}} \\right)\\) = \\(\\sin \\left( {\\frac{1}{n}(2n\\pi  + x)} \\right)\\)\\(\\Rightarrow{}\\)  Period of the function sin \\(\\left( {\\frac{x}{n}} \\right)\\)is\\(2n\\pi \\).\\(\\Rightarrow{}\\)  \\(2n\\pi  = 4\\pi \\)\\( \\Rightarrow n = 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Period of \\({\\sin ^2}x\\)is ','\(a\)\\(\\pi \\)','\(b\)\\(2\\pi \\)','\(c\)\\(\\frac{\\pi }{2}\\)','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\({\\sin ^2}x = \\frac{{1 - \\cos 2x}}{2}\\)\\(\\Rightarrow{}\\) Period \\( =\\frac{{2\\pi }}{2} = \\pi \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The period of the function \\(y = \\sin 2x\\)is','\(a\)\\(2\\pi \\)','\(b\)\\(\\pi \\)','\(c\)\\(\\pi /2\\)','\(d\)\\(4\\pi \\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)( Period of \\(\\sin (ax + b) = \\frac{{2\\pi }}{{|a|}}\\)\\(\\therefore{}\\) Period of \\(\\sin 2x = \\frac{{2\\pi }}{{|2|}} = \\pi .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Which of the following functions has period \\(2\\pi \\)','\(a\)\\(y = \\sin \\left( {2\\pi t + \\frac{\\pi }{3}} \\right) + \\)\\(2\\sin \\left( {3\\pit + \\frac{\\pi }{4}} \\right) + 3\\sin 5\\pi t\\)','\(b\)\\(y = \\sin \\frac{\\pi }{3}t + \\sin \\frac{\\pi }{4}t\\)','\(c\)\\(y = \\sin t + \\cos 2t\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)The period of the function in option \(a\) is 2.The period of the function in option \(b\) is 24.The period of the function in option \(c\) is \\(2\\pi \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The period of the function \\(\\sin \\left( {\\frac{{2x}}{3}} \\right) + \\sin \\left({\\frac{{3x}}{2}} \\right)\\)is','\(a\)\\(2\\pi \\)','\(b\)\\(10\\pi \\)','\(c\)\\(6\\pi \\)','\(d\)\\(12\\pi \\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)Period of \\(\\sin \\left( {\\frac{{2x}}{3}} \\right)\\, = \\frac{{2\\pi}}{{2/3}} = 3\\pi \\)Period of \\(\\sin \\,\\left( {\\frac{{3x}}{2}} \\right) = \\frac{{2\\pi }}{{3/2}} =\\frac{{4\\pi }}{3}\\)L.C.M. of \\(3\\pi \\)and \\(\\frac{{4\\pi }}{3}\\)= \\(12\\pi \\).  Hence period is\\(12\\pi \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let \\(f(x) = \\cos px + \\sin x\\) be periodic, then p must be','\(a\)Rational','\(b\)Irrational','\(c\)Positive real number','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)Let \\(f(x)\\) be periodic with period \\(\\lambda ,\\) then \\(\\sin \\,(x +\\lambda ) + \\cos p\\,(x + \\lambda ) = \\sin x + \\cos px,\\,\\,\\forall \\,\\,x \\in R\\)Putting \\(x = 0\\) and replace \\(\\lambda \\) by \\( - \\lambda \\), we have \\(\\sin\\lambda  + \\cos p\\lambda  = 1\\) and \\( - \\sin \\lambda  + \\cos p\\lambda  = 1\\)Solving these, we get \\(\\sin \\lambda  = 0\\) so \\(\\lambda  = n\\pi \\) and\\(\\cos p\\lambda  = 1\\) so \\(p\\lambda  = 2m\\pi .\\) As \\(\\lambda  \\ne 0,\\,\\,m\\)and \\(n\\) are non-zero integers. Hence \\(p = \\frac{{2m\\pi }}{\\lambda },\\)which isrational.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The period of the function \\(\\sin \\left( {\\frac{{\\pi x}}{2}} \\right) + \\cos\\left( {\\frac{{\\pi x}}{2}} \\right)\\) is','\(a\)4','\(b\)6','\(c\)12','\(d\)24','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)Period of \\(\\sin \\left( {\\frac{{\\pi x}}{2}} \\right) = \\frac{{2\\pi }}{{\\pi/2}} = 4\\)Period of \\(\\cos \\,\\left( {\\frac{{\\pi x}}{2}} \\right) = \\frac{{2\\pi }}{{\\pi /2}}= 4\\)\\(\\therefore \\) Period of \\(\\sin \\frac{{\\pi x}}{2} + \\cos \\frac{{\\pi x}}{2} =\\)L.C.M. of (4, 4)=4.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The function \\(f(x) = \\sin \\frac{{\\pi x}}{2} + 2\\cos \\frac{{\\pi x}}{3} -\\tan \\frac{{\\pi x}}{4}\\) is period with period  ','\(a\)6','\(b\)3','\(c\)4','\(d\)12','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)Period of \\(\\sin \\frac{{\\pi x}}{2} = \\frac{{2\\pi }}{{\\pi /2}} = 4\\)Period of \\(\\cos \\frac{{\\pi x}}{3} = \\frac{{2\\pi }}{{\\pi /3}} = 6\\)Period of \\(\\tan \\frac{{\\pi x}}{4} = \\frac{\\pi }{{\\pi /4}} = 4\\)\\(\\therefore \\)Period of \\(f(x) = \\)L.C.M. of (4, 6, 4) = 12.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The period of the function \\(\\vert{}\\)sin\\(\\pi{}\\) x\\(\\vert{}\\) is','\(a\)\\({\\pi ^2}\\)','\(b\)\\(2\\pi \\)','\(c\)2','\(d\)1','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)Period of \\(\\left| {\\,\\sin \\pi \\,x\\,} \\right| = \\frac{\\pi }{\\pi } = 1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The period of \\(f(x) = \\sin \\left( {\\frac{{\\pi x}}{{n - 1}}} \\right) + \\cos\\,\\left( {\\frac{{\\pi x}}{n}} \\right)\\,\\,,\\,n \\in Z\\), \\(n > 2\\) is','\(a\)\\(2\\pi n(n - 1)\\)','\(b\)\\(4n\\,(n - 1)\\)','\(c\)\\(2n\\,(n - 1)\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(f(x) = \\sin \\left( {\\frac{{\\pi x}}{{n - 1}}} \\right) + \\cos \\left({\\frac{{\\pi x}}{n}} \\right)\\)Period of \\(\\sin \\left( {\\frac{{\\pi x}}{{n - 1}}} \\right) = \\frac{{2\\pi}}{{\\left( {\\frac{\\pi }{{n - 1}}} \\right)}} = 2\\left( {n - 1} \\right)\\)and period of \\(\\cos \\left( {\\frac{{\\pi x}}{n}} \\right) = \\frac{{2\\pi}}{{\\left( {\\frac{\\pi }{n}} \\right)}} = 2n\\)Hence period of \\(f(x)\\) is L.C.M. of \\(2\\,n\\) and \\(2(n - 1)\\) \\( \\Rightarrow2n(n - 1)\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a triangle ABC, \\(a = 5,b = 7\\) and \\(\\sin A = \\frac{3}{4}\\) how manysuch triangles are possible','\(a\)1','\(b\)0','\(c\)2','\(d\)Infinite','2','TRIGONOMETRY(EPH)','2','Answer = \(b\) \\(\\frac{{\\sin A}}{a} = \\frac{{\\sin B}}{b} \\Rightarrow \\frac{3}{{4 \\times5}} = \\frac{{\\sin B}}{7} \\Rightarrow \\sin B = \\frac{{21}}{{20}}\\),which is not possible.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a triangle \\(ABC,\\)\\((s - a)(s - b) = s\\,\\,(s - c)\\), then  angle C isequal to ','\(a\)\\({90^o}\\)','\(b\)\\({45^o}\\)','\(c\)\\({30^o}\\)','\(d\)\\({60^o}\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\tan \\frac{C}{2} = \\sqrt {\\frac{{(s - a)(s - b)}}{{s(s - c)}}}  = 1\\)\\(= \\tan \\left( {\\frac{\\pi }{4}} \\right)\\), from given data. Hence \\(C = {90^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a \\(\\Delta ABC\\), if \\(2s = a + b + c\\)and \\((s - b)(s - c) = \\) \\(x{\\sin^2}\\frac{A}{2},\\) then x =','\(a\)bc','\(b\)ca','\(c\)ab','\(d\)abc','1','TRIGONOMETRY(EPH)','2','Answer = \(a\) \\(\\sin \\frac{A}{2} = \\sqrt {\\frac{{(s - b)(s - c)}}{{bc}}} \\)\\(\\Rightarrow bc{\\sin ^2}\\frac{A}{2} = (s - b)(s - c)\\)Hence \\(x = bc\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the angles of a triangle \\(ABC\\)be in A.P., then','\(a\)\\({c^2} = {a^2} + {b^2} - ab\\)','\(b\)\\({b^2} = {a^2} + {c^2} - ac\\)','\(c\)\\({a^2} = {b^2} + {c^2} - ac\\)','\(d\)\\({b^2} = {a^2} + {c^2}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)A, B, C are in A. P. then angle \\(B = {60^o},\\) \\(\\cos B = \\frac{{{a^2} +{c^2} - {b^2}}}{{2ac}}\\), \\(\\left\\{ \\begin{array}{l}{\\rm{since }}A + B + C = {Answer = o}\\,\\,{\\rm{and}}\\\\{\\rm{        }}A + C = 2B \\Rightarrow B = {60^o}\\end{array} \\right\\}\\)\\(\\Rightarrow{}\\) \\(\\frac{1}{2} = \\frac{{{a^2} + {c^2} - {b^2}}}{{2ac}}\\Rightarrow {a^2} + {c^2} - {b^2} = ac\\)\\(\\Rightarrow{}\\) \\({b^2} = {a^2} + {c^2} - ac\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In triangle \\(ABC\\), \\((b + c)\\cos A + (c + a)\\cos B\\) \\( + (a + b)\\cos C = \\)','\(a\)0','\(b\)1','\(c\)\\(a + b + c\\)','\(d\)\\(2(a + b + c)\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\((b + c)\\cos A + (c + a)\\cos B + (a + b)\\cos C = a + b + c\\)From expanding and collecting terms using projection rule, \\(a = b\\cos C + c\\cosB\\)etc.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC,\\frac{{\\sin B}}{{\\sin (A + B)}} = \\)  ','\(a\)\\(\\frac{b}{{a + b}}\\)','\(b\)\\(\\frac{b}{c}\\)','\(c\)\\(\\frac{c}{b}\\)','\(d\)None of these','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\frac{{\\sin B}}{{\\sin (A + B)}} = \\frac{{\\sin B}}{{\\sin C}} =\\frac{b}{c}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC,\\frac{{\\sin (A - B)}}{{\\sin (A + B)}} = \\)','\(a\)\\(\\frac{{{a^2} - {b^2}}}{{{c^2}}}\\)','\(b\)\\(\\frac{{{a^2} + {b^2}}}{{{c^2}}}\\)','\(c\)\\(\\frac{{{c^2}}}{{{a^2} - {b^2}}}\\)','\(d\)\\(\\frac{{{c^2}}}{{{a^2} + {b^2}}}\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\frac{{\\sin (A - B)}}{{\\sin (A + B)}} = \\frac{{\\sin A\\cos B - \\sinB\\cos A}}{{\\sin C}}\\)\\(= \\frac{a}{c}\\cos B - \\frac{b}{c}\\cos A\\)But \\(\\cos B = \\frac{{{a^2} + {c^2} - {b^2}}}{{2ac}},\\cos A = \\frac{{{b^2} +{c^2} - {a^2}}}{{2bc}}\\)\\( \\Rightarrow \\frac{a}{c}\\cos B - \\frac{b}{c}\\cos A = \\frac{1}{{2{c^2}}}\\)\\(({a^2} + {c^2} - {b^2} - {b^2} - {c^2} + {a^2})\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a \\(\\Delta ABC\\), if \\({b^2} + {c^2} = 3{a^2}\\), then \\(\\cot B + \\cot C -\\cot A = \\)','\(a\)1','\(b\)\\(\\frac{{ab}}{{4\\Delta }}\\)','\(c\)0','\(d\)\\(\\frac{{ac}}{{4\\Delta }}\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\cot B + \\cot C - \\cot A = \\frac{{\\cos B}}{{\\sin B}} + \\frac{{\\cosC}}{{\\sin C}} - \\cot A\\)\\( = \\frac{{\\sin C\\cos B + \\cos C\\sin B}}{{\\sin B\\sin C}} - \\cot A\\)\\(= \\frac{{\\sin (B + C)}}{{\\sin B\\sin C}} - \\frac{{\\cos A}}{{\\sin A}}\\)\\(= \\frac{{{{\\sin }^2}A - \\sin B\\sin C\\cos A}}{{\\sin A\\sin B\\sin C}} =\\frac{{{a^2} - bc\\cos A}}{{k(abc)}}\\)Since\\(\\frac{{\\sin A}}{a} = \\frac{{\\sin B}}{b} = \\frac{{\\sin C}}{c} = k\\)  (say)\\(= \\frac{{{a^2} - {b^2}}}{{{c^2}}}.\\)and \\(\\cos A = \\frac{{{b^2} + {c^2} - {a^2}}}{{2bc}}\\)\\( = \\frac{{{a^2} -bc\\frac{{({b^2} + {c^2} - {a^2})}}{{2bc}}}}{{(abc)k}}\\)\\(= \\frac{{({a^2} - {a^2})}}{{abc\\,k}} = 0,{\\rm{ }}\\left\\{{{\\rm{As}}\\,\\,\\frac{{{b^2} + {c^2} - {a^2}}}{2} = \\frac{{3{a^2} - {a^2}}}{2} =\\frac{{2{a^2}}}{2} = {a^2}} \\right\\}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a \\(\\Delta ABC\\), if  \\({c^2} + {a^2} - {b^2} = ac\\), then \\(\\angle B =\\)[MP PET , 89, 90]','\(a\)\\(\\frac{\\pi }{6}\\)','\(b\)\\(\\frac{\\pi }{4}\\)','\(c\)\\(\\frac{\\pi }{3}\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\cos B = \\frac{{{c^2} + {a^2} - {b^2}}}{{2ac}} \\Rightarrow \\cos B =\\frac{1}{2}i.e.,B = \\frac{\\pi }{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC,\\left( {\\cot \\frac{A}{2} + \\cot \\frac{B}{2}} \\right)\\,\\left({a{{\\sin }^2}\\frac{B}{2} + b{{\\sin }^2}\\frac{A}{2}} \\right)\\)=','\(a\)\\(\\cot C\\)','\(b\)\\(c\\cot C\\)','\(c\)\\(\\cot \\frac{C}{2}\\)','\(d\)\\(c\\cot \\frac{C}{2}\\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)\\(\\left\\{ {\\cot \\frac{A}{2} + \\cot \\frac{B}{2}} \\right\\}\\left\\{ {a{{\\sin}^2}\\frac{B}{2} + b{{\\sin }^2}\\frac{A}{2}} \\right\\}\\)= \\(\\left\\{ {\\frac{{\\cos \\frac{C}{2}}}{{\\sin \\frac{A}{2}\\sin \\frac{B}{2}}}}\\right\\}{\\rm{  }}\\left\\{ {a{{\\sin }^2}\\frac{B}{2} + b{{\\sin }^2}\\frac{A}{2}}\\right\\}\\)= \\(\\left\\{ {\\cos \\frac{C}{2}} \\right\\}{\\rm{ }}\\left\\{ {a\\frac{{\\sin\\frac{B}{2}}}{{\\sin \\frac{A}{2}}} + b\\frac{{\\sin \\frac{A}{2}}}{{\\sin\\frac{B}{2}}}} \\right\\}\\)\\(= \\sqrt {\\frac{{s(s - c)}}{{ab}}} \\left\\{ {a\\frac{{\\sqrt {\\frac{{(s - a)(s -c)}}{{ac}}} }}{{\\sqrt {\\frac{{(s - b)(s - c)}}{{bc}}} }} + b\\frac{{\\sqrt{\\frac{{(s - b)(s - c)}}{{bc}}} }}{{\\sqrt {\\frac{{(s - a)(s - c)}}{{ac}}} }}}\\right\\}\\)\\(= \\sqrt {\\frac{{s(s - c)}}{{ab}}} \\left\\{ {\\sqrt {\\left( {\\frac{{s - a}}{{s -b}}} \\right){\\rm{ }}ab}  + \\sqrt {\\left( {\\frac{{s - b}}{{s - a}}} \\right){\\rm{}}ab} } \\right\\}\\)=\\(\\sqrt {s(s - c)} \\left\\{ {\\frac{{s - a + s - b}}{{\\sqrt {(s - a)(s - b)} }}}\\right\\} = \\sqrt {s(s - c)} \\left\\{ {\\frac{{2s - a - b}}{{\\sqrt {(s - a)(s - b)}}}} \\right\\}\\)\\(= c\\sqrt {\\frac{{s(s - c)}}{{(s - a)(s - b)}}}  = c\\cot \\frac{C}{2}.\\)Trick : Such type of unconditional problems can be checked by putting theparticular values for \\(a = 1,\\) \\(b = \\sqrt 3 ,\\) \\(c = 2\\) and \\(A = {30^o}\\),\\(B = {60^o},C = {90^o}\\).Hence expression is equal to 2 which is given by \(d\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC,\\)if \\({\\sin ^2}\\frac{A}{2},{\\sin ^2}\\frac{B}{2},{\\sin^2}\\frac{C}{2}\\) be in H. P. then a, b, c will be in','\(a\)A. P.','\(b\)G. P.','\(c\)H. P.','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\frac{1}{{{{\\sin }^2}\\frac{A}{2}}},\\frac{1}{{{{\\sin}^2}\\frac{B}{2}}},\\frac{1}{{{{\\sin }^2}\\frac{C}{2}}}\\)are in A. P.\\(\\Rightarrow{}\\)   \\(\\frac{1}{{{{\\sin }^2}\\frac{C}{2}}} - \\frac{1}{{{{\\sin}^2}\\frac{B}{2}}} = \\frac{1}{{{{\\sin }^2}\\frac{B}{2}}} - \\frac{1}{{{{\\sin}^2}\\frac{A}{2}}}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{ab}}{{(s - a)(s - b)}} - \\frac{{ac}}{{(s - a)(s -c)}}\\)\\(= \\frac{{ac}}{{(s - a)(s - c)}} - \\frac{{bc}}{{(s - b)(s - c)}}\\)\\(\\Rightarrow{}\\)\\(\\left( {\\frac{a}{{s - a}}} \\right)\\,\\left( {\\frac{{b(s - c) -c(s - b)}}{{(s - b)(s - c)}}} \\right)\\)=\\(\\left( {\\frac{c}{{s - c}}}\\right)\\,\\left( {\\frac{{a(s - b) - b(s - a)}}{{(s - a)(s - b)}}} \\right)\\)\\(\\Rightarrow{}\\) \\(abs - abc - acs + abc = acs - abc - bcs + abc\\)\\(\\Rightarrow{}\\) \\(ab - ac = ac - bc \\Rightarrow ab + bc = 2ac\\)or \\(\\frac{1}{c} + \\frac{1}{a} = \\frac{2}{b}\\), i.e., \\(a,b,c\\) are in H. P.Note : Students should remember this question as a fact.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC,{(a - b)^2}{\\cos ^2}\\frac{C}{2} + {(a + b)^2}{\\sin^2}\\frac{C}{2} = \\)','\(a\)\\({a^2}\\)','\(b\)\\({b^2}\\)','\(c\)\\({c^2}\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(({a^2} + {b^2} - 2ab){\\cos ^2}\\frac{C}{2} + ({a^2} + {b^2} + 2ab){\\sin^2}\\frac{C}{2}\\)\\(= {a^2} + {b^2} + 2ab\\left( {{{\\sin }^2}\\frac{C}{2} - {{\\cos }^2}\\frac{C}{2}}\\right)\\)\\(= {a^2} + {b^2} - 2ab\\cos C = {a^2} + {b^2} - ({a^2} + {b^2} - {c^2}) = {c^2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC,\\)if \\(a = 16,b = 24\\) and \\(c = 20,\\)then \\(\\cos\\frac{B}{2} = \\)','\(a\)3/4','\(b\)1/4','\(c\)1/2','\(d\)1/3','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(2s = a + b + c\\); \\(\\cos \\frac{B}{2} = \\sqrt {\\frac{{30 \\times6}}{{Answer = }}  = \\frac{3}{4}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In\\(\\Delta ABC,\\) if \\(\\cos A + \\cos C = 4{\\sin ^2}\\frac{1}{2}B,\\) then\\(a,b,c\\) are in','\(a\)A. P.','\(b\)G. P.','\(c\)H. P.','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\cos A + \\cos C = 4{\\sin ^2}\\frac{1}{2}B\\)\\(\\Rightarrow{}\\)  \\(2\\cos \\frac{{A + C}}{2}\\cos \\frac{{A - C}}{2} = 4{\\sin^2}\\frac{B}{2}\\)\\(\\Rightarrow{}\\)  \\(\\cos \\frac{{A + C}}{2}\\cos \\frac{{A - C}}{2} = 2{\\sin^2}\\frac{B}{2}\\)\\(\\Rightarrow{}\\)  \\(\\cos \\left( {\\frac{{A - C}}{2}} \\right) = 2\\sin\\frac{B}{2}\\)\\(\\Rightarrow{}\\)  \\(\\cos \\frac{A}{2}\\cos \\frac{C}{2} + \\sin \\frac{A}{2}\\sin\\frac{C}{2} = 2\\sin \\frac{B}{2}\\)\\(\\Rightarrow{}\\)  \\(\\sqrt {\\frac{{s(s - a)}}{{bc}}} \\sqrt {\\frac{{s(s -c)}}{{ab}}}  + \\sqrt {\\frac{{(s - b)(s - c)}}{{bc}}} \\sqrt {\\frac{{(s - a)(s -b)}}{{ab}}} \\)\\(= 2\\sqrt {\\frac{{(s - a)(s - c)}}{{ac}}}\\)\\(\\frac{{\\sqrt {(s - a)\\,(s - c)} }}{{ac}} + \\frac{{s - b}}{b}\\sqrt{\\frac{{(s - c)\\,{\\rm{ (}}s - a{\\rm{)}}}}{{ac}}} \\)\\(= 2\\sqrt {\\frac{{(s - a)\\,{\\rm{(}}s - c)}}{{ac}}}\\)\\(\\Rightarrow{}\\) \\(\\frac{s}{b} + \\frac{{s - b}}{b} = 2\\) \\(\\Rightarrow{}\\) \\(a+ c = 2b\\)\\(\\Rightarrow{}\\) \\(a,b,c\\) are in A. P.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC,\\)\\(1 - \\tan \\frac{A}{2}\\tan \\frac{B}{2} = \\)','\(a\)\\(\\frac{{2c}}{{a + b + c}}\\)','\(b\)\\(\\frac{a}{{a + b + c}}\\)','\(c\)\\(\\frac{2}{{a + b + c}}\\)','\(d\)\\(\\frac{{4a}}{{a + b + c}}\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(1 - \\tan \\frac{A}{2}\\tan \\frac{B}{2} = \\frac{{\\cos \\frac{A}{2}\\cos\\frac{B}{2} - \\sin \\frac{A}{2}\\sin \\frac{B}{2}}}{{\\cos \\frac{A}{2}\\cos\\frac{B}{2}}}\\)\\(= \\frac{{\\cos \\left( {\\frac{A}{2} + \\frac{B}{2}} \\right)}}{{\\cos\\frac{A}{2}\\cos \\frac{B}{2}}} = \\frac{{\\sin \\frac{C}{2}}}{{\\cos \\frac{A}{2}\\cos\\frac{B}{2}}}\\)\\( = {\\left[ {\\frac{{(s - a)(s - b)bc.\\,ac}}{{ab.s(s - a)s(s - b)}}}\\right]^{1/2}}\\)\\(= \\frac{c}{s} = \\frac{{2c}}{{a + b + c}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC,\\)\\({b^2}\\cos 2A - {a^2}\\cos 2B = \\)','\(a\)\\({b^2} - {a^2}\\)','\(b\)\\({b^2} - {c^2}\\)','\(c\)\\({c^2} - {a^2}\\)','\(d\)\\({a^2} + {b^2} + {c^2}\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\({b^2}\\cos 2A - {a^2}\\cos 2B\\)\\( = {b^2}(1 - 2{\\sin ^2}A) - {a^2}(1 -2{\\sin ^2}B)\\)\\(= {b^2} - {a^2} - 2({b^2}{\\sin ^2}A - {a^2}{\\sin ^2}B) = {b^2} - {a^2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In\\(\\Delta ABC,\\)a\\(\\sin (B - C) + b\\sin (C - A) + c\\sin (A - B) = \\)','\(a\)0','\(b\)\\(a + b + c\\)','\(c\)\\({a^2} + {b^2} + {c^2}\\)','\(d\)\\(2({a^2} + {b^2} + {c^2})\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(a\\sin (B - C) + b\\sin (C - A) + c\\sin (A - B)\\)\\( = k\\,(\\Sigma \\sin A\\sin (B - C)\\)\\(= k\\,\\{ \\Sigma \\sin (B + C)\\sin (B - C)\\}\\)\\(= k\\,\\left\\{ {\\Sigma \\frac{1}{2}(\\cos 2C - \\cos 2B)} \\right\\} = 0.\\)Note: Students should note here that most of the expressions containing thecyclic factor associating with \\`-\\' reduces to 0.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In\\(\\Delta ABC,\\)if \\(\\cot A,\\cot B,\\cot C\\)be in A. P., then \\({a^2},{\\rm{}}{b^2},{\\rm{ }}{c^2}\\) are in ','\(a\)H. P.','\(b\)G. P.','\(c\)A. P. ','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\cot A,\\cot B\\)and \\(\\cot C\\)are in A. P.\\(\\Rightarrow{}\\)  \\(\\cot A + \\cot C = 2\\cot B\\) \\(\\Rightarrow{}\\) \\(\\frac{{\\cosA}}{{\\sin A}} + \\frac{{\\cos C}}{{\\sin C}} = \\frac{{2\\cos B}}{{\\sin B}}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{{b^2} + {c^2} - {a^2}}}{{2bc(ka)}} + \\frac{{{a^2} +{b^2} - {c^2}}}{{2ab(kc)}} = 2\\frac{{{a^2} + {c^2} - {b^2}}}{{2ac(kb)}}\\)\\(\\Rightarrow{}\\) \\({a^2} + {c^2} = 2{b^2}\\). Hence\\({a^2},{b^2},{c^2}\\)are inA. P.Note : Students should remember this question as a fact.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC,\\) if \\((a + b + c)(a - b + c)\\)=3ac, then  ','\(a\)\\(\\angle B = {60^o}\\)','\(b\)\\(\\angle B = {30^o}\\)','\(c\)\\(\\angle C = {60^o}\\)','\(d\)\\(\\angle A + \\angle C = {90^o}\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\({(a + c)^2} - {b^2} = 3ac \\Rightarrow {a^2} + {c^2} - {b^2} = ac\\)But \\(\\cos B = \\frac{{{a^2} + {c^2} - {b^2}}}{{2ac}} = \\frac{1}{2} \\Rightarrow B= \\frac{\\pi }{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In\\(\\Delta ABC,\\) if \\(2(bc\\cos A + ca\\cos B + ab\\cos C) = \\)','\(a\)0','\(b\)\\(a + b + c\\)','\(c\)\\({a^2} + {b^2} + {c^2}\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)On putting the values of \\(\\cos A,\\cos B\\)and \\(\\cos C\\), we get therequired result i.e., \\({a^2} + {b^2} + {c^2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC,\\) \\({\\rm{cosec }}A(\\sin B\\cos C + \\cos B\\sin C) = \\)[MP PET , ; Pb. CET , 94]','\(a\)\\(c/a\\)','\(b\)\\(a/c\\)','\(c\)1','\(d\)\\(c/ab\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\left( {\\frac{b}{a}\\cos C + \\frac{c}{a}\\cos B} \\right) = \\)1;   (byprojection rule).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({\\cos ^2}A + {\\cos ^2}C = {\\sin ^2}B,\\)then \\(\\Delta ABC\\)is ','\(a\)Equilateral','\(b\)Right angled','\(c\)Isosceles','\(d\)None of these','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)It is obvious.Trick: Obviously it is not an equilateral triangle because A=B=C=60\\(^{o }\\)doesnot satisfy the given condition. But B = 90\\(^{o}\\) then \\({\\sin ^2}B = 1\\) and\\({\\cos ^2}A + {\\cos ^2}C = {\\cos ^2}A + {\\cos ^2}\\left( {\\frac{\\pi }{2} - A}\\right)\\,\\)\\(= \\,{\\cos ^2}A + {\\sin ^2}A = 1\\)Hence this satisfy the condition, so it is a right angle triangle but notnecessarily isosceles.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the angles of a triangle be in the ratio 1: 2 : 7, then the ratio of its greatest side to the least side is','\(a\) 1:2','\(b\) 1','\(c\)\\((\\sqrt 5  + 1):(\\sqrt 5  - 1)\\)','\(d\)\\((\\sqrt 5  - 1):(\\sqrt 5  + 1)\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(x + 2x + 7x = {Answer = o} \\Rightarrow x = {18^o}\\)Hence the angles are \\({18^o},{\\rm{ }}{36^o},{\\rm{ }}{Answer = o}\\)Greatest side \\( \\propto \\)\\(\\sin ({Answer = o})\\)Smallest side \\( \\propto \\) \\(\\sin ({18^o})\\) and ratio\\( = \\frac{{\\sin{{Answer = ^o}}}{{\\sin ({{18}^o})}} = \\frac{{\\sqrt 5  + 1}}{{\\sqrt 5  - 1}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a triangle \\(ABC\\), \\(\\angle C = {60^o},\\)then \\(\\frac{1}{{a + c}} +\\frac{1}{{b + c}} = \\)','\(a\)\\(\\frac{1}{{a + b + c}}\\)','\(b\)\\(\\frac{2}{{a + b + c}}\\)','\(c\)\\(\\frac{3}{{a + b + c}}\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\cos C = \\frac{\\pi }{3} \\Rightarrow {a^2} + {b^2} - {c^2} = ab\\)\\(\\Rightarrow{}\\) \\({b^2} + bc + {a^2} + ac = ab + ac + bc + {c^2}\\)\\(\\Rightarrow{}\\) \\(b(b + c) + a(a + c) = (a + c)(b + c)\\)Divide by \\((a + c){\\rm{ }}(b + c)\\) and add 2 on both sides\\(\\Rightarrow{}\\) \\(1 + \\frac{b}{{a + c}} + 1 + \\frac{a}{{b + c}} =3\\)\\(\\Rightarrow{}\\) \\(\\frac{1}{{a + c}} + \\frac{1}{{b + c}} = \\frac{3}{{a + b +c}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC\\), if \\(\\tan \\frac{A}{2}\\tan \\frac{C}{2} = \\frac{1}{2},\\)then\\(a,b,c\\)are in','\(a\)A. P.','\(b\)G. P.','\(c\)H. P.','\(d\)None of these','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)\\(\\tan \\frac{A}{2}\\tan \\frac{C}{2} = \\frac{1}{2}\\) \\(\\Rightarrow{}\\) \\(\\sqrt {\\frac{{(s - b)(s - c)}}{{s(s - a)}}\\frac{{(s - a)(s - b)}}{{s(s - c)}}} = \\frac{1}{2}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{s - b}}{s} = \\frac{1}{2} \\Rightarrow 2s - 2b - s =0\\)\\(\\Rightarrow{}\\) \\(a + c - 3b = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In triangle \\(ABC\\)if \\(a,b,c\\)are in A. P., then the value of \\(\\frac{{\\sin\\frac{A}{2}\\sin \\frac{C}{2}}}{{\\sin \\frac{B}{2}}} = \\)','\(a\)1','\(b\)1/2','\(c\)2','\(d\)-1','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\frac{{\\sin \\frac{A}{2}\\sin \\frac{C}{2}}}{{\\sin \\frac{B}{2}}} = \\sqrt{\\frac{{ac(s - b)(s - c)(s - b)(s - a)}}{{(s - a)(s - c)bc \\times ab}}}  =\\frac{{s - b}}{b}\\)But a, b and c are in A. P. \\(\\Rightarrow{}\\) \\(2b = a + c\\)Hence \\(\\frac{{s - b}}{b} = \\frac{{\\frac{{3b}}{2} - b}}{b} = \\frac{1}{2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan \\frac{{B - C}}{2} = x\\cot \\frac{A}{2},\\)then \\(x = \\) ','\(a\)\\(\\frac{{c - a}}{{c + a}}\\)','\(b\)\\(\\frac{{a - b}}{{a + b}}\\)','\(c\)\\(\\frac{{b - c}}{{b + c}}\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\tan \\frac{{B - C}}{2} = x\\cot \\frac{A}{2} \\Rightarrow x = \\frac{{b -c}}{{b + c}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC\\), if \\(a = 3,b = 4,c = 5\\), then \\(\\sin 2B = \\)','\(a\)4/5','\(b\)3/20','\(c\)24/25','\(d\)1/50','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\cos B = \\frac{{9 + 25 - 16}}{{2.3.5}} = \\frac{{18}}{{2.3.5}} =\\frac{3}{5} \\Rightarrow \\sin B = \\frac{4}{5}\\)Therefore \\(\\sin 2B = 2.\\frac{4}{5}.\\frac{3}{5} = \\frac{{24}}{{25}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sides of a triangle are in the ratio \\(2:\\sqrt 6 :(\\sqrt 3  + 1)\\),then the largest angle of the triangle will be  ','\(a\)\\({60^o}\\)','\(b\)\\({75^o}\\)','\(c\)\\({90^o}\\)','\(d\)\\({120^o}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\cos \\theta  = \\frac{{4 + 6 - {{(\\sqrt 3  + 1)}^2}}}{{2.2.\\sqrt 6 }}\\Rightarrow \\theta  = {75^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the lengths of the sides of a triangle be \\(7,4\\sqrt 3 \\) and \\(\\sqrt{13} \\)cm, then the smallest angle is ','\(a\)\\({15^o}\\)','\(b\)\\({30^o}\\)','\(c\)\\(60^\\circ \\)','\(d\)\\({45^o}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\cos \\theta  = \\frac{{49 + 16.3 - 13}}{{2.7.4\\sqrt 3 }} \\Rightarrow\\theta  = {30^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sides of a right angled triangle be in A. P. , then their ratio willbe','\(a\)1: 2: 3','\(b\)2 : 3 : 4','\(c\)3: 4: 5','\(d\)4 : 5 : 6','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)Let sides be \\(a - d,a,a + d\\)and as it is a right angled triangle \\({(a- d)^2} + {a^2} = {\\left( {a + d} \\right)^2}\\)\\(\\Rightarrow{}\\) \\({a^2} + {d^2} - 2ad + {a^2} = {a^2} + {d^2} + 2ad\\)\\(\\Rightarrow{}\\) \\(a = 4d \\Rightarrow d = \\frac{a}{4}\\).Hence the sides are \\(\\frac{{3a}}{4},a,\\frac{{5a}}{4}\\) i.e., in ratio 3 : 4 : 5.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a \\(\\Delta ABC\\), if \\(\\angle C = {30^o}\\), \\(a = 47cm\\)and \\(b = 94\\)cm,then the triangle is ','\(a\)Right angled','\(b\)Right angled isosceles','\(c\)Isosceles','\(d\)Obtuse angled','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)\\(\\cos C = \\frac{{{a^2} + {b^2} - {c^2}}}{{2ab}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{\\sqrt 3 }}{2} = \\frac{{{{(47)}^2} + {{(94)}^2} -{c^2}}}{{2 \\times 47 \\times 94}}\\)\\( \\Rightarrow c = 58.24\\)\\(\\angle B = {Answer = o}\\). Hence obtuse angled triangle.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a \\(\\Delta ABC\\),side b is equal to          [MP PET ,  92]','\(a\)\\(c\\cos A + a\\cos C\\)','\(b\)\\(a\\cos B + b\\cos A\\)','\(c\)\\(b\\cos C + c\\cos B\\)','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)It is a fundamental  property.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC\\), if \\(\\angle C = {90^o}\\),\\(\\angle A = {30^o}\\), \\(c = 20\\),then the values of a and b are','\(a\)10, 10','\(b\)\\(10,\\,10\\sqrt 3 \\)','\(c\)\\(5,\\,\\,5\\sqrt 3 \\)','\(d\)\\(8,\\,\\,8\\sqrt 3 \\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\angle C = {90^o},\\angle A = {30^o},c = 20,\\)then \\(a = \\frac{{c\\sin A}}{{\\sin C}} = 10\\) and  \\(b = \\frac{{c\\sin B}}{{\\sinC}} = 10\\sqrt 3 \\).Trick : Since the angles are \\({30^o},{60^o},{90^o}\\), therefore sides must be\\(1:\\sqrt 3 :2\\). Hence \\(a = 10,b = 10\\sqrt 3 \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC\\), \\(c\\cos (A - \\alpha ) + a\\cos (C + \\alpha ) = \\)','\(a\)\\(a\\cos \\alpha \\)','\(b\)\\(b\\cos \\alpha \\)','\(c\)\\(c\\cos \\alpha \\)','\(d\)\\(2b\\cos \\alpha \\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(c\\cos (A - \\alpha ) + a\\cos (C + \\alpha )\\)\\( = c(\\cos A\\cos \\alpha  +\\sin A\\sin \\alpha )\\)\\(+ a(\\cos C\\cos \\alpha  - \\sin C\\sin \\alpha )\\)\\(= \\cos \\alpha (c\\cos A + a\\cos C) + c\\,\\sin A\\sin \\alpha  - a\\sin C\\sin \\alpha\\)\\(\\Rightarrow{}\\) \\(b\\cos \\alpha  + kac\\sin \\alpha  - kac\\sin \\alpha  = b\\cos\\alpha \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC\\), \\(\\frac{{\\cos A}}{a} + \\frac{{\\cos B}}{b} + \\frac{{\\cosC}}{c} = \\)','\(a\)\\(\\frac{{{a^2} + {b^2} + {c^2}}}{{abc}}\\)','\(b\)\\(\\frac{{{a^2} + {b^2} +{c^2}}}{{2abc}}\\)','\(c\)\\(\\frac{{2({a^2} + {b^2} + {c^2})}}{{abc}}\\)','\(d\)\\({a^2} + {b^2} + {c^2}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\frac{{\\cos A}}{a} + \\frac{{\\cos B}}{b} + \\frac{{\\cos C}}{c}\\)\\(= \\frac{{{b^2} + {c^2} - {a^2} + {a^2} + {c^2} - {b^2} + {a^2} + {b^2} -{c^2}}}{{2abc}}\\)= \\(\\frac{{{a^2} + {b^2} + {c^2}}}{{2abc}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC\\), \\({a^2}({\\cos ^2}B - {\\cos ^2}C) + \\) \\({b^2}({\\cos ^2}C- {\\cos ^2}A) + \\)\\({c^2}({\\cos ^2}A - {\\cos ^2}B) =\\)','\(a\)0','\(b\)1','\(c\)\\({a^2} + {b^2} + {c^2}\\)','\(d\)\\(2({a^2} + {b^2} + {c^2})\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\Sigma {a^2}({\\cos ^2}B - {\\cos ^2}C) = \\Sigma {a^2}({\\sin ^2}C - {\\sin^2}B)\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In triangle \\(ABC,\\)\\(\\frac{{1 + \\cos (A - B)\\cos C}}{{1 + \\cos (A - C)\\cosB}} = \\)','\(a\)\\(\\frac{{a - b}}{{a - c}}\\)','\(b\)\\(\\frac{{a + b}}{{a + c}}\\)','\(c\)\\(\\frac{{{a^2} - {b^2}}}{{{a^2} - {c^2}}}\\)','\(d\)\\(\\frac{{{a^2} + {b^2}}}{{{a^2}+ {c^2}}}\\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\) \\(\\frac{{1 + \\cos C\\cos (A - B)}}{{1 + \\cos (A - C)\\cos B}} = \\frac{{1 -\\cos (A + B)\\cos (A - B)}}{{1 - \\cos (A - C)\\cos (A + C)}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{1 - {{\\cos }^2}A + {{\\sin }^2}B}}{{1 - {{\\cos }^2}A +{{\\sin }^2}C}} = \\frac{{{{\\sin }^2}A + {{\\sin }^2}B}}{{{{\\sin }^2}A + {{\\sin}^2}C}} = \\frac{{{a^2} + {b^2}}}{{{a^2} + {c^2}}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta \\,ABC\\), \\(\\frac{{\\cos \\frac{1}{2}(B - C)}}{{\\sin \\frac{1}{2}A}}= \\)','\(a\)\\(\\frac{{b - c}}{a}\\)','\(b\)\\(\\frac{{b + c}}{a}\\)','\(c\)\\(\\frac{a}{{b - c}}\\)','\(d\)\\(\\frac{a}{{b + c}}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)  \\(\\frac{{\\cos \\frac{{B - C}}{2}}}{{\\sin \\frac{A}{2}}} = \\frac{{\\sin\\frac{{B + C}}{2}\\cos \\frac{{B - C}}{2}}}{{\\sin \\frac{{B + C}}{2}\\sin\\frac{A}{2}}} = \\frac{{\\sin B + \\sin C}}{{\\sin A}} = \\frac{{b + c}}{a}\\).\\(= {k^2}\\Sigma {a^2}({c^2} - {b^2}) = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In\\(\\Delta \\,ABC\\),\\(({b^2} - {c^2})\\cot A + ({c^2} - {a^2})\\cot B + ({a^2}- {b^2})\\cot C = \\)','\(a\)0','\(b\)\\({a^2} + {b^2} + {c^2}\\)','\(c\)\\(2\\,({a^2} + {b^2} + {c^2})\\)','\(d\)\\(\\frac{1}{{2abc}}\\)',' (a','TRIGONOMETRY(EPH)','2','Answer =  \(a\) \\(({b^2} - {c^2})\\cot A = ({b^2} - {c^2})\\frac{{\\cos A}}{{\\sin A}} =\\frac{{({b^2} - {c^2})({b^2} + {c^2} - {a^2})}}{{2bc.ka}}\\)Hence L.H.S. \\( = \\frac{1}{{2k\\,\\,abc}}\\)\\([({b^4} - {c^4}) + ({c^4} - {a^4}) + ({a^4} - {b^4}) - \\{ {a^2}({b^2} - {c^2})\\)\\(+ {b^2}({c^2} - {a^2}) + {c^2}({a^2} - {b^2})\\} ] = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in \\(\\Delta \\,ABC\\), \\(2{b^2} = {a^2} + {c^2},\\)then \\(\\frac{{\\sin3B}}{{\\sin B}} = \\)','\(a\)\\(\\frac{{{c^2} - {a^2}}}{{2ca}}\\)','\(b\)\\(\\frac{{{c^2} - {a^2}}}{{ca}}\\)','\(c\)\\({\\left( {\\frac{{{c^2} - {a^2}}}{{ca}}} \\right)^2}\\)','\(d\)\\({\\left({\\frac{{{c^2} - {a^2}}}{{2ca}}} \\right)^2}\\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)\\(\\frac{{\\sin 3B}}{{\\sin B}} = \\frac{{3\\sin B - 4{{\\sin }^3}B}}{{\\sin B}}= 3 - 4{\\sin ^2}B\\)\\(= 3 - 4 + 4{\\cos ^2}B =  - 1 + \\frac{{4{{({a^2} + {c^2} -{b^2})}^2}}}{{4{{(ac)}^2}}}\\)\\(=  - 1 + \\frac{{{{\\left( {\\frac{{{a^2} + {c^2}}}{2}}\\right)}^2}}}{{{{(ac)}^2}}} =  - 1 + \\frac{{{{({a^2} +{c^2})}^2}}}{{4{{(ac)}^2}}}\\)\\(= \\frac{{{{({a^2} + {c^2})}^2} - 4{a^2}{c^2}}}{{4{{(ac)}^2}}} = {\\left({\\frac{{{c^2} - {a^2}}}{{2ac}}} \\right)^2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sides of a triangle are in A. P., then the cotangent of its half theangles will be in  ','\(a\)H. P. ','\(b\)G . P.','\(c\)A. P. ','\(d\)No particular order','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)Let \\(\\cot \\frac{A}{2},\\cot \\frac{B}{2}\\)and \\(\\cot \\frac{C}{2}\\)be inA.P. ,then \\(2\\cot \\frac{B}{2} = \\cot \\frac{C}{2} + \\cot \\frac{A}{2}\\)\\(\\Rightarrow{}\\) \\(2\\sqrt {\\frac{{s(s - b)}}{{(s - a)(s - c)}}}  = \\sqrt{\\frac{{s(s - c)}}{{(s - a)(s - b)}}} \\)\\( + \\sqrt {\\frac{{s(s - a)}}{{(s - b)(s- c)}}} \\)R.H.S = \\(\\sqrt {\\frac{s}{{(s - b)}}} \\left( {\\sqrt {\\frac{{(s - c)}}{{(s -a)}}}  + \\sqrt {\\frac{{(s - a)}}{{(s - c)}}} } \\right)\\)\\( = \\sqrt {\\frac{s}{{s - b}}} \\left( {\\frac{{s - c + s - a}}{{\\sqrt {(s - a)(s- c)} }}} \\right)\\)\\(= \\sqrt {\\frac{s}{{s - b}}} \\left( {\\frac{{2s - a - c}}{{\\sqrt {(s - a)(s - c)}}}} \\right)\\)\\(= 2\\sqrt {\\frac{s}{{(s - b)}}} \\sqrt {\\frac{{{{(s - b)}^2}}}{{(s - a)(s - c)}}},\\)\\(\\{ a + c = 2b\\), \\(a + b + c = 2s\\) i.e., \\(2(s - b) = 2s - a - c\\} \\)\\( = 2\\sqrt {\\frac{{s(s - b)}}{{(s - a)(s - c)}}} \\)=L.H.S.Note : Students should remember this question as a fact.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the angles of a triangle are in the ratio 1: 2: 3, then their corresponding sides are in the ratio','\(a\)1: 2: 3','\(b\)\\(1:\\sqrt 3 :2\\)','\(c\)\\(\\sqrt 2 :\\sqrt 3 :3\\)','\(d\)\\(1:\\sqrt 3 :3\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)Angles are \\(x + 2x + 3x = {Answer = o}\\)or 30\\(^{o}\\), 60\\(^{o}\\) and90\\(^{o}\\), therefore sides are in ratio of \\(\\sin {30^o}:\\sin {60^o}:\\sin{90^o}\\)\\(= \\frac{1}{2}:\\frac{{\\sqrt 3 }}{2}:1 = 1:\\sqrt 3 :2.\\)Note: This is a fact. We have used it in so many questions.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a triangle \\(ABC\\), \\(\\frac{{2\\cos A}}{a} + \\frac{{\\cos B}}{b} +\\frac{{2\\cos C}}{c} = \\) \\(\\frac{a}{{bc}} + \\frac{b}{{ca}}\\), then the value ofangle A is ','\(a\)\\({45^o}\\)','\(b\)\\({30^o}\\)','\(c\)\\({90^o}\\)','\(d\)\\({60^o}\\)','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\frac{{2\\cos A}}{a} + \\frac{{\\cos B}}{b} + \\frac{{2\\cos C}}{c} =\\frac{a}{{bc}} + \\frac{b}{{ca}}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{2({b^2} + {c^2} - {a^2})}}{{2abc}} + \\frac{{{a^2} +{c^2} - {b^2}}}{{2abc}} + \\frac{{2({a^2} + {b^2} - {c^2})}}{{2abc}}\\)\\(= \\frac{a}{{bc}} + \\frac{b}{{ca}}\\)\\(\\Rightarrow{}\\)\\(\\frac{{3{b^2} + {c^2} + {a^2}}}{{2abc}}\\)\\( = \\frac{a}{{bc}}+ \\frac{b}{{ca}}\\)\\(\\Rightarrow{}\\)\\(\\frac{{3b}}{{2ac}} + \\frac{c}{{2ab}} +\\frac{a}{{2bc}} = \\frac{a}{{bc}} + \\frac{b}{{ca}}\\)\\(\\Rightarrow{}\\) \\({b^2} + {c^2} = {a^2}\\). Hence\\(\\angle A = {90^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(a = 9,b = 8\\)and \\(c = x\\)satisfies \\(3\\cos C = 2,\\)then','\(a\)\\(x = 5\\)','\(b\)\\(x = 6\\)','\(c\)\\(x = 4\\)','\(d\)\\(x = 7\\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)\\(\\cos C = \\frac{2}{3} = \\frac{{81 + 64 - {x^2}}}{{2.9.8}} \\Rightarrow{x^2} = 49 \\Rightarrow x = 7\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a triangle \\(ABC\\), \\(b = \\sqrt 3 \\), \\(c = 1\\) and \\(B - C ={90^o}\\)then \\(\\angle A\\) is ','\(a\)\\({30^o}\\)','\(b\)\\({45^o}\\)','\(c\)\\({75^o}\\)','\(d\)\\({15^o}\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\tan \\left( {\\frac{{B - C}}{2}} \\right) = \\frac{{b - c}}{{b + c}}\\cot\\frac{A}{2}\\)\\(\\Rightarrow{}\\) \\(\\tan \\left( {\\frac{{90^\\circ }}{2}} \\right) =\\frac{{\\sqrt 3  - 1}}{{\\sqrt 3  + 1}}\\cot \\frac{A}{2}\\)\\(\\Rightarrow{}\\) \\(\\tan \\left( {\\frac{A}{2}} \\right) = \\frac{{\\sqrt 3  -1}}{{\\sqrt 3  + 1}} = \\frac{{3 + 1 - 2\\sqrt 3 }}{2} = 2 - \\sqrt 3 \\)\\(\\Rightarrow{}\\) \\(\\frac{A}{2} = {15^o} \\Rightarrow A = {30^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a triangle, \\(a{\\cos ^2}\\frac{C}{2} + c{\\cos ^2}\\frac{A}{2} =\\frac{{3b}}{2},\\)then its sides will be in                     ','\(a\)A. P.','\(b\)G. P.','\(c\)H. P.','\(d\)A. G.','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(a\\frac{{s(s - c)}}{{ab}} + c\\frac{{s(s - a)}}{{bc}} = \\frac{{3b}}{2}\\)\\(\\Rightarrow{}\\) \\(2s(s - c + s - a) = 3{b^2}\\)  \\(\\Rightarrow{}\\) \\(2s\(b\) =3{b^2} \\Rightarrow 2s = 3b\\)\\(\\Rightarrow{}\\) \\(a + b + c = 3b \\Rightarrow a + c = 2b \\Rightarrow a,b,c\\)arein A.P.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a triangle the angles are in A. P. and \\(b:c = \\sqrt 3 :\\sqrt 2 \\),then \\(\\angle A\\)is equal to','\(a\)\\({30^o}\\)','\(b\)\\({60^o}\\)','\(c\)\\({15^o}\\)','\(d\)\\({75^o}\\)','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)Since the angles are in A.P., therefore \\(B = {60^o}\\)and \\(\\frac{b}{c} = \\frac{{\\sin B}}{{\\sin C}} = \\frac{{\\sqrt 3 }}{{\\sqrt 2 }} =\\frac{{\\sqrt 3 }}{{2\\sin C}} = \\frac{{\\sqrt 3 }}{{\\sqrt 2 }}\\)\\(\\Rightarrow{}\\) \\(C = {45^o}\\)so that \\(A = {Answer = o} - {60^o} - {45^o} ={75^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta \\,ABC\\), \\(a = 2cm,b = 3cm\\) and \\(c = 4cm\\) , then angle A is','\(a\)\\(\\frac{{a + b}}{{a - b}}\\)','\(b\)\\(\\frac{{a - b}}{{a + b}}\\)','\(c\)\\(\\frac{a}{{a + b}}\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)Use \\(\\cos A = \\frac{{{b^2} + {c^2} - {a^2}}}{{2bc}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the angles \\(A,B,C\\)of a triangle are in A.P. and the sides \\(a,b,c\\)opposite to these angles are in G. P. then \\({a^2},{b^2},{c^2}\\) are in ','\(a\)A. P.','\(b\)H. P.','\(c\)G. P.','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)Since A, B and C are in A.P., therefore \\(B = 60^\\circ \\) and  \\({b^2} =ac\\).\\(\\cos B = \\frac{{{a^2} + {c^2} - {b^2}}}{{2ac}} \\Rightarrow \\frac{1}{2} =\\frac{{{a^2} + {c^2} - {b^2}}}{{2{b^2}}}\\)\\({b^2} = {a^2} + {c^2} - {b^2} \\Rightarrow {a^2} + {c^2} = 2{b^2}.\\)\\((\\,{b^2} = ac),\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sides of a triangle are p,q and\\(\\sqrt {{p^2} + pq + {q^2}} \\), thenthe biggest angle is   ','\(a\)\\(\\pi /2\\)','\(b\)\\(2\\pi /3\\)','\(c\)\\(5\\pi /4\\)','\(d\)\\(7\\pi /4\\)(e)\\(5\\pi /3\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)Largest side is \\(\\sqrt {{p^2} + pq + {q^2}} \\). If largest angle is\\(\\theta \\), then \\(\\cos \\theta  = \\frac{{{p^2} + {q^2} - {p^2} - pq -{q^2}}}{{2pq}} =  - \\frac{1}{2} = \\cos \\left( {\\frac{{2\\pi }}{3}} \\right)\\)\\(\\Rightarrow{}\\) \\(\\theta  = \\frac{{2\\pi }}{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a triangle \\(ABC\\), if \\(B = 3C\\), then the values of \\(\\sqrt {\\left({\\frac{{b + c}}{{4c}}} \\right)} \\) and  \\(\\left( {\\frac{{b - c}}{{2c}}} \\right)\\)are','\(a\)\\(\\sin C,\\sin \\frac{A}{2}\\)','\(b\)\\(\\cos C,\\sin \\frac{A}{2}\\)','\(c\)\\(\\sin C,\\cos \\frac{A}{2}\\)','\(d\)None of these','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\sqrt {\\frac{{b + c}}{{4c}}}  = \\sqrt {\\frac{{\\sin 3C + \\sin C}}{{4\\sinC}}} \\)\\(\\Rightarrow{}\\) \\(\\sqrt {\\frac{{2\\sin 2C\\cos C}}{{4\\sin C}}}  = \\cos C\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In \\(\\Delta ABC\\), \\((b - c)\\cot \\frac{A}{2} + (c - a)\\cot \\frac{B}{2} + (a -b)\\)\\(\\cot \\frac{C}{2}\\) is equal to ','\(a\)0','\(b\)1','\(c\) \\( \\pm 1\\)','\(d\) 2','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\((b - c)\\cot \\frac{A}{2} = k(\\sin B - \\sin C)\\cot \\frac{A}{2}\\)\\(= 2k\\cos \\frac{{B + C}}{2}\\sin \\frac{{B - C}}{2}\\cot \\frac{A}{2}\\)\\(= 2k\\sin \\frac{A}{2}\\,.\\,\\,\\sin \\frac{{B - C}}{2}{\\rm{ }}{\\rm{. }}\\frac{{\\cos\\frac{A}{2}}}{{\\sin \\frac{A}{2}}}\\)\\(= 2k\\sin \\left( {\\frac{{B - C}}{2}} \\right)\\sin \\left( {\\frac{{B + C}}{2}}\\right) = 2k\\left( {{{\\sin }^2}\\frac{B}{2} - {{\\sin }^2}\\frac{C}{2}} \\right)\\)or we get L.H.S. =\\(\\Sigma 2k\\left( {{{\\sin }^2}\\frac{B}{2} - {{\\sin}^2}\\frac{C}{2}} \\right) = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a triangle \\(ABC\\) if \\(2{a^2}{b^2} + 2{b^2}{c^2} = \\) \\({a^4} + {b^4} +{c^4}\\), then angle B is equal to','\(a\)\\({45^o}\\)or \\({135^o}\\)','\(b\)\\({135^o}\\)or \\({120^o}\\)','\(c\)\\({30^o}\\)or \\({60^o}\\)','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(2{a^2}{b^2} + 2{b^2}{c^2} = {a^4} + {b^4} + {c^4}\\)Also,\\({({a^2} - {b^2} + {c^2})^2} = \\) \\({a^4} + {b^4} + {c^4} - 2({a^2}{b^2} +{b^2}{c^2} - {c^2}{a^2})\\)\\(\\Rightarrow{}\\) \\({({a^2} - {b^2} + {c^2})^2} =2{c^2}{a^2}\\)\\(\\Rightarrow{}\\)\\(\\frac{{{a^2} - {b^2} + {c^2}}}{{2ca}} =  \\pm\\frac{1}{{\\sqrt 2 }} = \\cos B\\)\\(\\Rightarrow{}\\) \\(B = {45^o}\\)or \\({Answer = o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Area of the triangle is \\(10\\sqrt 3 \\)sq. cm, angle \\(C = {60^o}\\)and itsperimeter is 20 cm, then side c will be','\(a\)5','\(b\)7','\(c\)8','\(d\)10','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\Delta  = 10\\sqrt 3 \\)\\(\\Delta  = \\frac{1}{2}ab\\sin C \\Rightarrow ab = 20\\sqrt 3 \\frac{2}{{\\sqrt 3 }}= 40\\)\\(\\ldots{} \\)..(i)Also \\(a + b + c = 20\\)or \\(a + b = (20 - c)\\)Now, \\(\\cos C = \\frac{{{a^2} + {b^2} - {c^2}}}{{2ab}} = \\frac{1}{2}\\)\\(\\Rightarrow{}\\) \\({a^2} + {b^2} - {c^2} = ab\\) \\(\\Rightarrow{}\\) \\({(a + b)^2}- {c^2} = ab + 2ab = 3ab\\)\\(\\Rightarrow{}\\) \\({(20 - c)^2} - {c^2} = 3(40)\\) \\(\\Rightarrow{}\\) \\( - 40c +Answer = = Answer = \\Rightarrow c = 7\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In triangle\\(ABC\\)if \\(A + C = 2B\\), then \\(\\frac{{a + c}}{{\\sqrt {{a^2} -ac + {c^2}} }}\\)is equal to','\(a\)\\(2\\cos \\frac{{A - C}}{2}\\)','\(b\)\\(\\sin \\frac{{A + C}}{2}\\)','\(c\)\\(\\sin \\frac{A}{2}\\)','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(A + C = 2B \\Rightarrow B = {60^o}\\),  \\(\\cos B = \\frac{{{a^2} + {c^2} -{b^2}}}{{2ac}}\\)Since \\(B = {60^o}\\)\\( \\Rightarrow ac = {a^2} + {c^2} - {b^2}\\)\\(\\Rightarrow{}\\) \\({b^2} = {a^2} + {c^2} - ac\\)Therefore \\(\\frac{{a + c}}{{\\sqrt {{a^2} - ac + {c^2}} }} = \\frac{{a + c}}{b} =\\frac{{\\sin A + \\sin C}}{{\\sin B}}\\)\\(= \\frac{{2\\sin \\frac{{A + C}}{2}\\cos \\frac{{A - C}}{2}}}{{2\\sin \\frac{B}{2}\\sin\\frac{{A + C}}{2}}} = \\frac{{\\cos \\frac{{A - C}}{2}}}{{\\sin \\frac{B}{2}}}\\)\\(= \\frac{{\\cos \\frac{{A - C}}{2}}}{{\\sin {{30}^o}}} \\Rightarrow 2\\cos \\frac{{A -C}}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a triangle \\(ABC,\\)\\(a = 4,b = 3\\), \\(\\angle A = {60^o}\\). Then c is theroot of the equation                       ','\(a\)\\({c^2} - 3c - 7 = 0\\)','\(b\)\\({c^2} + 3c + 7 = 0\\)','\(c\)\\({c^2} - 3c + 7 = 0\\)','\(d\)\\({c^2} + 3c - 7 = 0\\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\cos A = \\frac{{({b^2} + {c^2} - {a^2})}}{{2bc}}\\)\\( \\Rightarrow \\cos {60^o} = \\frac{1}{2} = \\frac{{9 + {c^2} -16}}{{2.3c}}\\)\\(\\Rightarrow{}\\) \\({c^2} - 3c - 7 = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(a = 2,b = 3,c = 5\\)in \\(\\Delta ABC\\), then C =  ','\(a\)\\(\\frac{\\pi }{6}\\)','\(b\)\\(\\frac{\\pi }{3}\\)','\(c\)\\(\\frac{\\pi }{2}\\)','\(d\)None of these','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)\\(\\cos C = \\frac{{{a^2} + {b^2} - {c^2}}}{{2ab}} =  - 1\\)\\(\\Rightarrow{}\\) \\(\\angle C = {Answer = o}\\), which is inadmissible in a triangle.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(A = {30^o},a = 7,b = 8\\)in \\(\\Delta ABC\\), then B has','\(a\)One solution','\(b\)Two solutions','\(c\)No solution','\(d\)None of these','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)Here b \\(\\sin A = 8\\)\\(\\sin {30^o} = 4,a = 7\\)Thus, we have \\(b > a > b\\sin A\\).Hence angle B has two solutions.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(b = 3,c = 4\\)and \\(B = \\frac{\\pi }{3}\\), then the number of trianglethat can be constructed is                 ','\(a\)Infinite','\(b\)Two','\(c\)One','\(d\)Nil','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)Hence \\(c\\sin B = 4\\sin \\frac{\\pi }{3} = 2\\sqrt 3  > b( = 3)\\)Thus, we have \\(b < c\\sin B\\).Hence no triangle is possible i.e., the number of triangles that can beconstructed is nil.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({a^2},{b^2},{c^2}\\)are in A. P. then which of the following are also inA.P.','\(a\)\\(\\sin A,\\sin B,\\sin C\\)','\(b\)\\(\\tan A,\\tan B,\\tan C\\)','\(c\)\\(\\cot A,\\cot B,\\cot C\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)  \\({\\sin ^2}B - {\\sin ^2}A = {\\sin ^2}C - {\\sin ^2}B\\)\\(\\therefore{}\\) \\(\\sin (B + A)\\sin (B - A) = \\sin (C + B)\\sin (C - B)\\)or \\(\\sin C(\\sin B\\cos A - \\cos B\\sin A)\\)\\(= \\sin A(\\sin C\\cos B - \\cos C\\sin B)\\)Divide by \\(\\sin A\\sin B\\sin C\\)\\(\\therefore \\,\\,\\,\\cot A - \\cot B = \\cot B - \\cot C\\). Hence the result.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The sides of a triangle are three consecutive natural numbers and its largestangle is twice the smallest one. Then the sides of the triangle are','\(a\)1, 2, 3','\(b\)2, 3, 4','\(c\)3, 4, 5','\(d\)4, 5, 6','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)Let the sides of \\(\\Delta ABC\\)be \\(a = n,b = n + 1,c = n + 2\\), where nis a natural number. Then C is the greatest and A the least angle. As given \\(C =2A\\).\\(\\therefore{}\\) \\(\\sin C = \\sin 2A = 2\\sin A\\cos A\\)\\(\\therefore{}\\) \\(kc = 2ka\\frac{{{b^2} + {c^2} - {a^2}}}{{2bc}}\\)or \\(b{c^2} =a{\\rm{ }}({b^2} + {c^2} - {a^2})\\)Substituting the values of a, b, c, we get\\((n + 1){(n + 2)^2} = n[{(n + 1)^2} + {(n + 2)^2} - {n^2}]\\)or \\((n + 1){(n + 2)^2} = n{\\rm{ }}({n^2} + 6n + 5) = n(n + 1)(n + 5)\\)Since \\(n \\ne  - 1\\), we can cancel \\(n + 1\\).Thus \\({(n + 2)^2} = n{\\rm{ }}(n + 5)\\)or \\({n^2} + 4n + 4 = {n^2} + 5n\\)This gives \\(n = 4,\\)Hence the sides are 4, 5 and 6.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a triangle \\(ABC,\\) \\(\\cos A\\cos B + \\sin A\\sin B\\sin C = 1,\\) then thesides are proportional to','\(a\)1: 1: \\(\\sqrt 2 \\)','\(b\)\\(1:\\sqrt 2 :1\\)','\(c\)\\(\\sqrt 2 :1:1\\)','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)From the given relation \\(\\sin C = \\frac{{1 - \\cos A\\cos B}}{{\\sin A\\sinB}} \\le 1\\)\\(\\ldots{} \\)..(i)\\(\\Rightarrow{}\\) \\(1 \\le \\cos A\\cos B + \\sin A\\sin B\\)\\(\\Rightarrow{}\\) \\(\\cos (A - B) \\ge 1\\);  \\(\\cos \\theta \\not  >1\\)\\(\\ldots{} \\)..(ii)\\(\\therefore{}\\) \\(A - B = 0\\) or \\(A = B\\)Hence from (i), \\(\\sin C = \\frac{{1 - {{\\cos }^2}A}}{{{{\\sin }^2}A}} =\\frac{{{{\\sin }^2}A}}{{{{\\sin }^2}A}} = 1\\)\\(\\therefore{}\\) \\(C = {90^o} \\Rightarrow A + B = {90^o}\\)or \\(A = B ={45^o}\\)\\{by (ii)\\}Hence, \\(a:b:c\\)=\\(\\sin A:\\sin B:\\sin C = 1:1:\\sqrt 2 \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Point D, E are taken on the side BC of a triangle \\(ABC\\)such that \\(BD = DE= EC\\).If \\(\\angle BAD = x\\), \\(\\angle DAE = y\\), \\(\\angle EAC = z\\), then thevalue of \\(\\frac{{\\sin (x + y)\\sin (y + z)}}{{\\sin x\\sin z}} = \\)','\(a\)1','\(b\)2','\(c\)4','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)From\\(\\Delta ADC\\), \\(\\frac{{\\sin (y + z)}}{{DC}} = \\frac{{\\sinC}}{{AD}}\\)From \\(\\Delta ABD,\\frac{{\\sin x}}{{BD}} = \\frac{{\\sin B}}{{AD}}\\)From \\(\\Delta AEC,\\frac{{\\sin z}}{{EC}} = \\frac{{\\sin C}}{{AE}}\\)From \\(\\Delta ABE,\\,\\frac{{\\sin (x + y)}}{{BE}} = \\frac{{\\sin B}}{{AE}}\\)Therefore\\(\\frac{{\\sin (x + y)\\sin (y + z)}}{{\\sin x\\sin z}}\\)\\(= \\frac{{BE}}{{AE}} \\times \\frac{{DC}}{{AD}} \\times \\frac{{AD}}{{BD}} \\times\\frac{{AE}}{{EC}} = \\frac{{2BD \\times 2EC}}{{BD \\times EC}} = 4.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a \\(\\Delta ABC\\), \\(\\cos A + 2\\cos B + \\cos C = 2\\), then\\(a,b,c\\)arein','\(a\)A. P.','\(b\)H. P.','\(c\)G. P.','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)\\(\\cos A + 2\\cos B + \\cos C = 2\\)\\(\\Rightarrow{}\\) \\(\\cos A + \\cos C = 2(1 - \\cos B)\\)\\(\\Rightarrow{}\\) \\(2\\cos \\frac{{A + C}}{2}\\cos \\frac{{A - C}}{2} = 4{\\sin^2}\\frac{B}{2}\\)\\(\\Rightarrow{}\\) \\(2\\cos \\left( {\\frac{{A - C}}{2}} \\right) = 4\\sin\\frac{B}{2}\\)\\(\\Rightarrow{}\\) \\(2\\cos \\frac{B}{2}\\cos \\left( {\\frac{{A - C}}{2}} \\right) =2\\left( {2\\sin \\frac{B}{2}\\cos \\frac{B}{2}} \\right)\\)\\(\\Rightarrow{}\\) \\(2\\sin \\,\\left( {\\frac{{A + C}}{2}} \\right)\\cos \\left({\\frac{{A - C}}{2}} \\right) = 2\\left( {2\\sin \\frac{B}{2}\\cos \\frac{B}{2}}\\right)\\)\\(\\Rightarrow{}\\) \\(\\sin A + \\sin C = 2\\sin B \\Rightarrow a + c = 2b\\)\\(\\Rightarrow{}\\) a, b, c are in A.P.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a \\(\\Delta ABC\\), \\(\\cos 3A + \\cos 3B + \\cos 3C = 1\\), then one anglemust be exactly equal to','\(a\)\\({90^o}\\)','\(b\)\\({45^o}\\)','\(c\)\\({120^o}\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)Since \\(\\cos 3A + \\cos 3B + \\cos 3C = 1\\)\\(\\Rightarrow{}\\) \\(4\\sin \\frac{{3A}}{2}\\sin \\frac{{3B}}{2}\\sin \\frac{{3C}}{2} =0\\)Either \\(\\frac{{3A}}{2} = {Answer = o}\\)or \\(\\frac{{3B}}{2} = {Answer = o}\\)or\\(\\frac{{3C}}{2} = {Answer = o}\\)Either \\(A = {Answer = o}\\)or \\(B = {Answer = o}\\)or \\(C = {Answer = o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','ABC is a triangle such that \\(\\sin (2A + B) = \\) \\(\\sin (C - A) = \\) \\( -\\sin (B + 2C) = \\frac{1}{2}\\). If A, B and C are in A.P., then A, B and C are','\(a\)\\({30^o},{60^o},{90^o}\\)','\(b\)\\({45^o},{60^o},{75^o}\\)','\(c\)\\({45^o},{45^o},{90^o}\\)','\(d\)\\({60^o},{60^o},{60^o}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)Since A,B,C are in A.P.,  therefore \\(B = {60^o}\\)[\\(A + B + C = Answer = )and \\(A + C = 2B\\)]Now, \\(\\sin (2A + B) = \\frac{1}{2}\\)(given)\\(\\Rightarrow{}\\) \\(2A + B = {30^o}\\)or \\({Answer = o}\\)But as \\(B = {60^o},\\) \\(2A + B \\ne {30^o}\\).Hence \\(2A + B = {Answer = o} \\Rightarrow A = {45^o}\\)Hence \\(A = {45^o}\\), \\(B = {60^o},C = {75^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in the \\(\\Delta ABC,AB = 2BC\\), then \\(\\tan \\frac{B}{2}:\\cot \\left({\\frac{{C - A}}{2}} \\right)\\)','\(a\)3 :1','\(b\)2: 1','\(c\)1: 2','\(d\)1: 3','4','TRIGONOMETRY(EPH)','2','Answer = \(d\)We have, \\(\\frac{{\\tan {\\rm{ }}\\left( {\\frac{B}{2}} \\right)}}{{\\cot {\\rm{}}\\left( {\\frac{{C - A}}{2}} \\right)}} = \\frac{{\\sin \\frac{B}{2}\\sin \\left({\\frac{{C - A}}{2}} \\right)}}{{\\cos \\frac{B}{2}\\cos {\\rm{ }}\\left( {\\frac{{C -A}}{2}} \\right)}}\\)\\(= \\frac{{\\sin C - \\sin A}}{{\\sin C + \\sin A}} = \\frac{{kc - ka}}{{kc + ka}} =\\frac{{c - a}}{{c + a}} = \\frac{a}{{3a}} = \\frac{1}{3},\\,\\{ \\,c = 2a\\} .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a triangle \\(ABC\\), if \\(a = 2,B = {60^o}\\)and \\(C = {75^o}\\), then b =','\(a\)\\(\\sqrt 3 \\)','\(b\)\\(\\sqrt 6 \\)','\(c\)\\(\\sqrt 9 \\)','\(d\)\\(1 + \\sqrt 2 \\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(B = {60^o}\\), \\(C = {75^o}\\) \\(\\Rightarrow{}\\) \\(A = {Answer = o} - {60^o} -{75^o} = {45^o}\\)Now \\(\\frac{b}{{\\sin B}} = \\frac{a}{{\\sin A}}\\)\\(\\Rightarrow{}\\)\\(\\frac{b}{{\\sin {{60}^o}}} = \\frac{2}{{\\sin {{45}^o}}} \\Rightarrow b = \\sqrt 6\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In triangle ABC, \\(A = {30^o},b = 8,a = 6\\), then \\(B = {\\sin ^{ - 1}}x\\),where x =','\(a\)\\(\\frac{1}{2}\\)','\(b\)\\(\\frac{1}{3}\\)','\(c\) \\( \\frac{2}{3}\\)','\(d\) 1','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)\\(\\frac{{\\sin B}}{b} = \\frac{{\\sin A}}{a}\\) \\(\\Rightarrow{}\\) \\(\\sin B =\\frac{{b\\sin A}}{a} = \\frac{{8\\sin {{30}^o}}}{6} = \\frac{2}{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a \\(\\Delta ABC\\), \\(b = 2,C = {60^o},c = \\sqrt 6 \\), then a =','\(a\)\\(\\sqrt 3  - 1\\)','\(b\)\\(\\sqrt 3 \\)','\(c\)\\(\\sqrt 3  + 1\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)We have \\(\\frac{b}{{\\sin B}} = \\frac{c}{{\\sin C}}\\)\\(\\Rightarrow{}\\)\\(\\sin B = \\frac{{b\\sin C}}{c}\\)\\(\\Rightarrow{}\\) \\(\\sin B = \\frac{{2\\sin {{60}^o}}}{{\\sqrt 6 }} =\\frac{2}{{\\sqrt 6 }}.\\frac{{\\sqrt 3 }}{2} = \\frac{1}{{\\sqrt 2}}\\)\\(\\Rightarrow{}\\)\\(B = {45^o}\\),\\((B \\ne {Answer = o})\\)\\(\\therefore{}\\) \\(A = {Answer = o} - (B + C) = {75^o}\\)Now,\\(\\frac{{\\sin A}}{a} = \\frac{{\\sin B}}{b}\\)\\(\\Rightarrow{}\\)\\(a =\\frac{{b\\sin A}}{{\\sin B}} = \\frac{{2\\sin {{75}^o}}}{{\\sin {{45}^o}}} = \\sqrt 3 + 1\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a \\(\\Delta ABC\\), \\(a = 5,b = 4\\)and \\(\\cos (A - B) =\\frac{{31}}{{32}}\\), then side c is equal to','\(a\)6','\(b\)7','\(c\)9','\(d\)None of these','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)We have \\(\\tan \\left( {\\frac{{A - B}}{2}} \\right) = \\sqrt {\\frac{{1 -\\cos (A - B)}}{{1 + \\cos (A - B)}}} \\)\\( = \\sqrt {\\frac{{1 - (31/32)}}{{1 + (31/32)}}}  = \\frac{1}{{\\sqrt {63} }}\\)\\(\\Rightarrow{}\\) \\(\\frac{{a - b}}{{a + b}}\\cot \\frac{C}{2} = \\frac{1}{{\\sqrt{63} }}\\)\\(\\Rightarrow{}\\) \\(\\frac{1}{9}\\cot \\frac{C}{2} = \\frac{1}{{\\sqrt {63} }}\\Rightarrow \\tan \\frac{C}{2} = \\frac{{\\sqrt 7 }}{3}\\)Now \\(\\cos C = \\frac{{1 - {{\\tan }^2}(C/2)}}{{1 + {{\\tan}^2}(C/2)}}\\)\\(\\Rightarrow{}\\) \\(\\cos C = \\frac{{1 - (7/9)}}{{1 + (7/9)}} =\\frac{1}{8}\\)\\(\\therefore{}\\)\\({c^2} = {a^2} + {b^2} - 2ab\\cos C\\)\\(\\Rightarrow{}\\) \\({c^2} = 25 + 16 - 40 \\times \\frac{1}{8} = 36 \\Rightarrow c =6\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In a \\(\\Delta ABC\\), if \\(A = {30^o}\\)\\(b = 2,c = \\sqrt 3  + 1\\), then\\(\\frac{{C - B}}{2} = \\)','\(a\)\\({15^o}\\)','\(b\)\\({30^o}\\)','\(c\)\\({45^o}\\)','\(d\)None of these','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)We have \\(\\tan \\frac{{C - B}}{2} = \\frac{{c - b}}{{c + b}}\\cot\\frac{A}{2}\\)\\(\\therefore{}\\) \\(\\tan \\left( {\\frac{{C - B}}{2}} \\right) = \\frac{{\\sqrt 3  + 1- 2}}{{\\sqrt 3  + 1 + 2}}\\cot {15^o} = \\frac{1}{{\\sqrt 3 }}\\)\\(\\Rightarrow{}\\)  \\(\\frac{{C - B}}{2} = {30^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The smallest angle of the triangle whose sides are \\(6 + \\sqrt {12} ,\\sqrt{48} ,\\sqrt {24} \\)is','\(a\)\\(\\frac{\\pi }{3}\\)','\(b\)\\(\\frac{\\pi }{4}\\)','\(c\)\\(\\frac{\\pi }{6}\\)','\(d\)None of these','3','TRIGONOMETRY(EPH)','2','Answer = \(c\)Let \\(A = 6 + \\sqrt {12} ,b = \\sqrt {48} ,c = \\sqrt {24} \\). Clearly, cis the smallest side. Therefore, the smallest angle C is given by \\(\\cos C =\\frac{{{a^2} + {b^2} - {c^2}}}{{2ab}} = \\frac{{\\sqrt 3 }}{2} \\Rightarrow C =\\frac{\\pi }{6}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(A = {30^o},c = 7\\sqrt 3 \\)and \\(C = {90^o}\\)in \\(\\Delta ABC\\), then a =','\(a\)\\(7\\sqrt 3 \\)','\(b\)\\(\\frac{{7\\sqrt 3 }}{2}\\)','\(c\)\\(\\frac{7}{2}\\)','\(d\)None of these','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)Since \\(C = {90^o}\\)Hence, \\(a = \\frac{{c\\sin A}}{{\\sin C}} = \\frac{{7\\sqrt 3 \\sin {{30}^o}}}{{\\sin90^\\circ }} = \\frac{{7\\sqrt 3 }}{2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If angles of a triangle are in the ratio of 2: 3: 7, then the sides are in the ratio of ','\(a\)\\(\\sqrt 2 :2:(\\sqrt 3  + 1)\\)','\(b\)\\(2:\\sqrt 2 :(\\sqrt 3  + 1)\\)','\(c\)\\(\\sqrt 2 :(\\sqrt 3  + 1):2\\)','\(d\)\\(2:(\\sqrt 3  + 1):\\sqrt 2 \\)','1','TRIGONOMETRY(EPH)','2','Answer = \(a\)Obviously, the angles are \\({30^o},{45^o},{Answer = o}\\).\\(\\therefore \\)\\(a:b:c = \\sin {30^o}:\\sin {45^o}:\\sin {Answer = o}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Sides of a triangle are \\(2cm,\\sqrt 6 \\,cm\\) and \\((\\sqrt 3  + 1)cm\\). Thesmallest angle of the triangle is','\(a\)\\({30^o}\\)','\(b\)\\({45^o}\\)','\(c\)\\({60^o}\\)','\(d\)\\({75^o}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(a = 2,b = \\sqrt 6 ,c = \\sqrt 3  + 1\\)\\(\\therefore{}\\) \\(\\cos A = \\frac{{6 + 3 + 1 + 2\\sqrt 3  - 4}}{{2\\sqrt 6 (\\sqrt3  + 1)}} \\Rightarrow A = {45^o}\\).\\(= \\frac{1}{2}:\\frac{1}{{\\sqrt 2 }}:\\frac{{\\sqrt 3  + 1}}{{2\\sqrt 2 }} = \\sqrt 2:2:(\\sqrt 3  + 1).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','In any triangle \\(ABC,\\frac{{\\tan \\frac{A}{2} - \\tan \\frac{B}{2}}}{{\\tan\\frac{A}{2} + \\tan \\frac{B}{2}}} = \\)','\(a\)\\(\\frac{{a - b}}{{a + b}}\\)','\(b\)\\(\\frac{{a - b}}{c}\\)','\(c\)\\(\\frac{{a - b}}{{a + b + c}}\\)','\(d\)\\(\\frac{c}{{a + b}}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\frac{{\\tan \\frac{A}{2} - \\tan \\frac{B}{2}}}{{\\tan \\frac{A}{2} + \\tan\\frac{B}{2}}} = \\frac{{\\sqrt {\\frac{{(s - b)(s - c)}}{{s(s - a)}}}  - \\sqrt{\\frac{{(s - a)(s - c)}}{{s(s - b)}}} }}{{\\sqrt {\\frac{{(s - b)(s - c)}}{{s(s -a)}}}  + \\sqrt {\\frac{{(s - a)(s - c)}}{{s(s - b)}}} }}\\)\\(= \\frac{{(s - b)\\sqrt {s(s - c)}  - (s - a)\\sqrt {s(s - c)} }}{{(s - b)\\sqrt{s(s - c)}  + (s - a)\\sqrt {s(s - c)} }}\\)\\(= \\frac{{\\sqrt {s(s - c)} (s - b - s + a)}}{{\\sqrt {s(s - c)} {\\rm{ }}(s - b +s - a)}} = \\frac{{a - b}}{c}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in a triangle ABC side \\(a = (\\sqrt 3  + 1)\\)cms and \\(\\angle B = {30^o},\\)\\(\\angle C = {45^o}\\), then the area of the triangle is  ','\(a\)\\(\\frac{{\\sqrt 3  + 1}}{3}c{m^2}\\)','\(b\)\\(\\frac{{\\sqrt 3  + 1}}{2}c{m^2}\\)','\(c\)\\(\\frac{{\\sqrt 3  + 1}}{{2\\sqrt 2 }}c{m^2}\\)','\(d\)\\(\\frac{{\\sqrt 3  +1}}{{3\\sqrt 2 }}c{m^2}\\)','2','TRIGONOMETRY(EPH)','2','Answer = \(b\)\\(\\Delta  = \\frac{1}{2}\\frac{{{a^2}\\sin B\\sin C}}{{\\sin (B + C)}} =\\frac{1}{2}\\frac{{{{(\\sqrt 3  + 1)}^2}.\\frac{1}{2} \\times \\frac{1}{{\\sqrt 2}}}}{{\\frac{{(\\sqrt 3  + 1)}}{{2\\sqrt 2 }}}} = \\frac{{\\sqrt 3  + 1}}{2}\\).',CURRENT_TIMESTAMP);
