INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Which of the following is the empty set','\(a\)\\(\\{ x:x\\) is a real number and \\({x^2} - 1 = 0\\} \\)','\(b\){x : x  is a real number and \\({x^2} + 1 = 0\\} \\)','\(c\){x : x is a real number and \\({x^2} - 9 = 0 \\)}','\(d\){x : x is a real number and \\({x^2} = x + 2\\)}','2','SETS  RELATIONS','2','Answer = \(b\)Since \\({x^2} + 1 = 0,\\) gives \\({x^2} =  - 1\\) \\( \\Rightarrow \\) \\(x = \\pm i\\)\\(\\therefore \\) x  is not real but x is  real (given)\\(\\therefore \\) No value of x is possible.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The set \\(A = \\{ x:x \\in R,\\,{x^2} = 16\\) and \\(2x = 6\\} \\) equals','\(a\)\\(\\varphi \\)','\(b\){14, 3, 4}','\(c\){3}','\(d\){4}','1','SETS  RELATIONS','2','Answer = \(a\)\\({x^2} = 16\\) \\(\\Rightarrow{}\\) \\(x =  \\pm 4\\)\\(2x = 6\\)  \\(\\Rightarrow{}\\) \\(x = 3\\)There is no value of x which satisfies both the above equations. Thus, \\(A =\\varphi \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If a set A has n  elements, then the total number of subsets of A is','\(a\)n','\(b\)\\({n^2}\\)','\(c\)\\({2^n}\\)','\(d\)\\(2n\\)','3','SETS  RELATIONS','2','Answer = \(c\)Number of subsets of \\(A{ = ^n}{C_0}{ + ^n}{C_1} + .........{ + ^n}{C_n}= {2^n}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The number of proper subsets of the set {1, 2, 3} is','\(a\)8','\(b\)7','\(c\)6','\(d\)5','3','SETS  RELATIONS','2','Answer = \(c\)Number of proper subsets of the set (1, 2, 3) =\\({2^3} - 2 = 6\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Given the sets \\(A = \\{ 1,\\,2,\\,3\\} ,\\,B = { 3,4} \\), C = {4, 5, 6},then \\(A \\cup (B \\cap C)\\) is   ','\(a\){3}','\(b\){1, 2, 3, 4}','\(c\){1, 2, 4, 5}','\(d\){1, 2, 3, 4, 5, 6}','2','SETS  RELATIONS','2','Answer = \(b\)\\(B \\cap C = \\{ 4\\} \\),  \\(\\therefore \\)\\(A \\cup (B \\cap C)\\) = {1, 2,3, 4}.',CURRENT_TIMESTAMP);
