INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If A and B  are two given sets, then \\(A \\cap {(A \\cap B)^c}\\) is equal to','\(a\)A','\(b\)B','\(c\)\\(\\varphi \\)','\(d\)\\(A \\cap {B^c}\\)','4','SETS  RELATIONS','2','Answer = \(d\)\\(A \\cap {(A \\cap B)^c} = A \\cap ({A^c} \\cup {B^c})\\)= \\((A \\cap {A^c}) \\cup (A \\cap {B^c})\\) = \\(\\varphi  \\cup (A \\cap{B^c}) = A \\cap {B^c}\\).',CURRENT_TIMESTAMP);
