INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If A and B are two sets, then \\(A \\cup B = A \\cap B\\) iff','\(a\)\\(A \\subseteq B\\)','\(b\)\\(B \\subseteq A\\)','\(c\)\\(A = B\\)','\(d\)None of these','3','SETS  RELATIONS','2','Answer = \(c\)Let \\(x \\in A \\Rightarrow x \\in A \\cup B\\),  \\([A \\subseteq A \\cup B]\\)\\(\\Rightarrow{}\\) \\(x \\in A \\cap B\\), \\([A \\cup B = A \\cap B]\\)\\(\\Rightarrow{}\\) \\(x \\in A\\)and \\(x \\in B\\) \\(\\Rightarrow{}\\) \\(x \\in B\\),\\(\\therefore{}\\)\\(A \\subseteq B\\)Similarly, \\(x \\in B\\)\\(\\Rightarrow{}\\) \\(x \\in A\\), \\(\\therefore{}\\)\\(B\\subseteq A\\)Now \\(A \\subseteq B,\\,\\,B \\subseteq A\\) \\(\\Rightarrow{}\\) \\(A = B\\).',CURRENT_TIMESTAMP);
