INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let\\(R = \\{(3,\\,3),\\;(6,\\;6),\\;(9,\\,9),\\;(12,\\,12),\\;(6,\\,12),\\;(3,\\,9),(3,\\,12),\\,(3,\\,6)\\}\\) be a relation on the set \\(A = \\{ 3,\\,6,\\,9,\\,12\\} \\). The relation is','\(a\)Symmetric','\(b\)Reflexive','\(c\)Transitive','\(d\)None of these','3','SETS  RELATIONS','2','Answer = \(c\)Here (3, 3), (6, 6), (9, 9), (12, 12), [Reflexive];(3, 6), (6, 12), (3, 12), [Transitive].Hence, reflexive and transitive only.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let R = {(1, 3), (4, 2), (2, 4), (2, 3), (3, 1)} be a relation on the setA = {1, 2, 3, 4}. The relation R is','\(a\)Reflexive','\(b\)Transitive','\(c\)Not symmetric','\(d\)A function','3','SETS  RELATIONS','2','Answer = \(c\)Given A = {1, 2, 3, 4}R = {(1, 3), (4, 2), (2, 4), (2, 3), (3, 1)}(2, 3) \\(\\in{}\\) R but (3, 2) \\(\\not\\in{}\\) R. Hence R is not symmetric.R is not reflexive as (1, 1) \\(\\not\\in{}\\) R.R is not a function as (2, 4) \\(\\in{}\\) R and (2, 3) \\(\\in{}\\) R.R is not transitive as (1, 3) \\(\\in{}\\) R and (3, 1) \\(\\in{}\\) R but (1, 1)\\(\\not\\in{}\\) R.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The number of reflexive relations of a set with four elements is equalto','\(a\)\\({2^{16}}\\)','\(b\)\\({2^{12}}\\)','\(c\)\\({2^8}\\)','\(d\)\\({2^4}\\)','4','SETS  RELATIONS','2','Answer = \(d\)Total number of reflexive relations in a set with n elements \\( ={2^n}\\).Therefore, total number of reflexive relation set with 4 elements \\( = {2^4}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let S be the set of all real numbers. Then the relation R = {(a, b) : 1 +ab \\(>\\) 0} on S is','\(a\)Reflexive and symmetric but not transitive','\(b\)Reflexive and transitive but not symmetric','\(c\)Symmetric, transitive but not reflexive','\(d\)Reflexive, transitive and symmetric(e)None of the above is true','1','SETS  RELATIONS','2','Answer = \(a\)Since \\(1 + a.a = 1 + {a^2} > 0\\,,\\forall a \\in S\\), \\(\\therefore{}\\)\\((a,\\,a) \\in R\\)\\(\\therefore{}\\) R is reflexive.Also\\((a,b) \\in R\\) \\(\\Rightarrow{}\\) \\(1 + ab > 0\\) \\(\\Rightarrow{}\\) \\(1 + ba> 0\\) \\(\\Rightarrow{}\\)\\((b,\\,a) \\in R\\),\\(\\therefore{}\\) R is symmetric.\\((a,\\,b) \\in R\\) and \\((b,\\,c) \\in R\\) need not imply \\((a,\\,c) \\in R\\). Hence,R is not transitive.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If A is the set of even natural numbers less than 8 and B is the set ofprime numbers less than 7, then the number of relations from A to B is','\(a\)\\({2^9}\\)','\(b\)\\({9^2}\\)','\(c\)\\({3^2}\\)','\(d\)\\({2^{9 - 1}}\\)','1','SETS  RELATIONS','2','Answer = \(a\)\\(49{[^n}{C_2}{ + ^n}{C_3}(7) + ......{ + ^n}{C_n}{7^{n - 2}}]\\); \\(B =\\{ 2,\\,3,\\,5\\} \\)\\(\\therefore{}\\) A \\(\\times{}\\) B contains \\(3 \\times 3 = 9\\) elements.Hence, number of relations from A to B \\( = {2^9}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(X = \\{ {8^n} - 7n - 1:n \\in N\\} \\) and \\(Y = \\{ 49(n - 1):n \\in N\\} ,\\)then','\(a\)\\(X \\subseteq Y\\)','\(b\)\\(Y \\subseteq X\\)','\(c\)\\(X = Y\\)','\(d\)None of these','1','SETS  RELATIONS','2','Answer = \(a\)Since \\({8^n} - 7n - 1 = {(7 + 1)^n} - 7n - 1\\)\\(= {7^n}{ + ^n}{C_1}{7^{n - 1}}{ + ^n}{C_2}{7^{n - 2}} + .....{ + ^n}{C_{n -1}}7{ + ^n}{C_n} - 7n - 1\\)\\({ = ^n}{C_2}{7^2}{ + ^n}{C_3}{7^3} + ..{ + ^n}{C_n}{7^n}\\)\\({(^n}{C_0}{ = ^n}{C_n},{\\,^n}{C_1}{ = ^n}{C_{n - 1}}\\,{\\rm{etc}}{\\rm{.)}},\\)\\(= 49{[^n}{C_2}{ + ^n}{C_3}(7) + ......{ + ^n}{C_n}{7^{n - 2}}]\\)\\(\\therefore \\) \\({8^n} - 7n - 1\\) is a multiple of 49 for \\(n \\ge 2\\)For \\(n = 1\\), \\({8^n} - 7n - 1 = 8 - 7 - 1 = 0\\);For \\(n = 2,\\) \\({8^n} - 7n - 1 = 64 - 14 - 1 = 49\\)\\(\\therefore \\) \\({8^n} - 7n - 1\\) is a multiple of 49 for all \\(n \\in N.\\)\\(\\therefore \\) X contains elements which are multiples of 49 and clearly Ycontains all multiplies of 49. \\(\\therefore \\) \\(X \\subseteq Y\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({N_a} = \\{ an:n \\in N\\} ,\\) then \\({N_3} \\cap {N_4} = \\)','\(a\)\\({N_7}\\)','\(b\)\\({N_{12}}\\)','\(c\)\\({N_3}\\)','\(d\)\\({N_4}\\)','2','SETS  RELATIONS','2','Answer = \(b\)\\({N_3} \\cap {N_4} = \\{ 3,\\,6,\\,9,\\,12,15......\\}  \\cap \\{4,\\,8,\\,12,\\,16,\\,20,.....\\} \\)= {12, 24, 36......} = \\({N_{12}}\\).Trick : \\({N_3} \\cap {N_4} = {N_{12}}\\)[ 3, 4 are relatively prime numbers].',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Sets A and B have 3 and 6 elements respectively. What can be the minimumnumber of elements in A \\(\\cup{}\\) B','\(a\)3','\(b\)6','\(c\)9','\(d\)18','2','SETS  RELATIONS','2','Answer = \(b\)n(A \\(\\cup{}\\) B) = n\(a\) + n\(b\) - n(A \\(\\cap{}\\) B) = \\(3 + 6 - n(A \\capB)\\)Since, maximum number of elements in \\(A \\cap B = 3\\)\\(\\therefore \\) Minimum number of elements in \\(A \\cup B = 9 - 3 = 6\\).',CURRENT_TIMESTAMP);
