INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\({(x + y)^2} - ({x^2} + {y^2}) = 0\\) represents','\(a\)A circle','\(b\)Two lines','\(c\)Two parallel lines','\(d\)Two mutually perpendicular lines','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Given equation \\(2xy = 0 \\Rightarrow x = 0\\) and\\(y = 0.\\)Hence these are two mutually perpendicular lines (both axes).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the slope of one of the lines represented by \\(a{x^2} + 2hxy + b{y^2} = 0\\)be the square of the other, then','\(a\)\\({a^2}b + a{b^2} - 6abh + 8{h^3} = 0\\)','\(b\)\\({a^2}b + a{b^2} + 6abh + 8{h^3} = 0\\)','\(c\)\\({a^2}b + a{b^2} - 3abh + 8{h^3} = 0\\)','\(d\)\\({a^2}b + a{b^2} - 6abh - 8{h^3} = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Here, \\({m_1} = m_2^2 \\Rightarrow m_2^2 + {m_2} = \\frac{{ -2h}}{b}\\).....(i)and \\(m_2^2{m_2} = \\frac{a}{b} \\Rightarrow {m_2} = {\\left( {\\frac{a}{b}}\\right)^{1/3}}\\).....(ii)Putting this value of \\({m_2}\\)in (i), we get\\({\\left\\{ {{{\\left( {\\frac{a}{b}} \\right)}^{1/3}}} \\right\\}^2} + {\\left({\\frac{a}{b}} \\right)^{1/3}} = \\frac{{ - 2h}}{b}\\)On cubing both sides, we get\\({\\left( {\\frac{a}{b}} \\right)^2} + \\frac{a}{b} + 3{\\left( {\\frac{a}{b}}\\right)^{2/3}}.{\\left( {\\frac{a}{b}} \\right)^{1/3}}.\\left\\{ {{{\\left({\\frac{a}{b}} \\right)}^{2/3}} + {{\\left( {\\frac{a}{b}} \\right)}^{1/3}}} \\right\\}= \\frac{{ - 8{h^3}}}{{{b^3}}}\\)\\(\\Rightarrow{}\\)\\({\\left( {\\frac{a}{b}} \\right)^2} + \\frac{a}{b} -\\frac{{6ah}}{{{b^2}}} = \\frac{{ - 8{h^3}}}{{{b^3}}}\\) \\(\\left\\{ {{{\\left({\\frac{a}{b}} \\right)}^{2/3}} + {{\\left( {\\frac{a}{b}} \\right)}^{1/3}} = \\frac{{- 2h}}{b}} \\right\\}\\)\\(\\Rightarrow{}\\) \\(ab(a + b) - 6abh + 8{h^3} = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Two lines represented by equation \\({x^2} + xy + {y^2} = 0\\) are','\(a\)Coincident','\(b\)Parallel','\(c\)Mutually perpendicular','\(d\)Imaginary','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)\\({x^2} + {y^2} + xy = 0 \\Rightarrow (x - \\omega y)(x - {\\omega ^2}y) =0\\)\\(1,\\;\\omega ,\\;{\\omega ^2}\\) are cube roots of unity. Hence the lines areimaginary.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(hxy + gx + fy + c = 0\\) represents a pair of straightlines, then','\(a\)\\(fh = cg\\)','\(b\)\\(fg = ch\\)','\(c\)\\({h^2} = gf\\)','\(d\)\\(fgh = c\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)\\(hxy + gx + fy + c = 0,\\)Apply \\(\\Delta \\' = 0\\)i.e., \\(a\\'b\\'c\\' + 2f\\'g\\'h\\' - a\\'f{\\'^2} - b\\'g{\\'^2} - c\\'\\,h{\\'^2} = 0\\)Here \\(h\\' = \\frac{h}{2},\\;g\\' = \\frac{g}{2},\\;f\\' = \\frac{f}{2},\\;c\\' = c,\\;a\\' =0,\\;b\\' = 0\\)Hence, \\(\\frac{{hgf}}{4} - \\frac{{c{h^2}}}{4} = 0\\)or \\(fg = ch\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of pair of straight lines perpendicular to the pair \\(a{x^2} +2hxy + b{y^2} = 0\\) is','\(a\)\\(a{x^2} - 2hxy + b{y^2} = 0\\)','\(b\)\\(b{x^2} + 2hxy + a{y^2} = 0\\)','\(c\)\\(a{y^2} - 2hxy + b{x^2} = 0\\)','\(d\)\\(a{y^2} - b{x^2} = 0\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Perpendicular lines are given by\\(b{x^2} - 2hxy + a{y^2} = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The values of h for which the equation \\(3{x^2} + 2hxy - 3{y^2} - 40x + 30y- 75 = 0\\) represents a pair of straight lines, are','\(a\)4, 4','\(b\)4, 6','\(c\)4, - 4','\(d\)0, 4','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)We have \\(abc + 2fgh - a{f^2} - b{g^2} - c{h^2} = 0\\)\\(\\Rightarrow (3)( - 3)( - 75) + 2(15)( - 20)(h)\\)\\(- 3{(15)^2} - ( - 3){( - 20)^2} - ( - 75){h^2} = 0\\)\\( \\Rightarrow Answer = - Answer =  - Answer = + Answer =  + 75{h^2} = 0\\)\\(\\Rightarrow h = 4,4.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of lines passing through the origin and parallel to the lines\\(y = {m_1}x + {c_1}\\) and \\(y = {m_2}x + {c_2}\\) is','\(a\)\\({m_1}{m_2}{x^2} - ({m_1} + {m_2})xy + {y^2} = 0\\)','\(b\)\\({m_1}{m_2}{x^2} + ({m_1} + {m_2})xy + {y^2} = 0\\)','\(c\)\\({m_1}{m_2}{y^2} - ({m_1} + {m_2})xy + {x^2} = 0\\)','\(d\)\\({m_1}{m_2}{y^2} + ({m_1} + {m_2})xy + {x^2} = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Passing through origin and is parallel to given lines are \\(y ={m_1}x\\)and \\(y = {m_2}x\\). If represented as pair of straight lines, we get \\((y- {m_1}x)(y - {m_2}x) = 0\\)\\(\\Rightarrow {m_1}{m_2}{x^2} - ({m_1} + {m_2})xy + {y^2} = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the lines represented by the equation \\(ab({x^2} - {y^2}) +({a^2} - {b^2})xy = 0\\) are','\(a\)\\(ax - by = 0,\\;bx + ay = 0\\)','\(b\)\\(ax - by = 0,\\;bx - ay = 0\\)','\(c\)\\(ax + by = 0,\\;bx + ay = 0\\)','\(d\)\\(ax + by = 0,\\;bx - ay = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\(ab({x^2} - {y^2}) + ({a^2} - {b^2})xy = 0\\)\\(\\Rightarrow ab{x^2} + {a^2}xy - {b^2}xy - ab{y^2} = 0\\)\\(\\Rightarrow ax(bx + ay) - by(bx + ay) = 0\\)\\(\\Rightarrow (bx + ay)(ax - by) = 0\\)\\( \\Rightarrow bx + ay = 0\\)and \\(ax - by = 0\\) .',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\({(x - 5)^2} + (x - 5)\\,(y - 6)\\, - 2\\,{(y - 6)^2} = 0\\)represents','\(a\)A circle','\(b\)Two straight lines passing through origin','\(c\)Two straight lines passing through the point (5, 6)','\(d\)None of these','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)\\({(x - 5)^2} + (x - 5)(y - 6) - 2{(y - 6)^2} = 0\\)\\(\\Rightarrow{}\\) \\({x^2} + 25 - 10x + xy + 30 - 6x - 5y - 2{y^2} - 72 + 24y =0\\)\\(\\Rightarrow {x^2} + xy - 2{y^2} - 16x + 19y - 17 = 0\\)Obviously, it is not a circle as \\(a \\ne b\\)and xy is present. On checking forpair of straight lines, we get that the equation represents a pair of straightlines.Also for \\(x = 5,\\;y = 6,\\)equation vanishes. Therefore it passes through (5,6).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','A second degree homogenous equation in x and y always represents','\(a\)A pair of straight lines','\(b\)A circle','\(c\)A conic section','\(d\)None of these','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)A second degree homogeneous equation in x and y always represents a pairof straight lines passing through origin. But general equation of pair ofstraight lines, circle and conics are not homogenous.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(6{x^2} + 11xy - 10{y^2} + x + 31y + k = 0\\) represents a pair of straightlines, then \\(k = \\)','\(a\)- 15','\(b\)6','\(c\)- 10','\(d\)- 4','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\( - 6.10k + \\frac{{11.1.31}}{4} - 6{\\left( {\\frac{{31}}{2}} \\right)^2} +10{\\left( {\\frac{1}{2}} \\right)^2} - k{\\left( {\\frac{{11}}{2}} \\right)^2} = 0\\)\\(\\Rightarrow  - k\\frac{{Answer = }{4} = \\frac{{Answer = }}{4} \\Rightarrow k =  - 15.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(4ab = 3{h^2}\\), then the ratio of slopes of the lines represented by theequation \\(a{x^2} + 2hxy + b{y^2} = 0\\) will be','\(a\)\\(\\sqrt 2 :1\\)','\(b\)\\(\\sqrt 3 :1\\)','\(c\)\\(2:1\\)','\(d\)\\(1:3\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Here \\({m_1} + {m_2} = \\frac{{ - 2h}}{b}\\).....(i)and \\({m_1}{m_2} = \\frac{a}{b}\\).....(ii)Also, given that \\(4ab = 3{h^2}.\\)Now we have to find\\(\\frac{{{m_1}}}{{{m_2}}}\\),therefore with the help of (i) and (ii), we get\\({({m_1} - {m_2})^2} = \\frac{{4{h^2} - 4ab}}{{{b^2}}} = \\frac{{4{h^2} -3{h^2}}}{{{b^2}}} = \\frac{{{h^2}}}{{{b^2}}}\\)\\(\\Rightarrow{}\\) \\({m_1} - {m_2} = \\frac{h}{b}\\).....(iii)Now on solving (i) and (iii), we get\\({m_1} = \\frac{{ - h}}{{2b}}\\)and \\({m_2} =\\frac{{ - 3h}}{{2b}}\\);  \\(\\therefore{}\\)\\({m_1}:{m_2} = 1:3\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines represented by the equation \\(a{x^2}(b - c) - xy(ab - bc) + c{y^2}(a- b) = 0\\) are','\(a\)\\(a(b - c)x - c(a - b)y = 0\\), \\(x + y = 0\\)','\(b\)\\(x + y = 0\\), \\(x - y = 0\\)','\(c\)\\(a(b - c)x - c(a - b)y = 0\\), \\(x - y = 0\\)','\(d\)None of these','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)From options, proceed as to find the equation represented by them \\(a(b -c)x - c(a - b)y = 0\\) and \\(x - y = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(a{x^2} + 2hxy + b{y^2} = 0\\) represents two lines \\(y ={m_1}x\\) and \\(y = {m_2}x\\), then','\(a\)\\({m_1} + {m_2} = \\frac{{ - 2h}}{b}\\) and \\({m_1}{m_2} = \\frac{a}{b}\\)','\(b\)\\({m_1} + {m_2} = \\frac{{2h}}{b}\\) and \\({m_1}{m_2} = \\frac{{ - a}}{b}\\)','\(c\)\\({m_1} + {m_2} = \\frac{{2h}}{b}\\) and \\({m_1}{m_2} = \\frac{a}{b}\\)\\paragraph{','\(d\)\\({m_1} + {m_2} = \\frac{{2h}}{b}\\) and \\({m_1}{m_2} =  - ab\\)}','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)It is a fundamental concept.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The nature of straight lines represented by the equation \\(4{x^2} + 12xy +9{y^2} = 0\\) is','\(a\)Real and coincident','\(b\)Real and different','\(c\)Imaginary and different','\(d\)None of the above','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\(4{x^2} + 12xy + 9{y^2} = 0\\)Here\\({h^2} - ab = 36 - 36 = 0\\), \\(\\left( {{\\rm{from}}\\tan \\theta  = \\frac{{\\pm 2\\sqrt {{h^2} - ab} }}{{a + b}}} \\right)\\)Hence, lines are real and coincident.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the perpendiculars drawn from the origin to the linesrepresented by the equation \\(2{x^2} - 10xy + 12{y^2} + 5x - 16y - 3 = 0\\) is','\(a\)\\(6{x^2} + 5xy + {y^2} = 0\\)','\(b\)\\(6{y^2} + 5xy + {x^2} = 0\\)','\(c\)\\(6{x^2} - 5xy + {y^2} = 0\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)We know that the equation of perpendicular drawn from origin on \\(a{x^2}+ 2hxy + b{y^2} + 2gx + 2fy + c = 0\\) is \\(b{x^2} - 2hxy + a{y^2} = 0\\).Therefore, the required equation is given by\\(12{x^2} + 10xy + 2{y^2} = 0\\)or \\(6{x^2} + 5xy + {y^2} = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Which of the following second degree equation represented a pair of straightlines','\(a\)\\({x^2} - xy - {y^2} = 1\\)','\(b\)\\( - {x^2} + xy - {y^2} = 1\\)','\(c\)\\(4{x^2} - 4xy + {y^2} = 4\\)','\(d\)\\({x^2} + {y^2} = 4\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)We have to check for each equation separately. Option \(d\) cannot bebecause it is a circle.Considering option \(a\) \\(\\Delta  \\ne 0,\\;\\)option \(b\) \\(\\Delta  \\ne 0\\)and foroption \(c\) \\(\\Delta  = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines \\({a^2}{x^2} + bc{y^2} = a(b + c)xy\\) will be coincident, if','\(a\)\\(a = 0\\) or \\(b = c\\)','\(b\)\\(a = b\\) or \\(a = c\\)','\(c\)\\(c = 0\\) or \\(a = b\\)','\(d\)\\(a = b + c\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)The lines are coincident, if \\({H^2} - AB = 0,\\)so \\({\\left\\{ {\\frac{{a(b + c)}}{2}} \\right\\}^2} - {a^2}(bc) = 0\\)\\(\\Rightarrow{}\\) \\({a^2}{(b - c)^2} = 0\\)\\(\\Rightarrow{}\\) a = 0 or \\(b = c\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(2{x^2} - 2hxy + 2{y^2} = 0\\) represents two coincidentstraight lines passing through the origin, then \\(h = \\)','\(a\)\\(\\pm{}\\) 6','\(b\)\\(\\sqrt 6 \\)','\(c\) \\(- \\sqrt 6\\)','\(d\)2','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)If it represents two coincident straight lines, then the condition\\({h^2} - ab = 0\\) should apply as angle between them would be zero. Hence\\({h^2} - 4 = 0\\)or \\(h =  \\pm 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If one of the lines represented by the equation \\(a{x^2} + 2hxy + b{y^2} = 0\\)be \\(y = mx\\), then','\(a\)\\(b{m^2} + 2hm + a = 0\\)','\(b\)\\(b{m^2} + 2hm - a = 0\\)','\(c\)\\(a{m^2} + 2hm + b = 0\\)','\(d\)\\(b{m^2} - 2hm + a = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\) Substituting the value of y in the equation \\(a{x^2} + 2hxy + b{y^2} =0\\).\\( \\Rightarrow a{x^2} + 2h(mx)x + b{(mx)^2} = 0\\)\\(\\Rightarrow a + 2hm + b{m^2} = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the lines passing through the origin and parallel to the linesrepresented by the equation \\(2{x^2} - xy - 6{y^2} + 7x + 21y - 15 = 0\\), is','\(a\)\\(2{x^2} - xy - 6{y^2} = 0\\)','\(b\)\\(6{x^2} - xy + 2{y^2} = 0\\)','\(c\)\\(6{x^2} - xy - 2{y^2} = 0\\)','\(d\)\\(2{x^2} + xy - 6{y^2} = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Equation is \\(2{x^2} - xy - 6{y^2} + 7x + 21y - 15 = 0\\)Therefore, equation of lines parallel to given lines and passes through originis homogeneous 2\\(^{nd}\\) degree equation i.e., \\(2{x^2} - xy - 6{y^2} = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\(2{x^2} + 4xy - p{y^2} + 4x + qy + 1 = 0\\) will represent twomutually perpendicular straight lines, if','\(a\)p = 1and q = 2or 6','\(b\)p = 2and q = 0 or 6','\(c\)p = 2and q = 0 or 8','\(d\)p = - 2and q = - 2or 8','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Here equation is \\(2{x^2} + 4xy - p{y^2} + 4x + qy + 1 = 0\\).The lines are perpendicular, if \\(a + b = 0\\)\\(\\Rightarrow 2 - p = 0 \\Rightarrow p = 2\\)and it will represent two lines, if\\(abc + 2fgh - a{f^2} - b{g^2} - c{h^2} = 0\\)\\(\\Rightarrow 2( - 2)(1) + 2{\\rm{ }}\\left( {\\frac{q}{2}} \\right){\\rm{ }}(2){\\rm{}}(2) - 2{\\left( {\\frac{q}{2}} \\right)^2} + 2{(2)^2} - 1{(2)^2} = 0\\)\\( \\Rightarrow {q^2} - 8q = 0 \\Rightarrow q = 0\\) or 8.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(A{x^2} + 2Bxy + C{y^2} + Dx + Ey + F = 0\\) represents a pairof straight lines, then \\({B^2} - AC\\)','\(a\)\\(<\\) 0','\(b\)= 0','\(c\)\\(>\\) 0','\(d\)None of these','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Using the condition \\(\\Delta  = 0\\)\\(\\Rightarrow ACF + 2.\\frac{E}{2}.\\frac{D}{2}.B - A.{\\left( {\\frac{E}{2}}\\right)^2} - C\\,.\\,{\\left( {\\frac{D}{2}} \\right)^2} - F.{\(b\)^2} = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the lines passing through the origin and having slopes 3 and\\( - \\frac{1}{3}\\) is','\(a\)\\(3{y^2} + 8xy - 3{x^2} = 0\\)','\(b\)\\(3{x^2} + 8xy - 3{y^2} = 0\\)','\(c\)\\(3{y^2} - 8xy + 3{x^2} = 0\\)','\(d\)\\(3{x^2} + 8xy + 3{y^2} = 0\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)\\({m_1} = 3,\\;\\;{m_2} =  - \\frac{1}{3}\\). Hence, the lines are \\(y =3x,\\;y =  - \\frac{1}{3}x\\).Multiplying both the lines, we get\\((y - 3x)(3y + x) = 0 \\Rightarrow 3{x^2} + 8xy - 3{y^2} = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\(xy + {a^2} = a(x + y)\\) represents','\(a\)A parabola','\(b\)A pair of straight lines','\(c\)An ellipse','\(d\)Two parallel straight lines','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)\\(xy + {a^2} = a(x + y).\\)Firstly always check for pair of straight linesi.e., \\(\\Delta  = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the slope of one of the line represented by the equation \\(a{x^2} + 2hxy +b{y^2} = 0\\) be \\(\\lambda{}\\) times that of the other, then','\(a\)\\(4\\lambda h = ab(1 + \\lambda )\\)','\(b\)\\(\\lambda h = ab{(1 + \\lambda )^2}\\)','\(c\)\\(4\\lambda {h^2} = ab{(1 + \\lambda )^2}\\)','\(d\)None of these','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)It is given that \\({m_2} = \\lambda {m_1} \\Rightarrow {m_1} + \\lambda{m_1} = \\frac{{ - 2h}}{b}\\)\\( \\Rightarrow {m_1} = \\frac{{ - 2h}}{{b(1 + \\lambda )}}\\).....(i)and \\({m_1}.\\lambda {m_1} = \\frac{a}{b} \\Rightarrow {m_1} = \\sqrt{\\frac{a}{{b\\lambda }}} \\)..\\(\\ldots{} \\)(ii)Hence, by (i) and (ii), \\(\\sqrt {\\frac{a}{{b\\lambda }}}  = \\frac{{ - 2h}}{{b(1 +\\lambda )}}\\)On squaring both sides, we get \\(4\\lambda {h^2} = ab{(1 + \\lambda )^2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If one of the line represented by the equation \\(a{x^2} + 2hxy + b{y^2} = 0\\)is coincident with one of the line represented by \\(a\\'{x^2} + 2h\\'xy + b\\'{y^2} =0\\), then','\(a\)\\({(ab\\' - a\\'b)^2} = 4(ah\\' - a\\'h)\\,(hb\\' - h\\'b)\\)','\(b\)\\({(ab\\' + a\\'b)^2} = 4(ah\\' - a\\'h)\\,(hb\\' - h\\'b)\\)','\(c\)\\({(ab\\' - a\\'b)^2} = (ah\\' - a\\'h)\\,(hb\\' - h\\'b)\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)The equation of given lines are\\(a{x^2} + 2hxy + b{y^2} = 0\\).....(i)\\(a\\'{x^2} + 2h\\'xy + b\\'{y^2} = 0\\).....(ii)Let common line to both is \\(y = mx\\), then it will satisfy both the aboveequations.Hence, \\(a + 2mh + b{m^2} = 0\\).....(iii)and \\(a\\'\\, + \\,2mh\\'\\, + \\,b\\'{m^2} = 0\\).....(iv)Now eliminating \\'m\\' from the equation (iii) and (iv),we get \\(\\frac{{{m^2}}}{{2ha\\' - 2h\\'a}} = \\frac{{ - m}}{{ba\\' - b\\'a}} =\\frac{1}{{2bh\\' - 2b\\'h}}\\)\\( \\Rightarrow {m^2} = \\frac{{ha\\' - h\\'a}}{{bh\\' - b\\'h}}\\).....(v)and \\({m^2} = \\frac{{{{(ab\\' - ba\\')}^2}}}{{4{{(bh\\' - b\\'h)}^2}}}\\).....(vi)From (v) and (vi), we get the required condition.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\(4{x^2} + 12xy + 9{y^2} + 2gx + 2fy + c = 0\\) will representstwo real parallel straight lines, if','\(a\)g = 4, f = 9, c = 0','\(b\)g = 2, f = 3, c = 1','\(c\)g = 2, f = 3, c is any number','\(d\)g = 4, f = 9, c \\(>\\) 1','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)The lines are parallel, if \\({h^2} = ab,\\,a{f^2} = b{g^2}\\)or \\(\\frac{a}{h} = \\frac{h}{b} = \\frac{g}{f} \\Rightarrow 4{f^2} = 9{g^2}\\)\\( \\Rightarrow f = \\frac{3}{2}g \\Rightarrow g = 2,\\;\\;f = 3\\) (let)Now \\(abc + 2fgh - a{f^2} - b{g^2} - c{h^2} = 0\\)\\(\\Rightarrow 4 \\times 9 \\times c + 2 \\times 3 \\times 2 \\times 6 - 4{(3)^2} -9{(2)^2} - c{(6)^2} = 0\\)\\( \\Rightarrow \\)c is any number.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\(2{y^2} - xy - {x^2} + 6x - 8 = 0\\) represents','\(a\)A pair of straight lines','\(b\)A circle','\(c\)An ellipse','\(d\)A parabola','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Using condition \\(\\Delta  = 0\\), i.e.,\\(( - 1)\\,(2)\\,( - 8) + 0 - 0 - 2{(3)^2} - ( - 8)\\,{\\left( {\\frac{{ - 1}}{2}}\\right)^2} = 16 - 18 + 2 = 0.\\)Hence, equation represents a pair of straight lines.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','One of the lines represented by the equation \\({x^2} + 6xy = 0\\) is','\(a\)Parallel to x-axis','\(b\)Parallel to y-axis','\(c\)x-axis','\(d\)y-axis','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)\\({x^2} + 6xy = 0 \\Rightarrow x(x + 6y) = 0\\)\\( \\Rightarrow x = 0\\) and \\(x + 6y = 0\\) are two straight lines.Hence, \\(x = 0\\) represents \\(y - \\)axis.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the lines represented by the equation \\({x^2} - 5xy + 6{y^2} =0\\) are','\(a\)\\(y + 2x = 0\\), \\(y - 3x = 0\\)','\(b\)\\(y - 2x = 0\\), \\(y - 3x = 0\\)','\(c\)\\(y + 2x = 0\\), \\(y + 3x = 0\\)','\(d\)None of these','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)\\({x^2} - 5xy + 6{y^2} = 0\\) \\(\\Rightarrow{}\\) \\({x^2} - 2xy - 3xy +6{y^2} = 0\\)\\(\\Rightarrow{}\\) \\(x(x - 2y) - 3y(x - 2y) = 0\\)\\(\\Rightarrow{}\\) \\((x - 2y) = 0\\) and \\(x - 3y = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(a{x^2} + b{y^2} + cx + cy = 0\\) represents a pair of straightlines, then','\(a\)\\(a(b + c) = 0\\)','\(b\)\\(b(c + a) = 0\\)','\(c\)\\(c(a + b) = 0\\)','\(d\)\\(a + b + c = 0\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)\\(ab(0) + 2\\left( {\\frac{c}{2}} \\right){\\rm{ }}\\left( {\\frac{c}{2}}\\right){\\rm{ }}(0) - a{\\rm{ }}{\\left( {\\frac{c}{2}} \\right)^2} - b{\\rm{ }}{\\left({\\frac{c}{2}} \\right)^2} - 0{(0)^2} = 0\\)\\( \\Rightarrow a{c^2} + b{c^2} = 0\\;\\; \\Rightarrow \\,{c^2}(a + b) = 0\\)\\(\\Rightarrow c(a + b) = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equations of the lines represented by the equation \\(a{x^2} + (a + b)xy+ b{y^2} + x + y = 0\\) are','\(a\)\\(ax + by + 1 = 0\\), \\(x + y = 0\\)','\(b\)\\(ax + by - 1 = 0\\), \\(x + y = 0\\)','\(c\)\\(ax + by + 1 = 0\\), \\(x - y = 0\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Equation \\(a{x^2} + (a + b)xy + b{y^2} + x + y = 0\\) can be written as\\((ax + by + 1)(x + y) = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\({x^2} - 7xy + 12{y^2} = 0\\) represents','\(a\)Circle','\(b\)Pair of parallel straight lines','\(c\)Pair of perpendicular straight lines','\(d\)Pair of non-perpendicular intersecting straight lines','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)\\({x^2} - 7xy + 12{y^2} = 0 \\Rightarrow (x - 3y)(x - 4y) = 0\\)Hence, the lines are non-perpendicular intersecting.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\({y^2} - {x^2} + 2x - 1 = 0\\) represents','\(a\)A pair of straight lines','\(b\)A circle','\(c\)A parabola','\(d\)An ellipse','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Using condition \\(\\Delta  = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(\\lambda {x^2} + 2{y^2} - 5xy + 5x - 7y + 3 = 0\\)represents two straight lines, then the value of \\(\\lambda{}\\) will be','\(a\)3','\(b\)2','\(c\)8','\(d\)- 8','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)\\(abc + 2fgh - a{f^2} - b{g^2} - c{h^2} = 0\\)\\(\\Rightarrow \\lambda (2)(3) + 2\\left( {\\frac{{ - 7}}{2}} \\right){\\rm{ }}\\left({\\frac{5}{2}} \\right){\\rm{ }}\\left( {\\frac{{ - 5}}{2}} \\right) - \\lambda {\\left({\\frac{{ - 7}}{2}} \\right)^2}\\)\\(- 2{\\rm{ }}{\\left( {\\frac{5}{2}} \\right)^2} - 3{\\left( {\\frac{{ - 5}}{2}}\\right)^2} = 0\\)\\( \\Rightarrow 6\\lambda  + \\frac{{Answer = }{4} - \\frac{{49}}{4}\\lambda  -\\frac{{25}}{2} - \\frac{{75}}{4} = 0\\)\\(\\Rightarrow 25\\lambda  = 50 \\Rightarrow \\lambda  = 2.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of one of the line represented by the equation \\({x^2} +2xy\\cot \\theta  - {y^2} = 0\\), is','\(a\)\\(x - y\\cot \\theta  = 0\\)','\(b\)\\(x + y\\tan \\theta  = 0\\)','\(c\)\\(x\\sin \\theta  + y(\\cos \\theta  + 1) = 0\\)','\(d\)\\(x\\cos \\theta  + y(\\sin \\theta  + 1) = 0\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)The lines represented by the equation \\({x^2} + 2xy\\cot \\theta  - {y^2} =0\\)are \\(ax + hy \\pm y\\sqrt {{h^2} - ab}  = 0\\)\\(\\Rightarrow x + y\\cot \\theta  \\pm y\\sqrt {{{\\cot }^2}\\theta  + 1}  = 0\\)\\( \\Rightarrow x + y\\left( {\\frac{{\\cos \\theta }}{{\\sin \\theta }} \\pm\\frac{1}{{\\sin \\theta }}} \\right) = 0\\)\\(\\Rightarrow x\\sin \\theta  + y(\\cos \\theta  \\pm 1) = 0\\)Hence, one line is \\(x\\sin \\theta  + y(\\cos \\theta  + 1) = 0\\) .',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of one of the line represented by the equation \\(pq({x^2} -{y^2}) + ({p^2} - {q^2})xy = 0\\), is','\(a\)\\(px + qy = 0\\)','\(b\)\\(px - qy = 0\\)','\(c\)\\({p^2}x + {q^2}y = 0\\)','\(d\)\\({q^2}x - {p^2}y = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Equation of lines are \\((px + qy)(py - qx) = 0\\).Hence, one line is \\(px + qy = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The pair of straight lines passes through the point (1, 2) and perpendicular to the pair of straight lines \\(3{x^2} - 8xy + 5{y^2} = 0\\),is','\(a\)\\((5x + 3y + 11)(x + y + 3) = 0\\)','\(b\)\\((5x + 3y - 11)(x + y - 3) = 0\\)','\(c\)\\((3x + 5y - 11)(x + y + 3) = 0\\)','\(d\)\\((3x - 5y + 11)(x + y - 3) = 0\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)The equation of lines represented by the equation \\(3{x^2} - 8xy + 5{y^2}= 0\\) are \\(3x - 5y = 0\\) and \\(x - y = 0.\\)Therefore, equation of lines passingthrough (1,2) and perpendicular to given lines are \\(x + y - 3 = 0\\) and \\(5x +3y - 11 = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in general quadratic equation \\(f(x,\\,y) = 0\\), \\(\\Delta  = 0\\) and\\({h^2} = ab\\), then the equation represents','\(a\)Two parallel straight lines','\(b\)Two perpendicular straight lines','\(c\)Two coincident lines','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\(f(x,y) = 0\\) gives us an equation \\(\\Delta  = 0\\)gives us a pair ofstraight lines. \\({h^2} = ab\\) gives angle between them equal to zero. Henceequation represents two parallel lines.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The condition of representing the coincident lines by the general quadraticequation \\(f(x,\\,y) = 0\\), is','\(a\)\\(\\Delta  = 0\\) and \\({h^2} = ab\\)','\(b\)\\(\\Delta  = 0\\) and \\(a + b = 0\\)','\(c\)\\(\\Delta  = 0\\) and \\({h^2} = ab\\), \\({g^2} = ac\\), \\({f^2} = bc\\)','\(d\)\\({h^2} = ab\\), \\({g^2} = ac\\) and \\({f^2} = bc\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)It is obvious.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\({x^2} + k{y^2} + 4xy = 0\\)represents two coincident lines, if k=','\(a\)0','\(b\)1','\(c\)4','\(d\)16','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\) To represent pair of coincident straight lines \\({x^2} + k{y^2} + 4xy =0\\) must be perfect square.Therefore, \\(k = 4\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The joint equation of the straight lines \\(x + y = 1\\)and \\(x - y = 4\\)is','\(a\)\\({x^2} - {y^2} =  - 4\\)','\(b\)\\({x^2} - {y^2} = 4\\)','\(c\)\\((x + y - 1)\\,(x - y - 4) = 0\\)','\(d\)\\((x + y + 1)(x - y + 4) = 0\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)The joint equation of the given lines is \\((x + y - 1)(x - y - 4) = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\(\\lambda \\) for which the equation \\({x^2} - \\lambda xy +2{y^2} + 3x - 5y + 2 = 0\\) may represent a pair of straight lines is ','\(a\)2','\(b\)4','\(c\)6','\(d\)8','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)\\(1 \\times 2 \\times 2 + 2 \\times \\left( {\\frac{{ - 5}}{2}} \\right) \\times\\frac{3}{2} \\times \\left( {\\frac{{ - \\lambda }}{2}} \\right) - 1 \\times\\frac{{25}}{4} - 2 \\times \\frac{9}{4} - 2\\frac{{{\\lambda ^2}}}{4} = 0\\)\\(\\Rightarrow \\lambda  = 3,\\,\\frac{9}{2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The gradient  of one of the lines \\({x^2} + hxy + 2{y^2} = 0\\) is twice thatof the other, then h =','\(a\)\\( \\pm \\,3\\)','\(b\)\\( \\pm \\,\\frac{3}{2}\\)','\(c\)\\( \\pm \\,2\\)','\(d\)\\( \\pm \\,1\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Applying the condition, \\(4\\lambda {h^2} = ab{(1 + \\lambda )^2}\\)Here \\(\\lambda  = 2\\), therefore\\(4 \\times 2 \\times {\\left( {\\frac{h}{2}} \\right)^2} = 1 \\times 2{(1 + 2)^2}\\Rightarrow {h^2} = 9 \\Rightarrow h =  \\pm 3.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the point (2,-3) lies on \\(k{x^2} - 3{y^2} + 2x + y - 2 = 0\\), then k is equal to','\(a\)\\(\\frac{1}{7}\\)','\(b\) 16','\(c\)7','\(d\)12','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Point \\((2, - 3)\\) lies on the line \\(k{x^2} - 3{y^2} + 2x + y - 2 = 0\\)\\(\\Rightarrow{}\\) \\({(2)^2}k - 3{( - 3)^2} + 2.2 - 3 - 2 = 0\\)\\(4k - 28 = 0 \\Rightarrow k = 7.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(L{x^2} - 10xy + 12{y^2}\\)\\( + 5x - 16y - 3 = 0\\) represents a pair ofstraight lines, then L is ','\(a\)1','\(b\)2','\(c\)3','\(d\)-1','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Given equation represents a pair of straight lines, if\\(abc + 2fgh - a{f^2} - b{g^2} - c{h^2} = 0\\)\\(\\Rightarrow  - 36L + Answer = - 64L - 75 + 75 = 0\\)\\(\\Rightarrow  - Answer =  + Answer = = 0,\\,\\,\\,\\therefore L = 2.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the ratio of gradients of the lines represented by \\(a{x^2} + 2hxy +b{y^2} = 0\\)is  1: 3, then the value of the ratio \\({h^2}:ab\\)is','\(a\)\\(\\frac{1}{3}\\)','\(b\)\\(\\frac{3}{4}\\)','\(c\)\\(\\frac{4}{3}\\)','\(d\) 1','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Gradients \\(\\frac{{{m_1}}}{{{m_2}}} = 1:3\\)\\({m_1} = m,\\;\\;{m_2} = 3m\\)\\(\\therefore {m_1} + {m_2} =  - \\frac{{2h}}{b}\\).....(i)and \\({m_1}.{m_2} = \\frac{a}{b}\\) .\\(\\ldots{} \\).(ii)From equation (i), \\(m + 3m =  - \\frac{{2h}}{b}\\)or \\(m = \\frac{{ - h}}{{2b}}\\)From equation (ii), \\(m.3m = \\frac{a}{b}\\)\\(3.\\frac{{{h^2}}}{{4{b^2}}} = \\frac{a}{b} \\Rightarrow \\frac{{{h^2}}}{{ab}} =\\frac{4}{3}.\\)Trick : If the gradients of two lines are in ratio \\(1:n\\).Then \\(\\frac{{{h^2}}}{{ab}} = \\frac{{{{(n + 1)}^2}}}{{4n}} = \\frac{{{{(3 +1)}^2}}}{{4.3}} = \\frac{4}{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the slope of one line of the pair of lines represented by \\(a{x^2} + 4xy+ {y^2} = 0\\)is 3 times the slope of the other line, then a is','\(a\)1','\(b\)2','\(c\)3','\(d\)4','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Here, \\({m_1} + {m_2} =  - 4\\).....(i)and \\({m_1}\\,{m_2} = a\\).\\(\\ldots{} \\).(ii)Given that \\({m_1} = 3{m_2}\\).By (i), \\(3{m_2} + {m_2} =  - 4\\)\\(\\Rightarrow{}\\) \\({m_2} =  - 1\\)Hence, \\({m_1} =  - 3\\). Now, by (ii) \\(a = 3.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sum of slopes of the pair of lines represented by \\(4{x^2} + 2hxy -7{y^2} = 0\\)is equal to the product of the slopes, then the value of h is','\(a\)- 6','\(b\)- 2','\(c\)- 4','\(d\)4','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Comparing the given equation with the standard equation, we get \\(a = 4\\)and \\(b =  - 7\\). Let \\({m_1}\\) and \\({m_2}\\) are the slopes of given lines.Therefore sum of the slopes \\(({m_1} + {m_2}) =  - \\frac{{2h}}{b} =\\frac{{2h}}{7}\\) and product of the slopes \\(({m_1}{m_2}) = \\frac{a}{b} =\\frac{4}{{ - 7}}\\).\\({m_1} + {m_2} = {m_1}{m_2}\\), therefore \\(\\frac{{2h}}{7} = \\frac{4}{{ - 7}}\\)or \\(h =  - 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The gradient of one of the lines of \\(a{x^2} + 2hxy + b{y^2} = 0\\) is twicethat of the other, then','\(a\)\\({h^2} = ab\\)','\(b\)\\(h = a + b\\)','\(c\)\\(8{h^2} = 9ab\\)','\(d\)\\(9{h^2} = 8ab\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Here, \\({m_1} + {m_2} = \\frac{{ - 2h}}{b}\\) and \\({m_1}{m_2} =\\frac{a}{b}\\)Given that \\({m_1} = 2{m_2}\\)\\(\\therefore \\)\\(3{m_2} = \\frac{{ - 2h}}{b}\\) and \\(2m_2^2 = \\frac{a}{b}\\)\\(\\Rightarrow{}\\) \\(2{\\left( {\\frac{{ - 2h}}{{3b}}} \\right)^2} =\\frac{a}{b}\\)\\(\\Rightarrow{}\\) \\(8{h^2} = 9ab\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\({x^2} + kxy + {y^2} - 5x - 7y + 6 = 0\\) represents a pair ofstraight lines, then k is ','\(a\)5/3','\(b\)10/3','\(c\)3/2','\(d\)3/10','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)\\(\\left| {\\begin{array}{*{20}{c}}1&{k/2}&{ - 5/2}\\\\{k/2}&1&{ - 7/2}\\\\{ - 5/2}&{ - 7/2}&6\\end{array}} \\right| = 0\\)\\( \\Rightarrow \\)\\(\\left( {6 - \\frac{{49}}{4}} \\right) - \\frac{k}{2}\\left( {\\frac{{6k}}{2} -\\frac{{35}}{4}} \\right) - \\frac{5}{2}\\left( { - \\frac{{7k}}{4} + \\frac{5}{2}}\\right) = 0\\)\\(\\Rightarrow{}\\) \\(6{k^2} - 35k + 50 = 0\\) \\(\\Rightarrow{}\\) \\(k =\\frac{{10}}{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\(2{x^2} + 4xy - k{y^2} + 4x + 2y - 1 = 0\\) represents a pairof lines.  The value of k is','\(a\)\\( - \\frac{5}{3}\\)','\(b\)\\(\\frac{5}{3}\\)','\(c\)\\(\\frac{1}{3}\\)','\(d\)\\( - \\frac{1}{3}\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Comparing the given equation with the standard equation, we get \\(a =2,\\;f = 1,\\;b =  - k\\), \\(g = 2,\\) \\(c =  - 1\\) and \\(h = 2\\). Since equationrepresents a pair of lines, therefore \\(abc + 2fgh - a{f^2} - b{g^2} - c{h^2} =0\\)or \\(2\\,k + 8 - 2 + 4k + 4 = 0\\) or \\(6k =  - 10\\). Therefore \\(k =  -\\frac{5}{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Separate equations of lines, for a pair of lines, whose equation is \\({x^2}+ xy - 12{y^2} = 0\\), are','\(a\)\\(x + 4y = 0\\)and \\(x + 3y = 0\\)','\(b\)\\(2x - 3y = 0\\) and \\(x - 4y = 0\\)','\(c\)\\(x - 6y = 0\\)and \\(x - 3y = 0\\)','\(d\)\\(x + 4y = 0\\)and \\(x - 3y = 0\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)\\({x^2} + xy - 12{y^2} = 0\\) \\(\\Rightarrow{}\\) \\({x^2} + 4xy - 3xy -12{y^2} = 0\\)\\(\\Rightarrow{}\\) \\((x + 4y)(x - 3y) = 0\\)Therefore separate equations for the lines are \\(x + 4y = 0\\) and \\(x - 3y =0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of k so that the equation \\(2{x^2} + 5xy + 3{y^2} + 6x + 7y + k =0\\) represents a pair of straight lines, is ','\(a\)4','\(b\)6','\(c\)0','\(d\)8','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\(\\left| {\\,\\begin{array}{*{20}{c}}2&{5/2}&3\\\\{5/2}&3&{7/2}\\\\3&{7/2}&k\\end{array}\\,} \\right| = 0\\) \\(\\Rightarrow{}\\) \\(\\left|{\\,\\begin{array}{*{20}{c}}4&5&6\\\\5&6&7\\\\6&7&{2k}\\end{array}\\,} \\right| = 0\\)\\(\\Rightarrow{}\\) \\(4(12k - 49) - 5(10k - 42) + 6(35 - 36) = 0\\)\\(\\Rightarrow{}\\) \\(48k - Answer = - 50k + Answer = + Answer = - Answer = = 0\\) \\(\\Rightarrow{}\\)\\(k = 4\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(3{x^2} + xy - {y^2} - 3x + 6y + k = 0\\) represents a pairof lines, then k is equal to ','\(a\)9','\(b\)1','\(c\)0','\(d\)- 9',' (d','PAIR OF STRAIGHT LINE','2','Answer =  \(d\)\\(\\left| {\\,\\begin{array}{*{20}{c}}3&{1/2}&{ - 3/2}\\\\{1/2}&{ - 1}&3\\\\{ - 3/2}&3&k\\end{array}\\,} \\right|\\,\\, = \\,\\,0\\) \\(\\Rightarrow{}\\) \\(\\left|{\\,\\begin{array}{*{20}{c}}6&1&{ - 3}\\\\1&{ - 2}&6\\\\{ - 3}&6&{2k}\\end{array}\\,} \\right|\\,\\, = \\,\\,0\\)\\(\\Rightarrow{}\\) \\(6( - 4k - 36) - 1(2k + 18) - 3(6 - 6) = 0\\)\\(\\Rightarrow{}\\) \\( - 24k - Answer = - 2k - 18 = 0\\)\\(\\Rightarrow{}\\) \\(k =  - 9\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Equation  \\(3{x^2} + 7xy + 2{y^2} + 5x + 5y + 2 = 0\\) represents','\(a\)Pair of straight line','\(b\)Ellipse','\(c\)Hyperbola','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)For a pair of straight lines,\\(abc + 2fgh - a{f^2} - b{g^2} - c{h^2} = 0\\)\\((3)(2)(2) + 2 \\cdot \\left( {\\frac{5}{2}} \\right)\\,\\left( {\\frac{5}{2}}\\right)\\,\\left( {\\frac{7}{2}} \\right) - 3{\\left( {\\frac{5}{2}} \\right)^2} -2{\\left( {\\frac{5}{2}} \\right)^2} - 2{\\left( {\\frac{7}{2}} \\right)^2} = 0.\\)Hence, given equation represents a pair of straight lines.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','For what value of \\(\\'p\\'\\), \\({y^2} + xy + p{x^2} - x - 2y = 0\\) representstwo straight lines ','\(a\)2','\(b\)\\(\\frac{1}{3}\\)','\(c\)\\(\\frac{1}{4}\\)','\(d\)\\(\\frac{1}{2}\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)For this to represent straight lines\\(abc + 2fgh - a{f^2} - b{g^2} - c{h^2} = 0\\)\\(\\Rightarrow{}\\) \\({p^2} + 2( - 1)\\left( {\\frac{{ - 1}}{2}} \\right)\\,\\left({\\frac{1}{2}} \\right) - p(1) - 1\\left( {\\frac{1}{4}} \\right) - p\\left({\\frac{1}{4}} \\right)\\)\\(\\Rightarrow{}\\) \\({p^2} + \\frac{1}{2} - p - \\frac{1}{4} - \\frac{p}{4} =0\\)\\(\\Rightarrow{}\\) \\({p^2} - \\frac{{5p}}{4} + \\frac{1}{4} = 0\\)\\(\\Rightarrow{}\\) \\(4{p^2} - 5p + 1 = 0\\) \\(\\Rightarrow{}\\) \\(4{p^2} - 4p - p +1 = 0\\)\\(\\Rightarrow{}\\) \\((4p - 1)(p - 1) = 0\\) \\(\\Rightarrow{}\\) \\(p =\\frac{1}{4},\\,1.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(2{x^2} + 7xy + 3{y^2} - 9x - 7y + k = 0\\) represents apair of lines, then k is equal to','\(a\)4','\(b\)2','\(c\)1','\(d\)- 4','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)For this to represent straight lines\\(abc + 2fgh - a{f^2} - b{g^2} - c{h^2} = 0\\)Here, a = 2, b = 3, c = k, f = -7/2, g = -9/2, h = 7/2.\\(\\Rightarrow{}\\) \\(2.3.k + 2( - 7/2)( - 9/2)(7/2) - 2{( - 7/2)^2}\\)\\(- 3{( - 9/2)^2} - k{(7/2)^2} = 0\\)\\(\\Rightarrow{}\\) \\(6k + \\frac{{Answer = }{4} - \\frac{{49}}{2} - \\frac{{Answer = }{4} -\\frac{{49k}}{4} = 0\\)\\(\\Rightarrow{}\\) \\(24k + Answer = - 98 - Answer = - 49k = 0\\)\\(\\Rightarrow{}\\) \\( - 25k + Answer = = 0\\)\\(\\Rightarrow{}\\) \\(k = 4\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(12{x^2} - 10xy + 2{y^2} + 11x - 5y + k = 0\\) representstwo straight lines, then the value  of k is','\(a\)1','\(b\)2','\(c\)0','\(d\)3','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Condition for pair of lines,\\(abc + 2fgh - a{f^2} - b{g^2} - c{h^2} = 0\\)Here, a = 12, h = -5, b = 2, g = 11/2, f = -5/2, c = k.On solving, \\(k = 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\(4{x^2} - 24xy + 11{y^2} = 0\\)represents','\(a\)Two parallel lines','\(b\)Two perpendicular lines','\(c\)Two lines through the origin','\(d\)A circle','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Given equation is \\(4{x^2} - 24xy + 11{y^2} = 0\\)\\(4{x^2} - 22xy - 2xy + 11{y^2} = 0\\)\\(2x(x - 11y) - 2y(x - 11y) = 0\\) \\(\\Rightarrow{}\\) \\((2x - 2y)\\,(x - 11y) = 0\\)\\(2x - 2y = 0\\) or \\(x - 11y = 0\\), \\(\\therefore{}\\) \\(y = x\\) or \\(y =\\frac{x}{{11}}\\)Since, both the lines-passing through the origin.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the slope of one of the lines given by \\(a{x^2} + 2hxy + b{y^2} = 0\\)is 5times the other, then','\(a\)\\(5{h^2} = ab\\)','\(b\)\\(5{h^2} = 9ab\\)','\(c\)\\(9{h^2} = 5ab\\)','\(d\)\\({h^2} = ab\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Given \\({m_2} = 5{m_1}\\,\\, \\Rightarrow \\,\\,{m_1} + 5{m_1} = \\frac{{ -2h}}{b}\\)\\(\\Rightarrow{}\\) \\({m_1} = \\frac{{ - 2h}}{{6b}}\\)\\(\\ldots{} \\)..(i)and \\({m_1}.5{m_1} = \\frac{a}{b}\\, \\Rightarrow \\,{m_1} = \\sqrt {\\frac{a}{{5b}}}\\)\\(\\ldots{} \\)..(ii)From (i) and (ii),\\(\\frac{{ - 2h}}{{6b}} = \\sqrt {\\frac{a}{{5b}}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{4{h^2}}}{{36{b^2}}} =\\frac{a}{{5b}}\\)\\(\\Rightarrow{}\\) \\(9ab = 5{h^2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation to the pair  of straight lines through the origin which areperpendicular to the lines \\(2{x^2} - 5xy + {y^2} = 0,\\)is','\(a\)\\(2{x^2} + 5xy + {y^2} = 0\\)','\(b\)\\({x^2} + 2{y^2} + 5xy = 0\\)','\(c\)\\({x^2} - 5xy + 2{y^2} = 0\\)','\(d\)\\(2{x^2} + {y^2} - 5xy = 0\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)In this case coefficient of \\({x^2}\\)and \\({y^2}\\)are inter-changed andsign of h is changed.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the lines represented by the equation \\(6{x^2} + 41xy - 7{y^2} = 0\\) makeangles \\(\\alpha \\)and \\(\\beta \\) with x-axis, then \\(\\tan \\alpha .\\tan \\beta \\)=','\(a\)- 6/7','\(b\) 6/ 7','\(c\)7/6','\(d\)- 7/6','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\(\\tan \\alpha \\tan \\beta  = {m_1}{m_2} = \\frac{a}{b} =  - \\frac{6}{7}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Difference of slopes of the lines represented by equation \\({x^2}({\\sec^2}\\theta  - {\\sin ^2}\\theta ) - 2xy\\tan \\theta  + {y^2}{\\sin ^2}\\theta  = 0\\)is','\(a\)4','\(b\)3','\(c\)2','\(d\)None of these','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)We know that \\({m_1} - {m_2} = \\sqrt {{{({m_1} + {m_2})}^2} -4{m_1}{m_2}} \\)\\(= \\sqrt {{{\\left( {\\frac{{2\\tan \\theta }}{{{{\\sin }^2}\\theta }}} \\right)}^2} -4\\left( {\\frac{{{{\\sec }^2}\\theta  - {{\\sin }^2}\\theta }}{{{{\\sin }^2}\\theta }}}\\right)}\\)\\(= \\sqrt {\\frac{{4{{\\tan }^2}\\theta }}{{{{\\sin }^4}\\theta }} - 4({{\\sec}^2}\\theta \\,{\\rm{cose}}{{\\rm{c}}^{\\rm{2}}}\\theta  - 1)}  = 2.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\(\\lambda ,\\) for which the equation \\({x^2} - {y^2} -x\\)-\\(\\lambda y - 2 = 0\\)represent a pair of straight line, are','\(a\)3, - 3','\(b\)- 3, 1','\(c\)3, 1','\(d\)-1, 1','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Given equation is \\({x^2} - {y^2} - x - \\lambda y - 2 = 0\\).Here, \\(a = 1\\), \\(b =  - 1\\), \\(c =  - 2\\), \\(h = 0\\), \\(g = \\frac{{ -1}}{2}\\), \\(f = \\frac{{ - \\lambda }}{2}\\)This equation represents a pair of straight line, if \\(abc + 2fgh - a{f^2} -b{g^2} - c{h^2} = 0\\)\\(2 - \\frac{{{\\lambda ^2}}}{4} + \\frac{1}{4} = 0\\) \\(\\Rightarrow{}\\)\\(\\frac{{{\\lambda ^2}}}{4} = \\frac{9}{4} \\Rightarrow {\\lambda ^2} = 9,\\,\\lambda =  \\pm 3,\\)i.e., (3, - 3).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(a{x^2} - {y^2} + 4x - y = 0\\)represents a pair of lines then \\(a = \\)','\(a\)- 16','\(b\)16','\(c\)4','\(d\)- 4','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Given equation is \\(a{x^2} - {y^2} + 4x - y = 0\\)Here \\(A = a,B =  - 1,\\,H = 0,\\,G = 2,F = \\frac{{ - 1}}{2},\\,C = 0\\)If the given equation represents a pair of straight lines, then \\(ABC + 2FGH -A{F^2} - B{G^2} - C{H^2} = 0\\)\\(0 - 0 - a\\,\\left( {\\frac{1}{4}} \\right) - ( - 1)(4) = 0\\) \\(\\Rightarrow{}\\)\\(\\frac{{ - a}}{4} = 4 = 0 \\Rightarrow a = 16\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If one of the lines given by \\(6{x^2} - xy + 4c{y^2} = 0\\) is \\(3x + 4y =0\\), then c equals ','\(a\)- 3','\(b\)- 1','\(c\)3','\(d\)1','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Given equation of pair of lines is\\(6{x^2} - xy + 4c{y^2} = 0\\)\\(\\ldots{} \\)..(i)Slope of line \\(3x + 4y = 0\\)is \\(\\frac{{ - 3}}{4} = {m_1}\\)   (say)Product of slopes of lines \\({m_1}{m_2} = \\frac{a}{b} = \\frac{6}{{4c}} =\\frac{3}{{2c}}\\)\\(\\therefore \\)\\({m_2} = \\frac{{3/2c}}{{ - 3/4}} = \\frac{{ - 2}}{c}\\). Also,\\({m_1} + {m_2} = \\frac{{ - 2h}}{b} = \\frac{1}{{4c}}\\)\\(\\Rightarrow \\,\\frac{{ - 3}}{4} - \\frac{2}{c} = \\frac{1}{{4c}} \\Rightarrow  - 3c- 8 = 1 \\Rightarrow c =  - 3.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sum of the slopes of the lines given by \\({x^2} - 2cxy - 7{y^2} = 0\\) isfour times their product, then c has the value','\(a\)- 2','\(b\)-- 1','\(c\)2','\(d\)1','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Given pair of lines is \\({x^2} - 2cxy - 7{y^2} = 0\\)Given, \\({m_1} + {m_2} = 4{m_1}{m_2} \\Rightarrow \\frac{{ - 2c}}{7} = \\frac{{ -4}}{7} \\Rightarrow c = 2\\)\\(\\left[ {\\;{m_1} + {m_2} =  - \\frac{{2h}}{b} = \\frac{{ - 2c}}{7};\\;{m_1}{m_2} =\\frac{{ - 1}}{7}} \\right]\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\frac{{{x^2}}}{a} + \\frac{{{y^2}}}{b} + \\frac{{2xy}}{h} = 0\\) representpair of straight lines and slope of one line is twice the other. Then\\(ab:{h^2}\\) is','\(a\)9 : 8','\(b\)8 : 9','\(c\)1 : 2','\(d\)2: 1','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Let \\({m_1},{m_2}\\) be the slopes\\(\\therefore \\)\\({m_1} + {m_2} =  - \\frac{{2b}}{h}\\) and \\({m_1}{m_2} =\\frac{b}{a}\\)Again\\({m_2}\\) =\\(2{m_1}\\)\\(\\therefore \\)\\(3{m_1} =  - \\frac{{2b}}{h}\\) and \\(2m_1^2 = \\frac{b}{a}\\)\\(\\therefore \\)\\(\\frac{{9m_1^2}}{{2m_1^2}} = \\frac{{4{b^2}}}{{{h^2}}} \\times \\frac{a}{b}\\Rightarrow ab:{h^2} = 9:8.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the lines represented by the equation \\(a{x^2} - bxy - {y^2} = 0\\) makeangles \\(\\alpha \\) and \\(\\beta \\) with the x-axis, then \\(\\tan (\\alpha  + \\beta)\\)=','\(a\)\\(\\frac{b}{{1 + a}}\\)','\(b\)\\(\\frac{{ - b}}{{1 + a}}\\)','\(c\)\\(\\frac{a}{{1 + b}}\\)','\(d\)None of these','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Here the equation is \\(a{x^2} - bxy - {y^2} = 0\\) and given that \\({m_1}= \\tan \\alpha \\) and \\({m_2} = \\tan \\beta \\)and we know that\\({m_1} + {m_2} = \\frac{b}{{ - 1}} = \\tan \\alpha  + \\tan \\beta\\)and \\({m_1}{m_2} = \\frac{a}{{ - 1}} = \\tan \\alpha .\\tan \\beta \\)\\(\\tan (\\alpha  + \\beta ) = \\frac{{\\tan \\alpha  + \\tan \\beta }}{{1 - \\tan \\alpha\\tan \\beta }} = \\frac{{ - b}}{{1 - ( - a)}} = \\frac{{ - b}}{{(1 + a)}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Angle between the lines represented by the equation \\({x^2} + 2xy\\sec \\theta + {y^2} = 0\\) is','\(a\)\\(\\theta \\)','\(b\)\\(2\\theta \\)','\(c\)\\(\\frac{\\theta }{2}\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Let angle between both the lines is \\(\\alpha \\), then\\(\\alpha  = {\\tan ^{ - 1}}\\left( {\\frac{{2\\sqrt {{h^2} - ab} }}{{a + b}}} \\right)= {\\tan ^{ - 1}}\\left( {\\frac{{2\\sqrt {{{\\sec }^2}\\theta  - 1} }}{{1 + 1}}}\\right) = \\theta .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the acute angles between the pairs of lines \\(3{x^2} - 7xy + 4{y^2} = 0\\)and \\(6{x^2} - 5xy + {y^2} = 0\\) be \\({\\theta _1}\\) and \\({\\theta _2}\\)respectively, then','\(a\)\\({\\theta _1} = {\\theta _2}\\)','\(b\)\\({\\theta _1} = 2{\\theta _2}\\)','\(c\)\\(2{\\theta _1} = {\\theta _2}\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Obviously \\({\\theta _1} = {\\tan ^{ - 1}}\\left( {\\frac{{2\\sqrt{\\frac{{49}}{4} - 12} }}{{3 + 4}}} \\right) = {\\tan ^{ - 1}}\\left( {\\frac{1}{7}}\\right)\\)and \\({\\theta _2} = {\\tan ^{ - 1}}\\left( {\\frac{{2\\sqrt {\\frac{{25}}{4} - 6}}}{{6 + 1}}} \\right) = {\\tan ^{ - 1}}\\left( {\\frac{1}{7}} \\right)\\).Hence,\\({\\theta _1} = {\\theta _2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines represented by the equation \\(a{x^2} + xy +b{y^2} = 0\\) will be \\({45^o}\\), if','\(a\)\\(a = 1,b = 6\\)','\(b\)\\(a = 1,b =  - 6\\)','\(c\)\\(a = 6,b = 1\\)','\(d\)None of these','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)\\(\\tan 45^\\circ  = \\frac{{2\\sqrt {\\frac{1}{4} - ab} }}{{a + b}}\\)\\(\\Rightarrow {(a + b)^2} = (1 - 4ab) \\Rightarrow {a^2} + {b^2} + 6ab - 1 = 0,\\)which is obviously satisfied by \\(a = 1\\)and \\(b =  - 6\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines represented by the equation \\(9{x^2} + 24xy + 16{y^2} + 21x + 28y+ 6 = 0\\) are','\(a\)Parallel','\(b\)Coincident','\(c\)Perpendicular','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Given lines are not perpendicular i.e., \\(a \\ne  - b\\)Checking for parallel lines, \\(\\frac{a}{h} = \\frac{h}{b} = \\frac{g}{f}\\)\\((3x + 4y + 6)(3x + 4y + 1) = 0 \\Rightarrow \\frac{9}{{12}} = \\frac{{12}}{{16}} =\\frac{{21}}{{28}}\\)Hence lines are parallel.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Acute angle between the lines represented by \\(({x^2} + {y^2})\\sqrt 3  =4xy\\) is ','\(a\)\\(\\pi /6\\)','\(b\)\\(\\pi /4\\)','\(c\)\\(\\pi /3\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\(\\tan \\theta  =  \\pm \\frac{{2\\sqrt {4 - 3} }}{{2\\sqrt 3 }} =\\frac{1}{{\\sqrt 3 }}\\)or \\(\\theta  = 30^\\circ \\)Hence, acute angle is 30\\(^\\circ{}\\) or \\(\\frac{\\pi }{6}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Angles made by the lines represented by the equation \\(xy + y = 0\\)with \\(y- \\)axis are','\(a\)\\({0^o}\\)and \\({90^o}\\)','\(b\)\\({0^o}\\)and \\({30^o}\\)','\(c\)\\({30^o}\\)and \\({60^o}\\)','\(d\)\\({30^o}\\)and \\({90^o}\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Here the lines are \\(x =  - 1\\)and \\(y = 0\\) represented by \\(xy + y =0\\).  Obviously line \\(y = 0\\)makes an angle of 90\\(^\\circ{}\\) with y-axis and x= -1 make an angle of 0\\(^\\circ{}\\) with y-axis.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the angle between the lines represented by the equation \\({y^2} + kxy -{x^2}\\)\\({\\tan ^2}A = 0\\)be \\(2A\\), then \\(k = \\)','\(a\)0','\(b\)1','\(c\)2','\(d\)\\(\\tan A\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Here \\(\\tan 2A = \\frac{{2\\sqrt {\\frac{{{k^2}}}{4} + {{\\tan }^2}A} }}{{1 -{{\\tan }^2}A}}\\)\\(\\Rightarrow \\frac{{2\\tan A}}{{1 - {{\\tan }^2}A}} = \\frac{{2\\sqrt{\\frac{{{k^2}}}{4} + {{\\tan }^2}A} }}{{1 - {{\\tan }^2}A}}\\)\\(\\Rightarrow \\frac{{{k^2}}}{4} + {\\tan ^2}A = {\\tan ^2}A \\Rightarrow k = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines \\(xy = 0\\) is [MP PET , 92]','\(a\)\\({45^o}\\)','\(b\)\\({60^o}\\)','\(c\)\\({90^o}\\)','\(d\)\\({180^o}\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)\\(xy = 0\\)represents two coordinate axes and hence angle between them is90\\(^\\circ{}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines represented by the equation \\(4{x^2} - 24xy +11{y^2} = 0\\) are ','\(a\)\\({\\tan ^{ - 1}}\\frac{3}{4},{\\tan ^{ - 1}}\\left( { - \\frac{3}{4}}\\right)\\)','\(b\)\\({\\tan ^{ - 1}}\\frac{1}{3},{\\tan ^{ - 1}}\\left( { - \\frac{1}{3}}\\right)\\)','\(c\)\\({\\tan ^{ - 1}}\\frac{4}{3},{\\tan ^{ - 1}}\\left( { - \\frac{4}{3}}\\right)\\)','\(d\)\\({\\tan ^{ - 1}}\\frac{1}{2},{\\tan ^{ - 1}}\\left( { - \\frac{1}{2}}\\right)\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)\\(\\tan \\theta  =  \\pm 2\\frac{{\\sqrt {{h^2} - ab} }}{{a + b}} =  \\pm2\\frac{{\\sqrt {Answer = - 44} }}{{15}} =  \\pm \\frac{4}{3}\\).\\(\\Rightarrow \\theta  = {\\tan ^{ - 1}}\\left( { \\pm \\frac{4}{3}} \\right).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the lines represented by the equation \\(2{x^2} - 3xy + {y^2} = 0\\) makeangles \\(\\alpha \\)and \\(\\beta \\) with x-axis, then \\({\\cot ^2}\\alpha  + {\\cot^2}\\beta \\)=','\(a\)0','\(b\)3/2','\(c\)7/4','\(d\)5/4','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)\\({m_1} = \\tan \\alpha \\)and \\({m_2} = \\tan \\beta \\)\\( \\Rightarrow \\cot \\alpha  = \\frac{1}{{{m_1}}}\\)and \\(\\cot \\beta  =\\frac{1}{{{m_2}}}\\)Hence, \\({\\cot ^2}\\alpha  + {\\cot ^2}\\beta  = \\frac{1}{{m_1^2}} +\\frac{1}{{m_2^2}} = \\frac{{m_1^2 + m_2^2}}{{{{({m_1}{m_2})}^2}}}\\)\\(= \\frac{{{{({m_1} + {m_2})}^2} - 2{m_1}{m_2}}}{{{{({m_1}{m_2})}^2}}} =\\frac{{{{(3)}^2} - 2(2)}}{{{{(2)}^2}}} = \\frac{5}{4}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Angle between the line joining the origin to the points of intersection ofthe curves \\(2{x^2} + 3{y^2} + 10x = 0\\) and \\(3{x^2} + 5{y^2} + 16x = 0\\) is','\(a\)\\({\\tan ^{ - 1}}\\frac{3}{2}\\)','\(b\)\\({\\tan ^{ - 1}}\\frac{4}{5}\\)','\(c\)\\({90^o}\\)','\(d\)None of these','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)The equation of any curve through the points of intersection of the givencurves is\\(2{x^2} + 3{y^2} + 10x + \\lambda (3{x^2} + 5{y^2} + 16x) = 0\\).....(i)If this equation represents two straight lines through the origin, then thismust be homogeneous equation of second degree i.e., coefficient of x in (i) mustvanish\\(\\therefore{}\\) \\(10 + 16\\lambda  = 0 \\Rightarrow \\lambda  = \\frac{{ -10}}{{16}} = \\frac{{ - 5}}{8}\\)Substituting this value of \\(\\lambda \\)in (i), we get the equation of pair ofstraight lines \\({x^2} - {y^2} = 0\\)\\(\\ldots{} \\)..(ii)Hence the lines represented by the equation (ii) are mutually perpendicular.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines \\({(lx + my)^2} - 3{(mx - ly)^2} = 0\\) and \\(lx + my + n = 0\\)form','\(a\)An isosceles triangle','\(b\)A right angled triangle','\(c\)An equilateral triangle','\(d\)None of these','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Lines are\\([(l + \\sqrt 3 m)x + (m - \\sqrt 3 l)y][(l - \\sqrt 3 m)x + (m + \\sqrt 3 l)y] = 0\\)and \\({L_3} = lx + my + n = 0\\). L\\(_{1}\\) and L\\(_{2}\\) are above two lines.\\({S_1} =  - \\frac{{(l + \\sqrt 3 m)}}{{(m - \\sqrt 3 l)}},\\;\\;\\;{S_2} =  -\\frac{{(l - \\sqrt 3 m)}}{{(m + \\sqrt 3 l)}},\\;\\;\\;{S_3} =  - \\frac{l}{m}\\)(where \\({S_1}\\), \\({S_2}\\)and \\({S_3}\\)are slopes of the lines)\\({\\theta _{13}} = {\\tan ^{ - 1}}\\left[ {\\frac{{ - \\left( {\\frac{{l + \\sqrt 3m}}{{m - \\sqrt 3 l}}} \\right) + \\frac{l}{m}}}{{1 + \\left( {\\frac{{l + \\sqrt 3m}}{{m - \\sqrt 3 l}}} \\right)\\frac{l}{m}}}} \\right]\\)\\(= {\\tan ^{ - 1}}\\left( {\\frac{{ - \\sqrt 3 {m^2} - \\sqrt 3 {l^2}}}{{{l^2} +{m^2}}}} \\right) = 60^\\circ\\)\\({\\theta _{23}} = {\\tan ^{ - 1}}\\left[ {\\frac{{ - \\left( {\\frac{{l - \\sqrt 3m}}{{m + \\sqrt 3 l}}} \\right) + \\frac{l}{m}}}{{1 + \\left( {\\frac{{l - \\sqrt 3m}}{{m + \\sqrt 3 l}}} \\right)\\,\\,\\left( {\\frac{l}{m}} \\right)}}} \\right]\\)\\(= {\\tan ^{ - 1}}\\left( {\\frac{{\\sqrt 3 {m^2} + \\sqrt 3 {l^2}}}{{{m^2} +{l^2}}}} \\right) = {\\tan ^{ - 1}}(\\sqrt 3 ) = 60^\\circ\\)Hence, triangle is equilateral.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines represented by the equation \\(\\lambda {x^2} +{(1 - \\lambda )^2}xy - \\lambda {y^2} = 0\\), is','\(a\)\\({30^o}\\)','\(b\)\\({45^o}\\)','\(c\)\\({60^o}\\)','\(d\)\\({90^o}\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)\\(\\tan \\theta  = \\frac{{2\\sqrt {{{\\left[ {\\frac{{{{(1 - \\lambda)}^2}}}{2}} \\right]}^2} + {\\lambda ^2}} }}{{\\lambda  - \\lambda }} \\Rightarrow\\theta  = 90^\\circ \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the sum of the slopes of the lines represented by the equation \\({x^2} -2xy\\tan A - {y^2} = 0\\)be 4, then \\(\\angle A = \\)','\(a\)\\({0^o}\\)','\(b\)\\({45^o}\\)','\(c\)\\({60^o}\\)','\(d\)\\({\\tan ^{ - 1}}( - 2)\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)\\({m_1} + {m_2} = \\frac{{2\\tan A}}{{ - 1}} = 4\\)\\(\\Rightarrow \\tan A =  - 2 \\Rightarrow \\angle A = {\\tan ^{ - 1}}( - 2).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\((a + 3b)(3a + b) = 4{h^2}\\), then the angle between the linesrepresented by \\(a{x^2} + 2hxy + b{y^2} = 0\\) is','\(a\)\\({30^o}\\)','\(b\)\\({45^o}\\)','\(c\)\\({60^o}\\)','\(d\)\\({\\tan ^{ - 1}}\\frac{1}{2}\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)\\(\\theta  = {\\tan ^{ - 1}}\\left( {\\frac{{2\\sqrt {{h^2} - ab} }}{{a + b}}}\\right) = {\\tan ^{ - 1}}\\left( {\\frac{{\\sqrt {4{h^2} - 4ab} }}{{a + b}}}\\right)\\)\\(= {\\tan ^{ - 1}}\\left( {\\frac{{\\sqrt {3{a^2} + 3{b^2} + 10ab - 4ab} }}{{a +b}}} \\right) = 60^\\circ .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines represented by the equation \\(({x^2} +{y^2})\\sin \\theta  + 2xy = 0\\)is','\(a\)\\(\\theta \\)','\(b\)\\(\\frac{\\theta }{2}\\)','\(c\)\\(\\frac{\\pi }{2} - \\theta \\)','\(d\)\\(\\frac{\\pi }{2} - \\frac{\\theta }{2}\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)\\(\\theta  = {\\tan ^{ - 1}}\\left( {\\frac{{2\\sqrt {1 - {{\\sin }^2}\\theta }}}{{2\\sin \\theta }}} \\right) = {\\tan ^{ - 1}}\\left( {\\frac{{\\cos \\theta }}{{\\sin\\theta }}} \\right)\\)\\({\\tan ^{ - 1}}\\left\\{ {\\tan \\left( {\\frac{\\pi }{2} - \\theta } \\right)} \\right\\}= \\frac{\\pi }{2} - \\theta .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The straight lines joining the origin to the points of intersection of theline \\(2x + y = 1\\) and curve \\(3{x^2} + 4xy - 4x + 1 = 0\\) include an angle','\(a\)\\(\\pi{}\\)/2','\(b\)\\(\\pi{}\\)/3','\(c\)\\(\\pi{}\\)/4','\(d\)\\(\\pi{}\\)/6','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Making equation of curve homogeneous w.r.t. line \\(3{x^2} + 4xy - 4x(2x +y) + {(2x + y)^2} = 0\\)\\(\\Rightarrow 3{x^2} + 4xy - 8{x^2} - 4xy + 4{x^2} + {y^2} + 4xy = 0\\)\\(\\Rightarrow {x^2} - 4xy - {y^2} = 0\\)Angle between these two lines is \\(\\tan \\theta  = \\frac{{2\\sqrt {{h^2} - ab}}}{{a + b}}\\) \\( = \\frac{{2\\sqrt {4 + 1} }}{0} = \\infty \\) i.e., lines areperpendicular.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the pair of straight lines \\({x^2} + 4{y^2} - 7xy = 0\\), is ','\(a\)\\({\\tan ^{ - 1}}\\left( {\\frac{1}{3}} \\right)\\)','\(b\)\\({\\tan ^{ - 1}}3\\)','\(c\)\\({\\tan ^{ - 1}}\\frac{{\\sqrt {33} }}{5}\\)','\(d\)\\({\\tan ^{ - 1}}\\frac{5}{{\\sqrt{33} }}\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)\\(\\alpha  = {\\tan ^{ - 1}}\\frac{{2\\sqrt {\\frac{{49}}{4} - 4} }}{5} ={\\tan ^{ - 1}}\\frac{{\\sqrt {33} }}{5}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the pair of lines given by equation \\({x^2} + 2xy - {y^2}= 0\\), is','\(a\)\\(\\pi{}\\)/3','\(b\)\\(\\pi{}\\)/6','\(c\)\\(\\pi{}\\)/2','\(d\)0','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Using condition \\(a + b = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the pair of straight lines \\({x^2} - {y^2} - 2y - 1 = 0\\),is    ','\(a\)\\({90^o}\\)','\(b\)\\({60^o}\\)','\(c\)\\({75^o}\\)','\(d\)\\({36^o}\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Using condition \\(a + b = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the angle between the two lines represented by \\(2{x^2} + 5xy + 3{y^2} +6x + 7y + 4 = 0\\) is \\({\\tan ^{ - 1}}m\\), then \\(m = \\)','\(a\)1/5','\(b\)1','\(c\)7/5','\(d\)7','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Angle \\( = {\\tan ^{ - 1}}\\left( {\\frac{{2\\sqrt {\\frac{{25}}{4} - 6} }}{{2+ 3}}} \\right) = {\\tan ^{ - 1}}\\left( {\\frac{1}{5}} \\right)\\)and it is given by\\({\\tan ^{ - 1}}m\\). Hence \\(m = \\frac{1}{5}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Pair of straight lines perpendicular to each other represented by','\(a\)\\(2{x^2} = 2y(2x + y)\\)','\(b\)\\({x^2} + {y^2} + 3 = 0\\)','\(c\)\\(2{x^2} = y(2x + y)\\)','\(d\)\\({x^2} = 2(x - y)\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Since \\(2{x^2} = 2y(2x + y) \\Rightarrow {x^2} - 2xy - {y^2} = 0\\).Hence, coefficient of \\({x^2} + \\)coefficient of \\({y^2} = 1 - 1 = 0\\).Hence the lines are perpendicular.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the lines \\((p - q){x^2} + 2(p + q)xy + (q - p){y^2} = 0\\) are mutuallyperpendicular, then','\(a\)\\(p = q\\)','\(b\) \\(q = 0\\)','\(c\)\\(p = 0\\)','\(d\)p and q may have any value','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Here \\(a + b = 0\\)for every p and q.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Which of the equation represents the pair of perpendicular straight lines','\(a\)\\({y^2} + xy - {x^2} = 0\\)','\(b\)\\({y^2} - xy + {x^2} = 0\\)','\(c\)\\({x^2} + xy + {y^2} = 0\\)','\(d\)\\({x^2} + xy - 2{y^2} = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Here \\(a + b = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines represented by the equation \\({x^2} + 2\\sqrt 3 xy + 3{y^2} - 3x -3\\sqrt 3 y - 4 = 0\\), are','\(a\)Perpendicular to each other','\(b\)Parallel','\(c\)Inclined at \\({45^o}\\)to each other','\(d\)None of these','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Since \\({h^2} - ab = 0\\). Therefore, the lines are parallel.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If two of the three lines represented by the equation \\(a{x^3} + b{x^2}y +cx{y^2} + d{y^3} = 0\\) are perpendicular, then','\(a\)\\({a^2} + {d^2} = 2ac\\) ','\(b\)\\({a^2} + {d^2} = 2bd\\)','\(c\)\\({a^2} + ac + bd + {d^2} = 0\\)','\(d\)\\({a^2} + {d^2} = 4bc\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)The given equation being homogeneous of third degree represents threestraight lines through the origin. Since two of these lines are to be at rightangles.Let pair of these lines be \\(({x^2} + pxy - {y^2})\\), p is constant and otherfactor is \\((ax - dy)\\).Hence,\\(a{x^3} + b{x^2}y + cx{y^2} + d{y^3} = ({x^2} + pxy - {y^2})(ax - dy)\\)Comparing the coefficients of similar terms, we get\\(b = ap - d\\).....(i); \\(c =  - pd - a\\).....(ii)Multiplying (i) by d and (ii) by a and adding, we get\\(bd + ac =  - {d^2} - {a^2}\\; \\Rightarrow \\;{a^2} + ac + bd + {d^2} = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If in general quadratic equation \\(f(x,y) = 0,\\Delta  = 0\\)and  \\(a + b = 0\\), then the equation represents','\(a\)Two parallel straight lines','\(b\)Two perpendicular straight lines','\(c\)Two lines passing through origin','\(d\)None of these','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)It is obvious.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines represented by the equation \\({x^2} - 2pxy + {y^2}= 0\\), is','\(a\)\\({\\sec ^{ - 1}}p\\)','\(b\)\\({\\cos ^{ - 1}}p\\)','\(c\)\\({\\tan ^{ - 1}}p\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\(\\tan \\theta  = \\frac{{ \\pm 2\\sqrt {{p^2} - 1} }}{{1 + 1}} =  \\pm \\sqrt{{p^2} - 1}  \\Rightarrow \\theta  = {\\sec ^{ - 1}}p\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines represented by the equation \\(a{x^2} + 2hxy +b{y^2} = 0\\)is given by','\(a\)\\(\\tan \\theta  = \\frac{{2({h^2} - ab)}}{{(a + b)}}\\)','\(b\)\\(\\tan \\theta  =\\frac{{2\\sqrt {{h^2} - ab} }}{{(a + b)}}\\)','\(c\)\\(\\tan \\theta  = \\frac{{2({h^2} - ab)}}{{\\sqrt {a + b} }}\\)','\(d\)\\(\\tan \\theta = \\frac{{2\\sqrt {{h^2} + ab} }}{{(a + b)}}\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)It is a formula.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\({x^2} + {k_1}{y^2} + {k_2}xy = 0\\) represents a pair ofperpendicular lines, if','\(a\)\\({k_1} =  - 1\\)','\(b\)\\({k_1} = 2{k_2}\\)','\(c\)\\(2{k_1} = {k_2}\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)It is obvious.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the pair of lines \\(2{x^2} + 5xy + 2{y^2} + 3x + 3y + 1 =0\\) is ','\(a\)\\({\\cos ^{ - 1}}\\left( {\\frac{4}{5}} \\right)\\)','\(b\)\\({\\tan ^{ - 1}}\\left({\\frac{4}{5}} \\right)\\)','\(c\)0','\(d\)\\(\\pi{}\\)/2','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Here \\(a = 2,\\;b = 2,\\;h = \\frac{5}{2},\\,g = \\frac{3}{2},\\,f =\\frac{3}{2},\\,c = 1\\)So angle \\(\\theta{}\\) between the lines is given by\\(\\therefore \\)\\(c = k\\)\\(\\Rightarrow \\theta  = {\\cos ^{ - 1}}\\left( {\\frac{4}{5}} \\right).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The straight lines represented by the equation \\(9{x^2} - 12xy + 4{y^2} =0\\) are','\(a\)Coincident ','\(b\)Perpendicular','\(c\)Parallel','\(d\)Inclined at an angle of 45\\(^\\circ{}\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Since \\({h^2} - ab\\)i.e., \\({(6)^2} - 9 \\times 4 = 0\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the two straight lines  \\(2{x^2} - 5xy + 2{y^2} - 3x + 3y+ 1 = 0\\) is','\(a\)\\({45^o}\\)','\(b\)\\({60^o}\\)','\(c\)\\({\\tan ^{ - 1}}\\frac{4}{3}\\)','\(d\)\\({\\tan ^{ - 1}}\\frac{3}{4}\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)\\(\\theta  = {\\tan ^{ - 1}}\\left( {\\frac{{2\\sqrt {\\frac{{25}}{4} - 4}}}{4}} \\right) = {\\tan ^{ - 1}}\\frac{3}{4}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The acute angle formed between the lines joining the origin to the points ofintersection of the curves \\({x^2} + {y^2} - 2x - 1 = 0\\) and \\(x + y = 1\\),is','\(a\)\\({\\tan ^{ - 1}}\\left( { - \\frac{1}{2}} \\right)\\)','\(b\)\\({\\tan ^{ - 1}}2\\)','\(c\)\\({\\tan ^{ - 1}}\\frac{1}{2}\\)','\(d\)\\({60^o}\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)From \\(x + y = 1,\\)to make the curve \\({x^2} + {y^2} - 2x - 1 = 0\\)homogenous.\\(\\Rightarrow {x^2} + {y^2} - 2x(x + y) - {(x + y)^2} = 0\\)\\(\\therefore 2{x^2} + 4xy = 0\\) or \\({x^2} + 2xy = 0\\)\\(\\therefore \\tan \\theta  = \\frac{{2\\sqrt {{h^2} - ab} }}{{a + b}}\\) and \\(a =1,\\;b = 0,\\;h = 1\\)\\(\\therefore \\tan \\theta  = \\frac{{2\\sqrt {{1^2} - 0} }}{1} \\Rightarrow \\theta  ={\\tan ^{ - 1}}(2).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines given by \\({x^2} - {y^2} = 0\\) is','\(a\)\\({15^o}\\)','\(b\)\\({45^o}\\)','\(c\)\\({75^o}\\)','\(d\)\\(9{0^o}\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Coefficient of \\({x^2}\\) + Coefficient of \\({y^2}\\) \\( = 1 - 1 = 0\\)\\(\\therefore{}\\) Lines are perpendicular to each other.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Condition that the two lines represented by the  equation \\(a{x^2} + 2hxy +b{y^2} = 0\\)to be perpendicular is','\(a\)\\(ab =  - 1\\)','\(b\)\\(a =  - b\\)','\(c\)\\(a = b\\)','\(d\)\\(ab = 1\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Coefficient of \\({x^2} + \\)coefficient of \\({y^2} = 0\\)\\(\\Rightarrow a + b = 0 \\Rightarrow a =  - b.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\(12{x^2} + 7xy + a{y^2} + 13x - y + 3 = 0\\) represents a pairof perpendicular lines. Then the value of \\`a\\' is ','\(a\)\\(\\frac{7}{2}\\)','\(b\) 19','\(c\)- 12','\(d\)12','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Comparing the given equation with standard equation, we get \\(a = 12\\)and \\(b = a\\). We also know that for perpendicular lines, coefficient of\\({x^2}\\)+ coefficient of \\({y^2} = 0\\) or \\(a + b = 0\\). Therefore \\(12 + a =0\\) or \\(a =  - 12\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines \\({x^2} + 4xy + {y^2} = 0\\) is','\(a\)\\({60^o}\\)','\(b\)\\({15^o}\\)','\(c\)\\({30^o}\\)','\(d\)\\({45^o}\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Comparing the given equation with the standard equation, we get \\(a =1,\\,h = 2\\)and \\(b = 1.\\)We also know that relation for the angle between the lines  \\((\\theta )\\) is\\(\\tan \\theta  = \\frac{{2\\sqrt {{h^2} - ab} }}{{a + b}}\\)\\(\\therefore{}\\)\\(\\tan \\theta  = \\frac{{2\\sqrt {{2^2} - 1} }}{{1 + 1}} = \\sqrt 3\\) or \\(\\theta  = {\\tan ^{ - 1}}(\\sqrt 3 ) = {60^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(12{x^2} + 7xy - p{y^2} - 18x + qy + 6 = 0\\) represents apair of perpendicular straight lines, then','\(a\)\\(p = 12,q = 1\\)','\(b\)\\(p = 1,q = 12\\)','\(c\)\\(p =  - 1,q = 12\\)','\(d\)\\(p = 1,q =  - 12\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\) The given equation represents a pair of straight lines therefore, \\( -72p - \\frac{{63}}{2}q - 3{q^2} + 81p - \\frac{{Answer = }{2} = 0\\)    .....(i)Also, since given equation represents perpendicular lines\\(\\therefore{}\\) \\(12 - p = 0 \\Rightarrow p = 12\\).\\(\\therefore{}\\) from (i), \\(2{q^2} + 21q - 23 = 0\\,\\,\\, \\Rightarrow \\,\\,q =1\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the pair of lines represented by \\(2{x^2} - 7xy + 3{y^2} =0\\), is ','\(a\)\\({60^o}\\)','\(b\)\\({45^o}\\)','\(c\)\\({\\tan ^{ - 1}}\\left( {\\frac{7}{6}} \\right)\\)','\(d\)\\({30^o}\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)\\(\\tan \\theta  = \\frac{{2\\sqrt {{{( - 7/2)}^2} - 6} }}{5}\\)\\(\\Rightarrow{}\\) \\(\\tan \\theta  = 1\\) \\(\\Rightarrow{}\\) \\(\\theta  = {45^o}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the angle \\(2\\theta \\)is acute, then the acute angle between \\({x^2}(\\cos\\theta  - \\sin \\theta ) + 2xy\\cos \\theta  + {y^2}(\\cos \\theta  + \\sin \\theta ) =0\\) is','\(a\)\\(2\\theta \\)','\(b\)\\(\\theta /3\\)','\(c\)\\(\\theta \\)','\(d\)\\(\\theta /2\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)\\(\\tan \\varphi  = \\frac{{2\\sqrt {{h^2} - ab} }}{{a + b}}\\)\\(\\Rightarrow{}\\) \\(\\tan \\varphi  = \\frac{{2\\sqrt {{{\\cos }^2}\\theta  - ({{\\cos}^2}\\theta  - {{\\sin }^2}\\theta )} }}{{\\cos \\theta  - \\sin \\theta  + \\cos \\theta + \\sin \\theta }}\\)=\\(\\frac{{2\\sin \\theta }}{{2\\cos \\theta }}\\)\\(\\Rightarrow{}\\) \\(\\tan \\varphi  = \\tan \\theta \\) \\(\\Rightarrow{}\\) \\(\\varphi = \\theta \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the angle between the pair of straight lines represented by the equation\\({x^2} - 3xy + \\lambda {y^2} + 3x - 5y + 2 = 0\\) is \\({\\tan ^{ - 1}}\\left({\\frac{1}{3}} \\right)\\), where \\(\\'\\lambda \\,\\'\\)is a non negative real number.Then\\(\\lambda \\)is','\(a\)2','\(b\)0','\(c\)3','\(d\)1','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Given that \\(\\theta  = {\\tan ^{ - 1}}\\left( {\\frac{1}{3}} \\right)\\)\\(\\Rightarrow{}\\) \\(\\tan \\theta  = \\frac{1}{3}\\)Now, since \\(\\tan \\theta  = \\frac{{2\\sqrt {{h^2} - ab} }}{{a +b}}\\)\\(\\Rightarrow{}\\) \\(\\frac{1}{3}\\, = \\frac{{2\\sqrt {{{\\left( {\\frac{{ -3}}{2}} \\right)}^2} - \\lambda } }}{{\\lambda  + 1}}\\)\\(\\Rightarrow{}\\) \\({(\\lambda  + 1)^2} = 9(9 - 4\\lambda )\\) \\(\\Rightarrow{}\\)\\({\\lambda ^2} + 38\\lambda  - 80 = 0\\)\\(\\Rightarrow{}\\) \\(\\lambda  = \\frac{{ - 38 \\pm \\sqrt {{{(38)}^2} + Answer = }}{2}\\)\\(\\Rightarrow{}\\) \\(\\lambda  = \\frac{{ - 38 \\pm 42}}{2}\\)\\(\\Rightarrow{}\\)\\(\\lambda  = 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines \\({x^2} - xy - 6{y^2} - 7x + 31y - 18 = 0\\)is','\(a\)\\({45^o}\\)','\(b\)\\({60^o}\\)','\(c\)\\({90^o}\\)','\(d\)\\({30^o}\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Comparing given equation with \\(a{x^2} + 2hxy + b{y^2} + 2gx + 2fy + c =0\\) we get \\(a = 1,\\,h =  - 1/2,\\,b =  - 6,g =  - \\frac{7}{2},\\,f =\\frac{{31}}{2},\\,c =  - 18\\)Now angle between the lines\\(\\theta  = {\\tan ^{ - 1}}\\left| {\\,\\frac{{2\\sqrt {{{\\left( {\\frac{{ - 1}}{2}}\\right)}^2} - 1 \\times ( - 6)} }}{{1 + ( - 6)}}\\,} \\right| = {\\tan ^{ - 1}}\\left|{\\,\\frac{{2\\sqrt {\\frac{1}{4} + 6} }}{{ - 5}}\\,} \\right|\\)\\( = {\\tan ^{ - 1}}\\left| {\\,\\frac{{\\left( {2 \\times \\frac{5}{2}} \\right)}}{{- 5}}} \\right|\\)=\\({\\tan ^1}| - 1|\\) = \\({\\tan ^{ - 1}}(1)\\)=\\(\\frac{\\pi }{4}\\)or \\({45^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The line \\(x - 2y = 0\\)will be a bisector of the angle between the linesrepresented by the equation \\({x^2} - 2hxy - 2{y^2} = 0\\), if \\(h = \\)','\(a\)1/2','\(b\)2','\(c\) - 2','\(d\) 2','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Here one equation of bisector is \\(x - 2y = 0.\\)We know that bothbisectors are perpendicular, therefore second bisector will be \\(2x + y =0\\)because it passes through origin.Hence the combined equations of bisectors is given by \\((2x + y)(x - 2y) = 0\\Rightarrow  - 2{x^2} + 3xy + 2{y^2} = 0.\\)Now comparing it by \\(h{x^2} + 3xy - h{y^2} = 0\\), we get h = -2.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the bisectors of the angle between lines represented byequation \\(4{x^2} - 16xy - 7{y^2} = 0\\)is','\(a\)\\(8{x^2} + 11xy - 8{y^2} = 0\\)','\(b\)\\(8{x^2} - 11xy - 8{y^2} = 0\\)','\(c\)\\(16{x^2} + 11xy - 16{y^2} = 0\\)','\(d\)\\(16{x^2} + 11xy + 16{y^2} = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\(\\frac{{{x^2} - {y^2}}}{{11}} = \\frac{{xy}}{{ - 8}} \\Rightarrow 8{x^2} +11xy - 8{y^2} = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the bisectors of angle between the lines represented byequation \\({(y - mx)^2} = {(x + my)^2}\\)is','\(a\)\\(m{x^2} + ({m^2} - 1)xy - m{y^2} = 0\\)','\(b\)\\(m{x^2} - ({m^2} - 1)xy - m{y^2} = 0\\)','\(c\)\\(m{x^2} + ({m^2} - 1)xy + m{y^2} = 0\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)The equation is\\({y^2} + {m^2}{x^2} - 2mxy - {x^2} - {m^2}{y^2} - 2mxy = 0\\)\\(\\Rightarrow {x^2}({m^2} - 1) + {y^2}(1 - {m^2}) - 4mxy = 0\\)Therefore, the equation of bisectors is \\(\\frac{{{x^2} - {y^2}}}{{xy}}\\)\\( = \\frac{{({m^2} - 1) - (1 - {m^2})}}{{ - 2m}}\\)\\(\\Rightarrow m{x^2} + ({m^2} - 1)xy - m{y^2} = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the bisectors of the angle between the lines represented bythe equation \\({x^2} - {y^2} = 0\\), is','\(a\)\\(x = 0\\)','\(b\)\\(y = 0\\)','\(c\)\\(xy = 0\\)','\(d\)None of these','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)By the formula for equation of bisector\\(\\frac{{{x^2} - {y^2}}}{{1 - ( - 1)}} = \\frac{{xy}}{{(0)}} \\Rightarrow xy = 0\\)(By cross-multiplication).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(y = mx\\)be one of the bisectors of the angle between the lines \\(a{x^2} -2hxy + b{y^2} = 0\\), then','\(a\)\\(h\\,(1 + {m^2}) + m(a - b) = 0\\)','\(b\)\\(h\\,(1 - {m^2}) + m(a + b) = 0\\)','\(c\)\\(h\\,(1 - {m^2}) + m(a - b) = 0\\)','\(d\)\\(h\\,(1 + {m^2}) + m(a + b) = 0\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Here equation of one bisector of angle is \\(y - mx = 0,\\) thereforeequation of second is \\(x + my = 0\\).Hence combined equation is \\((x + my)(y - mx) = 0\\)\\( \\Rightarrow  - m{x^2} - xy({m^2} - 1) + m{y^2} = 0\\).\\(\\ldots{} \\).(i)Also equations of bisectors of \\(a{x^2} - 2hxy + b{y^2} = 0\\) is\\( - h{x^2} - (a - b)xy + h{y^2} = 0\\).....(ii)Hence (i) and (ii) are the same equations, therefore\\(\\frac{m}{h} = \\frac{{{m^2} - 1}}{{(a - b)}} \\Rightarrow h({m^2} - 1) = m(a - b)\\)\\(\\Rightarrow m(a - b) + h(1 - {m^2}) = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The combined equation of bisectors of angles between coordinate axes, is','\(a\)\\({x^2} + {y^2} = 0\\)','\(b\)\\({x^2} - {y^2} = 0\\)','\(c\)\\(xy = 0\\)','\(d\)\\(x + y = 0\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Equations of axes are \\(x = 0,\\;y = 0\\). Combined equation is \\(xy = 0\\).Therefore, combined equation of bisectors is \\(\\frac{{{x^2} - {y^2}}}{0} =\\frac{{xy}}{{1/2}} \\Rightarrow {x^2} - {y^2} = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the bisectors of the angles between the pairs of lines given by theequation \\(a{x^2} + 2hxy + b{y^2} = 0\\) and \\(a{x^2} + 2hxy + b{y^2} + \\lambda({x^2} + {y^2}) = 0\\) be coincident, then \\(\\lambda  = \\)','\(a\)a','\(b\)b','\(c\)\\(h\\)','\(d\)Any real number','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Bisectors of \\(a{x^2} + 2hxy + b{y^2} = 0\\) are\\(\\frac{{{x^2} - {y^2}}}{{a - b}} = \\frac{{xy}}{h}\\).....(i)and of \\(a{x^2} + 2hxy + b{y^2} + \\lambda ({x^2} + {y^2}) = 0\\)i.e., \\((a + \\lambda ){x^2} + 2hxy + (b + \\lambda ){y^2} = 0\\)are\\(\\frac{{{x^2} - {y^2}}}{{(a + \\lambda ) - (b + \\lambda )}} =\\frac{{xy}}{h}\\).....(ii)Which is the same equation as equation (i). Hence for any \\(\\lambda \\)belongingto real numbers, the lines will have same bisectors.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The combined equation of the bisectors of the angle between the linesrepresented by \\(({x^2} + {y^2})\\sqrt 3  = \\) \\(4xy\\) is','\(a\)\\({y^2} - {x^2} = 0\\)','\(b\)\\(xy = 0\\)','\(c\)\\({x^2} + {y^2} = 2xy\\)','\(d\)\\(\\frac{{{x^2} - {y^2}}}{{\\sqrt 3 }} =\\frac{{xy}}{2}\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\(\\frac{{{x^2} - {y^2}}}{{\\sqrt 3  - \\sqrt 3 }} = \\frac{{xy}}{{ - 2}}\\Rightarrow {x^2} - {y^2} = 0\\) .',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the bisectors of the angles between the lines represented by\\({x^2} + 2xy\\cot \\theta  + {y^2} = 0\\), is','\(a\)\\({x^2} - {y^2} = 0\\)','\(b\)\\({x^2} - {y^2} = xy\\)','\(c\)\\(({x^2} - {y^2})\\cot \\theta  = 2xy\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Equation of bisectors is given by \\(\\frac{{{x^2} - {y^2}}}{{a - b}} =\\frac{{xy}}{h}\\)or \\(\\frac{{{x^2} - {y^2}}}{0} = \\frac{{xy}}{{\\cot \\theta }}\\)or \\({x^2} - {y^2}= 0\\) .',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the bisectors of angles represented by \\(a{x^2} + 2hxy + b{y^2} = 0\\) and\\(a\\'{x^2} + 2h\\'xy + b\\'{y^2} = 0\\) are same, then','\(a\)\\((a - b)h\\' = (a\\' - b\\')h\\)','\(b\)\\((a - b)h = (a\\' - b\\')h\\'\\)','\(c\)\\((a + b)h\\' = (a\\' - b\\')h\\) ','\(d\)\\((a - b)h\\' = (a\\' + b\\')h\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Since bisectors are same, therefore \\(\\frac{{a - b}}{h} = \\frac{{a\\' -b\\'}}{{h\\'}}\\)\\(\\Rightarrow{}\\) \\((a - b)h\\'\\, = (a\\' - b\\')h\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If  \\(r(1 - {m^2}) + m(p - q) = 0\\), then a bisector of the angle between thelines represented by the equation \\(p{x^2} - 2rxy + q{y^2} = 0\\), is','\(a\)\\(y = x\\)','\(b\)\\(y =  - x\\)','\(c\)\\(y = mx\\)','\(d\)\\(my = x\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Equation of bisectors of angle between the lines represented by \\(p{x^2}- 2rxy + q{y^2} = 0\\)is\\(\\frac{{{x^2} - {y^2}}}{{p - q}} = \\frac{{xy}}{{ - r}}\\)                 .....(i)On putting \\(y = mx\\)in (i), we get\\(\\frac{{1 - {m^2}}}{{p - q}} = \\frac{m}{{ - r}}\\)\\(\\Rightarrow r(1 - {m^2}) + m(p - q) = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the equation \\(a{x^2} + 2hxy + b{y^2} = 0\\)has the one line as the bisectorof angle between the coordinate axes, then','\(a\)\\({(a - b)^2} = {h^2}\\)','\(b\)\\({(a + b)^2} = {h^2}\\)','\(c\)\\({(a - b)^2} = 4{h^2}\\)','\(d\)\\({(a + b)^2} = 4{h^2}\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Let one line be \\(x + y = 0\\,\\,\\, \\Rightarrow \\,\\,{m_1} =  - 1\\)and we know that \\({m_1} + {m_2} =  - \\frac{{2h}}{b}\\).....(i)and \\({m_1}{m_2} = \\frac{a}{b}\\).....(ii)Therefore from (ii), \\({m_2} =  - \\frac{a}{b}\\)\\(\\Rightarrow  - 1 - \\frac{a}{b} = \\frac{{ - 2h}}{b} \\Rightarrow {(a + b)^2} =4{h^2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the bisectors of the angles of the lines represented by \\(3{x^2} - 4xy +5{y^2} = 0\\) and \\(5{x^2} + 4xy + 3{y^2} = 0\\) are same, then the angle made bythe lines represented by first with the second, is','\(a\)\\({30^o}\\)','\(b\)\\({60^o}\\)','\(c\)\\({45^o}\\)','\(d\)\\({90^o}\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Here both the equations are perpendicular to each other. So, under thegiven condition it is clear that angle is \\(90^\\circ \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','One bisector of the angle between the lines given by \\(a{(x - 1)^2} + 2h\\,(x- 1)y + b{y^2} = 0\\) is \\(2x + y - 2 = 0\\). The other bisector is','\(a\)\\(x - 2y + 1 = 0\\)','\(b\)\\(2x + y - 1 = 0\\)','\(c\)\\(x + 2y - 1 = 0\\)','\(d\)\\(x - 2y - 1 = 0\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)We have \\(a{(x - 1)^2} + 2h(x - 1)y + b{y^2} = 0\\)or \\(a{(x - 1)^2} + 2h(x - 1)(y - 0) + b{(y - 0)^2} = 0\\)This equation represents a pair of straight lines intersecting at (1, 0).Therefore shifting the origin at    (1, 0), we have \\(x = X + 1,\\,y = Y + 0\\) andthe equation reduces to \\(a{X^2} + 2hXY + b{Y^2} = 0\\).....(i)One of the bisectors of the angles between the lines given by (i) is \\(2x + y -2 = 0\\)or \\(2(X + 1) + Y - 2 = 0\\) i.e. \\(2X + Y = 0\\). Since the bisector arealways at right angle, therefore the other bisector is \\(X - 2Y = 0\\)i.e., \\(x - 1 - 2y = 0\\)or \\(x - 2y - 1 = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The point of intersection of the lines represented by the equation \\(2{x^2}+ 3{y^2} + 7xy + 8x + 14y + 8 = 0\\) is','\(a\)(0, 2)','\(b\)(1, 2)','\(c\)(-2, 0)','\(d\)(-2, 1)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)If the equation is general equation\\(a{x^2} + 2hxy + b{y^2} + 2gx + 2fy + c = 0\\)Then points of intersection are given by \\(\\left( {\\frac{{hf - bg}}{{ab -{h^2}}},\\frac{{hg - af}}{{ab - {h^2}}}} \\right)\\)Hence point is \\(( - 2,{\\rm{ }}0)\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The point of intersection of the lines represented by equation \\(2{(x +2)^2} + 3(x + 2)(y - 2) - 2{(y - 2)^2} = 0\\) is','\(a\)(2, 2)','\(b\)(-2, -2)','\(c\)(- 2, 2)','\(d\)(2, -2)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Putting \\(X = x + 2,\\;\\;Y = y - 2\\)Equation becomes\\(2{X^2} + 3XY - 2{Y^2} = 0\\)Which cuts at \\(X = 0,\\;Y = 0\\)So, \\(x + 2 = 0,\\;y - 2 = 0\\)Point of intersection \\(( - 2,2)\\).Trick : Only (-2,2) is satisfying the given equation.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines joining the origin to the points of intersection of the curves\\(a{x^2} + 2hxy + b{y^2} + 2gx = 0\\) and \\(a\\'{x^2} + 2h\\'xy + b\\'{y^2} + 2g\\'x = 0\\)will be mutually perpendicular, if','\(a\)\\(g(a\\' - b\\') = g\\'(a + b)\\)','\(b\)\\(g(a\\' + b\\') = g\\'(a + b)\\)','\(c\)\\(g(a\\' + b\\') = g\\'(a - b)\\)','\(d\)\\(g(a\\' - b\\') = g\\'(a - b)\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)The family of lines passing through point of intersection of the givencurves will be\\(a{x^2} + 2hxy + b{y^2} + 2gx + \\lambda (a\\'{x^2} + 2h\\'xy + b\\'{y^2} + 2g\\'x) =0\\)\\(\\Rightarrow (a + a\\'\\lambda ){x^2} + (2h + 2h\\'\\lambda )xy\\)\\(+ (b + b\\'\\lambda ){y^2} + (2g + 2g\\'\\lambda )x = 0\\)Now the condition for perpendicularity is \\(\\Delta  = 0\\) and \\(a + b = 0\\).\\(\\Rightarrow a + a\\'\\lambda  + b + b\\'\\lambda  = 0 \\Rightarrow \\lambda  =  -\\frac{{a + b}}{{a\\' + b\\'}}\\)and \\(\\Delta  = abc + 2fgh - a{f^2} - b{g^2} - c{h^2} = 0\\)\\(\\Rightarrow 0 + 0 - 0 - (b + b\\'\\lambda ){(2g + 2g\\'\\lambda )^2} - 0 = 0\\)\\(\\Rightarrow 4(b + b\\'\\lambda ){(g + g\\'\\lambda )^2} = 0\\)Now on putting the value of \\(\\lambda{}\\), we get\\(g(a\\' + b\\') = g\\'(a + b).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Distance between the lines represented by the equation \\({x^2} + 2\\sqrt 3 xy+ 3{y^2} - 3x - 3\\sqrt 3 y - 4 = 0\\)is ','\(a\)5/2','\(b\)5/4','\(c\)5','\(d\)0','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)First check for parallel linesi.e., \\(\\frac{a}{h} = \\frac{h}{b} = \\frac{g}{f} \\Rightarrow \\frac{1}{{\\sqrt 3 }}= \\frac{{\\sqrt 3 }}{3} = \\frac{{ - 3/2}}{{ - 3\\sqrt 3 /2}}\\)which is true, hence line are parallel. Applying formula for distance betweentwo parallel straight lines, the required distance is \\(2\\sqrt {\\frac{{{g^2} -ac}}{{a(a + b)}}}  = 2\\sqrt {\\frac{{\\frac{9}{4} + 4}}{4} = } \\frac{5}{2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the lines joining origin to the points of intersection of the line \\(fx -gy = \\lambda \\) and the curve \\({x^2} + hxy - {y^2} + gx + fy = 0\\) be mutuallyperpendicular, then','\(a\)\\(\\lambda  = h\\)','\(b\)\\(\\lambda  = g\\)','\(c\)\\(\\lambda  = fg\\)','\(d\)\\(\\lambda \\)may have any value','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Making the equation of curve homogeneous with the help of equation ofline \\(\\frac{{fx - gy}}{\\lambda } = 1\\)and to be perpendicular to both the linesrepresented by this homogeneous equation\\(a + b = 0 \\Rightarrow \\lambda  + gf - \\lambda  - gf = 0 \\Rightarrow 0 = 0\\)Hence, \\(\\lambda \\)may have any value.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of second degree \\({x^2} + 2\\sqrt 2 xy + 2{y^2} + 4x + 4\\sqrt 2y + 1 = 0\\) represents a pair of straight lines. The distance between them is','\(a\)4','\(b\)\\(4/\\sqrt 3 \\)','\(c\)2','\(d\)\\(2\\sqrt 3 \\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Distance \\( = 2\\sqrt {\\frac{{{g^2} - ac}}{{a(a + b)}}}  = 2\\sqrt{\\frac{{4 - 1}}{{1(1 + 2)}}}  = 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of pair of straight lines joining the point of intersection ofthe curve \\({x^2} + {y^2} = 4\\) and \\(y - x = 2\\) to the origin, is','\(a\)\\({x^2} + {y^2} = {(y - x)^2}\\)','\(b\)\\({x^2} + {y^2} + {(y - x)^2} = 0\\)','\(c\)\\({x^2} + {y^2} = 4{(y - x)^2}\\)','\(d\)\\({x^2} + {y^2} + 4{(y - x)^2} = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Equation can be found by homogenising the curve w.r.t. line.i.e., \\({x^2} + {y^2} = 4{\\left( {\\frac{{y - x}}{2}} \\right)^2}\\)or \\({x^2} +{y^2} = {(y - x)^2}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines joining the points of intersection of line \\(x + y = 1\\) and curve\\({x^2} + {y^2} - 2y + \\lambda  = 0\\)  to the origin are perpendicular, then thevalue of \\(1/\\sqrt {10} \\) will be','\(a\)1/2','\(b\)-1/2','\(c\) \\(1/\\sqrt 2\\)','\(d\) 0','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Making the equation of curve homogeneous with the help of line \\(x + y =1\\), we get \\({x^2} + {y^2} - 2y(x + y) + \\lambda {(x + y)^2} = 0\\)\\(\\Rightarrow {x^2}(1 + \\lambda ) + {y^2}( - 1 + \\lambda ) - 2yx = 0\\)Therefore the lines be perpendicular, if \\(A + B = 0\\).\\(\\Rightarrow 1 + \\lambda  - 1 + \\lambda  = 0 \\Rightarrow \\lambda  = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines joining the points of intersection of curve \\(5{x^2} + 12xy -8{y^2} + 8x - 4y + 12 = 0\\) and the line \\(x - y = 2\\) to the origin , makes theangles with the axes','\(a\)\\({30^o}\\)and \\({45^o}\\)','\(b\)\\({45^o}\\) and \\({60^o}\\)','\(c\)Equal ','\(d\)Parallel to axes','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)We get the desired lines by homogenising the equation of curve w.r.t.line.\\(5{x^2} + 12xy - 8{y^2} + 8x - 4y + 12 = 0\\)or \\(5{x^2} + 12xy - 8{y^2} + (8x - 4y)\\left( {\\frac{{x - y}}{2}} \\right) +12{\\rm{ }}{\\left( {\\frac{{x - y}}{2}} \\right)^2} = 0\\)\\(\\Rightarrow 5{x^2} + 12xy - 8{y^2} + 4{x^2} - 4xy - 2xy + 2{y^2} + 3{x^2}\\)\\(+ 3{y^2} - 6xy = 0\\)\\( \\Rightarrow 12{x^2} - 3{y^2} = 0\\) or \\(4{x^2} - {y^2} = 0\\)or \\((2x - y)(2x + y) = 0\\); \\(\\therefore {m_1} = 2,\\;\\;\\;{m_2} =  - 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines joining the points of intersection of the curve \\({(x - h)^2} +{(y - k)^2} - {c^2} = 0\\) and the line \\(kx + hy = 2hk\\)  to the origin areperpendicular, then','\(a\)\\(c = h \\pm k\\)','\(b\)\\({c^2} = {h^2} + {k^2}\\)','\(c\)\\({c^2} = {(h + k)^2}\\)','\(d\)\\(4{c^2} = {h^2} + {k^2}\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)The line is \\(\\frac{x}{{2h}} + \\frac{y}{{2k}} = 1\\)and circle is,\\({x^2} + {y^2} - 2(hx + ky) + ({h^2} + {k^2} - {c^2}) = 0\\)Making it homogeneous, we get\\(\\Rightarrow ({x^2} + {y^2}) - 2(hx + ky)\\left( {\\frac{x}{{2h}} +\\frac{y}{{2k}}} \\right) +\\)\\(({h^2} + {k^2} - {c^2}){\\left( {\\frac{x}{{2h}} + \\frac{y}{{2k}}} \\right)^2} = 0\\)If these lines be perpendicular, then \\(A + B = 0\\)\\(\\left[ {1 - 1 + \\frac{{({h^2} + {k^2} - {c^2})}}{{4{h^2}}}} \\right] + \\left[ {1- 1 + \\frac{{({h^2} + {k^2} - {c^2})}}{{4{k^2}}}} \\right] = 0\\)or \\(({h^2} + {k^2} - {c^2}){\\rm{ }}\\left( {\\frac{{{h^2} +{k^2}}}{{4{h^2}{k^2}}}} \\right) = 0\\)\\(\\therefore {h^2} + {k^2} = {c^2}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the distance of two lines passing through origin from the point\\(({x_1},{y_1})\\) is \\(\\'d\\'\\), then the equation of lines is','\(a\)\\({(x{y_1} - y{x_1})^2} = {d^2}({x^2} + {y^2})\\)','\(b\)\\({({x_1}{y_1} - xy)^2} = ({x^2} + {y^2})\\)','\(c\)\\({(x{y_1} + y{x_1})^2} = ({x^2} - {y^2})\\)','\(d\)\\(({x^2} - {y^2}) = 2({x_1} + {y_1})\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)If the equation of line is \\(y = mx\\) and the length of perpendiculardrawn on it from the point \\(({x_1},{y_1})\\) is d, then \\(\\frac{{{y_1} -m{x_1}}}{{\\sqrt {1 + {m^2}} }} =  \\pm d\\,\\, \\Rightarrow \\,{({y_1} - m{x_1})^2} ={d^2}(1 + {m^2}).\\) But \\(m = \\frac{y}{x},\\) therefore on eliminating \\'m\\' , therequired equation is \\({(x{y_1} - y{x_1})^2} = {d^2}({x^2} + {y^2})\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines joining the origin to the points of intersection of the line \\(3x- 2y = 1\\) and the curve \\(3{x^2} + 5xy - 3{y^2} + 2x + 3y = 0\\), are','\(a\)Parallel to each other','\(b\)Perpendicular to each other','\(c\)Inclined at \\({45^o}\\)to each other','\(d\)None of these','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Making the given equation homogeneous with the help of equation of line,we get \\(9{x^2} + 10xy - 9{y^2} = 0\\).Hence the lines are perpendicular.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The distance between the parallel lines \\(9{x^2} - 6xy + {y^2} + 18x - 6y +8 = 0\\) is ','\(a\)\\(1/\\sqrt {10} \\)','\(b\)\\(2/\\sqrt {10} \\)','\(c\)\\(4/\\sqrt {10} \\)','\(d\)\\(\\sqrt {10} \\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Distance \\( = 2\\sqrt {\\frac{{{g^2} - ac}}{{a(a + b)}}}  = \\frac{2}{{\\sqrt{10} }}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Two lines are given by \\({(x - 2y)^2} + k(x - 2y) = 0\\). The value of k sothat the distance between them is 3, is','\(a\)\\(\\frac{1}{{\\sqrt 5 }}\\)','\(b\)\\( \\pm \\frac{2}{{\\sqrt 5 }}\\)','\(c\)\\( \\pm 3\\sqrt 5 \\)','\(d\)None of these','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Applying the formula, the distance between them is \\(\\left| {2\\sqrt{\\frac{{({k^2}/4) - 0}}{{1.(1 + 4)}}} } \\right| = \\left| {\\frac{k}{{\\sqrt 5 }}}\\right|\\), \\(\\therefore \\left| {\\frac{k}{{\\sqrt 5 }}} \\right| = 3 \\Rightarrow k = \\pm 3\\sqrt 5 \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The pair of straight lines joining the origin to the points of intersectionof the line \\(y = 2\\sqrt 2 x + c\\)and the circle \\({x^2} + {y^2} = 2\\)are atright angles, if ','\(a\)\\({c^2} - 4 = 0\\)','\(b\)\\({c^2} - 8 = 0\\)','\(c\)\\({c^2} - 9 = 0\\)','\(d\)\\({c^2} - 10 = 0\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Homogenising the curve, we get\\({x^2} + {y^2} - 2{\\left( {\\frac{{y - 2\\sqrt 2 x}}{c}} \\right)^2} =0\\)\\(\\ldots{} \\)..(i)But lines represented by (i) are perpendicular. Hence coefficient of \\({x^2} +\\)coefficient of \\({y^2} = 0\\)\\(\\Rightarrow 1 - \\frac{{16}}{{{c^2}}} + 1 - \\frac{2}{{{c^2}}} = 0 \\Rightarrow\\frac{{18}}{{{c^2}}} = 2 \\Rightarrow {c^2} - 9 = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\(8{x^2} + 8xy + 2{y^2} + 26x + 13y + 15 = 0\\) represents apair of straight lines. The distance between  them is','\(a\)\\(7/\\sqrt 5 \\)','\(b\)\\(7/2\\sqrt 5 \\)','\(c\)\\(\\sqrt 7 /5\\)','\(d\)None of these','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)The distance between the parallel straight lines given by \\(a{x^2} + 2hxy+ b{y^2} + 2gx + 2fy + c = 0\\) is \\(2\\sqrt {\\frac{{{g^2} - ac}}{{a(a + b)}}} \\).Here, \\(a = 8,\\,b = 2,\\,c = 15,\\,g = 13\\).So, required distance \\( = 2\\sqrt {\\frac{{Answer = - Answer = }{{80}}}  = 2 \\times\\frac{7}{4}.\\sqrt 5  = \\frac{7}{2}\\sqrt 5 \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Distance between the pair of lines represented by the equation \\({x^2} - 6xy+ 9{y^2} + 3x - 9y - 4 = 0\\)is','\(a\)\\(\\frac{{15}}{{\\sqrt {10} }}\\)','\(b\)\\(\\frac{1}{2}\\)','\(c\)\\(\\sqrt {\\frac{5}{2}} \\)','\(d\)\\(\\frac{1}{{\\sqrt {10} }}\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)The distance between the pair of straight lines given by \\(a{x^2} + 2hxy+ b{y^2} + 2gx + 2fy + c = 0\\) is \\(2\\sqrt {\\frac{{{g^2} - ac}}{{a(a + b)}}} \\).Here \\(a = 1,\\,\\,b = 9\\)\\(\\Rightarrow{}\\) \\(c =  - 4,\\,\\,g = 3/2\\)= \\(2 \\times \\sqrt {\\frac{{9/4 - ( - 4)}}{{1(1 + 9)}}}  = 2 \\times \\sqrt{\\frac{{25/4}}{{10}}} \\) = \\(\\sqrt {5/2} \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of pair of lines joining origin to the points of intersection of\\({x^2} + {y^2} = 9\\)and \\(x + y = 3\\) is','\(a\)\\({(x + y)^2} = 9\\)','\(b\)\\({x^2} + {(3 - x)^2} = 9\\)','\(c\)\\(xy = 0\\)','\(d\)\\({(3 - x)^2} + {y^2} = 9\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Given, \\({x^2} + {y^2} = 9\\).....(i)\\(x + y = 3\\).....(ii)From equation (i) and (ii), we find the point of intersection. Now, \\({x^2} +{(x - 3)^2} = 9 \\Rightarrow {x^2} + 9 + {x^2} - 6x = 9\\)\\(\\Rightarrow 2{x^2} = 6x \\Rightarrow x = 3\\)Putting \\(x = 3\\) in equation (ii), \\(y = 0\\)Similarly to get second pointPutting \\(x = 3 - y\\) in equation (i) \\(y = 3,\\,x = 0\\). So, the co-ordinates ofboth points are \\((3,\\,0)\\) and \\((0,\\,3)\\). So, separate equation of linesjoining these points from origin \\(y - 0 = \\frac{{0 - 0}}{{0 - 3}}\\,(x - 3)\\Rightarrow y = 0\\).Now, taking point (0, 0) and (0, 3) we get \\(x = 0\\)Hence, required equation of pair of lines is \\(xy = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The distance between the pair of parallel lines \\({x^2} + 2xy + {y^2} - 8ax- 8ay - 9{a^2} = 0\\) is','\(a\)\\(2\\sqrt 5 a\\)','\(b\)\\(\\sqrt {10} \\,a\\)','\(c\)\\(10\\,a\\)','\(d\)\\(5\\sqrt 2 \\,a\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Distance between parallel lines given by\\(a{x^2} + 2hxy + b{y^2} + 2gx + 2fy + c = 0\\) is\\(2\\sqrt {\\frac{{{g^2} - ac}}{{a(a + b)}}} \\)\\(= 2\\sqrt {\\frac{{{{(4a)}^2} + 9{a^2}}}{{1(1 + 1)}}}  = 2\\sqrt{\\frac{{25{a^2}}}{2}}  = 5\\sqrt 2 a.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the locus of foot of perpendiculars drawn from the origin tothe line passing through a fixed point     (a, b), is','\(a\)\\({x^2} + {y^2} - ax - by = 0\\)','\(b\)\\({x^2} + {y^2} + ax + by = 0\\)','\(c\)\\({x^2} + {y^2} - 2ax - 2by = 0\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)\\(\\lambda (x - a) + (y - b) = 0\\)is the equation of line.\\(r =  - \\left( {\\frac{{ - a\\lambda  - b}}{{{\\lambda ^2} + 1}}} \\right)\\)Coordinates of point \\( \\equiv \\left\\{ { - \\lambda \\left( {\\frac{{ - a\\lambda  -b}}{{{\\lambda ^2} + 1}}} \\right){\\rm{ }}, - \\left( {\\frac{{ - a\\lambda  -b}}{{{\\lambda ^2} + 1}}} \\right)} \\right\\}\\)\\(h = \\lambda \\left( {\\frac{{a\\lambda  + b}}{{{\\lambda ^2} + 1}}} \\right){\\rm{}},k = \\frac{{a\\lambda  + b}}{{{\\lambda ^2} + 1}},\\lambda  = \\frac{h}{k}\\)\\(\\therefore h = h\\left( {\\frac{{ah + kb}}{{{h^2} + {k^2}}}} \\right) \\Rightarrow{x^2} + {y^2} = ax + by.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The orthocentre of the triangle formed by the lines \\(xy = 0\\)and \\(x + y =1\\)is ','\(a\)\\((0,0)\\)','\(b\)\\(\\left( {\\frac{1}{2},\\frac{1}{2}} \\right)\\)','\(c\)\\(\\left( {\\frac{1}{3},\\frac{1}{3}} \\right)\\)','\(d\)\\(\\left({\\frac{1}{4},\\frac{1}{4}} \\right)\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Since the triangle is right angled at \\(O(0,0)\\), therefore (0,0) is itsorthocentre.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The product of perpendiculars drawn from the origin to the lines representedby the equation \\(a{x^2} + 2hxy + b{y^2} + 2gx + 2fy + c = 0\\), will be','\(a\)\\(\\frac{{ab}}{{\\sqrt {{a^2} - {b^2} + 4{h^2}} }}\\)','\(b\)\\(\\frac{{bc}}{{\\sqrt{{a^2} - {b^2} + 4{h^2}} }}\\)','\(c\)\\(\\frac{{ca}}{{\\sqrt {({a^2} + {b^2}) + 4{h^2}} }}\\)','\(d\)\\(\\frac{c}{{\\sqrt{{{(a - b)}^2} + 4{h^2}} }}\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)The product of perpendiculars \\( = \\frac{c}{{\\sqrt {{{(a - b)}^2} +4{h^2}} }}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Area of the triangle formed by the lines \\({y^2} - 9xy + 18{x^2} = 0\\) and\\(y = 9\\) is','\(a\)\\(\\frac{{27}}{4}sq\\). units','\(b\)\\(27sq.\\)units','\(c\)\\(\\frac{{27}}{2}sq.\\) units','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)The lines represented by \\({y^2} - 9xy + 18{x^2} = 0\\) are \\(6x - y =0\\)and \\(3x - y = 0\\)and third line is \\(y = 9\\).Therefore, coordinates of vertices of triangle are given by \\(A(0,0)\\);\\(B{\\rm{}}(3,9)\\) and \\(C\\left( {\\frac{3}{2},9} \\right)\\).Hence, area of \\(\\Delta{}\\)ABC \\( = \\frac{1}{2}\\left| {\\begin{array}{*{20}{c}}0&0&1\\\\3&9&1\\\\{3/2}&9&1\\end{array}} \\right| = \\frac{{27}}{4}sq.\\,units.\\)Aliter : Applying the formula given in the theory part, the required area is\\(\\frac{{{{( - 9)}^2}\\sqrt {{{(9/2)}^2} - 18} }}{{18 \\times 1 + 9 \\times 0 \\times1 + 1 \\times 0}} = \\frac{{81}}{{18}}\\sqrt {\\frac{{81}}{4} - 18}\\)\\(= \\frac{{81}}{{18}} \\times \\frac{3}{2} = \\frac{{27}}{4}\\,sq.\\;units.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the pair of straight lines given by \\(A{x^2} + 2Hxy + B{y^2} = 0\\),\\(({H^2} > AB)\\) forms an equilateral triangle with line \\(ax + by + c = 0\\),then \\((A + 3B)(3A + B)\\) is ','\(a\)\\({H^2}\\)','\(b\)\\( - {H^2}\\)','\(c\)\\(2{H^2}\\)','\(d\)\\(4{H^2}\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)We know that the pair of lines\\(({a^2} - 3{b^2}){x^2} + 8abxy + ({b^2} - 3{a^2}){y^2} = 0\\) with the line \\(ax+ by + c = 0\\) form an equilateral triangle. Hence comparing with \\(A{x^2} + 2Hxy+ B{y^2} = 0\\), then\\(A = {a^2} - 3{b^2},\\,B = {b^2} - 3{a^2},\\,2H = 8ab.\\)Now, \\((A + 3B)\\,(3A + B) = ( - 8{a^2})\\,( - 8{b^2})\\)= \\({(8ab)^2} = {(2H)^2} = 4{H^2}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The area (in square units) of the quadrilateral formed by the two pairs oflines\\({l^2}{x^2} - {m^2}{y^2} - n(lx + my) = 0\\)and  \\({l^2}{x^2} - {m^2}{y^2} + n(lx - my) = 0\\) is ','\(a\)\\(\\frac{{{n^2}}}{{2|lm|}}\\)','\(b\)\\(\\frac{{{n^2}}}{{|lm|}}\\)','\(c\)\\(\\frac{n}{{2|lm|}}\\)','\(d\)\\(\\frac{{{n^2}}}{{4|lm|}}\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Given lines are (on factorising)\\(lx + my = 0,\\,lx + my + n = 0\\)\\(lx - my = 0,\\,lx + my - n = 0\\)Area =\\(\\left| {\\frac{{({c_1} - {d_1})\\,({c_2} - {d_2})}}{{({a_1}{b_2} -{a_2}{b_1})}}} \\right| = \\left| {\\frac{{(0 - n)\\,(0 + n)}}{{( - lm - lm)}}}\\right| = \\frac{{{n^2}}}{{2|lm|}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The locus of the point \\(P(x,y)\\) satisfying the relation \\(\\sqrt {{{(x -3)}^2} + {{(y - 1)}^2}}  + \\sqrt {{{(x + 3)}^2} + {{(y - 1)}^2}}  = 6\\) is','\(a\)Straight line','\(b\)Pair of straight lines','\(c\)Circle','\(d\)Ellipse','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)\\(\\sqrt {{{(x - 3)}^2} + {{(y - 1)}^2}}  + \\sqrt {{{(x + 3)}^2} + {{(y -1)}^2}}  = 6\\)\\(\\sqrt {{{(x - 3)}^2} + {{(y - 1)}^2}}  = 6 - \\sqrt {{{(x + 3)}^2} + {{(y -1)}^2}}\\)Squaring both sides. We get,\\(12x + 36 = 12\\sqrt {{{(x + 3)}^2} + {{(y - 1)}^2}}\\)Again squaring we get the given equation is pair of straight lines.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The square of distance between the point of intersection of the linesrepresented by the equation \\(a{x^2} + 2hxy + b{y^2} + 2gx + 2fy + c = 0\\) andorigin, is','\(a\)\\(\\frac{{c(a + b) - {f^2} - {g^2}}}{{ab - {h^2}}}\\)','\(b\)\\(\\frac{{c(a - b) +{f^2} + {g^2}}}{{\\sqrt {ab - {h^2}} }}\\)','\(c\)\\(\\frac{{c(a + b) - {f^2} - {g^2}}}{{ab + {h^2}}}\\)','\(d\)None of these','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Let the lines represented by given equation be\\(y = {m_1}x + {c_1}\\)and\\(y = {m_2}x + {c_2}\\).Then \\(a{x^2} + 2hxy + b{y^2} + 2gx + 2fy + c = 0\\)\\(= b(y - {m_1}x - {c_1})(y - {m_2}x - {c_2}) = 0\\)Comparing the coefficients of \\({x^2},\\;xy,\\;x,\\;y\\) and constant term, we get \\({m_1}{m_2} = \\frac{a}{b},\\;{m_1} + {m_2} = \\frac{{ - 2h}}{b},\\;{m_1}{c_2} +{m_2}{c_1} = \\frac{{2g}}{b},\\)\\({c_1} + {c_2} =  - \\frac{{2f}}{b}\\)and \\({c_1}{c_2} = \\frac{c}{b}\\)Also the point of intersection of \\(y = {m_1}x + {c_1}\\)and \\(y = {m_2}x +{c_2}\\) is \\(\\left( {\\frac{{{c_2} - {c_1}}}{{{m_1} - {m_2}}},\\frac{{{m_1}{c_2} -{m_2}{c_1}}}{{{m_1} - {m_2}}}} \\right)\\)Therefore, the square of distance of this point from origin is \\({\\left({\\frac{{{c_2} - {c_1}}}{{{m_1} - {m_2}}}} \\right)^2} + \\frac{{{{({m_1}{c_2} -{m_2}{c_1})}^2}}}{{{{({m_1} - {m_2})}^2}}}\\)\\(= \\frac{{[{{({c_1} + {c_2})}^2} - 4{c_1}{c_2}] + [{{({m_1}{c_2} +{m_2}{c_1})}^2} - 4{m_1}{m_2}{c_1}{c_2}]}}{{{{({m_1} + {m_2})}^2} - 4{m_1}{m_2}}}\\)Now putting the value defined above, we get the required distance i.e.,\\(\\frac{{ - c(a + b) + {f^2} + {g^2}}}{{{h^2} - ab}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation of the pair of straight lines, each of which makes an angle\\(\\alpha \\)with the line \\(y = x\\), is','\(a\)\\({x^2} + 2xy\\sec 2\\alpha  + {y^2} = 0\\)','\(b\) \\({x^2} + 2xy\\,{\\rm{cosec}}\\,2\\alpha  + {y^2} = 0\\)','\(c\)\\({x^2} - 2xy\\,{\\rm{cosec}}\\,2\\alpha  + {y^2} = 0\\)','\(d\)\\({x^2} - 2xy\\sec 2\\alpha  + {y^2} = 0\\)','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)Any line through the origin is \\(y = mx\\). If it makes an angle \\(\\alpha\\)with the line \\(y = x\\), then we should have\\(\\tan \\alpha  =  \\pm \\left\\{ {\\frac{{{m_1} - {m_2}}}{{1 + {m_1}{m_2}}}} \\right\\}=  \\pm \\frac{{(m - 1)}}{{1 + m}}\\)or \\({(1 + m)^2}{\\tan ^2}\\alpha  = {(m - 1)^2}\\)\\(\\Rightarrow {m^2} - 2m\\left\\{ {\\frac{{1 + {{\\tan }^2}\\alpha }}{{1 - {{\\tan}^2}\\alpha }}} \\right\\} + 1 = 0\\)\\( \\Rightarrow {m^2} - 2m\\sec 2\\alpha  + 1 = 0\\)But \\(m = \\frac{y}{x},\\)\\(\\left\\{ {\\frac{{1 + {{\\tan }^2}\\alpha }}{{1 - {{\\tan }^2}\\alpha }} = \\sec2\\alpha } \\right\\},\\)hence on eliminating m, we get the required equation \\({y^2} - 2xy\\sec 2\\alpha  +{x^2} = 0\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the bisectors of the lines \\({x^2} - 2pxy - {y^2} = 0\\) be \\({x^2} - 2qxy- {y^2} = 0,\\) then','\(a\)\\(pq + 1 = 0\\)','\(b\)\\(pq - 1 = 0\\)','\(c\)\\(p + q = 0\\)','\(d\)\\(p - q = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Bisector of the angle between the lines  \\({x^2} - 2pxy - {y^2} = 0\\) is\\(\\frac{{{x^2} - {y^2}}}{{xy}} = \\frac{{1 - ( - 1)}}{{ - p}}\\)\\(\\Rightarrow p{x^2} + 2xy - p{y^2} = 0\\)But it is represented by \\({x^2} - 2qxy - {y^2} = 0\\).Therefore \\(\\frac{p}{1} = \\frac{2}{{ - 2q}} \\Rightarrow pq =  - 1\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the pair of straight lines \\({y^2}{\\sin ^2}\\theta  -xy{\\sin ^2}\\theta  + {x^2}({\\cos ^2}\\theta  - 1) = 1,\\)is','\(a\)\\(\\frac{\\pi }{3}\\)','\(b\)\\(\\frac{\\pi }{4}\\)','\(c\)\\(\\frac{{2\\pi }}{3}\\)','\(d\)None of these','4','PAIR OF STRAIGHT LINE','2','Answer = \(d\)\\(\\alpha  = {\\tan ^{ - 1}}\\left\\{ {\\frac{{2\\sqrt {\\frac{{{{\\sin}^2}\\theta }}{4} - {{\\sin }^2}\\theta ({{\\cos }^2}\\theta  - 1)} }}{{{{\\sin}^2}\\theta  + {{\\cos }^2}\\theta  - 1}}} \\right\\}\\)\\(= {\\tan ^{ - 1}}\\infty  \\Rightarrow \\alpha  = \\frac{\\pi }{2}\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The pair of lines represented by \\(3a{x^2} + 5xy + ({a^2} - 2){y^2} = 0\\)are perpendicular to each other for  ','\(a\)Two values of \\(a\\)','\(b\)\\(\\forall a\\)','\(c\)For one value of \\(a\\)','\(d\)For no value of \\(a\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\) The lines are perpendicular, ifcoefficient of \\({x^2}\\) + coefficient of \\({y^2} = 0\\)\\(\\Rightarrow{}\\) \\(3a + ({a^2} - 2) = 0\\) \\(\\Rightarrow{}\\) \\({a^2} + 3a - 2 =0\\)The equation is a quadratic equation in \\`a\\' and \\({B^2} - 4AC > 0\\).\\(\\therefore{}\\) The roots of a are real and distinct. Therefore, the lines areperpendicular to each other for two values of \\`a\\'.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The figure formed by the lines \\({x^2} + 4xy + {y^2} = 0\\) and \\(x - y =4,\\) is ','\(a\)A right angled triangle','\(b\)An isosceles triangle','\(c\)An equilateral triangle','\(d\)None of these','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)\\({S_1} = \\frac{1}{{ - 2 + \\sqrt {4 - 1} }} = \\frac{1}{{ - 2 + \\sqrt 3 }}=  - (\\sqrt 3  + 2)\\)\\({S_2} = \\frac{1}{{ - 2 - \\sqrt {4 - 1} }} = \\frac{1}{{ - 2 - \\sqrt 3 }} =(\\sqrt 3  - 2)\\) and \\({S_3} = 1\\).\\({\\theta _{13}} = {\\tan ^{ - 1}}\\left| {\\frac{{ - (\\sqrt 3  + 2) - 1}}{{1 -(\\sqrt 3  + 2)}}} \\right| = {\\tan ^{ - 1}}\\left| {\\frac{{ - (\\sqrt 3  + 3)}}{{ -(\\sqrt 3  + 1)}}} \\right|\\)\\({\\theta _{23}} = {\\tan ^{ - 1}}\\left| {\\frac{{\\sqrt 3  - 2 - 1}}{{1 + \\sqrt 3  -2}}} \\right| = {\\tan ^{ - 1}}\\left| {\\frac{{\\sqrt 3  - 3}}{{\\sqrt 3  - 1}}}\\right|\\)\\(= {\\tan ^{ - 1}}(\\sqrt 3 ) = 60^\\circ .\\)\\(= {\\tan ^{ - 1}}(\\sqrt 3 ) = 60^\\circ .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\({x^2} - 3xy + \\lambda {y^2} + 3x - 5y + 2 = 0\\) when\\(\\lambda \\)is a real number, represents a pair of straight lines. If \\(\\theta \\)is the angle between the lines, then \\({\\rm{cose}}{{\\rm{c}}^2}\\theta \\)= ','\(a\)3','\(b\)9','\(c\)10','\(d\)100','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)The equation \\({x^2} - 3xy + \\lambda {y^2} + 3x - 5y + 2 = 0\\) represents a pairof straight lines.\\(\\therefore 2\\lambda  + 2\\left( { - \\frac{5}{2}} \\right){\\rm{ }}\\left({\\frac{3}{2}} \\right){\\rm{ }}\\left( { - \\frac{3}{2}} \\right) - \\frac{{25}}{4} -\\frac{{9\\lambda }}{4} - \\frac{{18}}{4} = 0\\)\\(\\Rightarrow \\lambda  = 2\\)If \\(\\theta \\)is the angle between the lines, then\\(\\tan \\theta  = \\frac{{2\\sqrt {{h^2} - ab} }}{{a + b}} = \\frac{{2\\sqrt {(9/4) -2} }}{{1 + 2}} = \\frac{1}{3}\\)\\(\\Rightarrow {\\rm{cose}}{{\\rm{c}}^2}\\theta  = 1 + {\\cot ^2}\\theta  = 1 + 9 = 10.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If one of the lines of the pair \\(a{x^2} + 2hxy + b{y^2} = 0\\) bisects theangle between positive directions of the axes, then a, b, h satisfy therelation','\(a\)\\(a + b = 2|h|\\)','\(b\)\\(a + b =  - 2h\\)','\(c\)\\(a - b = 2|h|\\)','\(d\)\\({(a - b)^2} = 4{h^2}\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Bisector of the angle between positive directions of the axes is \\(y =x\\). Since it is one of the lines of the given pair \\(a{x^2} + 2hxy + b{y^2} =0,\\) we have\\({x^2}(a + 2h + b) = 0\\) or \\(a + b =  - 2h\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines joining the origin to the points of intersection of the line \\(y =mx + c\\)and the circle \\({x^2} + {y^2} = {a^2}\\)will be mutually perpendicular,if ','\(a\)\\({a^2}({m^2} + 1) = {c^2}\\)','\(b\)\\({a^2}({m^2} - 1) = {c^2}\\)','\(c\)\\({a^2}({m^2} + 1) = {c^2}\\)','\(d\)\\({a^2}({m^2} - 1) = 2{c^2}\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Making the equation of circle homogeneous with the help of line \\(y = mx+ c,\\) we get \\({x^2} + {y^2} - {a^2}{\\left( {\\frac{{y - mx}}{c}} \\right)^2} =0\\).\\(\\Rightarrow {c^2}{x^2} + {c^2}{y^2} - {a^2}{y^2} - {a^2}{m^2}{x^2} + 2{a^2}mxy= 0\\)\\(\\, \\Rightarrow ({c^2} - {a^2}{m^2}){x^2} + ({c^2} - {a^2}){y^2} - 2{a^2}mxy =0\\)       .....(i)Hence lines represented by (i) are perpendicular, if\\({c^2} - {a^2}{m^2} + {c^2} - {a^2} = 0\\, \\Rightarrow \\,\\,2{c^2} = {a^2}(1 +{m^2}).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The angle between the lines joining the points of intersection of line \\(y =3x + 2\\) and the curve \\({x^2} + 2xy + 3{y^2} + 4x + 8y - 11 = 0\\) to the origin,is','\(a\)\\({\\tan ^{ - 1}}\\left( {\\frac{3}{{2\\sqrt 2 }}} \\right)\\)','\(b\)\\({\\tan ^{ -1}}\\left( {\\frac{2}{{2\\sqrt 2 }}} \\right)\\)','\(c\)\\({\\tan ^{ - 1}}\\left( {\\sqrt 3 } \\right)\\)','\(d\)\\({\\tan ^{ - 1}}\\left({\\frac{2}{{2\\sqrt 2 }}} \\right)\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Finding the equation of lines represented by the points of intersectionof curve and line with origin, we get \\({x^2} + 2xy + 3{y^2} + (4x + 8y)\\left({\\frac{{y - 3x}}{2}} \\right) - 11{\\left( {\\frac{{y - 3x}}{2}} \\right)^2} = 0\\)\\(\\Rightarrow {x^2} + 2xy + 3{y^2} + (2xy - 6{x^2} + 4{y^2} - 12xy)\\)\\(- \\frac{{11}}{4}{y^2} - \\frac{{99}}{4}{x^2} + \\frac{{33}}{2}xy = 0\\)Proceed and find the angle between the lines represented by it using \\(\\alpha  ={\\tan ^{ - 1}}\\frac{{2\\sqrt {{h^2} - ab} }}{{a + b}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If the pair of lines \\(a{x^2} + 2(a + b)xy + b{y^2} = 0\\) lie alongdiameters of a circle and divide the circle into four sectors such that the areaof one of the sectors is thrice the area of another sector then ','\(a\)\\(3{a^2} + 10ab + 3{b^2} = 0\\)','\(b\)\\(3{a^2} + 2ab + 3{b^2} = 0\\)','\(c\)\\(3{a^2} - 10ab + 3{b^2} = 0\\)','\(d\)\\(3{a^2} - 2ab + 3{b^2} = 0\\)','2','PAIR OF STRAIGHT LINE','2','Answer = \(b\)Angle between the given lines is\\(\\tan \\frac{\\pi }{4} = \\frac{{2\\sqrt {{{(a + b)}^2} - ab} }}{{a + b}}\\)\\(\\Rightarrow{}\\) \\(\\frac{{2\\sqrt {{{(a + b)}^2} - ab} }}{{a + b}} = 1\\)\\(\\Rightarrow{}\\) \\(3{a^2} + 2ab + 3{b^2} = 0\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Two of the lines represented by the equation \\(a{y^4} + bx{y^3} +c{x^2}{y^2} + d{x^3}y + e{x^4} = 0\\) will be perpendicular, then ','\(a\)\\((b + d)(ad + be) + {(e - a)^2}(a + c + e) = 0\\)','\(b\) \\((b + d)(ad + be) + {(e + a)^2}(a + c + e) = 0\\)','\(c\)\\((b - d)(ad - be) + {(e - a)^2}(a + c + e) = 0\\)','\(d\) \\((b - d)(ad - be) + {(e + a)^2}(a + c + e) = 0\\)','1','PAIR OF STRAIGHT LINE','2','Answer = \(a\)Let \\(a{y^4} + bx{y^3} + c{x^2}{y^2} + d{x^3}y + e{x^4}\\)\\(= (a{x^2} + pxy - a{y^2})({x^2} + qxy + {y^2})\\)Comparing the coefficient of similar terms.We get, \\(b = aq - p,\\;c =  - pq\\), \\(d = aq + p,\\;e =  - a\\)\\(b + d = 2aq,\\;e - a =  - 2a\\)\\(ad + be = 2ap,a + c + e =  - pq\\)\\((b + d)(ad + be) =  - {(e - a)^2}(a + c + e)\\)\\(\\therefore{}\\) \\((b + d)(ad + eb) + {(e - a)^2}(a + c + e) = 0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The lines represented by the equation \\(a{x^2} + 2hxy + b{y^2} + 2gx + 2fy +c = 0\\) will be equidistant from the origin, if','\(a\)\\({f^2} + {g^2} = c(b - a)\\)','\(b\)\\({f^4} + {g^4} = c(b{f^2} + a{g^2})\\)','\(c\)\\({f^4} - {g^4} = c(b{f^2} - a{g^2})\\) ','\(d\)\\({f^2} + {g^2} = a{f^2} + b{g^2}\\)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)Let the equations represented by\\(a{x^2} + 2hxy + b{y^2} + 2gx + 2fy + c = 0\\) be\\(lx + my + n = 0\\) and \\(l\\'x + m\\'y + n\\' = 0\\).Then the combined equation represented by these lines is given by \\((lx + my +n)(l\\'x + m\\'y + n\\') = 0\\)So, it must be similar with the given equation.On comparing, we get\\(ll\\' = a,\\,\\;mm\\' = b\\;\\;nn\\' = c,\\;\\;lm\\' + ml\\' = 2h,\\;\\;ln\\' + l\\'n = 2g,\\)\\(mn\\' + nm\\' = 2f\\)According to the condition, the length of perpendiculars drawn from origin tothe lines are same.So, \\(\\frac{n}{{\\sqrt {{l^2} + {m^2}} }} = \\frac{{n\\'}}{{\\sqrt {l{\\'^2} + m{\\'^2}}}} = \\frac{{{{(nn\\')}^2}}}{{({l^2} + {m^2})(l{\\'^2} + m{\\'^2})}}\\)Now on eliminating \\(l,\\;m,\\;l\\',\\;m\\'\\)and \\(n,\\;n\\'\\), we get the requiredcondition \\({f^4} - {g^4} = c(b{f^2} - a{g^2}).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The circumcentre of the triangle formed by the lines \\(xy + 2x + 2y + 4 =0\\) and \\(x + y + 2 = 0\\)','\(a\)(0, 0)','\(b\)(-2, - 2)','\(c\)(-1, -1)','\(d\)(-1, -2)','3','PAIR OF STRAIGHT LINE','2','Answer = \(c\)The separate equations of the lines given by \\(xy + 2x + 2y + 4 = 0\\) are\\((x + 2)(y + 2) = 0\\) or \\(x + 2 = 0\\), \\(y + 2 = 0\\). Solving the equations ofthe sides of the triangle, we obtain the coordinates of the vertices as \\(A( -2,0),\\;B(0, - 2)\\)and \\(C( - 2, - 2)\\). Clearly, \\(\\Delta ABC\\)is a right angledtriangle with right angle at C. Therefore the centre of the circum-circle is themidpoint of AB whose coordinates are (-1, -1).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If\\(\\frac{{2\\sin \\alpha }}{{\\{ 1 + \\cos \\alpha  + \\sin \\alpha \\} }} =y,\\)then \\(\\frac{{\\{ 1 - \\cos \\alpha  + \\sin \\alpha \\} }}{{1 + \\sin \\alpha }} =\\)','\(a\)\\(\\frac{1}{y}\\)','\(b\)\\(y\\)','\(c\)\\(1 - y\\)','\(d\)\\(1 + y\\)','2','Trigonometrical RFI','2','Answer = \(b\)We have, \\(\\frac{{2\\sin \\alpha }}{{1 + \\cos \\alpha  + \\sin \\alpha }} =y\\)Then \\(\\frac{{4\\sin \\frac{\\alpha }{2}\\cos \\frac{\\alpha }{2}}}{{2{{\\cos}^2}\\frac{\\alpha }{2} + 2\\sin \\frac{\\alpha }{2}\\cos \\frac{\\alpha }{2}}} = y\\)\\(\\Rightarrow{}\\) \\(\\frac{{2\\sin \\frac{\\alpha }{2}}}{{\\cos \\frac{\\alpha }{2} +\\sin \\frac{\\alpha }{2}}} \\times \\frac{{\\left( {\\sin \\frac{\\alpha }{2} + \\cos\\frac{\\alpha }{2}} \\right)}}{{\\left( {\\sin \\frac{\\alpha }{2} + \\cos \\frac{\\alpha}{2}} \\right)}} = y\\)\\(\\Rightarrow{}\\) \\(\\frac{{1 - \\cos \\alpha  + \\sin \\alpha }}{{1 + \\sin \\alpha }}= y\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan \\alpha  = \\frac{1}{7}\\)and \\(\\sin \\beta  = \\frac{1}{{\\sqrt {10}}}\\left( {0 < \\alpha ,\\,\\beta  < \\frac{\\pi }{2}} \\right)\\), then \\(2\\beta \\)isequal to','\(a\)\\(\\frac{\\pi }{4} - \\alpha \\)','\(b\)\\(\\frac{{3\\pi }}{4} - \\alpha \\)','\(c\)\\(\\frac{\\pi }{8} - \\frac{\\alpha }{2}\\)','\(d\)\\(\\frac{{3\\pi }}{8} - \\frac{\\alpha}{2}\\)','1','Trigonometrical RFI','2','Answer = \(a\)Since \\(\\sin \\beta  = \\frac{1}{{\\sqrt {10} }} \\Rightarrow \\tan \\beta  =\\frac{1}{3}\\)\\(\\Rightarrow{}\\) \\(\\tan 2\\beta  = \\frac{{2\\tan \\beta }}{{1 - {{\\tan }^2}\\beta}} = \\frac{3}{4}\\)\\(\\therefore \\tan (\\alpha  + 2\\beta ) = \\frac{{\\frac{1}{7} + \\frac{3}{4}}}{{1 -\\frac{1}{7}.\\frac{3}{4}}} = \\frac{{25}}{{25}} = 1\\)Now,\\(0 < \\beta  < \\frac{\\pi }{2}\\)and\\(\\tan 2\\beta  = \\frac{3}{4} >0\\)both\\(\\Rightarrow{}\\) \\(0 < 2\\beta  < \\frac{\\pi }{2}\\).Again,\\(0 < \\alpha  < \\frac{\\pi }{2}\\)and\\(0 < 2\\beta  < \\frac{\\pi}{2}\\)both\\(\\Rightarrow{}\\) \\(0 < \\alpha  + 2\\beta  < \\pi \\)Thus, \\(0 < \\alpha  + 2\\beta  < \\pi \\)and \\(\\tan (\\alpha  + 2\\beta ) = 1\\)both\\(\\Rightarrow{}\\) \\(\\alpha  + 2\\beta  = \\frac{\\pi }{4} \\Rightarrow 2\\beta  =\\frac{\\pi }{4} - \\alpha \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\cos (\\theta  - \\alpha ),\\;\\cos \\theta \\)and \\(\\cos (\\theta  + \\alpha)\\)are in H.P., then \\(\\cos \\theta \\sec \\frac{\\alpha }{2}\\)is equal to','\(a\)\\( \\pm \\sqrt 2 \\)','\(b\)\\( \\pm \\sqrt 3 \\)','\(c\)\\( \\pm 1/\\sqrt 2 \\)','\(d\)None of these','1','Trigonometrical RFI','2','Answer = \(a\)Given \\(\\cos (\\theta  - \\alpha ),\\cos \\theta \\)and \\(\\cos (\\theta  +\\alpha )\\)are in H.P.\\(\\Rightarrow{}\\) \\(\\frac{1}{{\\cos (\\theta  - \\alpha )}},\\frac{1}{{\\cos \\theta}},\\frac{1}{{\\cos (\\theta  + \\alpha )}}\\)will be in A.P.Hence, \\(\\frac{2}{{\\cos \\theta }} = \\frac{1}{{\\cos (\\theta  - \\alpha )}} +\\frac{1}{{\\cos (\\theta  + \\alpha )}}\\)\\( = \\frac{{\\cos (\\alpha  + \\theta ) + \\cos (\\theta  - \\alpha )}}{{{{\\cos}^2}\\theta  - {{\\sin }^2}\\alpha }}\\) \\(\\Rightarrow{}\\) \\(\\frac{2}{{\\cos \\theta }}= \\frac{{2\\cos \\theta \\cos \\alpha }}{{{{\\cos }^2}\\theta  - {{\\sin }^2}\\alpha }}\\)\\(\\Rightarrow{}\\) \\({\\cos ^2}\\theta  - {\\sin ^2}\\alpha  = {\\cos ^2}\\theta \\cos\\alpha \\)\\(\\Rightarrow{}\\) \\({\\cos ^2}\\theta \\,(1 - \\cos \\alpha ) = {\\sin ^2}\\alpha \\)\\(\\Rightarrow{}\\) \\({\\cos ^2}\\theta \\left( {2{{\\sin }^2}\\frac{\\alpha }{2}}\\right) = 4{\\sin ^2}\\frac{\\alpha }{2}{\\cos ^2}\\frac{\\alpha }{2}\\)\\({\\cos ^2}\\theta {\\sec ^2}\\frac{\\alpha }{2} = 2 \\Rightarrow \\cos \\theta \\sec\\frac{\\alpha }{2} =  \\pm \\sqrt 2 .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\theta  + \\sin \\varphi  = a\\)and \\(\\cos \\theta  + \\cos \\varphi  =b,\\)then \\(\\tan \\frac{{\\theta  - \\varphi }}{2}\\)is equal to','\(a\)\\(\\sqrt {\\frac{{{a^2} + {b^2}}}{{4 - {a^2} - {b^2}}}} \\)','\(b\)\\(\\sqrt {\\frac{{4- {a^2} - {b^2}}}{{{a^2} + {b^2}}}} \\)','\(c\)\\(\\sqrt {\\frac{{{a^2} + {b^2}}}{{4 + {a^2} + {b^2}}}} \\)','\(d\)\\(\\sqrt {\\frac{{4+ {a^2} + {b^2}}}{{{a^2} + {b^2}}}} \\)','2','Trigonometrical RFI','2','Answer = \(b\)Given that \\(\\sin \\theta  + \\sin \\varphi  = a\\)\\(\\ldots{} \\)..(i)and \\(\\cos \\theta  + \\cos \\varphi  = b\\)\\(\\ldots{} \\)..(ii)Squaring, \\({\\sin ^2}\\theta  + {\\sin ^2}\\varphi  + 2\\sin \\theta \\sin \\varphi  ={a^2}\\)and \\({\\cos ^2}\\theta  + {\\cos ^2}\\varphi  + 2\\cos \\theta \\cos \\varphi  ={b^2}\\)Adding, 2+ 2 \\((\\sin \\theta \\sin \\varphi  + \\cos \\theta \\cos \\varphi ) = {a^2} +{b^2}\\)\\(\\Rightarrow{}\\)\\(2\\cos (\\theta  - \\varphi ) = {a^2} + {b^2} -2\\)\\(\\Rightarrow{}\\)  \\(\\cos (\\theta  - \\varphi ) = \\frac{{{a^2} + {b^2} -2}}{2}\\)\\(\\Rightarrow \\frac{{1 - {{\\tan }^2}\\frac{{\\theta  - \\varphi }}{2}}}{{1 + {{\\tan}^2}\\frac{{\\theta  - \\varphi }}{2}}} = \\frac{{{a^2} + {b^2} - 2}}{2}\\)\\(\\Rightarrow{}\\) \\(({a^2} + {b^2}) + ({a^2} + {b^2}){\\tan ^2}\\frac{{\\theta  -\\varphi }}{2} - 2 - 2{\\tan ^2}\\frac{{\\theta  - \\varphi }}{2}\\)\\(= 2 - 2{\\tan ^2}\\frac{{\\theta  - \\varphi }}{2}\\)\\(\\Rightarrow{}\\)\\(\\frac{{4 - {a^2} - {b^2}}}{{{a^2} + {b^2}}} = {\\tan^2}\\frac{{\\theta  - \\varphi }}{2}\\)\\(\\Rightarrow{}\\) \\(\\tan \\frac{{(\\theta  -\\varphi )}}{2} = \\sqrt {\\frac{{4 - {a^2} - {b^2}}}{{{a^2} + {b^2}}}} \\)Trick : Put \\(\\theta  = \\frac{\\pi }{2},\\varphi  = {0^o}\\), then \\(a = 1 = b\\)\\(\\therefore{}\\)\\(\\tan \\frac{{\\theta  - \\varphi }}{2} = 1\\), which is given by\(a\) and \(b\).Again putting \\(\\theta  = \\frac{\\pi }{4} = \\varphi \\), we get \\(\\tan\\frac{{\\theta  - \\varphi }}{2} = 0\\), which is given by \(b\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan A = \\frac{{1 - \\cos B}}{{\\sin B}},\\)find\\(\\tan 2A\\)in terms of\\(\\tan B\\)and show that','\(a\)\\(\\tan 2A = \\tan B\\)','\(b\)\\(\\tan 2A = {\\tan ^2}B\\)','\(c\)\\(\\tan 2A = {\\tan ^2}B + 2\\tan B\\)','\(d\)None of the above','1','Trigonometrical RFI','2','Answer = \(a\)\\(\\tan A = \\frac{{1 - \\cos B}}{{\\sin B}} = \\frac{{2{{\\sin}^2}(B/2)}}{{2\\sin (B/2)\\cos (B/2)}} = \\tan \\frac{B}{2}\\)\\(\\Rightarrow{}\\) \\(\\tan 2A = \\tan B\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\beta \\)is the geometric mean between \\(\\sin \\alpha \\)and \\(\\cos\\alpha ,\\)then \\(\\cos 2\\beta \\)is equal to','\(a\)\\(2{\\sin ^2}\\left( {\\frac{\\pi }{4} - \\alpha } \\right)\\)\(b\)\\(2{\\cos ^2}\\left({\\frac{\\pi }{4} - \\alpha } \\right)\\)\(c\)\\(2{\\cos ^2}\\left( {\\frac{\\pi }{4} + \\alpha } \\right)\\)\(d\)\\(2{\\sin ^2}\\left({\\frac{\\pi }{4} + \\alpha } \\right)\\)707.\\(\\frac{{\\sec 8A - 1}}{{\\sec 4A - 1}} = \\)','','\(b\)\\(2{\\cos ^2}\\left({\\frac{\\pi }{4} - \\alpha } \\right)\\)\(c\)\\(2{\\cos ^2}\\left( {\\frac{\\pi }{4} + \\alpha } \\right)\\)\(d\)\\(2{\\sin ^2}\\left({\\frac{\\pi }{4} + \\alpha } \\right)\\)707.\\(\\frac{{\\sec 8A - 1}}{{\\sec 4A - 1}} = \\)\(a\)\\(\\frac{{\\tan 2A}}{{\\tan 8A}}\\)','\(b\)\\(\\frac{{\\tan 8A}}{{\\tan 2A}}\\)\(c\)\\(\\frac{{\\cot 8A}}{{\\cot 2A}}\\)\(d\)None of these','(a,','Trigonometrical RFI','2','Answer = (a,c)Since \\(\\sin \\beta \\) is G.M. between \\(\\sin \\alpha \\)and \\(\\cos \\alpha\\).\\(\\therefore \\,\\,{\\sin ^2}\\beta  = \\sin \\alpha \\cos \\alpha\\)Now \\(\\cos 2\\beta  = 1 - 2{\\sin ^2}\\beta  = 1 - 2\\sin \\alpha \\cos \\alpha \\)\\(= {(\\cos \\alpha  - \\sin \\alpha )^2} = 2\\,{\\left( {\\frac{1}{{\\sqrt 2 }}\\cos\\alpha  - \\frac{1}{{\\sqrt 2 }}\\sin \\alpha } \\right)^2}\\)\\( = 2{\\sin ^2}\\left( {\\frac{\\pi }{4} - \\alpha } \\right)\\), which is given in\(a\).Also \\(\\cos 2\\beta  = 2{\\cos ^2}\\left\\{ {\\frac{\\pi }{2} - \\left( {\\frac{\\pi }{4}- \\alpha } \\right)} \\right\\} = 2{\\cos ^2}\\left( {\\frac{\\pi }{4} + \\alpha }\\right)\\),which is given in \(c\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\frac{{\\cos A}}{{\\sin B\\sin C}} + \\frac{{\\cos B}}{{\\sin C\\sin A}} +\\frac{{\\cos C}}{{\\sin A\\sin B}} = \\)then \\(32\\sin \\left( {\\frac{A}{2}}\\right)\\sin \\left( {\\frac{{5A}}{2}} \\right) = \\)','\(a\)\\(\\frac{1}{3}\\)','\(b\)\\(\\sqrt 3  - 2\\)','\(c\)\\(2 - \\sqrt 3 \\)','\(d\)None of these','3','Trigonometrical RFI','2','Answer = \(c\)\\(32\\sin \\frac{A}{2}\\sin \\frac{{5A}}{2} = 16(\\cos 2A - \\cos 3A)\\)\\(= 16(2{\\cos ^2}A - 1 - 4{\\cos ^3}A + 3\\cos A)\\)\\(= 16\\left( {2 \\times \\frac{9}{{16}} - 1 - 4 \\times \\frac{{27}}{{64}} + 3 \\times\\frac{3}{4}} \\right) = 11.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan \\alpha  = \\frac{1}{7},\\;\\tan \\beta  = \\frac{1}{3},\\)then \\(\\cos2\\alpha  = \\)','\(a\)\\(\\sin 2\\beta \\)','\(b\)\\(\\sin 4\\beta \\)','\(c\)\\(\\sin 3\\beta \\)','\(d\)None of these','2','Trigonometrical RFI','2','Answer = \(b\)\\(\\cos 2\\alpha  = \\frac{{1 - {t^2}}}{{1 + {t^2}}} =\\frac{{24}}{{25}}\\) {Here \\(t = \\tan \\alpha \\)}\\(\\sin 2\\beta  = \\frac{{2T}}{{1 + {T^2}}} = \\frac{3}{5} \\Rightarrow \\cos 2\\beta = \\frac{4}{5}\\) {\\(T = \\tan \\beta \\)}\\(\\therefore \\,\\,\\sin 4\\beta  = 2\\sin 2\\beta \\cos 2\\beta  =2.\\frac{3}{5}.\\frac{4}{5} = \\frac{{24}}{{25}} = \\cos 2\\alpha .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan \\beta  = \\cos \\theta \\tan \\alpha ,\\)then \\({\\tan ^2}\\frac{\\theta}{2} = \\)','\(a\)\\(\\frac{{\\sin (\\alpha  + \\beta )}}{{\\sin (\\alpha  - \\beta)}}\\)','\(b\)\\(\\frac{{\\cos (\\alpha  - \\beta )}}{{\\cos (\\alpha  + \\beta )}}\\)','\(c\)\\(\\frac{{\\sin (\\alpha  - \\beta )}}{{\\sin (\\alpha  + \\beta)}}\\)','\(d\)\\(\\frac{{\\cos (\\alpha  + \\beta )}}{{\\cos (\\alpha  - \\beta )}}\\)','3','Trigonometrical RFI','2','Answer = \(c\)\\({\\tan ^2}\\frac{\\theta }{2} = \\frac{{1 - \\cos \\theta }}{{1 + \\cos \\theta}} = \\frac{{\\tan \\alpha  - \\tan \\beta }}{{\\tan \\alpha  + \\tan \\beta }} =\\frac{{\\sin (\\alpha  - \\beta )}}{{\\sin (\\alpha  + \\beta )}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\cos A = \\frac{3}{4}\\), then \\(32\\sin \\frac{A}{2}\\cos \\frac{5}{2}A =\\)','\(a\)\\(\\sqrt 7 \\)','\(b\)\\( - \\sqrt 7 \\)','\(c\)7','\(d\)-7','2','Trigonometrical RFI','2','Answer = \(b\)\\(\\cos A = \\frac{3}{4} \\Rightarrow \\sin A = \\frac{{\\sqrt 7 }}{4}\\)L.H.S \\( = 16(\\sin 3A - \\sin 2A)\\)\\(= 16\\sin A(3 - 4{\\sin ^2}A - 2\\cos A)\\)\\(= 16.\\frac{{\\sqrt 7 }}{4}\\left( {3 - 4.\\frac{7}{{16}} - 2.\\frac{3}{4}} \\right)=  - \\sqrt 7 .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\theta \\)and \\(\\varphi \\)are angles in the 1st quadrant such that\\(\\tan \\theta  = 1/7\\)and \\(\\sin \\varphi  = 1/\\sqrt {10} \\).Then','\(a\)\\(\\theta  + 2\\varphi  = 90^\\circ \\)\(b\)\\(\\theta  + 2\\varphi  = 60^\\circ \\)\(c\)\\(\\theta  + 2\\varphi  = 30^\\circ \\)\(d\)\\(\\theta  + 2\\varphi  = 45^\\circ \\)714.\\(\\frac{{\\cos A}}{{1 - \\sin A}} = \\)','\(a\)\\(\\sec A - \\tan A\\)\(b\)\\({\\rm{cosec}}\\,A + \\cot A\\)\(c\)\\(\\tan \\left( {\\frac{\\pi }{4} - \\frac{A}{2}} \\right)\\)\(d\)\\(\\tan \\left({\\frac{\\pi }{4} + \\frac{A}{2}} \\right)\\)715.\\(\\tan \\frac{A}{2}\\)is equal to','','\(b\)\\(\\theta  + 2\\varphi  = 60^\\circ \\)\(c\)\\(\\theta  + 2\\varphi  = 30^\\circ \\)\(d\)\\(\\theta  + 2\\varphi  = 45^\\circ \\)714.\\(\\frac{{\\cos A}}{{1 - \\sin A}} = \\)\(a\)\\(\\sec A - \\tan A\\)\(b\)\\({\\rm{cosec}}\\,A + \\cot A\\)\(c\)\\(\\tan \\left( {\\frac{\\pi }{4} - \\frac{A}{2}} \\right)\\)\(d\)\\(\\tan \\left({\\frac{\\pi }{4} + \\frac{A}{2}} \\right)\\)715.\\(\\tan \\frac{A}{2}\\)is equal to\(a\)\\( \\pm \\sqrt {\\frac{{1 - \\sin A}}{{1 + \\sin A}}} \\)\(b\)\\( \\pm \\sqrt {\\frac{{1+ \\sin A}}{{1 - \\sin A}}} \\)\(c\)\\( \\pm \\sqrt {\\frac{{1 - \\cos A}}{{1 + \\cos A}}} \\)\(d\)\\( \\pm \\sqrt {\\frac{{1+ \\cos A}}{{1 - \\cos A}}} \\)','4','Trigonometrical RFI','2','Answer = \(d\)Given, \\(\\tan \\theta  = \\frac{1}{7},\\sin \\varphi  = \\frac{1}{{\\sqrt {10} }}\\)\\(\\sin \\theta  = \\frac{1}{{\\sqrt {50} }},\\,\\,\\cos \\theta  = \\frac{7}{{\\sqrt {50}}},\\,\\,\\cos \\varphi  = \\frac{3}{{\\sqrt {10} }}\\)\\(\\therefore \\,\\,\\cos 2\\varphi  = 2{\\cos ^2}\\varphi  - 1 = 2.\\frac{9}{{10}} - 1 =\\frac{8}{{10}}\\)\\(\\sin 2\\varphi  = 2\\sin \\varphi \\cos \\varphi  = 2 \\times .\\frac{1}{{\\sqrt {10} }}\\times \\frac{3}{{\\sqrt {10} }} = \\frac{6}{{10}}\\)\\(\\therefore{}\\) \\(\\cos (\\theta  + 2\\varphi ) = \\cos \\theta \\cos 2\\varphi  -\\sin \\theta \\sin 2\\varphi \\)\\(= \\frac{7}{{\\sqrt {50} }} \\times \\frac{8}{{10}} - \\frac{1}{{\\sqrt {50}}}.\\frac{6}{{10}}\\)\\(= \\frac{{56 - 6}}{{10\\sqrt {50} }} = \\frac{{50}}{{10\\sqrt {50} }} =\\frac{{5\\sqrt 2 }}{{10}} = \\frac{1}{{\\sqrt 2 }}\\)\\(\\therefore{}\\)\\(\\theta  + 2\\varphi  = {45^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\alpha  = \\frac{{ - 3}}{5},\\)where \\(\\pi  < \\alpha  < \\frac{{3\\pi}}{2},\\)then \\(\\cos \\frac{1}{2}\\alpha  = \\)','\(a\)\\(\\frac{{ - 1}}{{\\sqrt {10} }}\\)','\(b\)\\(\\frac{1}{{\\sqrt {10} }}\\)','\(c\)\\(\\frac{3}{{\\sqrt {10} }}\\)','\(d\)\\(\\frac{{ - 3}}{{\\sqrt {10} }}\\)','1','Trigonometrical RFI','2','Answer = \(a\) \\(\\cos (\\alpha /2) =  - \\sqrt {\\frac{{1 + \\cos \\alpha }}{2}} \\)\\(\\cos \\alpha  =  - \\sqrt {1 - {{\\sin }^2}\\alpha } \\)[\\(\\alpha \\)lies inIII\\(^{rd}\\) Quadrant]\\( =  - \\sqrt {1 - \\frac{9}{{25}}}  =  - \\frac{4}{5}\\)\\(\\therefore \\,\\,\\,\\cos (\\alpha /2) =  - \\sqrt {\\frac{{1 - \\frac{4}{5}}}{2}}  =  -\\frac{1}{{\\sqrt {10} }}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let\\(0 < x < \\frac{\\pi }{4}.\\)Then \\(\\sec 2x - \\tan 2x = \\)','\(a\)\\(\\tan \\left( {x - \\frac{\\pi }{4}} \\right)\\)','\(b\)\\(\\tan \\left( {\\frac{\\pi }{4}- x} \\right)\\)','\(c\)\\(\\tan \\left( {x + \\frac{\\pi }{4}} \\right)\\)','\(d\)\\({\\tan ^2}\\left( {x +\\frac{\\pi }{4}} \\right)\\)','2','Trigonometrical RFI','2','Answer = \(b\)\\(\\sec 2x - \\tan 2x = \\frac{{1 - \\sin 2x}}{{\\cos 2x}}\\)\\(= \\frac{{{{(\\cos x - \\sin x)}^2}}}{{({{\\cos }^2}x - {{\\sin }^2}x)}} =\\frac{{\\cos x - \\sin x}}{{\\cos x + \\sin x}} = \\frac{{1 - \\tan x}}{{1 + \\tan x}}\\)\\(= \\frac{{\\tan \\frac{\\pi }{4} - \\tan x}}{{1 + \\tan \\left( {\\frac{\\pi }{4}}\\right)\\sin x}} = \\tan \\left( {\\frac{\\pi }{4} - x} \\right).\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\theta  + \\cos \\theta  = x,\\) then \\({\\sin ^6}\\theta  + {\\cos^6}\\theta  = \\frac{1}{4}[4 - 3{({x^2} - 1)^2}]\\) for','\(a\)All real x','\(b\)\\({x^2} \\le 2\\)','\(c\)\\({x^2} \\ge 2\\)','\(d\)None of these','2','Trigonometrical RFI','2','Answer = \(b\)On squaring the given relation\\(\\sin 2\\theta  = {x^2} - 1 \\le 1 \\Rightarrow {x^2} \\le 2\\)or \\( - \\sqrt 2  \\le x \\le \\sqrt 2 \\)\\([\\,\\,\\sin 2\\theta  \\le 1]\\)Now \\({\\sin ^6}\\theta  + {\\cos ^6}\\theta \\)\\(= {({\\sin ^2}\\theta  + {\\cos ^2}\\theta )^3} - 3{\\sin ^2}\\theta {\\cos ^2}\\theta({\\sin ^2}\\theta  + {\\cos ^2}\\theta )\\)\\(= 1 - 3{\\sin ^2}\\theta {\\cos ^2}\\theta  = 1 - \\frac{3}{4}{\\sin ^2}2\\theta\\)\\(= 1 - \\frac{3}{4}{({x^2} - 1)^2} = \\frac{1}{4}\\{ 4 - 3{({x^2} - 1)^2}\\}\\)Thus the given result will hold true only when \\({x^2} \\le 2\\)and not for allreal values of x.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan \\theta  = t,\\)then \\(\\tan 2\\theta  + \\sec 2\\theta  = \\)','\(a\)\\(\\sec \\left( {\\frac{\\alpha }{2} - \\frac{\\pi }{8}} \\right)\\)','\(b\)\\(\\cos \\left({\\frac{\\pi }{8} - \\frac{\\alpha }{2}} \\right)\\)','\(c\)\\(\\tan \\left( {\\frac{\\alpha }{2} - \\frac{\\pi }{8}} \\right)\\)','\(d\)\\(\\cot \\left({\\frac{\\alpha }{2} - \\frac{\\pi }{2}} \\right)\\)','1','Trigonometrical RFI','2','Answer = \(a\)\\(\\tan 2\\theta  = \\frac{{2\\tan \\theta }}{{1 - {{\\tan }^2}\\theta }},\\cos2\\theta  = \\frac{{1 - {{\\tan }^2}\\theta }}{{1 + {{\\tan }^2}\\theta }}\\)\\(\\tan 2\\theta  + \\sec 2\\theta  = \\frac{{2t}}{{1 - {t^2}}} + \\frac{{1 + {t^2}}}{{1- {t^2}}} = \\frac{{{{(1 + t)}^2}}}{{(1 - t)(1 + t)}} = \\frac{{1 + t}}{{1 - t}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\cos \\theta  = \\frac{1}{2}\\left( {a + \\frac{1}{a}} \\right),\\)then thevalue of \\(\\cos 3\\theta \\)is','\(a\)\\(\\frac{1}{8}\\left( {{a^3} + \\frac{1}{{{a^3}}}}\\right)\\)','\(b\)\\(\\frac{3}{2}\\left( {a + \\frac{1}{a}} \\right)\\)','\(c\)\\(\\frac{1}{2}\\left( {{a^3} + \\frac{1}{{{a^3}}}}\\right)\\)','\(d\)\\(\\frac{1}{3}\\left( {{a^3} + \\frac{1}{{{a^3}}}} \\right)\\)','3','Trigonometrical RFI','2','Answer = \(c\)\\(\\;\\cos 3\\theta  = 4{\\cos ^3}\\theta  - 3\\cos \\theta \\)\\(\\therefore \\cos 3\\theta  = 4\\frac{1}{{{2^3}}}{\\left( {a + \\frac{1}{a}}\\right)^3} - 3\\frac{1}{2}\\left( {a + \\frac{1}{a}} \\right)\\)\\(\\Rightarrow \\cos 3\\,\\theta  = \\frac{1}{2}\\left( {a + \\frac{1}{a}}\\right)\\,\\left[ {{{\\left( {a + \\frac{1}{a}} \\right)}^2} - 3} \\right]\\)\\(\\Rightarrow{}\\) \\(\\cos 3\\theta  = \\frac{1}{2}\\left( {{a^3} +\\frac{1}{{{a^3}}}} \\right)\\).',CURRENT_TIMESTAMP);
