INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(90^\\circ  < A < \\circ \\)and \\(\\sin A = \\frac{4}{5},\\)then \\(\\tan\\frac{A}{2}\\)is equal to','\(a\)\\(1/2\\)','\(b\)\\(3/5\\)','\(c\)\\(3/2\\)','\(d\)\\(2\\)','4','Trigonometrical RFI','2','Answer = \(d\)\\(\\sin \\,A = \\frac{4}{5}\\)\\(\\Rightarrow{}\\)\\(\\tan A =  - \\frac{4}{3}\\),\\(({90^o} < A < {Answer = o})\\)\\(\\tan A = \\frac{{2\\tan \\frac{A}{2}}}{{1 - {{\\tan }^2}\\frac{A}{2}}}\\), (Let\\(\\tan \\frac{A}{2} = P\\))\\(\\Rightarrow{}\\) \\( - \\frac{4}{3} = \\frac{{2P}}{{1 - {P^2}}}\\)\\(\\Rightarrow{}\\) \\(4{P^2} - 6P - 4 = 0\\)\\(\\Rightarrow{}\\) \\(P = \\frac{{ - 1}}{2}{\\rm{  (impossible),}}\\,\\)hence \\(\\tan\\frac{A}{2} = 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(2\\tan A = 3\\tan B,\\)then \\(\\frac{{\\sin 2B}}{{5 - \\cos 2B}}\\)is equal to','\(a\)\\(\\tan A - \\tan B\\)','\(b\)','\(c\)\\(\\tan (A + B)\\)','\(d\)\\(\\tan (A + 2B)\\)','2','Trigonometrical RFI','2','Answer = \(b\)\\(2\\tan {\\rm A} = 3\\tan B\\)\\(\\Rightarrow{}\\) \\(\\tan A = \\frac{3}{2}\\tan B = \\frac{3}{2}t\\),  [Let \\(\\tan B= t\\)]\\(\\Rightarrow{}\\) \\(\\sin 2B = \\frac{{2t}}{{1 + {t^2}}},\\cos 2B = \\frac{{1 -{t^2}}}{{1 + {t^2}}}\\)\\(\\therefore{}\\) \\(\\frac{{\\left( {\\frac{{2t}}{{1 + {t^2}}}} \\right)}}{{5 -\\left( {\\frac{{1 - {t^2}}}{{1 + {t^2}}}} \\right)}}\\)\\( = \\frac{{2t}}{{4 +6{t^2}}} = \\frac{t}{{2 + 3{t^2}}} = \\tan (A - B)\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Given that \\(\\cos \\left( {\\frac{{\\alpha  - \\beta }}{2}} \\right) = 2\\cos\\left( {\\frac{{\\alpha  + B}}{2}} \\right)\\), then \\(\\tan \\frac{\\alpha }{2}\\tan\\frac{\\beta }{2}\\)is equal to','\(a\)\\(\\frac{1}{2}\\)','\(b\)\\(\\frac{1}{3}\\)','\(c\)\\(\\frac{1}{4}\\)','\(d\)\\(\\frac{1}{8}\\)','2','Trigonometrical RFI','2','Answer = \(b\)\\(\\cos \\left( {\\frac{{\\alpha  - \\beta }}{2}} \\right) = 2\\cos \\left({\\frac{{\\alpha  + \\beta }}{2}} \\right)\\)\\(\\Rightarrow{}\\) \\(\\cos \\frac{\\alpha }{2}\\cos \\frac{\\beta }{2} + \\sin\\frac{\\alpha }{2}\\sin \\frac{\\beta }{2} = 2\\cos \\frac{\\alpha }{2}\\cos \\frac{\\beta}{2} - 2\\sin \\frac{\\alpha }{2}\\sin \\frac{\\beta }{2}\\)\\( \\Rightarrow 3\\sin \\frac{\\alpha }{2}\\sin \\frac{\\beta }{2} = \\cos\\frac{\\alpha }{2}\\cos \\frac{\\beta }{2}\\) \\(\\Rightarrow{}\\) \\(\\tan \\frac{\\alpha}{2}\\tan \\frac{\\beta }{2} = \\frac{1}{3}\\).',CURRENT_TIMESTAMP);
