INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If A lies in the third quadrant and \\(3\\,\\tan A - 4 = 0,\\) then \\(5\\,\\sin 2A +3\\,\\sin A + 4\\,\\cos A = \\)','\(a\)\\(\\cot 7\\frac{{{1^o}}}{2}\\)','\(b\)\\(\\sin 7\\frac{{{1^o}}}{2}\\)','\(c\)\\(\\sin \\,{15^o}\\)','\(d\)\\(\\cos \\,\\,{15^o}\\)','1','Trigonometrical RFI','2','Answer = \(a\)\\(3\\tan A - 4 = 0 \\Rightarrow \\tan A = \\frac{4}{3} \\Rightarrow \\sin A = - \\frac{4}{5},\\cos A =  - \\frac{3}{5}\\)\\(\\therefore \\)\\(5\\sin 2A + 3\\sin A + 4\\cos A\\)= \\(10\\sin A\\cos A + 3\\sin A + 4\\cos A\\)= \\(10\\,\\left( {\\frac{{12}}{{25}}} \\right) - \\frac{{12}}{5} - \\frac{{12}}{5} =0\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan \\,(A + B) = p,\\,\\,\\tan \\,(A - B) = q,\\) then the value of \\(\\tan\\,2A\\) in terms of p and q is ','\(a\)\\(\\sin \\,\\,2\\alpha \\)','\(b\)\\(\\cos \\,\\,2\\beta \\)','\(c\)\\(\\cos \\,\\,2\\alpha \\)','\(d\)\\(\\sin \\,\\,2\\beta \\)','3','Trigonometrical RFI','2','Answer = \(c\)\\(2A = (A + B) + (A - B)\\)\\( \\Rightarrow \\)\\(\\tan 2A = \\frac{{\\tan (A + B) + \\tan (A - B)}}{{1 - \\tan (A + B)\\tan (A - B)}} =\\frac{{p + q}}{{1 - pq}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\(\\sin \\theta  + \\cos \\theta \\) will be greatest when','\(a\)\\(\\theta  = {30^o}\\)','\(b\)\\(\\theta  = {45^o}\\)','\(c\)\\(\\theta  = {60^o}\\)','\(d\)\\(\\theta  = {90^o}\\)','2','Trigonometrical RFI','2','Answer = \(b\)Let \\(f(x) = \\sin \\theta  + \\cos \\theta  = \\sqrt 2 \\sin \\left( {\\theta  +\\frac{\\pi }{4}} \\right)\\)But\\( - 1 \\le \\sin \\left( {\\theta  + \\frac{\\pi }{2}} \\right) \\le 1 \\Rightarrow - \\sqrt 2  \\le \\sqrt 2 \\sin \\left( {\\theta  + \\frac{\\pi }{4}} \\right) \\le \\sqrt 2\\).Hence the maximum value of \\((\\sin \\theta  + \\cos \\theta )\\)i.e., of \\(\\sqrt 2 \\sin \\left( {\\theta  + \\frac{\\pi }{4}} \\right) = \\sqrt 2 \\).\\(\\therefore \\)\\(\\sin \\left( {\\theta  + \\frac{\\pi }{4}} \\right) = 1 \\Rightarrow \\sin \\left({\\theta  + \\frac{\\pi }{4}} \\right) = \\sin \\frac{\\pi }{2}\\)\\(\\Rightarrow{}\\) \\(\\theta  + \\frac{\\pi }{4} = \\frac{\\pi }{2} \\Rightarrow \\theta = \\frac{\\pi }{4} = {45^o}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(f(x) = {\\cos ^2}x + {\\sec ^2}x,\\) then  ','\(a\)\\(f(x) < 1\\)','\(b\)\\(f(x) = 1\\)','\(c\)\\(1 < f(x) < 2\\)','\(d\)\\(f(x) \\ge 2\\)','4','Trigonometrical RFI','2','Answer = \(d\)Since \\({\\left( {x - \\frac{1}{x}} \\right)^2} \\ge 0,\\,\\,{\\rm{\\rlap{-}V}}\\,\\,x \\in R,\\)we have \\({x^2} + \\frac{1}{{{x^2}}} \\ge 2\\) andHence, \\(f(x) = {\\cos ^2}x + \\frac{1}{{{{\\cos }^2}x}} \\ge 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\(\\frac{{\\tan x}}{{\\tan \\,3x}}\\)whenever defined never liebetween','\(a\)1/3 and 3\(b\)1/4  and 4\(c\)1/5 and 5\(d\)5 and 6826.\\(\\cos \\,\\,2\\theta  + 2\\,\\,\\cos \\theta \\) is always','','\(b\)1/4  and 4\(c\)1/5 and 5\(d\)5 and 6826.\\(\\cos \\,\\,2\\theta  + 2\\,\\,\\cos \\theta \\) is always\(a\)Greater than \\( - \\frac{3}{2}\\)','\(b\)Less than or equal to \\(\\frac{3}{2}\\)\(c\)Greater than or equal to \\( - \\frac{3}{2}\\) and less than or equal to 3\(d\)None of these','1','Trigonometrical RFI','2','Answer = \(a\)Let \\(y = \\frac{{\\tan x}}{{\\tan 3x}} = \\frac{{\\tan x}}{{\\frac{{3\\tan x -{{\\tan }^3}x}}{{1 - 3{{\\tan }^2}x}}}}\\)\\(y = \\frac{{1 - 3{{\\tan }^2}x}}{{3 - {{\\tan }^2}x}} = \\frac{{\\frac{1}{3} - {{\\tan}^2}x}}{{1 - \\frac{1}{3}.{{\\tan }^2}x}}\\)Hence, y should never lie between \\(\\frac{1}{3}\\)and 3 whenever defined.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','Let A, B and C are the angles of a plain triangle and \\(\\tan \\frac{A}{2} =\\frac{1}{3},\\,\\,\\tan \\frac{B}{2} = \\frac{2}{3}\\). Then \\(\\tan \\frac{C}{2}\\) isequal to','\(a\)7/9','\(b\)2/9','\(c\)1/3','\(d\)2/3','1','Trigonometrical RFI','2','Answer = \(a\)\\(A + B + C = \\pi \\)\\(\\therefore \\,\\,\\,\\tan \\left( {\\frac{{A + B}}{2}} \\right) = \\tan \\left({\\frac{\\pi }{2} - \\frac{C}{2}} \\right)\\)\\(\\Rightarrow{}\\) \\(\\frac{{\\tan \\frac{A}{2} + \\tan \\frac{B}{2}}}{{1 - \\tan\\frac{A}{2}.\\tan \\frac{B}{2}}} = \\cot \\frac{C}{2} \\Rightarrow \\frac{{\\frac{1}{3}+ \\frac{2}{3}}}{{1 - \\frac{1}{3}.\\frac{2}{3}}} = \\frac{9}{7} = \\cot \\frac{C}{2}\\)\\(\\therefore{}\\) \\(\\tan \\frac{C}{2} = \\frac{7}{9}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(A + B + C = \\pi \\) and \\(\\cos A = \\cos B\\,\\cos C,\\) then \\(\\tan B\\,\\,\\tanC\\) is equal to','\(a\) \\(\\frac{1}{2}\\)','\(b\) 2','\(c\)1','\(d\)\\( - \\frac{1}{2}\\)','2','Trigonometrical RFI','2','Answer = \(b\)\\(\\cos [\\pi  - (B + C)] = \\cos B\\cos C\\)\\(\\Rightarrow{}\\) \\( - \\cos (B + C) = \\cos B\\cos C\\)\\(\\Rightarrow{}\\) \\( - [\\cos B\\cos C - \\sin B\\sin C] = \\cos B\\cos C\\)\\(\\Rightarrow{}\\) \\(\\sin B\\sin C = 2\\cos B\\cos C\\)\\(\\Rightarrow{}\\) \\(\\tan B\\tan C = 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(A + C = B,\\) then \\(\\tan A\\,\\tan B\\,\\tan C = \\)','\(a\)\\(\\tan A\\,\\tan B + \\tan \\,C\\)','\(b\)\\(\\tan \\,B - \\tan \\,C - \\tan \\,A\\)','\(c\)\\(\\tan A + \\tan C - \\tan B\\)','\(d\)\\( - \\,(\\tan A\\tan B + \\tan C)\\)','2','Trigonometrical RFI','2','Answer = \(b\)\\(B = A + C \\Rightarrow \\tan B = \\tan (A + C)\\)\\(\\Rightarrow{}\\) \\(\\tan B = \\frac{{\\tan A + \\tan C}}{{1 - \\tan A\\tan C}}\\)\\(\\Rightarrow{}\\) \\(\\tan A\\tan B\\tan C = \\tan B - \\tan A - \\tan C\\).',CURRENT_TIMESTAMP);
