INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\({(a + b)^2} = 4ab{\\sin ^2}\\theta \\)is possible only when','\(a\)\\(2a = b\\)','\(b\)\\(a = b\\)','\(c\)\\(a = 2b\\)','\(d\)None of these','2','Trigonometrical RFI','2','Answer = \(b\)We have \\({(a + b)^2} = 4ab{\\sin ^2}\\theta \\)\\(\\Rightarrow {\\sin ^2}\\theta  = \\frac{{{{(a + b)}^2}}}{{4ab}} \\le 1 \\Rightarrow{(a + b)^2} - 4ab \\le 0\\)\\(\\Rightarrow {(a - b)^2} \\le 0 \\Rightarrow a = b.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The equation \\({\\sec ^2}\\theta  = \\frac{{4xy}}{{{{(x + y)}^2}}}\\)is onlypossible when','\(a\)\\(x = y\\)','\(b\)\\(x < y\\)','\(c\)\\(x > y\\)','\(d\)None of these','1','Trigonometrical RFI','2','Answer = \(a\)Since \\({\\cos ^2}\\theta  \\le 1\\)\\({\\sec ^2}\\theta  = \\frac{{4xy}}{{{{(x + y)}^2}}} \\ge 1 \\Rightarrow 4xy \\ge {(x +y)^2} \\Rightarrow {(x - y)^2} \\le 0\\)It is possible only when \\(x = y\\), \\((x,\\,y \\in R)\\).',CURRENT_TIMESTAMP);
