INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\theta  + {\\rm{cosec}}\\theta  = 2,\\) the value of \\({\\sin^{10}}\\theta  + {\\rm{cose}}{{\\rm{c}}^{10}}\\theta \\) is','\(a\)10','\(b\)\\({2^{10}}\\)','\(c\) \\( {2^9}\\)','\(d\) 2','4','Trigonometrical RFI','2','Answer = \(d\)We have,\\(\\sin \\theta  + {\\rm{cosec}}\\theta  = 2\\)\\( \\Rightarrow \\)\\({\\sin ^2}\\theta  + 1 = 2\\sin \\theta\\)\\( \\Rightarrow \\)\\({\\sin ^2}\\theta  - 2\\sin \\theta  + 1 = 0\\)\\( \\Rightarrow \\)\\({(\\sin \\theta  - 1)^2} = 0 \\Rightarrow \\sin \\theta  = 1\\)Required value of \\({\\sin ^{10}}\\theta  + {\\rm{cose}}{{\\rm{c}}^{10}}\\theta  ={(1)^{10}} + \\frac{1}{{{{(1)}^{10}}}} = 2\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\theta  + {\\rm{cosec}}\\theta  = {\\rm{2}}\\), then \\({\\sin ^2}\\theta + {\\rm{cose}}{{\\rm{c}}^{\\rm{2}}}\\theta  = \\)','\(a\)1','\(b\)4','\(c\)2','\(d\)None of these','3','Trigonometrical RFI','2','Answer = \(c\)\\({\\sin ^2}\\theta  + {\\rm{cose}}{{\\rm{c}}^2}\\theta  = {(\\sin \\theta  +{\\rm{cosec}}\\theta )^2} - 2\\sin \\theta {\\rm{ cosec}}\\theta \\)\\( = {(2)^2} - 2 = 4 - 2 = 2,\\)since\\((\\sin \\theta  + {\\rm{cosec}}\\theta ) =2.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\theta  + \\cos \\theta  = m\\)and \\(\\sec \\theta  +{\\rm{cosec}}\\theta  = n\\), then \\(n(m + 1)(m - 1) = \\)      ','\(a\)m','\(b\)n','\(c\)2m','\(d\)2n','3','Trigonometrical RFI','2','Answer = \(c\)\\(n({m^2} - 1) = (\\sec \\theta  + {\\rm{cosec}}\\theta ).2\\sin \\theta \\cos\\theta \\)(\\({m^2} = 1 + 2\\sin \\theta \\cos \\theta \\))\\(= \\frac{{\\sin \\theta  + \\cos \\theta }}{{\\sin \\theta .\\cos \\theta }}.2\\sin\\theta \\cos \\theta  = 2m.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\theta  + \\cos \\theta  = 1\\), then \\(\\sin \\theta \\cos \\theta  = \\)','\(a\)0','\(b\)1','\(c\)2','\(d\)1/2','1','Trigonometrical RFI','2','Answer = \(a\)\\(\\sin \\theta  + \\cos \\theta  = 1\\)Squaring on both sides, we get\\(\\Rightarrow {\\sin ^2}\\theta  + {\\cos ^2}\\theta  + 2\\sin \\theta \\cos \\theta  = 1\\)\\(\\therefore \\sin \\theta \\cos \\theta  = 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\theta  = \\frac{{24}}{{25}}\\)and \\(\\theta \\) lies in the secondquadrant, then \\(\\sec \\theta  + \\tan \\theta  = \\)      ','\(a\)- 3','\(b\)- 5','\(c\)- 7','\(d\)- 9','3','Trigonometrical RFI','2','Answer = \(c\)\\(\\sin \\theta  = \\frac{{24}}{{25}} \\Rightarrow \\cos \\theta  = \\frac{{ -7}}{{25}},\\,\\tan \\theta  = \\frac{{ - 24}}{7}\\)\\(\\cos A\\cos B = \\frac{1}{5}\\)\\(\\sec \\theta  + \\tan \\theta  = \\frac{{ - 25}}{7} + \\frac{{ - 24}}{7} =  - 7\\)',CURRENT_TIMESTAMP);
