INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(5\\tan \\theta  = 4,\\) then \\(\\frac{{5\\sin \\theta  - 3\\cos \\theta}}{{5\\sin \\theta  + 2\\cos \\theta }} = \\)','\(a\)0','\(b\)1','\(c\)1/6','\(d\)6','3','Trigonometrical RFI','2','Answer = \(c\)\\(5\\tan \\theta  = 4 \\Rightarrow \\tan \\theta  = \\frac{4}{5}\\)\\(\\therefore \\sin \\theta  = \\frac{4}{{\\sqrt {41} }}\\)and \\(\\cos \\theta  =\\frac{5}{{\\sqrt {41} }}\\)\\(\\frac{{5\\sin \\theta  - 3\\cos \\theta }}{{5\\sin \\theta  + 2\\cos \\theta }} =\\frac{{5 \\times \\frac{4}{{\\sqrt {41} }} - 3 \\times \\frac{5}{{\\sqrt {41} }}}}{{5\\times \\frac{4}{{\\sqrt {41} }} + 2 \\times \\frac{5}{{\\sqrt {41} }}}}\\)\\(\\frac{{20 - 15}}{{20 + 10}} = \\frac{5}{{30}} = \\frac{1}{6}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan \\theta  = \\frac{{20}}{{21}},\\) cos\\(\\theta{}\\)  will be','\(a\)\\( \\pm \\frac{{20}}{{41}}\\)','\(b\)\\( \\pm \\frac{1}{{21}}\\)','\(c\)\\( \\pm \\frac{{21}}{{29}}\\)','\(d\)\\( \\pm \\frac{{20}}{{21}}\\)','3','Trigonometrical RFI','2','Answer = \(c\)\\(\\tan \\theta  = \\frac{{20}}{{21}} \\Rightarrow \\cos \\theta  =  \\pm\\frac{{21}}{{29}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin x = \\frac{{ - 24}}{{25}},\\) then the value of \\(\\tan x\\) is','\(a\)\\(\\frac{{24}}{{25}}\\)','\(b\)\\(\\frac{{ - 24}}{7}\\)','\(c\)\\(\\frac{{25}}{{24}}\\)','\(d\)None of these','2','Trigonometrical RFI','2','Answer = \(b\)\\(\\cos x = \\sqrt {1 - {{\\sin }^2}x}  = \\sqrt {1 - {{\\left( {\\frac{{ -24}}{{25}}} \\right)}^2}}  = \\frac{7}{{25}}\\)\\(\\Rightarrow \\tan x = \\frac{{\\sin x}}{{\\cos x}} = \\frac{{ - 24}}{7}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan \\theta  = \\frac{{ - 4}}{3},\\)then\\(\\sin \\theta  = \\)','\(a\)- 4/5 but not 4/5','\(b\)- 4/5 or 4/5','\(c\)4/5 but not - 4/5','\(d\)None of these','2','Trigonometrical RFI','2','Answer = \(b\)Since \\({\\rm{cose}}{{\\rm{c}}^2}\\theta  = 1 + {\\cot ^2}\\theta  = 1 +\\frac{9}{{16}} = \\frac{{25}}{{16}}\\)\\(\\left( {\\tan \\theta  =  - \\frac{4}{3}} \\right)\\)\\({\\sin ^2}\\theta  = \\frac{1}{{{\\rm{cose}}{{\\rm{c}}^2}\\theta }} =\\frac{{16}}{{25}} \\Rightarrow \\sin \\theta  =  \\pm \\frac{4}{5},\\)Both the values are acceptable, since \\(\\tan \\theta  =  - \\frac{4}{3}\\,\\,\\)\\(\\,i.e.,\\theta \\) lies in \\({2^{nd}}\\)or \\({4^{th}}\\)quadrant.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\theta  =  - \\frac{1}{{\\sqrt 2 }}\\) and \\(\\tan \\theta  = 1,\\) then\\(\\theta \\) lies in which quadrant','\(a\)First','\(b\)Second','\(c\)Third','\(d\)Fourth','3','Trigonometrical RFI','2','Answer = \(c\)\\(\\sin \\theta  =  - \\frac{1}{{\\sqrt 2 }}\\)and \\(\\tan \\theta  = 1\\)\\( \\Rightarrow \\)\\(\\sin \\theta  = \\sin Answer = \\circ  \\Rightarrow \\theta  = Answer = \\circ\\)Since \\(\\sin \\theta \\)is \\( - ve\\)and \\(\\tan \\theta \\)is \\( + ve\\)in thirdquadrant.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\theta  = \\frac{{ - 4}}{5}\\) and \\(\\theta \\) lies in the thirdquadrant, then \\(\\cos \\frac{\\theta }{2} = \\)','\(a\)\\(\\frac{1}{{\\sqrt 5 }}\\)','\(b\)\\( - \\frac{1}{{\\sqrt 5 }}\\)','\(c\)\\(\\sqrt {\\frac{2}{5}} \\)','\(d\)\\( - \\sqrt {\\frac{2}{5}} \\)','2','Trigonometrical RFI','2','Answer = \(b\)Given that \\(\\sin \\theta  =  - \\frac{4}{5}\\)and \\(\\theta \\)lies in theIII quadrant.\\(\\Rightarrow \\cos \\theta  = \\sqrt {1 - \\frac{{16}}{{25}}}  =  \\pm \\frac{3}{5}\\)\\(\\cos \\frac{\\theta }{2} =  \\pm \\sqrt {\\frac{{1 + \\cos \\theta }}{2}}  = \\sqrt{\\frac{{1 - 3/5}}{2}}  =  \\pm \\sqrt {\\frac{1}{5}}\\)But \\(\\cos \\frac{\\theta }{2} =  - \\frac{1}{{\\sqrt 5 }}.\\)since \\(\\frac{\\theta}{2}\\)will be in II quadrant.Hence \\(\\cos \\frac{\\theta }{2} =  - \\frac{1}{{\\sqrt 5 }}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin (\\alpha  - \\beta ) = \\frac{1}{2}\\)and \\(\\cos (\\alpha  + \\beta ) =\\frac{1}{2},\\)where \\(\\alpha \\) and \\(\\beta \\) are positive acute angles, then','\(a\)\\(\\alpha  = 45^\\circ ,\\beta  = 15^\\circ \\)','\(b\)\\(\\alpha  = 15^\\circ ,\\beta  =45^\\circ \\)','\(c\)\\(\\alpha  = 60^\\circ ,\\beta  = 15^\\circ \\)','\(d\)None of these','1','Trigonometrical RFI','2','Answer = \(a\)\\(\\sin (\\alpha  - \\beta ) = \\frac{1}{2} = \\sin 30^\\circ  \\Rightarrow\\alpha  - \\beta  = 30^\\circ \\)\\(\\ldots{} \\)..(i)and \\(\\cos (\\alpha  + \\beta ) = \\frac{1}{2} \\Rightarrow \\alpha  + \\beta  =60^\\circ \\)\\(\\ldots{} \\)..(ii)Solving (i) and (ii), we get \\(\\alpha  = 45^\\circ \\)and \\(\\beta  = 15^\\circ \\).Trick :  In such type of problems, students should satisfy the given conditionswith the values given in the options. Here \\(\\alpha  = 45^\\circ \\)and \\(\\beta  =15^\\circ \\)satisfy both the conditions.',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan \\theta  =  - \\frac{1}{{\\sqrt {10} }}\\)and \\(\\theta \\) lies in thefourth quadrant, then \\(\\cos \\theta  = \\)','\(a\)\\(1/\\sqrt {11} \\)\(b\)\\( - 1/\\sqrt {11} \\)\(c\)\\(\\sqrt {\\frac{{10}}{{11}}} \\)\(d\)\\( - \\sqrt {\\frac{{10}}{{11}}} \\)526.\\((m + 2)\\sin \\theta  + (2m - 1)\\cos \\theta  = 2m + 1,\\)if','','\(b\)\\( - 1/\\sqrt {11} \\)\(c\)\\(\\sqrt {\\frac{{10}}{{11}}} \\)\(d\)\\( - \\sqrt {\\frac{{10}}{{11}}} \\)526.\\((m + 2)\\sin \\theta  + (2m - 1)\\cos \\theta  = 2m + 1,\\)if\(a\)\\(\\tan \\theta  = \\frac{3}{4}\\)','\(b\)\\(\\tan \\theta  = \\frac{4}{3}\\)\(c\)\\(\\tan \\theta  = \\frac{{2m}}{{{m^2} + 1}}\\)\(d\)None of these','3','Trigonometrical RFI','2','Answer = \(c\)We have \\(\\tan \\theta  =  - \\frac{1}{{\\sqrt {10} }},\\)therefore \\(\\theta\\)is in IV quadrant. So \\(\\cos \\theta  =  + ve\\).Now \\(1 + {\\tan ^2}\\theta  = {\\sec ^2}\\theta  \\Rightarrow 1 + \\frac{1}{{10}} ={\\sec ^2}\\theta \\)\\(\\Rightarrow {\\sec ^2}\\theta  = \\frac{{11}}{{10}} \\Rightarrow \\cos \\theta  =\\sqrt {\\left( {\\frac{{10}}{{11}}} \\right)} .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(A\\) lies in the second quadrant and \\(3\\tan A + 4 = 0,\\) the value of\\(2\\cot A - 5\\cos A + \\sin A\\)is equal to','\(a\)\\(\\frac{{ - 53}}{{10}}\\)','\(b\)\\(\\frac{{ - 7}}{{10}}\\)','\(c\)\\(\\frac{7}{{10}}\\)','\(d\)\\(\\frac{{23}}{{10}}\\)','4','Trigonometrical RFI','2','Answer = \(d\)\\(3\\,\\tan A + 4 = 0\\, \\Rightarrow \\,\\tan A =  - \\frac{4}{3}\\)\\(\\Rightarrow \\,\\,\\sin A\\, =  \\pm \\,\\frac{{\\tan A}}{{\\sqrt {1 + {{\\tan }^2}A} }}=  \\pm \\frac{{ - 4/3}}{{\\sqrt {1 + 16/9} }} = \\frac{4}{5}\\)is in 2\\(^{nd}\\) quadrant)and \\(\\cos \\,A =  - \\frac{3}{5}\\). Thus, \\(2\\cot A - 5\\cos A + \\sin A\\)          \\( = 2\\,\\left( { - \\frac{3}{4}} \\right) - 5\\,\\left( { - \\frac{3}{5}} \\right) +\\frac{4}{5} = \\frac{{23}}{{10}}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin x + \\sin y = 3(\\cos y - \\cos x),\\) then the value of \\(\\frac{{\\sin3x}}{{\\sin 3y}}\\) is','\(a\)1','\(b\)- 1','\(c\)0','\(d\)None of these','2','Trigonometrical RFI','2','Answer = \(b\)We have \\(\\sin x + \\sin y = 3\\,(\\cos y - \\cos x)\\)\\( \\Rightarrow \\,\\sin x + 3\\cos x = 3\\cos y - \\sin y\\)\\(\\ldots{} \\)..(i)\\(\\Rightarrow \\,r\\cos \\,(x - \\alpha ) = r\\cos \\,(y + \\alpha ),\\)where \\(r = \\sqrt {10} ,\\,\\tan \\alpha  = \\frac{1}{3}\\)\\( \\Rightarrow \\,x - \\alpha  =  \\pm (y + \\alpha )\\, \\Rightarrow \\,x =  - y\\) or\\(x + y = 2\\alpha \\)Clearly, \\(x =  - y\\)satisfies (i); \\(\\therefore \\;\\frac{{\\sin \\,3x}}{{\\sin\\,3y}} = \\frac{{ - \\sin \\,3y}}{{\\sin \\,3y}} =  - 1\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin A,\\cos A\\) and \\(\\tan A\\) are in G.P., then \\({\\cos ^3}A + {\\cos^2}A\\) is equal to','\(a\)1','\(b\)2','\(c\)4','\(d\)None of these','1','Trigonometrical RFI','2','Answer = \(a\)We have \\(\\sin A,\\,\\cos A\\) and \\(\\tan A\\) are in G.P.\\({\\cos ^2}A = \\sin A\\,\\tan A = \\frac{{{{\\sin }^2}A}}{{\\cos A}}\\, \\Rightarrow\\,{\\cos ^3}A - {\\sin ^2}A = 0\\)Hence \\({\\cos ^3}A + {\\cos ^2}A = {\\sin ^2}A + {\\cos ^2}A = 1\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\theta \\) lies in the second quadrant, then the value of \\(\\sqrt{\\left( {\\frac{{1 - \\sin \\theta }}{{1 + \\sin \\theta }}} \\right)}  + \\sqrt {\\left({\\frac{{1 + \\sin \\theta }}{{1 - \\sin \\theta }}} \\right)} \\)','\(a\)\\(2\\sec \\theta \\)\(b\)\\( - 2\\sec \\theta \\)\(c\)\\(2{\\rm{cosec}}\\theta \\)\(d\)None of these531.\\(\\frac{{\\sin \\theta }}{{1 - \\cot \\theta }} + \\frac{{\\cos \\theta }}{{1 -\\tan \\theta }} = \\)    ','','\(b\)\\( - 2\\sec \\theta \\)\(c\)\\(2{\\rm{cosec}}\\theta \\)\(d\)None of these531.\\(\\frac{{\\sin \\theta }}{{1 - \\cot \\theta }} + \\frac{{\\cos \\theta }}{{1 -\\tan \\theta }} = \\)    \(a\)0','\(b\)1\(c\)\\(\\cos \\theta  - \\sin \\theta \\)\(d\)\\(\\cos \\theta  + \\sin \\theta \\)','2','Trigonometrical RFI','2','Answer = \(b\)\\(\\sqrt {\\left( {\\frac{{1 - \\sin \\theta }}{{1 + \\sin \\theta }}} \\right)} + \\sqrt {\\left( {\\frac{{1 + \\sin \\theta }}{{1 - \\sin \\theta }}} \\right)} \\) isthe sum of two positive quantities and hence the result must be positive. But for\\(\\frac{\\pi }{2} < \\theta  < \\pi ,\\) we have the sum equal to \\(\\frac{{1 - \\sin\\theta  + 1 + \\sin \\theta }}{{\\sqrt {1 - {{\\sin }^2}\\theta } }} = \\frac{2}{{\\cos\\theta }};\\) which is negative.( \\(\\cos \\theta \\) is negative for \\(\\theta{}\\) lying in 2\\(^{nd}\\) quadrant).So the required positive value \\( = \\frac{{ - 2}}{{\\cos \\theta }} =  - 2\\,\\sec\\theta ,\\,\\left( {\\frac{\\pi }{2} < \\theta  < \\pi } \\right)\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\tan \\theta  + \\sec \\theta  = {e^x},\\)then \\(\\cos \\theta \\) equals','\(a\)\\(\\frac{{({e^x} + {e^{ - x}})}}{2}\\)','\(b\)\\(\\frac{2}{{({e^x} + {e^{ - x}})}}\\)','\(c\)\\(\\frac{{({e^x} - {e^{ - x}})}}{2}\\)','\(d\)\\(\\frac{{({e^x} - {e^{ -x}})}}{{({e^x} + {e^{ - x}})}}\\)','2','Trigonometrical RFI','2','Answer = \(b\)\\(\\tan \\theta  + \\sec \\theta  = {e^x}\\)\\(\\ldots{} \\)..(i)\\(\\therefore \\,\\,\\,\\sec \\theta  - \\tan \\theta  = {e^{ - x}}\\)\\(\\ldots{} \\)..(ii)From (i) and (ii),\\(\\,2\\sec \\theta  = {e^x} + {e^{ - x}}\\, \\Rightarrow \\,\\cos \\theta  =\\frac{2}{{{e^x} + {e^{ - x}}}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\cos \\theta  - \\sin \\theta  = \\sqrt 2 \\sin \\theta ,\\)then \\(\\cos \\theta + \\sin \\theta \\)is equal to','\(a\)\\(\\sqrt 2 \\cos \\theta \\)','\(b\)\\(\\sqrt 2 \\sin \\theta \\)','\(c\)\\(2\\cos \\theta \\)','\(d\)\\( - \\sqrt 2 \\cos \\theta \\)','1','Trigonometrical RFI','2','Answer = \(a\)We have \\(\\cos \\theta  - \\sin \\theta  = \\sqrt 2 \\,\\sin \\theta \\)\\(\\Rightarrow \\,\\cos \\theta  = (\\sqrt 2  + 1)\\,\\sin \\theta \\, \\Rightarrow\\,(\\sqrt 2  - 1)\\cos \\theta  = \\sin \\theta\\)\\(\\Rightarrow \\,\\sqrt 2 \\,\\cos \\theta  - \\cos \\theta  = \\sin \\theta  \\Rightarrow\\,\\sin \\theta  + \\cos \\theta  = \\sqrt 2 \\,\\cos \\theta .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sec \\theta  + \\tan \\theta  = p,\\)then \\(\\tan \\theta \\)is equal to','\(a\)\\(\\frac{{2p}}{{{p^2} - 1}}\\)','\(b\)\\(\\frac{{{p^2} - 1}}{{2p}}\\)','\(c\)\\(\\frac{{{p^2} + 1}}{{2p}}\\)','\(d\)\\(\\frac{{2p}}{{{p^2} + 1}}\\)','2','Trigonometrical RFI','2','Answer = \(b\)\\(\\sec \\theta  + \\tan \\theta  = p\\,\\, \\Rightarrow \\,\\sec \\,\\theta  - \\tan\\theta  = \\frac{1}{p}\\)Subtracting second from first, we get \\(2\\tan \\theta  = p - \\frac{1}{p}\\)\\(\\Rightarrow \\,\\tan \\theta  = \\frac{{{p^2} - 1}}{{2p}}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(x = \\sec \\theta  + \\tan \\theta ,\\)then \\(x + \\frac{1}{x} = \\)','\(a\)1','\(b\)\\(2\\sec \\theta \\)','\(c\)2','\(d\)\\(2\\tan \\theta \\)','2','Trigonometrical RFI','2','Answer = \(b\)Given that \\(x = \\sec \\theta  + \\tan \\theta \\)\\(\\Rightarrow \\,x + \\frac{1}{x} = \\sec \\theta  + \\tan \\theta  + \\frac{1}{{\\sec\\theta  + \\tan \\theta }}\\)\\(= \\sec \\theta  + \\tan \\theta  + \\sec \\theta  - \\tan \\theta  = 2\\sec \\theta\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(x + \\frac{1}{x} = 2\\cos \\alpha \\), then \\({x^n} + \\frac{1}{{{x^n}}} =\\)','\(a\)\\({2^n}\\cos \\alpha \\)','\(b\)\\({2^n}\\cos n\\alpha \\)','\(c\)\\(2i\\,\\sin \\,n\\,\\alpha \\)','\(d\)\\(2\\cos \\,n\\alpha \\)','4','Trigonometrical RFI','2','Answer = \(d\)We have, \\(x + \\frac{1}{x} = 2\\cos \\alpha \\)\\({x^2} + \\frac{1}{{{x^2}}} + 2 = 4{\\cos ^2}\\alpha .\\)\\({x^2} + \\frac{1}{{{x^2}}} = 4{\\cos ^2}\\alpha  - 2,\\)\\({x^2} + \\frac{1}{{{x^2}}} = 2(2{\\cos ^2}\\alpha  - 1) = 2\\cos 2\\alpha\\)Similarly \\({x^n} + \\frac{1}{{{x^n}}} = 2\\cos \\,n\\alpha \\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\cos \\theta  = \\frac{1}{2}\\left( {x + \\frac{1}{x}} \\right)\\), then\\(\\frac{1}{2}\\left( {{x^2} + \\frac{1}{{{x^2}}}} \\right) = \\)','\(a\)\\(\\sin 2\\theta \\)','\(b\)\\(\\cos \\,2\\theta \\)','\(c\)\\(\\tan \\,2\\theta \\)','\(d\)\\(\\sec \\,2\\theta \\)','2','Trigonometrical RFI','2','Answer = \(b\)Given that   \\(\\cos \\theta  = \\frac{1}{2}\\,\\left( {x + \\frac{1}{x}}\\right)\\,\\, \\Rightarrow \\,x + \\frac{1}{x} = 2\\,\\cos \\theta \\)We know that \\({x^2} + \\frac{1}{{{x^2}}} = {\\left( {x + \\frac{1}{x}} \\right)^2}- 2\\)\\(= {(2\\cos \\theta )^2} - 2 = 4\\,{\\cos ^2}\\theta  - 2 = 2\\,\\cos \\,\\,2\\theta\\)\\(\\therefore \\,\\,\\frac{1}{2}\\,\\left( {{x^2} + \\frac{1}{{{x^2}}}} \\right) =\\frac{1}{2} \\times 2\\,\\cos \\,2\\theta  = \\cos \\,2\\theta\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\({e^{{{\\log }_{10}}\\tan 1^\\circ  + {{\\log }_{10}}\\tan 2^\\circ + {{\\log }_{10}}\\tan 3^\\circ  + ........... + {{\\log }_{10}}\\tan 89^\\circ }}\\) is','\(a\)0\(b\)e\(c\)1/e\(d\)None of these539.\\(\\cot x - \\tan x = \\)      ','','\(b\)e\(c\)1/e\(d\)None of these539.\\(\\cot x - \\tan x = \\)      \(a\)\\(\\frac{{\\sin \\,\\theta }}{{1 + \\tan \\theta }}\\)','\(b\)\\(\\frac{{2\\,\\sin \\theta}}{{1 + \\tan \\theta }}\\)\(c\)\\(\\frac{{2\\sin \\theta }}{{{{(1 + \\tan \\theta )}^2}}}\\)\(d\)None of these','4','Trigonometrical RFI','2','Answer = \(d\) We have\\({e^{{{\\log }_{10}}\\tan \\,\\,{1^o} + {{\\log }_{10}}\\tan \\,\\,{2^o} + {{\\log}_{10}}\\,\\tan \\,\\,{3^o} + .......... + {{\\log }_{10}}\\,\\tan \\,\\,{{89}^o}}}\\)\\(= {e^{{{\\log }_{10}}\\,(\\tan \\,\\,{1^o}\\,\\tan \\,\\,{2^o}\\,\\,\\tan\\,\\,{3^o}.....\\tan \\,\\,{{89}^o})}} = {e^{{{\\log }_{10}}\\,\\,1}} = {e^o} = 1\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of the expression\\(1 - \\frac{{{{\\sin }^2}y}}{{1 + \\cos \\,y}} + \\frac{{1 + \\cos \\,y}}{{\\sin \\,y}}- \\frac{{\\sin \\,\\,y}}{{1 - \\cos \\,y}}\\) is equal to','\(a\)0','\(b\)1','\(c\)\\(\\sin \\,y\\)','\(d\)\\(\\cos \\,y\\)','4','Trigonometrical RFI','2','Answer = \(d\)The expression can be written as\\(\\frac{{1 + \\cos y - {{\\sin }^2}y}}{{1 + \\cos y}} + \\frac{{(1 - {{\\cos }^2}y) -{{\\sin }^2}y}}{{\\sin y\\,(1 - \\cos y)}}\\)\\(= \\frac{{\\cos y\\,(1 + \\cos y)}}{{1 + \\cos y}} + 0 = \\cos y.\\)',CURRENT_TIMESTAMP);
