INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(\\sin \\left( {{{\\sin }^{ - 1}}\\frac{1}{5} + {{\\cos }^{ - 1}}x} \\right) =1\\),then x is equal to','\(a\)1','\(b\)0','\(c\)\\(\\frac{4}{5}\\)','\(d\)\\(\\frac{1}{5}\\)','4','INVERSE TRIGONOMETRIC FUNCTIONS','2','Answer = \(d\) \\({\\sin ^{ - 1}}\\frac{1}{5} + {\\cos ^{ - 1}}x = \\frac{\\pi }{2}\\)\\(\\therefore \\,\\,{\\sin ^{ - 1}}\\frac{1}{5} = \\frac{\\pi }{2} - {\\cos ^{ - 1}}x ={\\sin ^{ - 1}}x\\)\\(\\therefore \\,\\,\\,x = \\frac{1}{5}.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({\\sin ^{ - 1}}x = \\theta  + \\beta \\)and \\({\\sin ^{ - 1}}y = \\theta  -\\beta ,\\)then \\(1 + xy = \\)','\(a\)\\({\\sin ^2}\\theta  + {\\sin ^2}\\beta \\)','\(b\)\\({\\sin ^2}\\theta  + {\\cos ^2}\\beta\\)','\(c\)\\({\\cos ^2}\\theta  + {\\cos ^2}\\beta \\)','\(d\)\\({\\cos ^2}\\theta  + {\\sin ^2}\\beta\\)','2','INVERSE TRIGONOMETRIC FUNCTIONS','2','Answer = \(b\) Obviously \\(x = \\sin \\,(\\theta  + \\beta )\\) and \\(y = \\sin \\,(\\theta  -\\beta )\\)\\(\\therefore \\,\\,1 + xy = 1 + \\sin \\,(\\theta  + \\beta )\\sin \\,(\\theta  - \\beta )\\)\\(= 1 + {\\sin ^2}\\theta  - {\\sin ^2}\\beta  = {\\sin ^2}\\theta  + {\\cos ^2}\\beta .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\({\\sin ^{ - 1}}\\frac{1}{3} + {\\sin ^{ - 1}}\\frac{2}{3} = {\\sin ^{ -1}}x,\\)then x is equal to','\(a\)\\(\\frac{{\\sqrt {1 - {x^2}} }}{x}\\)','\(b\)\\(\\frac{x}{{1 + {x^2}}}\\)','\(c\)\\(\\frac{{\\sqrt {1 + {x^2}} }}{x}\\)','\(d\)\\(\\sqrt {1 - {x^2}} \\)','3','INVERSE TRIGONOMETRIC FUNCTIONS','2','Answer = \(c\) \\({\\sin ^{ - 1}}\\frac{1}{3} + {\\sin ^{ - 1}}\\frac{2}{3}\\)\\(= {\\sin ^{ - 1}}\\left[ {\\frac{1}{3}\\sqrt {1 - \\frac{4}{9}}  + \\frac{2}{3}\\sqrt{1 - \\frac{1}{9}} } \\right] = {\\sin ^{ - 1}}\\,\\left[ {\\frac{{\\sqrt 5  + 4\\sqrt 2}}{9}} \\right]\\)Therefore \\(x = \\frac{{\\sqrt 5  + 4\\sqrt 2 }}{9}\\).',CURRENT_TIMESTAMP);
