INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of \\({\\sin ^{ - 1}}(\\sin 10)\\)is','\(a\)10','\(b\)\\(10 - 3\\pi \\)','\(c\)\\(3\\pi  - 10\\)','\(d\)None of these','3','INVERSE TRIGONOMETRIC FUNCTIONS','2','Answer = \(c\) Since \\(3\\pi  < 10 < 3\\pi  + \\frac{\\pi }{2}\\,\\, \\Rightarrow \\,0 < 10 -3\\pi  < \\frac{\\pi }{2}\\)\\( \\Rightarrow \\,\\,\\frac{{ - \\pi }}{2} < 3\\pi  - 10 < 0\\)\\(\\Rightarrow \\,\\,{\\sin ^{ - 1}}\\left\\{ {\\sin \\,(3\\pi  - 10)} \\right\\} = 3\\pi  -10.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The smallest and the largest values of \\({\\tan ^{ - 1}}\\left( {\\frac{{1 -x}}{{1 + x}}} \\right){\\rm{  }},\\,\\,0 \\le x \\le 1\\)are','\(a\)\\(0,\\,\\,\\pi \\)','\(b\)\\(0,\\,\\frac{\\pi }{4}\\)','\(c\)\\( - \\frac{\\pi }{4},\\frac{\\pi }{4}\\)','\(d\)\\(\\frac{\\pi }{4},\\,\\frac{\\pi }{2}\\)','2','INVERSE TRIGONOMETRIC FUNCTIONS','2','Answer = \(b\) We have,\\({\\tan ^{ - 1}}\\,\\left( {\\frac{{1 - x}}{{1 + x}}} \\right) = {\\tan ^{ - 1}}1 -{\\tan ^{ - 1}}x = \\frac{\\pi }{4} - {\\tan ^{ - 1}}x\\)Since \\(0 \\le x \\le 1\\,\\, \\Rightarrow \\,\\,0 \\le {\\tan ^{ - 1}}x \\le \\frac{\\pi}{4}\\)\\(\\Rightarrow \\,\\,0 \\ge  - {\\tan ^{ - 1}}x \\ge \\frac{{ - \\pi }}{4} \\Rightarrow\\,\\,\\frac{\\pi }{4} \\ge \\frac{\\pi }{4} - {\\tan ^{ - 1}}x \\ge 0\\)\\(\\Rightarrow \\,\\,\\frac{\\pi }{4} \\ge {\\tan ^{ - 1}}\\left( {\\frac{{1 - x}}{{1 +x}}} \\right) \\ge 0.\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','If \\(x\\)takes non-positive permissible value, then \\({\\sin ^{ - 1}}x\\)=','\(a\)\\({\\cos ^{ - 1}}\\sqrt {1 - {x^2}} \\)\(b\)\\( - {\\cos ^{ - 1}}\\sqrt {1 - {x^2}}\\)\(c\)\\({\\cos ^{ - 1}}\\sqrt {{x^2} - 1} \\)\(d\)\\(\\pi  - {\\cos ^{ - 1}}\\sqrt {1 -{x^2}} \\)533.\\({\\left','','\(b\)\\( - {\\cos ^{ - 1}}\\sqrt {1 - {x^2}}\\)\(c\)\\({\\cos ^{ - 1}}\\sqrt {{x^2} - 1} \\)\(d\)\\(\\pi  - {\\cos ^{ - 1}}\\sqrt {1 -{x^2}} \\)533.\\({\\left\(a\)\\(\\frac{3}{5}\\)','\(b\)\\(\\frac{5}{3}\\)\(c\)\\(\\frac{9}{{25}}\\)\(d\)\\(\\frac{{25}}{9}\\)','2','INVERSE TRIGONOMETRIC FUNCTIONS','2','Answer = \(b\) Let \\({\\sin ^{ - 1}}x = y.\\) Then \\(x = \\sin y\\)Since \\( - 1 \\le x \\le 0,\\) therefore \\(\\frac{{ - \\pi }}{2} \\le {\\sin ^{ - 1}}x\\le 0\\) and so \\(\\frac{{ - \\pi }}{2} \\le y \\le 0\\)We have \\(\\cos y = \\sqrt {1 - {{\\sin }^2}y} \\)\\( \\Rightarrow \\,\\,\\cos y = \\sqrt {1 - {x^2}} \\), for \\(0 \\le y \\le \\pi \\)                 \\(\\ldots{} \\)..(i)Now \\( - \\frac{\\pi }{2} \\le y \\le 0\\,\\, \\Rightarrow \\,\\,\\frac{\\pi }{2} \\ge  - y\\ge 0\\)\\( \\Rightarrow \\,\\,\\cos \\,\\left( { - y} \\right) = \\sqrt {1 - {x^2}} \\) \\{from(i)\\}\\(\\Rightarrow \\,\\, - y = {\\cos ^{ - 1}}\\sqrt {1 - {x^2}} \\,\\, \\Rightarrow \\,\\,y = - {\\cos ^{ - 1}}\\sqrt {1 - {x^2}} .\\)',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The principal value of \\({\\sin ^{ - 1}}\\left( {\\sin \\frac{{5\\pi }}{3}}\\right)\\)is ','\(a\)\\(\\frac{{5\\pi }}{3}\\)','\(b\)\\( - \\frac{{5\\pi }}{3}\\)','\(c\)\\( - \\frac{\\pi }{3}\\)','\(d\)\\(\\frac{{4\\pi }}{3}\\)','3','INVERSE TRIGONOMETRIC FUNCTIONS','2','Answer = \(c\) \\({\\sin ^{ - 1}}\\,\\left( {\\sin \\frac{{5\\pi }}{3}} \\right) = {\\sin ^{ -1}}\\,\\left( { - \\frac{{\\sqrt 3 }}{2}} \\right) =  - \\frac{\\pi }{3}\\).',CURRENT_TIMESTAMP);
INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The value of x which satisfies the equation \\({\\tan ^{ - 1}}x = \\) \\({\\sin^{ - 1}}\\left( {\\frac{3}{{\\sqrt {10} }}} \\right)\\) is','\(a\)\\({\\rm{cosec}}({\\sec ^{ - 1}}x)\\)','\(b\)\\(\\cot x\\)','\(c\)\\(\\pi \\)','\(d\)None of these','1','INVERSE TRIGONOMETRIC FUNCTIONS','2','Answer = \(a\) Given, \\({\\tan ^{ - 1}}x = {\\sin ^{ - 1}}\\left[ {\\frac{3}{{\\sqrt {10}}}} \\right]\\)\\(\\Rightarrow{}\\) \\(x = \\tan \\left\\{ {{{\\sin }^{ - 1}}\\left[ {\\frac{3}{{\\sqrt{10} }}} \\right]} \\right\\}\\, = \\tan \\,\\,\\{ {\\tan ^{ - 1}}3\\} \\)\\(\\Rightarrow{}\\) x = 3.',CURRENT_TIMESTAMP);
