INSERT INTO `question`(`section`, `question_content`, `a`, `b`, `c`, `d`, `ans`, `sub_section`, `level`, `solution`, `date_posted`) VALUES ('Maths_finished files','The solution set of the equation \\({\\sin ^{ - 1}}x = 2{\\tan ^{ - 1}}x\\) is','\(a\)\\{1, 2\\}','\(b\)\\{-1, 2\\}','\(c\)\\{-1,1, 0\\}','\(d\)\\{1, 1/2, 0\\}','3','INVERSE TRIGONOMETRIC FUNCTIONS','2','Answer = \(c\) \\({\\sin ^{ - 1}}x = 2{\\tan ^{ - 1}}x\\) \\(\\Rightarrow{}\\) \\({\\sin ^{ -1}}x = {\\sin ^{ - 1}}\\frac{{2x}}{{1 + {x^2}}}\\)\\( \\Rightarrow \\frac{{2x}}{{1 + {x^2}}} = x\\)              \\(\\Rightarrow{}\\)\\({x^3} - x = 0\\)\\(\\Rightarrow{}\\)  \\(x(x + 1)(x - 1) = 0\\)  \\(\\Rightarrow{}\\) \\(x = \\left\\{ { -1,\\,\\,1,\\,\\,0} \\right\\}\\).',CURRENT_TIMESTAMP);
